'use client'

import React from 'react'
import { VehicleActivityLog } from './VehicleActivityLog'
import { UserActivityLog } from './UserActivityLog'
import { logVehicleActivity, logUserActivity, getClientInfo } from '../../utils/activityLogger'
import { VehicleAction, UserAction } from '../../types/logs'

/**
 * Integration component showing how to use the activity log system
 * with existing fleet management components
 */
export function ActivityLogIntegration() {
  // Example: Log vehicle deletion when a vehicle is deleted
  const handleVehicleDelete = async (vehicleId: string, vehicleRegistration: string, deletedBy: string) => {
    try {
      const clientInfo = getClientInfo()
      
      await logVehicleActivity(
        vehicleId,
        VehicleAction.DELETED,
        deletedBy,
        {
          notes: `Vehicle ${vehicleRegistration} deleted`,
          ...clientInfo,
          metadata: {
            vehicle_registration: vehicleRegistration,
            deletion_reason: 'User requested',
            timestamp: new Date().toISOString()
          }
        }
      )
      
      console.log('Vehicle deletion logged successfully')
    } catch (error) {
      console.error('Failed to log vehicle deletion:', error)
    }
  }

  // Example: Log user login activity
  const handleUserLogin = async (userId: string, loginMethod: string = 'email') => {
    try {
      const clientInfo = getClientInfo()
      
      await logUserActivity(
        userId,
        UserAction.LOGIN,
        'auth',
        'login',
        {
          ...clientInfo,
          metadata: {
            login_method: loginMethod,
            session_start: new Date().toISOString()
          }
        }
      )
      
      console.log('User login logged successfully')
    } catch (error) {
      console.error('Failed to log user login:', error)
    }
  }

  // Example: Log data export activity
  const handleDataExport = async (userId: string, exportType: string, recordCount: number) => {
    try {
      const clientInfo = getClientInfo()
      
      await logUserActivity(
        userId,
        UserAction.EXPORT,
        'data',
        exportType,
        {
          ...clientInfo,
          metadata: {
            export_type: exportType,
            record_count: recordCount,
            export_format: 'csv',
            timestamp: new Date().toISOString()
          }
        }
      )
      
      console.log('Data export logged successfully')
    } catch (error) {
      console.error('Failed to log data export:', error)
    }
  }

  return (
    <div className="space-y-6">
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
        <h3 className="text-sm font-medium text-blue-900 mb-2">Integration Examples</h3>
        <p className="text-sm text-blue-800 mb-4">
          This component demonstrates how to integrate the activity log system with your existing components.
        </p>
        
        <div className="space-y-2">
          <button
            onClick={() => handleVehicleDelete('vehicle-123', 'MH12AB1234', 'John Doe')}
            className="px-3 py-1 bg-blue-600 text-white text-xs rounded hover:bg-blue-700"
          >
            Log Vehicle Deletion
          </button>
          
          <button
            onClick={() => handleUserLogin('user-123', 'email')}
            className="px-3 py-1 bg-green-600 text-white text-xs rounded hover:bg-green-700 ml-2"
          >
            Log User Login
          </button>
          
          <button
            onClick={() => handleDataExport('user-123', 'vehicle_data', 150)}
            className="px-3 py-1 bg-purple-600 text-white text-xs rounded hover:bg-purple-700 ml-2"
          >
            Log Data Export
          </button>
        </div>
      </div>

      {/* Activity Log Components */}
      <VehicleActivityLog limit={10} />
      <UserActivityLog limit={10} />
    </div>
  )
}

/**
 * Higher-order component to automatically log activities
 */
export function withActivityLogging<T extends object>(
  WrappedComponent: React.ComponentType<T>,
  logConfig: {
    onMount?: (props: T) => void
    onUnmount?: (props: T) => void
    onAction?: (action: string, props: T) => void
  }
) {
  return function ActivityLoggedComponent(props: T) {
    React.useEffect(() => {
      if (logConfig.onMount) {
        logConfig.onMount(props)
      }
      
      return () => {
        if (logConfig.onUnmount) {
          logConfig.onUnmount(props)
        }
      }
    }, [])

    return <WrappedComponent {...props} />
  }
}

/**
 * Hook for automatic activity logging
 */
export function useActivityLogging(userId: string) {
  const logActivity = React.useCallback(async (
    action: UserAction | string,
    resource?: string,
    resourceId?: string,
    metadata?: Record<string, any>
  ) => {
    try {
      await logUserActivity(userId, action, resource, resourceId, {
        metadata: {
          ...metadata,
          timestamp: new Date().toISOString()
        }
      })
    } catch (error) {
      console.error('Failed to log activity:', error)
    }
  }, [userId])

  return { logActivity }
}

import React, { useState, useEffect } from 'react';
import { AuditTrailLogger, AuditTrailEntry, AuditSearchFilters, AuditTrailStats } from '../../utils/auditTrailLogger';
import { 
  User, AlertTriangle, Info, CheckCircle, XCircle, Filter, 
  Download, BarChart3, Activity, Shield, Eye, RefreshCw, 
  FileText, Settings, TrendingUp, AlertCircle
} from 'lucide-react';
import { toast } from 'react-toastify';

interface AuditTrailDashboardProps {
  className?: string;
}

const AuditTrailDashboard: React.FC<AuditTrailDashboardProps> = ({ className = '' }) => {
  const [auditStats, setAuditStats] = useState<AuditTrailStats | null>(null);
  const [auditEntries, setAuditEntries] = useState<AuditTrailEntry[]>([]);
  const [loading, setLoading] = useState(false);
  const [searchFilters, setSearchFilters] = useState<AuditSearchFilters>({
    limit: 50,
    offset: 0
  });
  const [totalEntries, setTotalEntries] = useState(0);
  const [selectedEntry, setSelectedEntry] = useState<AuditTrailEntry | null>(null);
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    loadAuditData();
  }, []);

  useEffect(() => {
    searchAuditTrail();
  }, [searchFilters]);

  const loadAuditData = async () => {
    try {
      const stats = await AuditTrailLogger.getAuditTrailStats();
      setAuditStats(stats);
    } catch (error) {
      console.error('Error loading audit stats:', error);
      toast.error('Failed to load audit trail statistics');
    }
  };

  const searchAuditTrail = async () => {
    setLoading(true);
    try {
      const { entries, total } = await AuditTrailLogger.searchAuditTrail(searchFilters);
      setAuditEntries(entries);
      setTotalEntries(total);
    } catch (error) {
      console.error('Error searching audit trail:', error);
      toast.error('Failed to search audit trail');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (field: keyof AuditSearchFilters, value: any) => {
    setSearchFilters(prev => ({
      ...prev,
      [field]: value,
      offset: 0 // Reset to first page when filters change
    }));
  };

  const handlePageChange = (newOffset: number) => {
    setSearchFilters(prev => ({
      ...prev,
      offset: newOffset
    }));
  };

  const getOperationIcon = (operationType: string) => {
    switch (operationType) {
      case 'data_correction': return <Settings className="h-4 w-4" />;
      case 'validation_check': return <CheckCircle className="h-4 w-4" />;
      case 'edge_case_detection': return <AlertTriangle className="h-4 w-4" />;
      case 'baseline_management': return <TrendingUp className="h-4 w-4" />;
      case 'sequence_monitoring': return <BarChart3 className="h-4 w-4" />;
      case 'return_trip_validation': return <Activity className="h-4 w-4" />;
      default: return <FileText className="h-4 w-4" />;
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return 'text-red-700 bg-red-100 border-red-200';
      case 'error': return 'text-red-600 bg-red-50 border-red-200';
      case 'warning': return 'text-yellow-600 bg-yellow-50 border-yellow-200';
      case 'info': return 'text-blue-600 bg-blue-50 border-blue-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'critical': return <XCircle className="h-4 w-4" />;
      case 'error': return <AlertCircle className="h-4 w-4" />;
      case 'warning': return <AlertTriangle className="h-4 w-4" />;
      case 'info': return <Info className="h-4 w-4" />;
      default: return <Info className="h-4 w-4" />;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  const formatOperationType = (operationType: string) => {
    return operationType
      .split('_')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const exportAuditTrail = async () => {
    try {
      const { entries } = await AuditTrailLogger.searchAuditTrail({
        ...searchFilters,
        limit: 10000 // Large limit for export
      });
      
      const csvContent = generateCSV(entries);
      downloadCSV(csvContent, `audit_trail_${new Date().toISOString().split('T')[0]}.csv`);
      toast.success('Audit trail exported successfully');
    } catch (error) {
      toast.error('Failed to export audit trail');
    }
  };

  const generateCSV = (entries: AuditTrailEntry[]) => {
    const headers = [
      'Date', 'Operation Type', 'Entity Type', 'Entity ID', 'Action', 
      'Performed By', 'Severity', 'Confidence Score', 'Business Context'
    ];
    
    const rows = entries.map(entry => [
      entry.performed_at ? formatDate(entry.performed_at) : '',
      entry.operation_type,
      entry.entity_type,
      entry.entity_id,
      entry.action_performed,
      entry.performer_name || 'System',
      entry.severity_level || 'info',
      entry.confidence_score?.toString() || '',
      entry.business_context || ''
    ]);
    
    return [headers, ...rows].map(row => 
      row.map(cell => `"${(cell || '').toString().replace(/"/g, '""')}"`).join(',')
    ).join('\n');
  };

  const downloadCSV = (csvContent: string, fileName: string) => {
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', fileName);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  if (loading && !auditEntries.length) {
    return (
      <div className={`bg-white rounded-lg shadow p-6 ${className}`}>
        <div className="flex items-center justify-center py-8">
          <RefreshCw className="h-6 w-6 animate-spin text-blue-600 mr-2" />
          <span>Loading audit trail...</span>
        </div>
      </div>
    );
  }

  return (
    <div className={`bg-white rounded-lg shadow ${className}`}>
      {/* Header */}
      <div className="border-b p-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-xl font-semibold text-gray-900">Audit Trail</h2>
            <p className="text-sm text-gray-600 mt-1">
              Comprehensive tracking of all data integrity operations and system changes
            </p>
          </div>
          <div className="flex space-x-3">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className={`flex items-center px-4 py-2 rounded ${showFilters ? 'bg-blue-600 text-white' : 'text-gray-700 bg-gray-100'} hover:bg-blue-700`}
            >
              <Filter className="h-4 w-4 mr-2" />
              Filters
            </button>
            <button
              onClick={exportAuditTrail}
              className="flex items-center px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
            >
              <Download className="h-4 w-4 mr-2" />
              Export
            </button>
            <button
              onClick={() => { loadAuditData(); searchAuditTrail(); }}
              className="flex items-center px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              <RefreshCw className="h-4 w-4 mr-2" />
              Refresh
            </button>
          </div>
        </div>
      </div>

      {/* Statistics Overview */}
      {auditStats && (
        <div className="p-6 border-b">
          <h3 className="text-lg font-medium text-gray-900 mb-4">System Overview</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4 mb-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">{auditStats.total_operations}</div>
              <div className="text-sm text-gray-600">Total Operations</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">{auditStats.operations_today}</div>
              <div className="text-sm text-gray-600">Today</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">{auditStats.operations_this_week}</div>
              <div className="text-sm text-gray-600">This Week</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-red-600">{auditStats.error_rate.toFixed(1)}%</div>
              <div className="text-sm text-gray-600">Error Rate</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">{auditStats.avg_quality_score.toFixed(0)}</div>
              <div className="text-sm text-gray-600">Avg Quality</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-indigo-600">{auditStats.avg_confidence_score.toFixed(0)}</div>
              <div className="text-sm text-gray-600">Avg Confidence</div>
            </div>
          </div>

          {/* Operation Type Breakdown */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3 mb-4">
            {Object.entries(auditStats.operations_by_type).map(([type, count]) => (
              <div key={type} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                <div className="flex items-center">
                  {getOperationIcon(type)}
                  <span className="ml-2 font-medium">{formatOperationType(type)}</span>
                </div>
                <span className="text-lg font-bold text-gray-900">{count}</span>
              </div>
            ))}
          </div>

          {/* Severity Breakdown */}
          <div className="grid grid-cols-4 gap-4">
            {['critical', 'error', 'warning', 'info'].map(severity => (
              <div key={severity} className={`flex items-center justify-between p-3 rounded border ${getSeverityColor(severity)}`}>
                <div className="flex items-center">
                  {getSeverityIcon(severity)}
                  <span className="ml-2 font-medium capitalize">{severity}</span>
                </div>
                <span className="text-lg font-bold">{auditStats.operations_by_severity[severity] || 0}</span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Filters */}
      {showFilters && (
        <div className="p-6 border-b bg-gray-50">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Operation Type</label>
              <select
                value={searchFilters.operation_types?.[0] || ''}
                onChange={(e) => handleFilterChange('operation_types', e.target.value ? [e.target.value] : undefined)}
                className="w-full border rounded px-3 py-2"
              >
                <option value="">All Operations</option>
                <option value="data_correction">Data Correction</option>
                <option value="validation_check">Validation Check</option>
                <option value="edge_case_detection">Edge Case Detection</option>
                <option value="baseline_management">Baseline Management</option>
                <option value="sequence_monitoring">Sequence Monitoring</option>
                <option value="return_trip_validation">Return Trip Validation</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Severity</label>
              <select
                value={searchFilters.severity_levels?.[0] || ''}
                onChange={(e) => handleFilterChange('severity_levels', e.target.value ? [e.target.value] : undefined)}
                className="w-full border rounded px-3 py-2"
              >
                <option value="">All Severities</option>
                <option value="critical">Critical</option>
                <option value="error">Error</option>
                <option value="warning">Warning</option>
                <option value="info">Info</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Entity Type</label>
              <select
                value={searchFilters.entity_types?.[0] || ''}
                onChange={(e) => handleFilterChange('entity_types', e.target.value ? [e.target.value] : undefined)}
                className="w-full border rounded px-3 py-2"
              >
                <option value="">All Entities</option>
                <option value="trip">Trip</option>
                <option value="vehicle">Vehicle</option>
                <option value="driver">Driver</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Search Text</label>
              <input
                type="text"
                value={searchFilters.search_text || ''}
                onChange={(e) => handleFilterChange('search_text', e.target.value || undefined)}
                placeholder="Search descriptions..."
                className="w-full border rounded px-3 py-2"
              />
            </div>
          </div>
        </div>
      )}

      {/* Audit Entries */}
      <div className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-medium text-gray-900">
            Audit Entries ({totalEntries} total)
          </h3>
          <div className="text-sm text-gray-500">
            Showing {searchFilters.offset! + 1}-{Math.min(searchFilters.offset! + (searchFilters.limit || 50), totalEntries)} of {totalEntries}
          </div>
        </div>

        {auditEntries.length === 0 ? (
          <div className="text-center py-8">
            <Shield className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">No audit entries found</p>
            <p className="text-sm text-gray-500 mt-1">Try adjusting your filters or search criteria</p>
          </div>
        ) : (
          <div className="space-y-3">
            {auditEntries.map((entry) => (
              <div key={entry.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-gray-100 text-gray-700">
                      {getOperationIcon(entry.operation_type)}
                      <span className="ml-2">{formatOperationType(entry.operation_type)}</span>
                    </span>
                    <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium border ${getSeverityColor(entry.severity_level || 'info')}`}>
                      {getSeverityIcon(entry.severity_level || 'info')}
                      <span className="ml-1">{(entry.severity_level || 'info').toUpperCase()}</span>
                    </span>
                    {entry.confidence_score && (
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-700">
                        {entry.confidence_score}% confidence
                      </span>
                    )}
                  </div>
                  <div className="text-sm text-gray-500">
                    {entry.performed_at ? formatDate(entry.performed_at) : ''}
                  </div>
                </div>

                <div className="mb-3">
                  <div className="flex items-center justify-between mb-1">
                    <h4 className="font-medium text-gray-900">
                      {entry.entity_description || `${entry.entity_type} ${entry.entity_id}`}
                    </h4>
                    <div className="flex items-center text-sm text-gray-600">
                      <User className="h-3 w-3 mr-1" />
                      {entry.performer_name || 'System'}
                    </div>
                  </div>
                  <div className="flex items-center space-x-4 text-sm text-gray-600">
                    <span>Action: {entry.action_performed}</span>
                    <span>Entity: {entry.entity_type}</span>
                    <span>ID: {entry.entity_id}</span>
                  </div>
                </div>

                {entry.business_context && (
                  <div className="mb-3 p-2 bg-blue-50 rounded text-sm">
                    <span className="font-medium text-blue-800">Context: </span>
                    <span className="text-blue-700">{entry.business_context}</span>
                  </div>
                )}

                <div className="flex items-center justify-between">
                  <div className="flex space-x-4 text-xs text-gray-500">
                    <span>Category: {entry.operation_category}</span>
                    {entry.data_quality_score && <span>Quality: {entry.data_quality_score}</span>}
                    {entry.operation_duration_ms && <span>Duration: {entry.operation_duration_ms}ms</span>}
                  </div>
                  <button
                    onClick={() => setSelectedEntry(selectedEntry?.id === entry.id ? null : entry)}
                    className="text-sm text-blue-600 hover:text-blue-800"
                  >
                    <Eye className="h-4 w-4 inline mr-1" />
                    {selectedEntry?.id === entry.id ? 'Hide Details' : 'View Details'}
                  </button>
                </div>

                {selectedEntry?.id === entry.id && (
                  <div className="mt-4 pt-4 border-t bg-gray-50 -mx-4 px-4 -mb-4 pb-4 rounded-b-lg">
                    {entry.changes_made && (
                      <div className="mb-3">
                        <h5 className="font-medium text-gray-900 mb-2">Changes Made:</h5>
                        <pre className="text-xs bg-white p-2 rounded border overflow-x-auto">
                          {JSON.stringify(entry.changes_made, null, 2)}
                        </pre>
                      </div>
                    )}
                    
                    {entry.validation_results && (
                      <div>
                        <h5 className="font-medium text-gray-900 mb-2">Validation Results:</h5>
                        <pre className="text-xs bg-white p-2 rounded border overflow-x-auto">
                          {JSON.stringify(entry.validation_results, null, 2)}
                        </pre>
                      </div>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {/* Pagination */}
        {totalEntries > (searchFilters.limit || 50) && (
          <div className="flex items-center justify-between mt-6">
            <button
              onClick={() => handlePageChange(Math.max(0, searchFilters.offset! - (searchFilters.limit || 50)))}
              disabled={searchFilters.offset === 0}
              className="px-4 py-2 bg-gray-100 text-gray-700 rounded disabled:opacity-50 hover:bg-gray-200"
            >
              Previous
            </button>
            <span className="text-sm text-gray-600">
              Page {Math.floor(searchFilters.offset! / (searchFilters.limit || 50)) + 1} of{' '}
              {Math.ceil(totalEntries / (searchFilters.limit || 50))}
            </span>
            <button
              onClick={() => handlePageChange(searchFilters.offset! + (searchFilters.limit || 50))}
              disabled={searchFilters.offset! + (searchFilters.limit || 50) >= totalEntries}
              className="px-4 py-2 bg-gray-100 text-gray-700 rounded disabled:opacity-50 hover:bg-gray-200"
            >
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AuditTrailDashboard;

import React, { useState, useMemo, useRef, useEffect } from 'react';
import { Trip, Vehicle, Driver } from '@/types';
import { ChevronDown, ChevronUp, Trash2, IndianRupee } from 'lucide-react';
import Button from '../ui/Button';
import Input from '../ui/Input';
import Select from '../ui/Select';
import { format } from 'date-fns';
import { NumberFormatter } from '@/utils/numberFormatter';

interface Column {
  id: string;
  label: string;
  accessor: (
    trip: Trip,
    vehiclesById: Record<string, Vehicle>,
    driversById: Record<string, Driver>
  ) => string | number;
  sortable?: boolean;
  editable?: boolean;
  width?: string;
  type?: 'text' | 'number' | 'date' | 'select';
  options?: { value: string; label: string }[];
  description?: string;
}

interface TripsTableProps {
  trips: Trip[];
  vehicles: Vehicle[];
  drivers: Driver[];
  onUpdateTrip: (tripId: string, updates: Partial<Trip>) => void;
  onDeleteTrip: (tripId: string) => Promise<void>;
}

const TripsTable: React.FC<TripsTableProps> = ({
  trips,
  vehicles,
  drivers,
  onUpdateTrip,
  onDeleteTrip
}) => {
  const vehiclesById = useMemo(() => {
    const map: Record<string, Vehicle> = {};
    (vehicles || []).forEach(v => {
      map[v.id] = v;
    });
    return map;
  }, [vehicles]);

  const driversById = useMemo(() => {
    const map: Record<string, Driver> = {};
    (drivers || []).forEach(d => {
      map[d.id] = d;
    });
    return map;
  }, [drivers]);

  const vehicleOptions = useMemo(
    () =>
      Object.values(vehiclesById).map(v => ({
        value: v.id,
        label: v.registration_number
      })),
    [vehiclesById]
  );

  const driverOptions = useMemo(
    () =>
      Object.values(driversById).map(d => ({
        value: d.id,
        label: d.name
      })),
    [driversById]
  );

  const columns: Column[] = useMemo(
    () => [
      {
        id: 'trip_serial_number',
        label: 'Trip ID',
        accessor: trip => trip.trip_serial_number || '-',
        sortable: true,
        width: '120px',
        description: 'Unique identifier for the trip'
      },
      {
        id: 'vehicle',
        label: 'Vehicle',
        accessor: (trip, vehiclesById) =>
          vehiclesById[trip.vehicle_id]?.registration_number || 'Unknown',
        sortable: true,
        editable: true,
        type: 'select',
        options: vehicleOptions,
        width: '120px',
        description: 'Vehicle registration number'
      },
      {
        id: 'driver',
        label: 'Driver',
        accessor: (trip, _vehiclesById, driversById) =>
          driversById[trip.driver_id]?.name || 'Unknown',
        sortable: true,
        editable: true,
        type: 'select',
        options: driverOptions,
        width: '150px',
        description: 'Driver name'
      },
      {
        id: 'start_date',
        label: 'Start Date',
        accessor: trip => format(new Date(trip.trip_start_date), 'dd/MM/yyyy'),
        sortable: true,
        editable: true,
        type: 'date',
        width: '100px',
        description: 'Trip start date'
      },
      {
        id: 'end_date',
        label: 'End Date',
        accessor: trip => format(new Date(trip.trip_end_date), 'dd/MM/yyyy'),
        sortable: true,
        editable: true,
        type: 'date',
        width: '100px',
        description: 'Trip end date'
      },
      {
        id: 'start_km',
        label: 'Start KM',
        accessor: trip => trip.start_km || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Starting odometer reading'
      },
      {
        id: 'end_km',
        label: 'End KM',
        accessor: trip => trip.end_km || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Ending odometer reading'
      },
      {
        id: 'distance',
        label: 'Distance',
        accessor: trip => (trip.end_km || 0) - (trip.start_km || 0),
        sortable: true,
        width: '90px',
        description: 'Total distance covered'
      },
      {
        id: 'gross_weight',
        label: 'Weight (kg)',
        accessor: trip => trip.gross_weight || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Gross weight of cargo'
      },
      {
        id: 'fuel_quantity',
        label: 'Fuel (L)',
        accessor: trip => trip.fuel_quantity || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '80px',
        description: 'Fuel quantity in liters'
      },
      {
        id: 'fuel_rate',
        label: 'Fuel Rate',
        accessor: trip => trip.fuel_rate_per_liter || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Fuel rate per liter'
      },
      {
        id: 'total_fuel_cost',
        label: 'Fuel Cost',
        accessor: trip => trip.total_fuel_cost || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Total fuel cost'
      },
      {
        id: 'mileage',
        label: 'Mileage',
        accessor: trip => trip.calculated_kmpl ? NumberFormatter.display(trip.calculated_kmpl, 2) : '-',
        sortable: true,
        width: '80px',
        description: 'Fuel efficiency in km/L'
      },
      {
        id: 'road_expenses',
        label: 'Road Exp',
        accessor: trip => trip.total_road_expenses || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Road expenses'
      },
      {
        id: 'unloading_expense',
        label: 'Unloading',
        accessor: trip => trip.unloading_expense || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Unloading expense'
      },
      {
        id: 'driver_expense',
        label: 'Driver Exp',
        accessor: trip => trip.driver_expense || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Driver expense'
      },
      {
        id: 'rto_expense',
        label: 'RTO Exp',
        accessor: trip => trip.road_rto_expense || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '80px',
        description: 'RTO expense'
      },
      {
        id: 'breakdown_expense',
        label: 'Breakdown',
        accessor: trip => trip.breakdown_expense || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Breakdown expense'
      },
      {
        id: 'misc_expense',
        label: 'Misc Exp',
        accessor: trip => trip.miscellaneous_expense || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '80px',
        description: 'Miscellaneous expense'
      },
      {
        id: 'total_expenses',
        label: 'Total Exp',
        accessor: trip => {
          const fuel = trip.total_fuel_cost || 0;
          const road = trip.total_road_expenses || 0;
          const unloading = trip.unloading_expense || 0;
          const driver = trip.driver_expense || 0;
          const rto = trip.road_rto_expense || 0;
          const breakdown = trip.breakdown_expense || 0;
          const misc = trip.miscellaneous_expense || 0;
          const total = fuel + road + unloading + driver + rto + breakdown + misc;
          return NumberFormatter.roundUp(total, 2);
        },
        sortable: true,
        width: '90px',
        description: 'Total expenses'
      },
      {
        id: 'freight_rate',
        label: 'Freight Rate',
        accessor: trip => trip.freight_rate || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '100px',
        description: 'Freight rate'
      },
      {
        id: 'billing_type',
        label: 'Billing',
        accessor: trip => trip.billing_type || '-',
        sortable: true,
        editable: true,
        type: 'select',
        options: [
          { value: 'per_km', label: 'Per KM' },
          { value: 'per_ton', label: 'Per Ton' },
          { value: 'manual', label: 'Manual' }
        ],
        width: '80px',
        description: 'Billing type'
      },
      {
        id: 'income_amount',
        label: 'Income',
        accessor: trip => trip.income_amount || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Income amount'
      },
      {
        id: 'net_profit',
        label: 'Net Profit',
        accessor: trip => trip.net_profit || 0,
        sortable: true,
        width: '90px',
        description: 'Net profit or loss'
      },
      {
        id: 'profit_status',
        label: 'Status',
        accessor: trip => trip.profit_status || '-',
        sortable: true,
        width: '80px',
        description: 'Profit status'
      },
      {
        id: 'refueling_done',
        label: 'Refuel',
        accessor: trip => trip.refueling_done ? 'Yes' : 'No',
        sortable: true,
        editable: true,
        type: 'select',
        options: [
          { value: 'true', label: 'Yes' },
          { value: 'false', label: 'No' }
        ],
        width: '70px',
        description: 'Refueling done'
      },
      {
        id: 'is_return_trip',
        label: 'Return',
        accessor: trip => trip.is_return_trip ? 'Yes' : 'No',
        sortable: true,
        width: '70px',
        description: 'Return trip'
      },
      {
        id: 'route_deviation',
        label: 'Deviation',
        accessor: trip => trip.route_deviation || 0,
        sortable: true,
        width: '90px',
        description: 'Route deviation'
      },
      {
        id: 'advance_amount',
        label: 'Advance',
        accessor: trip => trip.advance_amount || 0,
        sortable: true,
        editable: true,
        type: 'number',
        width: '90px',
        description: 'Advance amount'
      },
      {
        id: 'remarks',
        label: 'Remarks',
        accessor: trip => trip.remarks || '-',
        sortable: true,
        editable: true,
        type: 'text',
        width: '150px',
        description: 'Trip remarks'
      }
    ],
    [vehicleOptions, driverOptions, vehiclesById, driversById]
  );

  const [sortConfig, setSortConfig] = useState<{
    key: string;
    direction: 'asc' | 'desc';
  } | null>(null);

  const [editingCell, setEditingCell] = useState<{
    tripId: string;
    columnId: string;
  } | null>(null);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 10;
  
  // Add ref for scrollable container
  const tableContainerRef = useRef<HTMLDivElement>(null);
  
  // Handle scroll detection for indicator
  useEffect(() => {
    const handleScroll = () => {
      if (!tableContainerRef.current) return;
      
      const { scrollLeft, scrollWidth, clientWidth } = tableContainerRef.current;
      
      // Check if scrolled at all
      if (scrollLeft > 0) {
        tableContainerRef.current.classList.add('scrolled-right');
      } else {
        tableContainerRef.current.classList.remove('scrolled-right');
      }
    };
    
    const tableContainer = tableContainerRef.current;
    if (tableContainer) {
      tableContainer.addEventListener('scroll', handleScroll);
    }
    
    return () => {
      if (tableContainer) {
        tableContainer.removeEventListener('scroll', handleScroll);
      }
    };
  }, []);

  const sortedTrips = useMemo(() => {
    if (!sortConfig) return trips;

    return [...trips].sort((a, b) => {
      const column = columns.find(col => col.id === sortConfig.key);
      if (!column) return 0;

      const aValue = column.accessor(a, vehiclesById, driversById);
      const bValue = column.accessor(b, vehiclesById, driversById);

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sortConfig.direction === 'asc'
          ? aValue.localeCompare(bValue)
          : bValue.localeCompare(aValue);
      }

      return sortConfig.direction === 'asc'
        ? Number(aValue) - Number(bValue)
        : Number(bValue) - Number(aValue);
    });
  }, [trips, sortConfig, columns, vehiclesById, driversById]);

  const paginatedTrips = useMemo(() => {
    const start = (currentPage - 1) * rowsPerPage;
    return sortedTrips.slice(start, start + rowsPerPage);
  }, [sortedTrips, currentPage]);

  const totalPages = Math.ceil(sortedTrips.length / rowsPerPage) || 1;

  const handleSort = (columnId: string) => {
    setSortConfig(current => {
      if (!current || current.key !== columnId) {
        return { key: columnId, direction: 'asc' };
      }
      if (current.direction === 'asc') {
        return { key: columnId, direction: 'desc' };
      }
      return null;
    });
  };

  const handleCellEdit = (tripId: string, columnId: string, value: any) => {
    const updates: Partial<Trip> = {};
    
    switch (columnId) {
      case 'vehicle':
        updates.vehicle_id = value;
        break;
      case 'driver':
        updates.driver_id = value;
        break;
      case 'start_date':
        updates.trip_start_date = value;
        break;
      case 'end_date':
        updates.trip_end_date = value;
        break;
      case 'start_km':
        updates.start_km = Number(value);
        break;
      case 'end_km':
        updates.end_km = Number(value);
        break;
    }

    onUpdateTrip(tripId, updates);
    setEditingCell(null);
  };

  const handleDeleteTrip = async (tripId: string) => {
    if (window.confirm('Are you sure you want to permanently delete this trip? This action cannot be undone.')) {
      await onDeleteTrip(tripId);
    }
  };

  // Function to get profit/loss cell style
  const getProfitLossStyle = (value: number) => {
    if (value > 0) return 'text-success-600 font-medium';
    if (value < 0) return 'text-error-600 font-medium';
    return 'text-gray-500';
  };

  return (
    <div className="space-y-4">
      <div className="bg-white rounded-lg shadow-sm overflow-hidden">
        <div 
          className="overflow-x-auto scroll-indicator" 
          ref={tableContainerRef}
          style={{
            scrollbarWidth: 'thin',
            scrollbarColor: '#cbd5e0 #f7fafc'
          }}
        >
          <table className="min-w-full divide-y divide-gray-200" style={{ minWidth: '2000px' }}>
            <thead className="bg-gray-50 sticky top-0 z-10">
              <tr>
                {columns.map(column => (
                  <th
                    key={column.id}
                    className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                    onClick={() => column.sortable && handleSort(column.id)}
                    style={{ 
                      cursor: column.sortable ? 'pointer' : 'default',
                      width: column.width,
                      minWidth: column.width
                    }}
                  >
                    <div className="flex items-center space-x-1">
                      <span className="truncate">{column.label}</span>
                      {column.sortable && sortConfig?.key === column.id && (
                        sortConfig.direction === 'asc' ? (
                          <ChevronUp className="h-3 w-3 flex-shrink-0" />
                        ) : (
                          <ChevronDown className="h-3 w-3 flex-shrink-0" />
                        )
                      )}
                    </div>
                  </th>
                ))}
                <th className="px-3 py-2 text-center text-xs font-medium text-gray-500 uppercase tracking-wider w-20">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {sortedTrips.length > 0 ? (
                paginatedTrips.map(trip => (
                  <tr key={trip.id} className="hover:bg-gray-50">
                    {columns.map(column => (
                      <td
                        key={`${trip.id}-${column.id}`}
                        className="px-3 py-2 whitespace-nowrap text-xs"
                        onClick={() => column.editable && setEditingCell({
                          tripId: trip.id,
                          columnId: column.id
                        })}
                        style={{ width: column.width, minWidth: column.width }}
                      >
                        {editingCell?.tripId === trip.id && 
                         editingCell?.columnId === column.id ? (
                          column.type === 'select' ? (
                            <Select
                              options={column.options || []}
                              value={column.id === 'vehicle' ? trip.vehicle_id : trip.driver_id}
                              onChange={e => handleCellEdit(trip.id, column.id, e.target.value)}
                              autoFocus
                            />
                          ) : (
                            <Input
                              type={column.type || 'text'}
                              value={column.accessor(trip, vehiclesById, driversById).toString()}
                              onChange={e => handleCellEdit(trip.id, column.id, e.target.value)}
                              autoFocus
                              onBlur={() => setEditingCell(null)}
                            />
                          )
                        ) : column.id === 'profit_loss' ? (
                          <div className="flex items-center">
                            <IndianRupee className="h-4 w-4 mr-1 text-gray-400" />
                            <span className={getProfitLossStyle(trip.net_profit || 0)}>
                              {trip.net_profit ? trip.net_profit.toLocaleString() : '0'}
                            </span>
                          </div>
                        ) : (
                          <span className={column.editable ? 'cursor-pointer' : ''}>
                            {column.accessor(trip, vehiclesById, driversById)}
                          </span>
                        )}
                      </td>
                    ))}
                    <td className="px-3 py-2 whitespace-nowrap text-center w-20">
                      <button
                        onClick={() => handleDeleteTrip(trip.id)}
                        className="text-error-600 hover:text-error-900"
                        title="Delete Trip"
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={columns.length + 1} className="px-3 py-10 text-center text-gray-500">
                    No trips match your filter criteria
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        <div className="flex items-center justify-between p-4 border-t">
          <span className="text-sm text-gray-700">
            Page {currentPage} of {totalPages}
          </span>
          <div className="space-x-2">
            <Button
              variant="outline"
              inputSize="sm"
              onClick={() => setCurrentPage(p => Math.max(p - 1, 1))}
              disabled={currentPage === 1}
            >
              Previous
            </Button>
            <Button
              variant="outline"
              inputSize="sm"
              onClick={() => setCurrentPage(p => Math.min(p + 1, totalPages))}
              disabled={currentPage === totalPages}
            >
              Next
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TripsTable;

import React, { useState, useEffect } from 'react';
import { getVehicles } from '../../utils/storage';
import { getAllVehicleActivityLogs, VehicleActivityLog } from '../../utils/vehicleActivity';
import { Clock, UserCircle, Truck, Filter, Search, RefreshCw } from 'lucide-react';
import Select from '../ui/Select';
import Input from '../ui/Input';
import Button from '../ui/Button';
import { format, isValid } from 'date-fns';

interface VehicleActivityLogTableProps {
  limit?: number;
  vehicleId?: string;
  refreshTrigger?: number;
}

interface VehicleActivityLogWithVehicle extends VehicleActivityLog {
  vehicles: {
    registration_number: string;
  };
}

const VehicleActivityLogTable: React.FC<VehicleActivityLogTableProps> = ({ 
  limit,
  vehicleId,
  refreshTrigger = 0
}) => {
  const [logs, setLogs] = useState<VehicleActivityLogWithVehicle[]>([]);
  const [vehicles, setVehicles] = useState<{ id: string; registration_number: string }[]>([]);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    actionType: '',
    vehicleFilter: vehicleId || '',
    search: ''
  });

  const fetchLogs = async () => {
    setLoading(true);
    try {
      let fetchedLogs;
      if (vehicleId) {
        // If vehicleId is provided, only fetch logs for that vehicle
        const vehicleLogs = await getAllVehicleActivityLogs();
        fetchedLogs = vehicleLogs.filter(log => log.vehicle_id === vehicleId) as VehicleActivityLogWithVehicle[];
        
        // Add vehicle registration info
        const vehicle = await getVehicles();
        const vehicleMap = new Map(vehicle.map(v => [v.id, v]));
        
        fetchedLogs = fetchedLogs.map(log => ({
          ...log,
          vehicles: {
            registration_number: vehicleMap.get(log.vehicle_id)?.registration_number || 'Unknown'
          }
        }));
      } else {
        // Otherwise fetch all logs with vehicle info
        fetchedLogs = await getAllVehicleActivityLogs(limit) as VehicleActivityLogWithVehicle[];
      }
      
      setLogs(fetchedLogs);
    } catch (error) {
      console.error('Error fetching vehicle activity logs:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchVehicles = async () => {
    try {
      const vehiclesData = await getVehicles();
      setVehicles(
        vehiclesData.map(vehicle => ({
          id: vehicle.id,
          registration_number: vehicle.registration_number
        }))
      );
    } catch (error) {
      console.error('Error fetching vehicles:', error);
    }
  };

  useEffect(() => {
    fetchLogs();
    if (!vehicleId) {
      fetchVehicles();
    }
  }, [vehicleId, limit, refreshTrigger]);

  const handleRefresh = () => {
    fetchLogs();
  };

  // Filter logs based on current filters
  const filteredLogs = logs.filter(log => {
    if (filters.actionType && log.action_type !== filters.actionType) {
      return false;
    }
    
    if (filters.vehicleFilter && log.vehicle_id !== filters.vehicleFilter) {
      return false;
    }
    
    if (filters.search) {
      const searchTerm = filters.search.toLowerCase();
      return (
        log.action_by?.toLowerCase().includes(searchTerm) ||
        log.notes?.toLowerCase().includes(searchTerm) ||
        log.action_type.toLowerCase().includes(searchTerm) ||
        log.vehicles?.registration_number.toLowerCase().includes(searchTerm)
      );
    }
    
    return true;
  });

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'N/A';
    
    try {
      const date = new Date(dateString);
      if (!isValid(date)) return 'Invalid Date';
      
      return format(date, 'dd MMM yyyy, HH:mm');
    } catch (error) {
      return 'Invalid Date';
    }
  };

  const getActionTypeColor = (actionType: string) => {
    switch (actionType) {
      case 'deleted':
      case 'permanently_deleted':
        return 'bg-error-100 text-error-800';
      case 'archived':
        return 'bg-warning-100 text-warning-800';
      case 'assigned_driver':
        return 'bg-success-100 text-success-800';
      case 'unassigned_driver':
        return 'bg-blue-100 text-blue-800';
      case 'exported':
        return 'bg-indigo-100 text-indigo-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatActionType = (actionType: string) => {
    return actionType
      .split('_')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  return (
    <div className="bg-white rounded-lg shadow-sm overflow-hidden">
      <div className="p-4 border-b border-gray-200">
        <h3 className="text-lg font-medium text-gray-900">Vehicle Activity Log</h3>
        <p className="mt-1 text-sm text-gray-500">
          Track all changes to vehicles including deletions, archiving, and driver assignments
        </p>
      </div>

      <div className="p-4 border-b border-gray-200 space-y-4">
        <div className="flex flex-wrap gap-3">
          {!vehicleId && (
            <div className="w-60">
              <Select
                label="Vehicle"
                options={[
                  { value: '', label: 'All Vehicles' },
                  ...vehicles.map(v => ({ 
                    value: v.id, 
                    label: v.registration_number 
                  }))
                ]}
                value={filters.vehicleFilter}
                onChange={e => setFilters({ ...filters, vehicleFilter: e.target.value })}
              />
            </div>
          )}
          
          <div className="w-60">
            <Select
              label="Action Type"
              options={[
                { value: '', label: 'All Actions' },
                { value: 'deleted', label: 'Deleted' },
                { value: 'archived', label: 'Archived' },
                { value: 'assigned_driver', label: 'Driver Assigned' },
                { value: 'unassigned_driver', label: 'Driver Unassigned' },
                { value: 'updated', label: 'Updated' },
                { value: 'exported', label: 'Exported' },
                { value: 'permanently_deleted', label: 'Permanently Deleted' }
              ]}
              value={filters.actionType}
              onChange={e => setFilters({ ...filters, actionType: e.target.value })}
            />
          </div>
          
          <div className="flex-1 min-w-[200px]">
            <Input
              label="Search"
              placeholder="Search by user or notes..."
              icon={<Search className="h-4 w-4" />}
              value={filters.search}
              onChange={e => setFilters({ ...filters, search: e.target.value })}
            />
          </div>
          
          <div className="flex items-end">
            <Button
              variant="outline"
              inputSize="sm"
              onClick={handleRefresh}
              icon={<RefreshCw className="h-4 w-4" />}
              isLoading={loading}
            >
              Refresh
            </Button>
          </div>
        </div>
      </div>

      <div className="overflow-x-auto scroll-indicator">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              {!vehicleId && (
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Vehicle
                </th>
              )}
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Action
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                User
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Timestamp
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Notes
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {loading ? (
              <tr>
                <td colSpan={vehicleId ? 4 : 5} className="px-6 py-4 whitespace-nowrap">
                  <div className="flex justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-primary-600"></div>
                    <span className="ml-2">Loading...</span>
                  </div>
                </td>
              </tr>
            ) : filteredLogs.length === 0 ? (
              <tr>
                <td colSpan={vehicleId ? 4 : 5} className="px-6 py-4 whitespace-nowrap text-center text-gray-500">
                  No activity logs found
                </td>
              </tr>
            ) : (
              filteredLogs.map(log => (
                <tr key={log.id} className="hover:bg-gray-50">
                  {!vehicleId && (
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <Truck className="h-4 w-4 text-gray-400 mr-2" />
                        <span className="text-sm font-medium text-gray-900">
                          {log.vehicles?.registration_number || 'Unknown'}
                        </span>
                      </div>
                    </td>
                  )}
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 inline-flex text-xs leading-5 font-medium rounded-full ${getActionTypeColor(log.action_type)}`}>
                      {formatActionType(log.action_type)}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <UserCircle className="h-4 w-4 text-gray-400 mr-2" />
                      <span className="text-sm text-gray-500">{log.action_by}</span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <Clock className="h-4 w-4 text-gray-400 mr-2" />
                      <span className="text-sm text-gray-500">{formatDate(log.timestamp)}</span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-normal">
                    <p className="text-sm text-gray-500 max-w-sm">{log.notes || '-'}</p>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
      
      {limit && filteredLogs.length >= limit && (
        <div className="p-4 text-center">
          <p className="text-sm text-gray-500">
            Showing {limit} most recent logs. There may be more records available.
          </p>
        </div>
      )}
    </div>
  );
};

export default VehicleActivityLogTable;

import React, { useState, useEffect, useMemo } from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  Activity, 
  AlertTriangle, 
  CheckCircle,
  Clock,
  Fuel,
  MapPin,
  Users,
  Truck
} from 'lucide-react';
import { Trip, Vehicle, Driver } from '@/types';
import { useQuery } from '@tanstack/react-query';
import { getTrips, getVehicles } from '../../utils/storage';
import { getDrivers } from '../../utils/api/drivers';

interface RealTimeMetrics {
  activeTrips: number;
  vehiclesInUse: number;
  driversOnDuty: number;
  totalDistanceToday: number;
  fuelConsumedToday: number;
  averageMileage: number;
  alertsCount: number;
  maintenanceDue: number;
}

interface TrendData {
  period: string;
  value: number;
  change: number;
  changeType: 'increase' | 'decrease' | 'stable';
}

interface PredictiveInsight {
  type: 'maintenance' | 'fuel' | 'route' | 'driver' | 'vehicle';
  title: string;
  description: string;
  confidence: number;
  impact: 'low' | 'medium' | 'high';
  recommendation: string;
  timeframe: string;
}

interface DuplicateRouteSummary {
  route: string;
  count: number;
}

const RealTimeAnalytics: React.FC = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [selectedTimeframe, setSelectedTimeframe] = useState<'1h' | '6h' | '24h' | '7d'>('24h');

  // Helper functions - moved before their usage to avoid hoisting issues
  const getTimeframeMs = (timeframe: string): number => {
    switch (timeframe) {
      case '1h': return 60 * 60 * 1000;
      case '6h': return 6 * 60 * 60 * 1000;
      case '24h': return 24 * 60 * 60 * 1000;
      case '7d': return 7 * 24 * 60 * 60 * 1000;
      default: return 24 * 60 * 60 * 1000;
    }
  };

  const getTimeframePeriods = (timeframe: string) => {
    const now = new Date();
    switch (timeframe) {
      case '1h':
        return [
          { label: 'Now', offset: 0, duration: 10 * 60 * 1000 },
          { label: '10m ago', offset: 10 * 60 * 1000, duration: 10 * 60 * 1000 },
          { label: '20m ago', offset: 20 * 60 * 1000, duration: 10 * 60 * 1000 },
          { label: '30m ago', offset: 30 * 60 * 1000, duration: 10 * 60 * 1000 },
          { label: '40m ago', offset: 40 * 60 * 1000, duration: 10 * 60 * 1000 },
          { label: '50m ago', offset: 50 * 60 * 1000, duration: 10 * 60 * 1000 }
        ];
      case '6h':
        return [
          { label: 'Now', offset: 0, duration: 60 * 60 * 1000 },
          { label: '1h ago', offset: 60 * 60 * 1000, duration: 60 * 60 * 1000 },
          { label: '2h ago', offset: 2 * 60 * 60 * 1000, duration: 60 * 60 * 1000 },
          { label: '3h ago', offset: 3 * 60 * 60 * 1000, duration: 60 * 60 * 1000 },
          { label: '4h ago', offset: 4 * 60 * 60 * 1000, duration: 60 * 60 * 1000 },
          { label: '5h ago', offset: 5 * 60 * 60 * 1000, duration: 60 * 60 * 1000 }
        ];
      default:
        return [];
    }
  };

  // Update time every minute
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  // Fetch data with React Query
  const { data: trips = [], isLoading: tripsLoading } = useQuery({
    queryKey: ['trips'],
    queryFn: getTrips,
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  const { data: vehicles = [], isLoading: vehiclesLoading } = useQuery({
    queryKey: ['vehicles'],
    queryFn: getVehicles,
    refetchInterval: 60000, // Refetch every minute
  });

  const { data: drivers = [], isLoading: driversLoading } = useQuery({
    queryKey: ['drivers'],
    queryFn: getDrivers,
    refetchInterval: 60000, // Refetch every minute
  });

  const loading = tripsLoading || vehiclesLoading || driversLoading;

  // Calculate real-time metrics
  const realTimeMetrics = useMemo((): RealTimeMetrics => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    const timeframeStart = new Date(now.getTime() - getTimeframeMs(selectedTimeframe));

    // Active trips (currently in progress)
    const activeTrips = trips.filter(trip => {
      const startDate = new Date(trip.trip_start_date);
      const endDate = new Date(trip.trip_end_date);
      return startDate <= now && endDate >= now;
    }).length;

    // Vehicles in use
    const vehiclesInUse = new Set(
      trips
        .filter(trip => {
          const startDate = new Date(trip.trip_start_date);
          const endDate = new Date(trip.trip_end_date);
          return startDate <= now && endDate >= now;
        })
        .map(trip => trip.vehicle_id)
    ).size;

    // Drivers on duty
    const driversOnDuty = new Set(
      trips
        .filter(trip => {
          const startDate = new Date(trip.trip_start_date);
          const endDate = new Date(trip.trip_end_date);
          return startDate <= now && endDate >= now;
        })
        .map(trip => trip.driver_id)
    ).size;

    // Today's metrics
    const todayTrips = trips.filter(trip => {
      const tripDate = new Date(trip.trip_start_date);
      return tripDate >= today;
    });

    const totalDistanceToday = todayTrips.reduce((sum, trip) => 
      sum + (trip.end_km - trip.start_km), 0
    );

    const fuelConsumedToday = todayTrips.reduce((sum, trip) => 
      sum + (trip.fuel_quantity || 0), 0
    );

    const tripsWithMileage = todayTrips.filter(trip => trip.calculated_kmpl && trip.calculated_kmpl > 0);
    const averageMileage = tripsWithMileage.length > 0
      ? tripsWithMileage.reduce((sum, trip) => sum + (trip.calculated_kmpl || 0), 0) / tripsWithMileage.length
      : 0;

    // Alerts and maintenance (simplified)
    const alertsCount = 0; // Would be calculated from actual alerts
    const maintenanceDue = vehicles.filter(v => v.status === 'maintenance').length;

    return {
      activeTrips,
      vehiclesInUse,
      driversOnDuty,
      totalDistanceToday,
      fuelConsumedToday,
      averageMileage,
      alertsCount,
      maintenanceDue
    };
  }, [trips, vehicles, drivers, selectedTimeframe]);

  // Calculate trends
  const trends = useMemo((): TrendData[] => {
    const now = new Date();
    const periods = getTimeframePeriods(selectedTimeframe);
    
    return periods.map(period => {
      const periodStart = new Date(now.getTime() - period.offset);
      const periodEnd = new Date(now.getTime() - (period.offset - period.duration));
      
      const periodTrips = trips.filter(trip => {
        const tripDate = new Date(trip.trip_start_date);
        return tripDate >= periodStart && tripDate < periodEnd;
      });

      const distance = periodTrips.reduce((sum, trip) => sum + (trip.end_km - trip.start_km), 0);
      
      return {
        period: period.label,
        value: distance,
        change: 0, // Would calculate actual change
        changeType: 'stable' as const
      };
    });
  }, [trips, selectedTimeframe]);

  // Generate predictive insights
  const predictiveInsights = useMemo((): PredictiveInsight[] => {
    const insights: PredictiveInsight[] = [];

    // Maintenance insights
    const vehiclesNeedingMaintenance = vehicles.filter(v => {
      // Simplified logic - would use actual maintenance data
      return v.current_odometer > 100000;
    });

    if (vehiclesNeedingMaintenance.length > 0) {
      insights.push({
        type: 'maintenance',
        title: 'Maintenance Due Soon',
        description: `${vehiclesNeedingMaintenance.length} vehicles approaching maintenance intervals`,
        confidence: 85,
        impact: 'medium',
        recommendation: 'Schedule preventive maintenance to avoid breakdowns',
        timeframe: 'Next 2 weeks'
      });
    }

    // Fuel efficiency insights
    const lowEfficiencyTrips = trips.filter(trip => 
      trip.calculated_kmpl && trip.calculated_kmpl < 3.0
    );

    if (lowEfficiencyTrips.length > 5) {
      insights.push({
        type: 'fuel',
        title: 'Fuel Efficiency Alert',
        description: 'Multiple trips showing below-average fuel efficiency',
        confidence: 75,
        impact: 'high',
        recommendation: 'Review driving patterns and vehicle maintenance',
        timeframe: 'Immediate attention'
      });
    }

    // Route optimization insights
    const duplicateRoutes = findDuplicateRoutes(trips);
    if (duplicateRoutes.length > 0) {
      insights.push({
        type: 'route',
        title: 'Route Optimization Opportunity',
        description: 'Multiple vehicles traveling similar routes',
        confidence: 90,
        impact: 'medium',
        recommendation: 'Consider route consolidation to reduce costs',
        timeframe: 'Next planning cycle'
      });
    }

    return insights;
  }, [trips, vehicles]);

  function findDuplicateRoutes(trips: Trip[]): DuplicateRouteSummary[] {
    const routeMap = new Map<string, DuplicateRouteSummary>();
    trips.forEach(trip => {
      const key = `${trip.destination}-${trip.warehouse_id}`;
      const existing = routeMap.get(key);
      if (existing) {
        existing.count += 1;
      } else {
        routeMap.set(key, { route: key, count: 1 });
      }
    });
    return Array.from(routeMap.values()).filter(route => route.count > 1);
  }

  const getInsightIcon = (type: string) => {
    switch (type) {
      case 'maintenance': return <Truck className="h-5 w-5" />;
      case 'fuel': return <Fuel className="h-5 w-5" />;
      case 'route': return <MapPin className="h-5 w-5" />;
      case 'driver': return <Users className="h-5 w-5" />;
      case 'vehicle': return <Truck className="h-5 w-5" />;
      default: return <Activity className="h-5 w-5" />;
    }
  };

  const getInsightColor = (impact: string) => {
    switch (impact) {
      case 'high': return 'text-red-600 bg-red-50 border-red-200';
      case 'medium': return 'text-yellow-600 bg-yellow-50 border-yellow-200';
      case 'low': return 'text-blue-600 bg-blue-50 border-blue-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-gray-100">
            Real-Time Analytics
          </h2>
          <p className="text-gray-600 dark:text-gray-400">
            Live fleet monitoring and insights
          </p>
        </div>
        <div className="flex items-center space-x-4">
          <div className="text-sm text-gray-500 dark:text-gray-400">
            Last updated: {currentTime.toLocaleTimeString()}
          </div>
          <div className="flex space-x-2">
            {(['1h', '6h', '24h', '7d'] as const).map((timeframe) => (
              <button
                key={timeframe}
                onClick={() => setSelectedTimeframe(timeframe)}
                className={`px-3 py-1 rounded-md text-sm font-medium transition-colors ${
                  selectedTimeframe === timeframe
                    ? 'bg-primary-100 text-primary-800'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {timeframe}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Real-time Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Active Trips</p>
              <p className="text-2xl font-bold text-gray-900 dark:text-gray-100">
                {realTimeMetrics.activeTrips}
              </p>
            </div>
            <div className="p-3 bg-blue-100 dark:bg-blue-900/20 rounded-full">
              <Activity className="h-6 w-6 text-blue-600 dark:text-blue-400" />
            </div>
          </div>
          <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
            Currently in progress
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Vehicles in Use</p>
              <p className="text-2xl font-bold text-gray-900 dark:text-gray-100">
                {realTimeMetrics.vehiclesInUse}
              </p>
            </div>
            <div className="p-3 bg-green-100 dark:bg-green-900/20 rounded-full">
              <Truck className="h-6 w-6 text-green-600 dark:text-green-400" />
            </div>
          </div>
          <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
            {((realTimeMetrics.vehiclesInUse / vehicles.length) * 100).toFixed(1)}% utilization
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Drivers on Duty</p>
              <p className="text-2xl font-bold text-gray-900 dark:text-gray-100">
                {realTimeMetrics.driversOnDuty}
              </p>
            </div>
            <div className="p-3 bg-purple-100 dark:bg-purple-900/20 rounded-full">
              <Users className="h-6 w-6 text-purple-600 dark:text-purple-400" />
            </div>
          </div>
          <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
            Active drivers
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Distance Today</p>
              <p className="text-2xl font-bold text-gray-900 dark:text-gray-100">
                {realTimeMetrics.totalDistanceToday.toLocaleString()}
              </p>
            </div>
            <div className="p-3 bg-orange-100 dark:bg-orange-900/20 rounded-full">
              <MapPin className="h-6 w-6 text-orange-600 dark:text-orange-400" />
            </div>
          </div>
          <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
            Kilometers traveled
          </p>
        </div>
      </div>

      {/* Predictive Insights */}
      {predictiveInsights.length > 0 && (
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700">
          <div className="p-6 border-b border-gray-200 dark:border-gray-700">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100">
              Predictive Insights
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-400">
              AI-powered recommendations based on current data patterns
            </p>
          </div>
          
          <div className="p-6 space-y-4">
            {predictiveInsights.map((insight, index) => (
              <div
                key={index}
                className={`p-4 rounded-lg border ${getInsightColor(insight.impact)}`}
              >
                <div className="flex items-start justify-between">
                  <div className="flex items-start space-x-3">
                    <div className="flex-shrink-0">
                      {getInsightIcon(insight.type)}
                    </div>
                    <div className="flex-1">
                      <h4 className="font-medium">{insight.title}</h4>
                      <p className="text-sm mt-1 opacity-90">{insight.description}</p>
                      <p className="text-sm mt-2 font-medium">
                        <strong>Recommendation:</strong> {insight.recommendation}
                      </p>
                      <p className="text-xs mt-1 opacity-75">
                        Timeframe: {insight.timeframe}
                      </p>
                    </div>
                  </div>
                  <div className="flex flex-col items-end space-y-1">
                    <span className="text-xs font-medium">
                      {insight.confidence}% confidence
                    </span>
                    <span className="text-xs px-2 py-1 rounded-full bg-white/50">
                      {insight.impact} impact
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Trends Chart Placeholder */}
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6">
        <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100 mb-4">
          Activity Trends ({selectedTimeframe})
        </h3>
        <div className="h-64 flex items-center justify-center text-gray-500 dark:text-gray-400">
          <div className="text-center">
            <TrendingUp className="h-12 w-12 mx-auto mb-2 opacity-50" />
            <p>Trend visualization would be implemented here</p>
            <p className="text-sm">Using a charting library like Recharts or Chart.js</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RealTimeAnalytics;

import React, { useState, useMemo } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid, Legend } from 'recharts';
import { Filter, Calendar, TrendingUp, AlertTriangle, Download, ChevronDown, ExternalLink } from 'lucide-react';
import { Trip, Vehicle } from '@/types';
import { format, subDays, isAfter, isBefore, parseISO, startOfDay, endOfDay } from 'date-fns';
import { useNavigate } from 'react-router-dom';
import { supabase } from '@/utils/supabaseClient';
import { useQuery } from '@tanstack/react-query';

interface EnhancedMileageChartProps {
  trips: Trip[];
  vehicles: Vehicle[];
}

interface Anomaly {
  trip_id: string;
  trip_serial_number: string;
  vehicle_registration: string;
  date: string;
  mileage: number;
  distance: number;
  fuel: number;
  issues: Array<{
    type: string;
    severity: 'critical' | 'high' | 'medium';
    message: string;
  }>;
}

// Client-side enhanced anomaly detection with more sensitive criteria
const detectClientSideAnomalies = (trips: Trip[], vehicles: Vehicle[]) => {
  const anomalies: any[] = [];
  
  trips.forEach(trip => {
    const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
    if (!vehicle || !trip.calculated_kmpl) return;
    
    const distance = trip.end_km - trip.start_km;
    const fuel = trip.fuel_quantity || 0;
    const kmpl = trip.calculated_kmpl;
    
    // More sensitive criteria for demonstration - lowered thresholds
    if (kmpl > 15) { // Lowered from 25 to 15 for more sensitive detection
      anomalies.push({
        anomaly_type: 'suspicious_efficiency',
        severity: 'medium',
        trip_ids: [trip.id],
        trip_serials: [trip.trip_serial_number || 'N/A'],
        anomaly_details: {
          vehicle_registration: vehicle.registration_number,
          mileage: kmpl,
          distance: distance,
          fuel: fuel
        }
      });
    }
    
    if (kmpl < 8 && fuel > 0) { // Lowered from 5 to 8 for more sensitive detection
      anomalies.push({
        anomaly_type: 'poor_efficiency',
        severity: 'high',
        trip_ids: [trip.id],
        trip_serials: [trip.trip_serial_number || 'N/A'],
        anomaly_details: {
          vehicle_registration: vehicle.registration_number,
          mileage: kmpl,
          distance: distance,
          fuel: fuel
        }
      });
    }
    
    if (distance > 500) { // Lowered from 1000 to 500 for more sensitive detection
      anomalies.push({
        anomaly_type: 'excessive_distance',
        severity: 'high',
        trip_ids: [trip.id],
        trip_serials: [trip.trip_serial_number || 'N/A'],
        anomaly_details: {
          vehicle_registration: vehicle.registration_number,
          mileage: kmpl,
          distance: distance,
          fuel: fuel
        }
      });
    }
    
    if (fuel > 100) { // Lowered from 200 to 100 for more sensitive detection
      anomalies.push({
        anomaly_type: 'excessive_fuel',
        severity: 'medium',
        trip_ids: [trip.id],
        trip_serials: [trip.trip_serial_number || 'N/A'],
        anomaly_details: {
          vehicle_registration: vehicle.registration_number,
          mileage: kmpl,
          distance: distance,
          fuel: fuel
        }
      });
    }
    
    // Add more sensitive criteria for demonstration
    if (kmpl > 20) { // Very high efficiency
      anomalies.push({
        anomaly_type: 'suspicious_efficiency',
        severity: 'critical',
        trip_ids: [trip.id],
        trip_serials: [trip.trip_serial_number || 'N/A'],
        anomaly_details: {
          vehicle_registration: vehicle.registration_number,
          mileage: kmpl,
          distance: distance,
          fuel: fuel
        }
      });
    }
  });
  
  return anomalies;
};

const EnhancedMileageChart: React.FC<EnhancedMileageChartProps> = ({ trips, vehicles }) => {
  const navigate = useNavigate();
  const [selectedVehicles, setSelectedVehicles] = useState<string[]>(['all']);
  const [dateRange, setDateRange] = useState<string>('30days');
  const [customDateStart, setCustomDateStart] = useState<string>('');
  const [customDateEnd, setCustomDateEnd] = useState<string>('');
  const [showVehicleDropdown, setShowVehicleDropdown] = useState(false);
  const [showDateDropdown, setShowDateDropdown] = useState(false);
  const [showAverageDropdown, setShowAverageDropdown] = useState(false);
  const [averageType, setAverageType] = useState<'overall' | 'perVehicle' | 'rolling7'>('overall');

  // Get unique vehicles from the vehicles prop
  const vehicleOptions = useMemo(() => {
    return ['all', ...vehicles.map(v => v.registration_number)];
  }, [vehicles]);

  // Calculate date range
  const { startDate, endDate } = useMemo(() => {
    const now = new Date();
    let start = new Date();
    let end = now;

    switch (dateRange) {
      case '7days':
        start = subDays(now, 7);
        break;
      case '14days':
        start = subDays(now, 14);
        break;
      case '30days':
        start = subDays(now, 30);
        break;
      case '60days':
        start = subDays(now, 60);
        break;
      case '90days':
        start = subDays(now, 90);
        break;
      case 'custom':
        if (customDateStart && customDateEnd) {
          start = startOfDay(parseISO(customDateStart));
          end = endOfDay(parseISO(customDateEnd));
        }
        break;
    }

    return { startDate: start, endDate: end };
  }, [dateRange, customDateStart, customDateEnd]);

  // Filter trips based on selections
  const filteredTrips = useMemo(() => {
    return trips.filter(trip => {
      // Filter by vehicle
      if (!selectedVehicles.includes('all')) {
        const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
        if (!vehicle || !selectedVehicles.includes(vehicle.registration_number)) {
          return false;
        }
      }

      // Filter by date
      const tripDate = parseISO(trip.trip_end_date);
      if (isBefore(tripDate, startDate) || isAfter(tripDate, endDate)) {
        return false;
      }

      // Only include trips with calculated mileage
      return trip.calculated_kmpl !== undefined && trip.calculated_kmpl !== null;
    });
  }, [trips, vehicles, selectedVehicles, startDate, endDate]);

  // Fetch anomalies from database with more sensitive criteria
  const { data: anomaliesData } = useQuery({
    queryKey: ['mileage-anomalies', selectedVehicles, startDate, endDate],
    queryFn: async () => {
      const vehicleId = selectedVehicles.includes('all') 
        ? null 
        : vehicles.find(v => selectedVehicles.includes(v.registration_number))?.id;

      // First try the database function
      const { data: dbAnomalies, error: dbError } = await supabase.rpc('analyze_value_anomalies', {
        p_vehicle_id: vehicleId,
        p_date_from: format(startDate, 'yyyy-MM-dd'),
        p_date_to: format(endDate, 'yyyy-MM-dd'),
        p_include_edge_cases: true
      });

      if (dbError) {
        console.error('Error fetching anomalies from DB:', dbError);
      }

      // Also run client-side enhanced anomaly detection for more sensitive criteria
      const clientAnomalies = detectClientSideAnomalies(filteredTrips, vehicles);
      
      // Combine and deduplicate anomalies
      const allAnomalies = [...(dbAnomalies || []), ...clientAnomalies];
      return allAnomalies;
    },
    enabled: trips.length > 0 && filteredTrips.length > 0
  });

  // Helper function to get anomaly message
  const getAnomalyMessage = (type: string): string => {
    const messages: Record<string, string> = {
      'suspicious_efficiency': 'Unusually high mileage (>40 km/L)',
      'poor_efficiency': 'Very low mileage (<2 km/L)',
      'excessive_distance': 'Very long trip (>2000 km)',
      'excessive_fuel': 'High fuel consumption (>300 L)',
      'negative_distance': 'Invalid odometer readings',
      'impossible_speed': 'Unrealistic speed detected',
      'excessive_duration': 'Trip duration too long (>48 hours)',
      'high_fuel_expense': 'Fuel expense exceeds threshold',
      'high_driver_expense': 'Driver expense exceeds threshold'
    };
    return messages[type] || 'Data quality issue detected';
  };

  // Process anomalies from database response
  const anomalies = useMemo(() => {
    if (!anomaliesData) return [];

    const processed: Anomaly[] = [];

    anomaliesData.forEach((anomalyGroup: any) => {
      const { anomaly_type, severity, trip_ids, trip_serials, anomaly_details } = anomalyGroup;

      // Map each trip in this anomaly group
      trip_ids?.forEach((tripId: string, index: number) => {
        const trip = filteredTrips.find(t => t.id === tripId);
        if (!trip) return;

        const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
        
        processed.push({
          trip_id: tripId,
          trip_serial_number: trip_serials?.[index] || trip.trip_serial_number || 'N/A',
          vehicle_registration: vehicle?.registration_number || 'Unknown',
          date: format(parseISO(trip.trip_end_date), 'dd MMM yyyy'),
          mileage: trip.calculated_kmpl || 0,
          distance: trip.end_km - trip.start_km,
          fuel: trip.fuel_quantity || 0,
          issues: [{
            type: anomaly_type,
            severity: severity as 'critical' | 'high' | 'medium',
            message: getAnomalyMessage(anomaly_type)
          }]
        });
      });
    });

    return processed;
  }, [anomaliesData, filteredTrips, vehicles, getAnomalyMessage]);

  // Calculate average mileage
  const averageMileage = useMemo(() => {
    if (filteredTrips.length === 0) return 0;

    if (averageType === 'overall') {
      const sum = filteredTrips.reduce((acc, trip) => acc + (trip.calculated_kmpl || 0), 0);
      return (sum / filteredTrips.length).toFixed(2);
    } else if (averageType === 'perVehicle') {
      const vehicleAverages: Record<string, { sum: number; count: number }> = {};
      
      filteredTrips.forEach(trip => {
        const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
        if (!vehicle) return;
        
        const key = vehicle.registration_number;
        if (!vehicleAverages[key]) {
          vehicleAverages[key] = { sum: 0, count: 0 };
        }
        vehicleAverages[key].sum += trip.calculated_kmpl || 0;
        vehicleAverages[key].count += 1;
      });
      
      return vehicleAverages;
    } else if (averageType === 'rolling7') {
      const last7Days = filteredTrips.slice(-7);
      if (last7Days.length === 0) return '0.00';
      const sum = last7Days.reduce((acc, trip) => acc + (trip.calculated_kmpl || 0), 0);
      return (sum / last7Days.length).toFixed(2);
    }

    return 0;
  }, [filteredTrips, averageType, vehicles]);

  // Prepare chart data with anomaly indicators
  const chartData = useMemo(() => {
    const dateMap = new Map<string, Record<string, any>>();

    filteredTrips.forEach(trip => {
      const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
      if (!vehicle) return;

      const dateKey = format(parseISO(trip.trip_end_date), 'dd/MM');
      const vehicleKey = vehicle.registration_number;

      if (!dateMap.has(dateKey)) {
        dateMap.set(dateKey, { date: dateKey });
      }

      const dateEntry = dateMap.get(dateKey)!;
      
      // Check if this trip has anomalies
      const tripAnomalies = anomalies.filter(a => a.trip_id === trip.id);
      const hasAnomaly = tripAnomalies.length > 0;
      const anomalySeverity = hasAnomaly ? tripAnomalies[0].issues[0]?.severity : null;
      
      // Calculate average if multiple trips on same day
      if (dateEntry[vehicleKey]) {
        dateEntry[vehicleKey] = (dateEntry[vehicleKey] + (trip.calculated_kmpl || 0)) / 2;
      } else {
        dateEntry[vehicleKey] = trip.calculated_kmpl || 0;
      }
      
      // Add anomaly metadata
      dateEntry[`${vehicleKey}_anomaly`] = hasAnomaly;
      dateEntry[`${vehicleKey}_severity`] = anomalySeverity;
      dateEntry[`${vehicleKey}_tripId`] = trip.id;
    });

    return Array.from(dateMap.values())
      .sort((a, b) => {
        const [dayA, monthA] = a.date.split('/').map(Number);
        const [dayB, monthB] = b.date.split('/').map(Number);
        return monthA !== monthB ? monthA - monthB : dayA - dayB;
      });
  }, [filteredTrips, vehicles, anomalies]);

  // Get unique vehicle registrations for chart lines
  const chartVehicles = useMemo(() => {
    const vehicleSet = new Set<string>();
    filteredTrips.forEach(trip => {
      const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
      if (vehicle) vehicleSet.add(vehicle.registration_number);
    });
    return Array.from(vehicleSet);
  }, [filteredTrips, vehicles]);

  const toggleVehicle = (vehicle: string) => {
    if (vehicle === 'all') {
      setSelectedVehicles(['all']);
    } else {
      setSelectedVehicles(prev => {
        const filtered = prev.filter(v => v !== 'all');
        if (filtered.includes(vehicle)) {
          const updated = filtered.filter(v => v !== vehicle);
          return updated.length === 0 ? ['all'] : updated;
        } else {
          return [...filtered, vehicle];
        }
      });
    }
  };

  const exportAnomalies = () => {
    const csv = [
      ['Trip ID', 'Vehicle', 'Date', 'Mileage (km/L)', 'Distance (km)', 'Fuel (L)', 'Issues', 'Severity'],
      ...anomalies.map(a => [
        a.trip_serial_number,
        a.vehicle_registration,
        a.date,
        a.mileage.toFixed(2),
        a.distance.toFixed(0),
        a.fuel.toFixed(1),
        a.issues.map(i => i.message).join('; '),
        a.issues[0]?.severity || 'medium'
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `mileage_anomalies_${format(new Date(), 'yyyy-MM-dd')}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const handleAnomalyClick = (tripId: string) => {
    navigate(`/trips`, { state: { highlightTripId: tripId } });
  };

  // Custom dot component for anomaly indicators
  const CustomDot = (props: any) => {
    const { cx, cy, payload, dataKey } = props;
    const hasAnomaly = payload[`${dataKey}_anomaly`];
    const severity = payload[`${dataKey}_severity`];
    const tripId = payload[`${dataKey}_tripId`];
    
    if (!hasAnomaly) {
      return (
        <circle
          cx={cx}
          cy={cy}
          r={3}
          fill={colors[chartVehicles.indexOf(dataKey) % colors.length]}
          stroke="white"
          strokeWidth={2}
          style={{ cursor: 'pointer' }}
          onClick={() => tripId && navigate(`/trips`, { state: { highlightTripId: tripId } })}
        />
      );
    }
    
    // Anomaly indicator with different colors based on severity
    const anomalyColor = severity === 'critical' ? '#ef4444' : 
                        severity === 'high' ? '#f59e0b' : '#3b82f6';
    
    return (
      <g>
        {/* Outer ring for anomaly */}
        <circle
          cx={cx}
          cy={cy}
          r={6}
          fill="none"
          stroke={anomalyColor}
          strokeWidth={3}
          style={{ cursor: 'pointer' }}
          onClick={() => tripId && navigate(`/trips`, { state: { highlightTripId: tripId } })}
        />
        {/* Inner dot */}
        <circle
          cx={cx}
          cy={cy}
          r={3}
          fill={anomalyColor}
          stroke="white"
          strokeWidth={2}
          style={{ cursor: 'pointer' }}
          onClick={() => tripId && navigate(`/trips`, { state: { highlightTripId: tripId } })}
        />
        {/* Alert icon for critical anomalies */}
        {severity === 'critical' && (
          <text
            x={cx}
            y={cy - 12}
            textAnchor="middle"
            fontSize="10"
            fill={anomalyColor}
            fontWeight="bold"
            style={{ cursor: 'pointer' }}
            onClick={() => tripId && navigate(`/trips`, { state: { highlightTripId: tripId } })}
          >
            
          </text>
        )}
      </g>
    );
  };

  const colors = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#06b6d4'];

  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm p-6 border border-gray-200 dark:border-gray-700">
      {/* Header with Filters */}
      <div className="mb-6">
        <div className="flex items-center justify-between mb-4 flex-wrap gap-4">
          <h2 className="text-xl sm:text-2xl font-bold text-gray-900 dark:text-gray-100">
            Mileage Trends
          </h2>

          {/* Average Display with Dropdown */}
          <div className="relative">
            <button
              onClick={() => setShowAverageDropdown(!showAverageDropdown)}
              className="flex items-center gap-2 px-4 py-2 bg-blue-50 dark:bg-blue-900/20 rounded-lg hover:bg-blue-100 dark:hover:bg-blue-900/30 transition-colors"
            >
              <TrendingUp className="w-4 h-4 text-blue-600 dark:text-blue-400" />
              <div className="text-left">
                <div className="text-xs text-gray-600 dark:text-gray-400">
                  {averageType === 'overall' ? 'Overall Average' :
                    averageType === 'perVehicle' ? 'Per Vehicle' :
                      'Rolling 7-Day'}
                </div>
                <div className="text-lg font-bold text-blue-600 dark:text-blue-400">
                  {typeof averageMileage === 'object' ?
                    'View Details' :
                    `${averageMileage} km/L`
                  }
                </div>
              </div>
              <ChevronDown className="w-4 h-4" />
            </button>

            {showAverageDropdown && (
              <div className="absolute right-0 mt-2 w-64 bg-white dark:bg-gray-800 border dark:border-gray-700 rounded-lg shadow-lg z-10">
                <div className="p-2">
                  <button
                    onClick={() => {
                      setAverageType('overall');
                      setShowAverageDropdown(false);
                    }}
                    className="w-full text-left px-3 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded text-sm"
                  >
                    Overall Average
                  </button>
                  <button
                    onClick={() => {
                      setAverageType('rolling7');
                      setShowAverageDropdown(false);
                    }}
                    className="w-full text-left px-3 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded text-sm"
                  >
                    Rolling 7-Day Average
                  </button>
                  <button
                    onClick={() => {
                      setAverageType('perVehicle');
                      setShowAverageDropdown(false);
                    }}
                    className="w-full text-left px-3 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded text-sm"
                  >
                    Per Vehicle Average
                  </button>
                </div>

                {typeof averageMileage === 'object' && (
                  <div className="border-t dark:border-gray-700 p-3">
                    <h4 className="text-xs font-semibold text-gray-600 dark:text-gray-400 mb-2">Per Vehicle:</h4>
                    {Object.entries(averageMileage).map(([vehicle, data]) => (
                      <div key={vehicle} className="flex justify-between text-sm py-1">
                        <span className="text-gray-700 dark:text-gray-300">{vehicle}</span>
                        <span className="font-semibold text-gray-900 dark:text-gray-100">
                          {(data.sum / data.count).toFixed(2)} km/L
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Filter Bar */}
        <div className="flex flex-wrap gap-3">
          {/* Vehicle Filter */}
          <div className="relative">
            <button
              onClick={() => setShowVehicleDropdown(!showVehicleDropdown)}
              className="flex items-center gap-2 px-4 py-2 border dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
            >
              <Filter className="w-4 h-4" />
              <span className="text-sm font-medium">
                {selectedVehicles.includes('all')
                  ? 'All Vehicles'
                  : `${selectedVehicles.length} Selected`}
              </span>
              <ChevronDown className="w-4 h-4" />
            </button>

            {showVehicleDropdown && (
              <div className="absolute top-full mt-2 w-64 bg-white dark:bg-gray-800 border dark:border-gray-700 rounded-lg shadow-lg z-10 max-h-64 overflow-y-auto">
                {vehicleOptions.map(vehicle => (
                  <label
                    key={vehicle}
                    className="flex items-center px-4 py-2 hover:bg-gray-50 dark:hover:bg-gray-700 cursor-pointer"
                  >
                    <input
                      type="checkbox"
                      checked={selectedVehicles.includes(vehicle)}
                      onChange={() => toggleVehicle(vehicle)}
                      className="mr-3"
                    />
                    <span className="text-sm">
                      {vehicle === 'all' ? 'All Vehicles' : vehicle}
                    </span>
                  </label>
                ))}
              </div>
            )}
          </div>

          {/* Date Range Filter */}
          <div className="relative">
            <button
              onClick={() => setShowDateDropdown(!showDateDropdown)}
              className="flex items-center gap-2 px-4 py-2 border dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
            >
              <Calendar className="w-4 h-4" />
              <span className="text-sm font-medium">
                {dateRange === 'custom' ? 'Custom Range' :
                  dateRange.replace('days', ' Days')}
              </span>
              <ChevronDown className="w-4 h-4" />
            </button>

            {showDateDropdown && (
              <div className="absolute top-full mt-2 w-64 bg-white dark:bg-gray-800 border dark:border-gray-700 rounded-lg shadow-lg z-10">
                <div className="p-2">
                  <div className="text-xs font-semibold text-gray-600 dark:text-gray-400 mb-2 px-2">Quick Options</div>
                  {['7days', '14days', '30days', '60days', '90days'].map(option => (
                    <button
                      key={option}
                      onClick={() => {
                        setDateRange(option);
                        setShowDateDropdown(false);
                      }}
                      className="w-full text-left px-3 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded text-sm"
                    >
                      Last {option.replace('days', ' Days')}
                    </button>
                  ))}

                  <div className="border-t dark:border-gray-700 mt-2 pt-2">
                    <div className="text-xs font-semibold text-gray-600 dark:text-gray-400 mb-2 px-2">Custom Range</div>
                    <div className="px-2 space-y-2">
                      <input
                        type="date"
                        value={customDateStart}
                        onChange={(e) => setCustomDateStart(e.target.value)}
                        className="w-full px-2 py-1 border dark:border-gray-600 dark:bg-gray-700 rounded text-sm"
                        placeholder="Start Date"
                      />
                      <input
                        type="date"
                        value={customDateEnd}
                        onChange={(e) => setCustomDateEnd(e.target.value)}
                        className="w-full px-2 py-1 border dark:border-gray-600 dark:bg-gray-700 rounded text-sm"
                        placeholder="End Date"
                      />
                      <button
                        onClick={() => {
                          if (customDateStart && customDateEnd) {
                            setDateRange('custom');
                            setShowDateDropdown(false);
                          }
                        }}
                        disabled={!customDateStart || !customDateEnd}
                        className="w-full px-3 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        Apply
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Enhanced Chart */}
      <div className="mb-6" style={{ height: '450px' }}>
        {chartData.length > 0 ? (
          <ResponsiveContainer width="100%" height="100%">
            <LineChart
              data={chartData}
              margin={{ top: 20, right: 30, left: 0, bottom: 20 }}
            >
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis
                dataKey="date"
                tick={{ fontSize: 12 }}
                tickLine={false}
                axisLine={{ stroke: '#d1d5db' }}
                angle={-45}
                textAnchor="end"
                height={60}
              />
              <YAxis
                tickLine={false}
                axisLine={{ stroke: '#d1d5db' }}
                tick={{ fontSize: 12 }}
                domain={['auto', 'auto']}
                label={{
                  value: 'Mileage (km/L)',
                  angle: -90,
                  position: 'insideLeft',
                  style: { fontSize: 12, fontWeight: 600 }
                }}
              />
              <Tooltip
                contentStyle={{
                  backgroundColor: 'white',
                  borderRadius: '8px',
                  border: '1px solid #e5e7eb',
                  boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                }}
                formatter={(value: number) => `${value.toFixed(2)} km/L`}
              />
              <Legend
                wrapperStyle={{ paddingTop: '20px' }}
                iconType="line"
              />
              {chartVehicles.map((vehicle, index) => (
                <Line
                  key={vehicle}
                  type="monotone"
                  dataKey={vehicle}
                  stroke={colors[index % colors.length]}
                  strokeWidth={2}
                  dot={<CustomDot />}
                  activeDot={{ r: 5 }}
                  name={vehicle}
                  connectNulls
                />
              ))}
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <div className="flex items-center justify-center h-full text-gray-500 dark:text-gray-400">
            <div className="text-center">
              <TrendingUp className="w-12 h-12 mx-auto mb-2 opacity-50" />
              <p>No data available for the selected period</p>
            </div>
          </div>
        )}
      </div>

      {/* Anomaly Legend */}
      {anomalies.length > 0 && (
        <div className="mb-4 p-3 bg-gray-50 dark:bg-gray-700/50 rounded-lg">
          <h4 className="text-sm font-semibold text-gray-700 dark:text-gray-300 mb-2">Chart Legend:</h4>
          <div className="flex flex-wrap gap-4 text-xs">
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-full bg-red-500 border-2 border-white"></div>
              <span className="text-gray-600 dark:text-gray-400">Critical Anomaly</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-full bg-orange-500 border-2 border-white"></div>
              <span className="text-gray-600 dark:text-gray-400">High Priority</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-full bg-blue-500 border-2 border-white"></div>
              <span className="text-gray-600 dark:text-gray-400">Medium Priority</span>
            </div>
            <div className="flex items-center gap-2">
              <span className="text-gray-600 dark:text-gray-400">Click any point to view trip details</span>
            </div>
          </div>
        </div>
      )}

      {/* Anomalies Section */}
      {anomalies.length > 0 && (
        <div className="border-t dark:border-gray-700 pt-6">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-2">
              <AlertTriangle className="w-5 h-5 text-orange-500" />
              <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                Anomalies Detected ({anomalies.length})
              </h3>
            </div>
            <button
              onClick={exportAnomalies}
              className="flex items-center gap-2 px-3 py-2 text-sm border dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
            >
              <Download className="w-4 h-4" />
              Export CSV
            </button>
          </div>

          <div className="text-sm text-gray-600 dark:text-gray-400 mb-3">
            Click on any entry to review and verify the trip data
          </div>

          <div className="space-y-2 max-h-96 overflow-y-auto">
            {anomalies.map((anomaly, index) => (
              <div
                key={index}
                className="p-4 border dark:border-gray-700 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700/50 cursor-pointer transition-colors"
                onClick={() => handleAnomalyClick(anomaly.trip_id)}
              >
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2 flex-wrap">
                      <span className="font-mono text-sm font-semibold text-blue-600 dark:text-blue-400">
                        {anomaly.trip_serial_number}
                      </span>
                      <span className="text-sm text-gray-600 dark:text-gray-400">{anomaly.vehicle_registration}</span>
                      <span className="text-xs text-gray-500 dark:text-gray-500">{anomaly.date}</span>
                      <ExternalLink className="w-3 h-3 text-gray-400" />
                    </div>
                    <div className="flex items-center gap-2 mb-2 flex-wrap">
                      <span className="text-sm">
                        <span className="font-medium">Mileage:</span> {anomaly.mileage.toFixed(2)} km/L
                      </span>
                      <span className="text-gray-400">|</span>
                      <span className="text-sm">
                        <span className="font-medium">Distance:</span> {anomaly.distance.toFixed(0)} km
                      </span>
                      <span className="text-gray-400">|</span>
                      <span className="text-sm">
                        <span className="font-medium">Fuel:</span> {anomaly.fuel.toFixed(1)} L
                      </span>
                    </div>
                    <div className="flex flex-wrap gap-2">
                      {anomaly.issues.map((issue, idx) => (
                        <span
                          key={idx}
                          className={`px-2 py-1 text-xs rounded-full ${issue.severity === 'critical' ? 'bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-400' :
                              issue.severity === 'high' ? 'bg-orange-100 text-orange-700 dark:bg-orange-900/30 dark:text-orange-400' :
                                'bg-yellow-100 text-yellow-700 dark:bg-yellow-900/30 dark:text-yellow-400'
                            }`}
                        >
                          {issue.message}
                        </span>
                      ))}
                    </div>
                  </div>
                  <div className="ml-4">
                    <span className={`px-3 py-1 text-xs font-semibold rounded-full ${anomaly.issues.some(i => i.severity === 'critical')
                        ? 'bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-400'
                        : anomaly.issues.some(i => i.severity === 'high')
                          ? 'bg-orange-100 text-orange-700 dark:bg-orange-900/30 dark:text-orange-400'
                          : 'bg-yellow-100 text-yellow-700 dark:bg-yellow-900/30 dark:text-yellow-400'
                      }`}>
                      {anomaly.issues.some(i => i.severity === 'critical') ? 'Critical' :
                        anomaly.issues.some(i => i.severity === 'high') ? 'High' : 'Medium'}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {anomalies.length === 0 && filteredTrips.length > 0 && (
        <div className="border-t dark:border-gray-700 pt-6">
          <div className="text-center text-gray-500 dark:text-gray-400 mb-4">
            <AlertTriangle className="w-12 h-12 mx-auto mb-2 opacity-30" />
            <p>No anomalies detected in the current data range</p>
            <p className="text-sm mt-1">Your fleet data looks good! </p>
          </div>
          
          {/* Demo section to show how anomalies would appear */}
          <div className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-4 border border-blue-200 dark:border-blue-800">
            <h4 className="text-sm font-semibold text-blue-800 dark:text-blue-200 mb-2">
               Anomaly Detection Demo
            </h4>
            <p className="text-xs text-blue-700 dark:text-blue-300 mb-3">
              The system will automatically detect and highlight trips with unusual patterns:
            </p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 text-xs">
              <div className="flex items-center gap-2">
                <div className="w-3 h-3 rounded-full bg-red-500 border-2 border-white"></div>
                <span className="text-blue-700 dark:text-blue-300">Critical: Very high efficiency (&gt;20 km/L)</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-3 h-3 rounded-full bg-orange-500 border-2 border-white"></div>
                <span className="text-blue-700 dark:text-blue-300">High: Poor efficiency (&lt;8 km/L)</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-3 h-3 rounded-full bg-blue-500 border-2 border-white"></div>
                <span className="text-blue-700 dark:text-blue-300">Medium: Suspicious efficiency (&gt;15 km/L)</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-3 h-3 rounded-full bg-orange-500 border-2 border-white"></div>
                <span className="text-blue-700 dark:text-blue-300">High: Excessive distance (&gt;500 km)</span>
              </div>
            </div>
            <p className="text-xs text-blue-600 dark:text-blue-400 mt-3">
              Click any data point on the chart to navigate to trip details for correction.
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default EnhancedMileageChart;

import React, { useState, useEffect } from "react";
import { useForm, Controller, useFieldArray } from "react-hook-form";
import { Driver, Vehicle } from "@/types";
import Input from "../ui/Input";
import Select from "../ui/Select";
import MultiSelect from "../ui/MultiSelect";
import DocumentUploader from "../shared/DocumentUploader";
import Button from "../ui/Button";
import Textarea from "../ui/Textarea";
import {
  User,
  Phone,
  Mail,
  Calendar,
  FileText,
  Upload,
  Trash2,
  Plus,
  Users,
  FileCheck,
  Database,
  CheckCircle,
  AlertTriangle,
  AlertCircle,
  Shield,
} from "lucide-react";
import { getVehicles } from "../../utils/storage";
import CollapsibleSection from "../ui/CollapsibleSection";
import { toast } from "react-toastify";
import { supabase } from "../../utils/supabaseClient";
import { format, differenceInYears, differenceInMonths } from "date-fns";
import { 
  validateIndianLicense, 
  validateIndianMobile, 
  validateAadhar,
  parseLicenseNumber,
  formatIndianMobile,
  formatAadhar,
  LICENSE_TYPES,
  canDriveVehicleType,
  VEHICLE_AUTHORIZATION
} from "../../utils/indianValidation";
import { checkDriverLicenseExpiry, getExpiryStatusColor } from "../../utils/documentExpiry";

interface DriverFormProps {
  initialData: Partial<Driver>;
  onSubmit: (data: Omit<Driver, "id">) => void;
  isSubmitting?: boolean;
}

const DriverForm: React.FC<DriverFormProps> = ({
  initialData,
  onSubmit,
  isSubmitting = false,
}) => {
  const [photoPreview, setPhotoPreview] = useState<string | null>(
    initialData?.driver_photo_url || null
  );
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [fieldsDisabled, setFieldsDisabled] = useState(true);
  const [isFetching, setIsFetching] = useState(false);
  const [fetchStatus, setFetchStatus] = useState<
    "idle" | "fetching" | "success" | "error"
  >("idle");
  const [uploadedDocuments, setUploadedDocuments] = useState<Record<string, string[]>>({});
  const [experienceDisplay, setExperienceDisplay] = useState<string>('0 years, 0 months');

  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
    reset,
    watch,
    setValue,
  } = useForm<Driver>({
    defaultValues: {
      name: "",
      license_number: "",
      dob: "",
      father_or_husband_name: "",
      contact_number: "",
      email: "",
      status: "active",
      ...initialData,
      other_documents: initialData.other_documents
        ? initialData.other_documents //  Confirm field refactor here
        : [], //  Confirm field refactor here
      medical_doc_file: initialData.medical_doc_url ? [] : initialData.medical_doc_file,
      police_doc_file: initialData.police_doc_url ? [] : initialData.police_doc_file,
      aadhar_doc_file: initialData.aadhar_doc_url ? [] : initialData.aadhar_doc_file,
      license_doc_file: initialData.license_doc_url ? [] : initialData.license_doc_file,
    },
  });

  // Enable fields if initialData is present (for edit mode)
  useEffect(() => {
    if (initialData?.id) {
      setFieldsDisabled(false);
      
      // Calculate experience display for existing data
      if (initialData.join_date) {
        const joinDate = new Date(initialData.join_date);
        const today = new Date();
        const totalMonths = differenceInMonths(today, joinDate);
        const years = Math.floor(totalMonths / 12);
        const months = totalMonths % 12;
        setExperienceDisplay(`${years} year${years !== 1 ? 's' : ''}, ${months} month${months !== 1 ? 's' : ''}`);
      }
    }
  }, [initialData]);
  // Field array for other documents
  const { fields, append, remove } = useFieldArray({
    control,
    name: "other_documents",
  });

  // Fetch vehicles for the dropdown
  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const vehiclesData = await getVehicles();
        // Filter out archived vehicles
        const activeVehicles = vehiclesData.filter(
          (v) => v.status !== "archived"
        );
        setVehicles(activeVehicles);
      } catch (error) {
        console.error("Error fetching vehicles:", error);
        toast.error("Failed to load vehicles");
      }
    };

    fetchVehicles();
  }, []);

  const handlePhotoChange = (file: File | null) => {
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPhotoPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    } else {
      setPhotoPreview(initialData?.driver_photo_url || null);
    }
  };

  // --- Fetch Driver Details Handler ---
  const handleFetchDetails = async () => {
    const licenseNumber = watch("license_number");
    const dob = watch("dob");

    if (!licenseNumber || !dob) {
      toast.error("Please enter license number and date of birth.");
      return;
    }

    setIsFetching(true);
    setFetchStatus("fetching");
    setFieldsDisabled(true);

    try {
      const dob_formatted = dob.split("-").reverse().join("-");

      const { data: result, error } = await supabase.functions.invoke(
        "fetch-driver-details",
        {
          body: {
            licence_number: licenseNumber,
            dob: dob_formatted,
          },
        }
      );

      if (error || !result?.success) {
        throw new Error(
          result?.message || error?.message || "Failed to fetch details"
        );
      }

      const driver = result.response || result.data?.response || {};

      // Convert base64 image to data URL if present
      let photoUrl = undefined; //  Confirm field refactor here
      if (driver.image) {
        photoUrl = `data:image/jpeg;base64,${driver.image}`;
        setPhotoPreview(photoUrl);
      }

      // Map API response to form fields
      const mapped: Driver = {
        id: initialData?.id || undefined,
        name: driver.holder_name || "",
        father_or_husband_name: driver.father_or_husband_name || "",
        gender:
          (driver.gender &&
            (driver.gender.toUpperCase() === "MALE"
              ? "MALE"
              : driver.gender.toUpperCase() === "FEMALE"
              ? "FEMALE"
          : "OTHER")) || //  Confirm field refactor here
          "MALE",
        dob: (driver?.dob && driver?.dob.split("-").reverse().join("-")) || dob,
        blood_group:
          (driver?.blood_group && driver.blood_group.toUpperCase()) || "",
        address: driver?.permanent_address || driver?.temporary_address || "",
        contact_number: driver?.contact_number || "",
        email: driver?.email || "", //  Confirm field refactor here
        license_number: driver?.license_number || licenseNumber,
        vehicle_class:
          (driver?.vehicle_class &&
            driver?.vehicle_class.map((v: any) => v?.cov)) ||
          [],
        valid_from:
          (driver?.valid_from &&
            driver?.valid_from.split("-").reverse().join("-")) ||
          "",
        license_expiry_date:
          (driver?.valid_upto &&
            driver?.valid_upto.split("-").reverse().join("-")) ||
          "",
        license_issue_date:
          (driver?.issue_date &&
            driver?.issue_date.split("-").reverse().join("-")) ||
          "",
        rto_code: driver.rto_code || "",
        rto: driver.rto || "",
        state: driver.state || "",
        join_date: initialData.join_date || format(new Date(), 'yyyy-MM-dd'),
        experience_years: 0,
        primary_vehicle_id: "",
        status: "active",
        driver_photo_url: photoUrl,
      };

      reset(mapped);
      setFieldsDisabled(false);
      setFetchStatus("success");
      toast.success(
        "Driver details fetched. Please verify and complete the form."
      );
    } catch (err: any) {
      toast.error(err.message || "Failed to fetch driver details.");
      setFieldsDisabled(false);
      setFetchStatus("error");
    } finally {
      setIsFetching(false);
    }
  };

  // Auto-calculate experience based on join_date
  useEffect(() => {
    if (watch('join_date')) {
      const joinDate = new Date(watch('join_date'));
      const today = new Date();
      const totalMonths = differenceInMonths(today, joinDate);
      const years = Math.floor(totalMonths / 12);
      const months = totalMonths % 12;
      
      // Store the decimal representation for database
      const decimalYears = parseFloat((totalMonths / 12).toFixed(2));
      setValue('experience_years', decimalYears);
      
      // Set display string
      setExperienceDisplay(`${years} year${years !== 1 ? 's' : ''}, ${months} month${months !== 1 ? 's' : ''}`);
    } else {
      setValue('experience_years', 0);
      setExperienceDisplay('0 years, 0 months');
    }
  }, [watch('join_date'), setValue]);

  // Handle document upload completion
  const handleDocumentUpload = (docType: string, filePaths: string[]) => {
    setUploadedDocuments(prev => ({
      ...prev,
      [docType]: filePaths
    }));
    
    // Update form field with the uploaded file paths
    const fieldName = `${docType}_doc_url` as keyof Driver;
    setValue(fieldName, filePaths as any);
  };

  const onFormSubmit = (data: Driver) => {
    // Include uploaded document URLs in the submission
    const formData = {
      ...data,
      license_doc_url: uploadedDocuments.license || [],
      aadhar_doc_url: uploadedDocuments.aadhar || [],
      police_doc_url: uploadedDocuments.police || [],
      medical_doc_url: uploadedDocuments.medical || [],
    };

    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit(onFormSubmit)} className="space-y-4">
      {/* Top-level Fetch Block */}
      <div className="bg-gray-50 p-3 sm:p-4 rounded-lg border border-gray-200 mb-6">
        <p className="text-sm text-gray-600 mb-3">
          Fetch Driver Info from Government Portal
        </p>
        <div className="flex flex-col md:flex-row items-center gap-3 sm:gap-4">
          <div className="w-full md:w-2/5 mb-2 md:mb-0">
            <Input
              label="License Number"
              placeholder="MH12 20080001234"
              icon={<FileText className="h-4 w-4" />}
              error={errors.license_number?.message}
              required
              disabled={isFetching || isSubmitting}
              {...register("license_number", {
                required: "License number is required",
                validate: (value) => {
                  if (!validateIndianLicense(value)) {
                    return "Please enter a valid Indian driving license number (e.g., MH12 20080001234)";
                  }
                  return true;
                }
              })}
            />
          </div>
          <div className="w-full md:w-2/5 mb-2 md:mb-0">
            <Input
              label="Date of Birth"
              type="date"
              icon={<Calendar className="h-4 w-4" />}
              placeholder="DD-MM-YYYY"
              disabled={isFetching || isSubmitting}
              {...register("dob")}
            />
          </div>
          <div className="w-full md:w-1/5 pt-0 md:pt-2">
            <Button
              type="button"
              disabled={isFetching || isSubmitting}
              isLoading={isFetching}
              className="w-full"
              onClick={handleFetchDetails}
            >
              {isFetching ? "Fetching..." : "Fetch Details"}
            </Button>
            {fetchStatus === "success" && (
              <div className="text-center mt-1">
                <span className="text-xs text-success-600 flex items-center justify-center">
                  <CheckCircle className="h-3 w-3 mr-1" />
                  Details found!
                </span>
              </div>
            )}
            {fetchStatus === "error" && (
              <div className="text-center mt-1">
                <span className="text-xs text-error-600 flex items-center justify-center">
                  <AlertTriangle className="h-3 w-3 mr-1" />
                  Not found
                </span>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Driver Photo */}
      <div className="flex justify-center mb-4">
        <div className="relative">
          <div className="h-24 w-24 rounded-full overflow-hidden bg-gray-100 flex items-center justify-center border-2 border-gray-200">
            {photoPreview ? (
              <img
                src={photoPreview}
                alt="Driver"
                className="h-full w-full object-cover"
              />
            ) : (
              <User className="h-12 w-12 text-gray-400" />
            )}
          </div>
          <Controller
            control={control}
            name="photo"
            render={({ field: { onChange, ...field } }) => (
              <div className="absolute bottom-0 right-0">
                <label
                  htmlFor="photo-upload"
                  className="cursor-pointer bg-primary-600 text-white p-1.5 rounded-full hover:bg-primary-700 transition-colors"
                >
                  <Upload className="h-3.5 w-3.5 inline align-baseline" />
                  <input
                    id="photo-upload"
                    type="file"
                    className="hidden"
                    accept="image/*"
                    onChange={(e) => {
                      const file = e.target.files?.[0] || null;
                      onChange(file);
                      handlePhotoChange(file);
                    }}
                    disabled={fieldsDisabled || isSubmitting}
                    {...field}
                  />
                </label>
              </div>
            )}
          />
        </div>
      </div>

      {/* Personal Information */}
      <CollapsibleSection
        title="Personal Information"
        icon={<User className="h-5 w-5" />}
        iconColor="text-blue-600"
        defaultExpanded={true}
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3 sm:gap-4">
          <Input
            label="Full Name"
            icon={<User className="h-4 w-4" />}
            error={errors.name?.message}
            required
            disabled={fieldsDisabled || isSubmitting}
            {...register("name", { required: "Full name is required" })}
          />

          <Input
            label="Father/Husband Name"
            icon={<Users className="h-4 w-4" />}
            disabled={fieldsDisabled || isSubmitting}
            {...register("father_or_husband_name")}
          />

          <Controller
            control={control}
            name="gender"
            render={({ field }) => (
              <Select
                label="Gender"
                options={[
                  { value: "MALE", label: "Male" },
                  { value: "FEMALE", label: "Female" },
                  { value: "OTHER", label: "Other" },
                ]}
                disabled={fieldsDisabled || isSubmitting}
                {...field}
              />
            )}
          />

          <Controller
            control={control}
            name="blood_group"
            render={({ field }) => (
              <Select
                label="Blood Group"
                options={[
                  { value: "", label: "Select Blood Group" },
                  { value: "A+", label: "A+" },
                  { value: "A-", label: "A-" },
                  { value: "B+", label: "B+" },
                  { value: "B-", label: "B-" },
                  { value: "AB+", label: "AB+" },
                  { value: "AB-", label: "AB-" },
                  { value: "O+", label: "O+" },
                  { value: "O-", label: "O-" },
                ]}
                disabled={fieldsDisabled || isSubmitting}
                {...field}
              />
            )}
          />
        </div>
        <div className="mt-3 sm:mt-4">
          <Controller
            control={control}
            name="address"
            render={({ field }) => (
              <Textarea
                label="Address"
                placeholder="Enter full address"
                error={errors.address?.message}
                required
                disabled={fieldsDisabled || isSubmitting}
                {...field}
              />
            )}
          />
        </div>
      </CollapsibleSection>

      {/* Contact & License Details */}
      <CollapsibleSection
        title="Contact & License Details"
        icon={<FileCheck className="h-5 w-5" />}
        iconColor="text-green-600"
        defaultExpanded={true}
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3 sm:gap-4">
          <Input
            label="Contact Number"
            icon={<Phone className="h-4 w-4" />}
            placeholder="e.g., 9876543210"
            error={errors.contact_number?.message}
            required
            disabled={fieldsDisabled || isSubmitting}
            {...register("contact_number", {
              required: "Contact number is required",
              validate: (value) => {
                if (!validateIndianMobile(value)) {
                  return "Please enter a valid 10-digit Indian mobile number";
                }
                return true;
              },
              onChange: (e) => {
                // Auto-format the mobile number
                const formatted = formatIndianMobile(e.target.value);
                e.target.value = formatted;
              }
            })}
          />

          <Input
            label="Email"
            type="email"
            icon={<Mail className="h-4 w-4" />}
            error={errors.email?.message}
            disabled={fieldsDisabled || isSubmitting}
            {...register("email")}
          />

          <Controller
            control={control}
            name="vehicle_class"
            render={({ field }) => (
              <MultiSelect
                label="Vehicle Class / License Types"
                options={[
                  { value: "MCWG", label: "MCWG - Motorcycle Without Gear" },
                  { value: "MCW/G", label: "MCW/G - Motorcycle With Gear" },
                  { value: "LMV", label: "LMV - Light Motor Vehicle" },
                  { value: "HMV", label: "HMV - Heavy Motor Vehicle" },
                  { value: "HGMV", label: "HGMV - Heavy Goods Motor Vehicle" },
                  { value: "HTV", label: "HTV - Heavy Transport Vehicle" },
                  { value: "HPMV", label: "HPMV - Heavy Passenger Motor Vehicle" },
                  { value: "PSV", label: "PSV - Public Service Vehicle" },
                  { value: "TRANS", label: "TRANS - Transport Vehicle" },
                ]}
                value={field.value || []}
                onChange={field.onChange}
                disabled={fieldsDisabled || isSubmitting}
                required
              />
            )}
          />
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-3 sm:gap-4 mt-3 sm:mt-4">
          <Input
            label="License Issue Date"
            type="date"
            icon={<Calendar className="h-4 w-4" />}
            disabled={fieldsDisabled || isSubmitting}
            {...register("license_issue_date")}
          />

          <Input
            label="Valid From"
            type="date"
            icon={<Calendar className="h-4 w-4" />}
            disabled={fieldsDisabled || isSubmitting}
            {...register("valid_from")}
          />

          <Input
            label="Valid Upto"
            type="date"
            icon={<Calendar className="h-4 w-4" />}
            error={errors.license_expiry_date?.message}
            required
            disabled={fieldsDisabled || isSubmitting}
            {...register("license_expiry_date", {
              required: "License expiry date is required",
            })}
          />
        </div>
      </CollapsibleSection>

      {/* RTO Information */}
      <CollapsibleSection
        title="RTO Information"
        icon={<FileText className="h-5 w-5" />}
        iconColor="text-orange-600"
        defaultExpanded={false}
      >
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-3 sm:gap-4">
          <Input
            label="RTO Code"
            placeholder="e.g., CG06"
            disabled={fieldsDisabled || isSubmitting}
            {...register("rto_code")}
          />

          <Input
            label="RTO Name"
            placeholder="e.g., MAHASAMUND"
            disabled={fieldsDisabled || isSubmitting}
            {...register("rto")}
          />

          <Input
            label="State"
            placeholder="Chhattisgarh"
            disabled={fieldsDisabled || isSubmitting}
            {...register("state")}
          />
        </div>
      </CollapsibleSection>

      {/* Employment Details */}
      <CollapsibleSection
        title="Employment Details"
        icon={<Users className="h-5 w-5" />}
        iconColor="text-purple-600"
        defaultExpanded={true}
      >
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-3 sm:gap-4">
          <Input
            label="Join Date"
            type="date"
            icon={<Calendar className="h-4 w-4" />}
            error={errors.join_date?.message}
            required
            disabled={isSubmitting}
            {...register("join_date", { required: "Join date is required" })}
          />

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Experience
            </label>
            <div className="px-3 py-2 border border-gray-300 rounded-md bg-gray-50">
              <span className="text-sm text-gray-700">{experienceDisplay}</span>
            </div>
            <input
              type="hidden"
              {...register("experience_years", {
                valueAsNumber: true,
                min: { value: 0, message: "Experience must be positive" },
              })}
            />
          </div>

          <Controller
            control={control}
            name="primary_vehicle_id"
            render={({ field }) => {
              // Group vehicles by type
              const vehiclesByType = vehicles.reduce((acc, vehicle) => {
                const type = vehicle.type || 'other';
                if (!acc[type]) acc[type] = [];
                acc[type].push(vehicle);
                return acc;
              }, {} as Record<string, typeof vehicles>);
              
              // Create options with category headers
              const vehicleOptions = [
                { value: "", label: "Select Vehicle" },
                ...Object.entries(vehiclesByType).flatMap(([type, vehicleList]) => [
                  { value: `__header_${type}`, label: `--- ${type.charAt(0).toUpperCase() + type.slice(1)} ---`, disabled: true },
                  ...vehicleList.map((vehicle) => ({
                    value: vehicle.id,
                    label: `${vehicle.registration_number} - ${vehicle.make} ${vehicle.model}`,
                  })),
                ])
              ];
              
              return (
                <Select
                  label="Assigned Vehicle (by Category)"
                  options={vehicleOptions}
                  disabled={isSubmitting}
                  {...field}
                />
              );
            }}
          />
        </div>

        <div className="mt-3 sm:mt-4">
          <Controller
            control={control}
            name="status"
            rules={{ required: "Status is required" }}
            render={({ field }) => (
              <Select
                label="Status"
                options={[
                  { value: "active", label: "Active" },
                  { value: "inactive", label: "Inactive" },
                  { value: "onLeave", label: "On Leave" },
                  { value: "suspended", label: "Suspended" },
                  { value: "blacklisted", label: "Blacklisted" },
                ]}
                error={errors.status?.message}
                required
                disabled={isSubmitting}
                {...field}
              />
            )}
          />
        </div>
      </CollapsibleSection>

      {/* Documents */}
      <CollapsibleSection
        title="Documents"
        icon={<FileText className="h-5 w-5" />}
        iconColor="text-red-600"
        defaultExpanded={false}
      >
        <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
          <DocumentUploader
            label="License Document"
            bucketType="driver"
            entityId={initialData?.id || 'temp'}
            docType="license"
            accept=".jpg,.jpeg,.png,.pdf"
            multiple={true}
            onUploadComplete={(paths) => handleDocumentUpload('license', paths)}
            initialFilePaths={initialData?.license_doc_url || []}
            required
            helperText="Upload license document (PDF/Image)"
          />
          <DocumentUploader
            label="Aadhar / ID Proof"
            bucketType="driver"
            entityId={initialData?.id || 'temp'}
            docType="aadhar"
            accept=".jpg,.jpeg,.png,.pdf"
            multiple={true}
            onUploadComplete={(paths) => handleDocumentUpload('aadhar', paths)}
            initialFilePaths={initialData?.aadhar_doc_url || []}
            helperText="Upload Aadhar card or ID proof (PDF/Image)"
          />
          <DocumentUploader
            label="Police Verification"
            bucketType="driver"
            entityId={initialData?.id || 'temp'}
            docType="police"
            accept=".jpg,.jpeg,.png,.pdf"
            multiple={true}
            onUploadComplete={(paths) => handleDocumentUpload('police', paths)}
            initialFilePaths={initialData?.police_doc_url || []}
            helperText="Upload police verification (PDF/Image)"
          />
          <DocumentUploader
            label="Medical Certificate"
            bucketType="driver"
            entityId={initialData?.id || 'temp'}
            docType="medical"
            accept=".jpg,.jpeg,.png,.pdf"
            multiple={true}
            onUploadComplete={(paths) => handleDocumentUpload('medical', paths)}
            initialFilePaths={initialData?.medical_doc_url || []}
            helperText="Upload medical certificate (PDF/Image)"
          />
        </div>
      </CollapsibleSection>

      {/* Notes */}
      <CollapsibleSection
        title="Notes"
        icon={<Database className="h-5 w-5" />}
        iconColor="text-gray-600"
        defaultExpanded={false}
      >
        <div className="space-y-3 sm:space-y-4">
          <div className="form-group">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Remarks / Internal Notes
            </label>
            <textarea
              className="w-full px-2 sm:px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
              rows={4}
              placeholder="Add any additional notes or remarks about this driver (for internal use only)"
              disabled={isSubmitting}
              {...register("notes")}
            ></textarea>
          </div>
        </div>
      </CollapsibleSection>

      {/* Submit Button */}
      <div className="flex justify-end pt-3 sm:pt-4">
        <Button type="submit" isLoading={isSubmitting}>
          {initialData?.id ? "Update Driver" : "Add Driver"}
        </Button>
      </div>
    </form>
  );
};

export default DriverForm;

import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { differenceInDays } from 'date-fns';
import { TrendingUp, Truck, CheckCircle, Award } from 'lucide-react';
import { supabase } from '../../utils/supabaseClient';
import { usePermissions } from '../../hooks/usePermissions';

interface Organization {
  id: string;
  name: string;
  logo_url?: string;
  tagline?: string;
  contact_email?: string;
  contact_phone?: string;
  address?: string;
  city?: string;
  state?: string;
  pincode?: string;
  gst_number?: string;
  pan_number?: string;
  owner_id: string;
  created_at?: string;
}

interface DashboardHeaderProps {
  vehicleCount?: number;
  className?: string;
}

const DashboardHeader: React.FC<DashboardHeaderProps> = ({ className = '' }) => {
  const { permissions } = usePermissions();
  const { t } = useTranslation();
  const [organization, setOrganization] = useState<Organization | null>(null);
  const [loading, setLoading] = useState(true);
  const [logoError, setLogoError] = useState(false);
  const [metrics, setMetrics] = useState({
    fleetSize: 0,
    tripsToday: 0,
    totalTrips: 0,
    onTimeRate: 95,
    activeDays: 0
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        setLoading(false);
        return;
      }

      // Load organization - get the most recent one for this user
      const { data: orgData } = await supabase
        .from('organizations')
        .select('name, logo_url, created_at, tagline')
        .eq('owner_id', user.id)
        .order('created_at', { ascending: false })
        .limit(1);
      
      const org = Array.isArray(orgData) && orgData.length > 0 ? orgData[0] : null;
      
      if (org) {
        setOrganization(org);
        setLogoError(false);
      } else if (permissions?.organizationName) {
        //  USE PERMISSIONS AS FALLBACK if no org found
        setOrganization({
          id: permissions.organizationId || '',
          name: permissions.organizationName,
          owner_id: user.id
        } as Organization);
      }

      // Load fleet metrics
      const { count: vehicleCount } = await supabase
        .from('vehicles')
        .select('*', { count: 'exact', head: true })
        .eq('created_by', user.id);

      // Get trip count and earliest trip date in a single query
      const { data: tripStats } = await supabase
        .from('trips')
        .select('created_at')
        .eq('created_by', user.id);

      const totalTrips = tripStats?.length || 0;
      const earliestTripDate = tripStats && tripStats.length > 0 
        ? tripStats.reduce((earliest: string, trip: { created_at: string }) => 
            new Date(trip.created_at) < new Date(earliest) ? trip.created_at : earliest, 
            tripStats[0].created_at
          )
        : null;

      const today = new Date().toISOString().split('T')[0];
      const { count: todayTrips } = await supabase
        .from('trips')
        .select('*', { count: 'exact', head: true })
        .eq('created_by', user.id)
        .gte('created_at', today);

      // Calculate active days from first trip
      let activeDays = 0;
      if (earliestTripDate) {
        activeDays = differenceInDays(new Date(), new Date(earliestTripDate));
      } else if (org?.created_at) {
        // Fallback to organization creation date
        activeDays = differenceInDays(new Date(), new Date(org.created_at));
      }

      setMetrics(prev => ({
        ...prev,
        fleetSize: vehicleCount || 0,
        totalTrips: totalTrips || 0,
        tripsToday: todayTrips || 0,
        activeDays: activeDays
      }));
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };


  if (loading) {
    return (
      <div className={`rounded-xl border bg-white dark:bg-white px-4 py-3 shadow-sm mb-6 ${className}`}>
        <div className="flex items-center space-x-3 mb-2">
          <div className="h-8 w-8 bg-gray-200 rounded animate-pulse"></div>
          <div>
            <div className="h-6 w-32 bg-gray-200 rounded animate-pulse mb-1"></div>
            <div className="h-4 w-48 bg-gray-200 rounded animate-pulse"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`mb-3 ${className}`}>
      {/* Company branding header */}
      <div className="bg-white rounded-xl border border-gray-200 shadow-sm p-3 sm:p-6 mb-3 sm:mb-6">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-2 sm:gap-4">
          <div className="flex items-center space-x-4">
            {organization?.logo_url && !logoError && (
              <div className="relative flex items-center justify-center h-14 w-14 bg-white rounded-xl shadow-md border border-gray-200 hover:shadow-lg transition-all duration-200">
                <img 
                  src={organization.logo_url} 
                  alt={organization.name}
                  className="h-13 w-13 object-contain p-0.5 filter drop-shadow-sm"
                  onError={() => {
                    setLogoError(true);
                  }}
                />
                {/* Subtle client company indicator */}
                <div className="absolute -top-1 -right-1 h-3 w-3 bg-blue-500 rounded-full border border-white shadow-sm"></div>
              </div>
            )}
            <div>
              <div className="flex items-center gap-2">
                <h1 className="text-xl font-bold text-gray-900">
                  {organization?.name || permissions?.organizationName || 'Dashboard'}
                </h1>
                {/* Subtle "Powered by AVS" indicator */}
                <div className="flex items-center gap-1 px-2 py-0.5 bg-primary-50 rounded-full border border-primary-200">
                  <div className="h-1.5 w-1.5 bg-primary-500 rounded-full"></div>
                  <span className="text-[10px] font-medium text-primary-700">Powered by AVS</span>
                </div>
              </div>
              {organization?.tagline && (
                <p className="text-sm text-gray-600 mt-1">{organization.tagline}</p>
              )}
            </div>
          </div>
          
          {/* Motivational metrics */}
          <div className="hidden md:flex items-center space-x-4 text-sm">
            <div className="flex items-center space-x-2 bg-primary-50 rounded-lg px-2 py-1">
              <Truck className="h-4 w-4 text-primary-600" />
              <span className="text-gray-600">{t('dashboard.fleetVehicles', { count: metrics.fleetSize })}</span>
            </div>
            <div className="flex items-center space-x-2 bg-green-50 rounded-lg px-2 py-1">
              <CheckCircle className="h-4 w-4 text-green-600" />
              <span className="text-gray-600">{t('dashboard.todayTrips', { count: metrics.tripsToday })}</span>
            </div>
            <div className="flex items-center space-x-2 bg-blue-50 rounded-lg px-2 py-1">
              <TrendingUp className="h-4 w-4 text-blue-600" />
              <span className="text-gray-600">{t('dashboard.totalTrips', { count: metrics.totalTrips })}</span>
            </div>
            <div className="flex items-center space-x-2 bg-yellow-50 rounded-lg px-2 py-1">
              <Award className="h-4 w-4 text-yellow-600" />
              <span className="text-gray-600">{t('dashboard.activeDays', { count: metrics.activeDays })}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Mobile metrics */}
      <div className="md:hidden grid grid-cols-2 gap-2 text-sm">
        <div className="flex items-center space-x-2 bg-primary-50 rounded-lg px-2 py-1">
          <Truck className="h-4 w-4 text-primary-600" />
          <span className="font-medium">{t('dashboard.fleetVehicles', { count: metrics.fleetSize })}</span>
        </div>
        <div className="flex items-center space-x-2 bg-green-50 rounded-lg px-2 py-1">
          <CheckCircle className="h-4 w-4 text-green-600" />
          <span className="font-medium">{t('dashboard.todayTrips', { count: metrics.tripsToday })}</span>
        </div>
        <div className="flex items-center space-x-2 bg-blue-50 rounded-lg px-2 py-1">
          <TrendingUp className="h-4 w-4 text-blue-600" />
          <span className="font-medium">{t('dashboard.totalTrips', { count: metrics.totalTrips })}</span>
        </div>
        <div className="flex items-center space-x-2 bg-yellow-50 rounded-lg px-2 py-1">
          <Award className="h-4 w-4 text-yellow-600" />
          <span className="font-medium">{t('dashboard.activeDays', { count: metrics.activeDays })}</span>
        </div>
      </div>
    </div>
  );
};

export default DashboardHeader;

import React, { useState, useEffect, useCallback, useRef, useMemo } from 'react';
import { useFormContext } from 'react-hook-form';
import { Clock, Calendar, AlertTriangle, CheckCircle } from 'lucide-react';
import { ANIMATIONS } from '@/utils/animations';
import { MaintenanceTask } from '@/types/maintenance';
import AnimatedButton from '../ui/AnimatedButton';
import AnimatedError from '../ui/AnimatedError';

interface EnhancedDowntimeSectionProps {
  className?: string;
}

const DOWNTIME_PRESETS = [
  { id: "2h", label: "2 h", days: 0, hours: 2, color: "bg-blue-100 text-blue-700 border-blue-200" },
  { id: "4h", label: "4 h", days: 0, hours: 4, color: "bg-blue-100 text-blue-700 border-blue-200" },
  { id: "6h", label: "6 h", days: 0, hours: 6, color: "bg-blue-100 text-blue-700 border-blue-200" },
  { id: "8h", label: "8 h", days: 0, hours: 8, color: "bg-blue-100 text-blue-700 border-blue-200" },
  { id: "1d", label: "1 d", days: 1, hours: 0, color: "bg-orange-100 text-orange-700 border-orange-200" },
  { id: "2d", label: "2 d", days: 2, hours: 0, color: "bg-orange-100 text-orange-700 border-orange-200" },
  { id: "3d", label: "3 d", days: 3, hours: 0, color: "bg-red-100 text-red-700 border-red-200" },
];

const DOWNTIME_CATEGORIES = [
  { value: 'maintenance', label: 'Maintenance', icon: '', color: 'bg-blue-50 text-blue-700' },
  { value: 'repair', label: 'Repair', icon: '', color: 'bg-orange-50 text-orange-700' },
  { value: 'inspection', label: 'Inspection', icon: '', color: 'bg-green-50 text-green-700' },
  { value: 'accident', label: 'Accident', icon: '', color: 'bg-red-50 text-red-700' },
  { value: 'breakdown', label: 'Breakdown', icon: '', color: 'bg-red-50 text-red-700' },
  { value: 'scheduled', label: 'Scheduled', icon: '', color: 'bg-purple-50 text-purple-700' },
  { value: 'emergency', label: 'Emergency', icon: '', color: 'bg-red-50 text-red-700' },
];

const IMPACT_LEVELS = [
  { value: 'low', label: 'Low Impact', color: 'bg-green-100 text-green-700', description: 'Minimal business impact' },
  { value: 'medium', label: 'Medium Impact', color: 'bg-yellow-100 text-yellow-700', description: 'Moderate business impact' },
  { value: 'high', label: 'High Impact', color: 'bg-orange-100 text-orange-700', description: 'Significant business impact' },
  { value: 'critical', label: 'Critical Impact', color: 'bg-red-100 text-red-700', description: 'Severe business impact' },
];

// Debounce utility function
const debounce = <T extends (...args: any[]) => void>(func: T, delay: number) => {
  let timeoutId: ReturnType<typeof setTimeout>;
  return (...args: Parameters<T>) => {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => func(...args), delay);
  };
};

const EnhancedDowntimeSection: React.FC<EnhancedDowntimeSectionProps> = ({ className = '' }) => {
  const { register, watch, setValue, formState: { errors } } = useFormContext<Partial<MaintenanceTask>>();
  const [selectedPreset, setSelectedPreset] = useState<string | null>(null);
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [downtimeStartTime, setDowntimeStartTime] = useState<string>('');
  const [downtimeEndTime, setDowntimeEndTime] = useState<string>('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [clickCount, setClickCount] = useState(0);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);
  const lastClickTimeRef = useRef<number>(0);

  const downtimeDays = watch('downtime_days') || 0;
  const downtimeHours = watch('downtime_hours') || 0;
  const downtimeCategory = watch('downtime_category') || 'maintenance';
  const downtimeImpactLevel = watch('downtime_impact_level') || 'medium';
  const downtimeReason = watch('downtime_reason') || '';

  // CRASH-PROOF: Use useMemo to prevent expensive recalculations on every render
  // This prevents main thread blocking that causes RESULT_CODE_HUNG crashes
  const downtimeCalculations = useMemo(() => {
    const totalDowntimeHours = (downtimeDays * 24) + downtimeHours;
    const totalDowntimeDays = Math.floor(totalDowntimeHours / 24);
    const remainingHours = totalDowntimeHours % 24;
    
    return {
      totalDowntimeHours,
      totalDowntimeDays,
      remainingHours
    };
  }, [downtimeDays, downtimeHours]);

  // Destructure for easier use
  const { totalDowntimeHours, totalDowntimeDays, remainingHours } = downtimeCalculations;

  // CRASH-PROOF handler to prevent RESULT_CODE_HUNG errors
  const handlePresetSelectInternal = useCallback((preset: typeof DOWNTIME_PRESETS[0]) => {
    const currentTime = Date.now();
    
    // Clear any existing timeout
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    
    // CRITICAL: Prevent rapid clicks that cause main thread blocking
    if (isProcessing) {
      console.log(' Still processing previous click, ignoring to prevent RESULT_CODE_HUNG crash...');
      return;
    }
    
    // CRITICAL: Prevent clicks too close together (less than 100ms apart)
    if (currentTime - lastClickTimeRef.current < 100) {
      console.log(' Click too rapid, ignoring to prevent crash...');
      return;
    }
    
    // CRITICAL: Prevent excessive clicking that can overwhelm the main thread
    if (clickCount > 10) {
      console.log(' Too many clicks detected, temporarily blocking to prevent crash...');
      setTimeout(() => setClickCount(0), 2000); // Reset after 2 seconds
      return;
    }
    
    // Update tracking variables
    lastClickTimeRef.current = currentTime;
    setClickCount(prev => prev + 1);
    setIsProcessing(true);
    
    console.log(` Quick select clicked: ${preset.id} (${preset.days}d ${preset.hours}h) - Click #${clickCount + 1}`);
    
    // Use requestAnimationFrame to defer state updates and prevent main thread blocking
    // This is CRITICAL to prevent RESULT_CODE_HUNG crashes
    requestAnimationFrame(() => {
      try {
        // Batch all state updates together to prevent multiple re-renders
        setValue('downtime_days', preset.days, { shouldDirty: true, shouldValidate: false });
        setValue('downtime_hours', preset.hours, { shouldDirty: true, shouldValidate: false });
        setSelectedPreset(preset.id);
        
        console.log(` Successfully updated downtime: ${preset.days}d ${preset.hours}h`);
        
        // Add success animation with error handling
        timeoutRef.current = setTimeout(() => {
          setSelectedPreset(null);
        }, 1000);
        
      } catch (error) {
        console.error(' Error updating downtime (preventing crash):', error);
        setSelectedPreset(null);
      } finally {
        // Reset processing state after a delay to prevent rapid successive clicks
        timeoutRef.current = setTimeout(() => {
          setIsProcessing(false);
        }, 500);
      }
    });
  }, [isProcessing, setValue, clickCount]);

  // Debounced version with longer delay to prevent rapid clicks that cause crashes
  const handlePresetSelect = useCallback(
    debounce(handlePresetSelectInternal, 500), // Increased to 500ms for better crash prevention
    [handlePresetSelectInternal]
  );

  // Handle custom downtime input
  const handleCustomDowntimeChange = (field: 'downtime_days' | 'downtime_hours', value: number) => {
    try {
      setValue(field, Math.max(0, value));
      setSelectedPreset(null);
    } catch (error) {
      console.error('Error in handleCustomDowntimeChange:', error);
    }
  };

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  // Auto-calculate downtime from start/end times
  useEffect(() => {
    if (downtimeStartTime && downtimeEndTime) {
      const start = new Date(downtimeStartTime);
      const end = new Date(downtimeEndTime);
      
      if (end > start) {
        const diffMs = end.getTime() - start.getTime();
        const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
        const days = Math.floor(diffHours / 24);
        const hours = diffHours % 24;
        
        setValue('downtime_days', days);
        setValue('downtime_hours', hours);
      }
    }
  }, [downtimeStartTime, downtimeEndTime, setValue]);

  return (
    <div className={`bg-white rounded-lg shadow-sm p-5 space-y-4 ${className}`}>
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-medium text-gray-900 flex items-center">
          <Clock className="h-5 w-5 mr-2 text-primary-500" />
          Downtime Tracking
        </h3>
        
        <AnimatedButton
          variant="ghost"
          size="sm"
          onClick={() => setShowAdvanced(!showAdvanced)}
          className="text-primary-600 hover:text-primary-700"
        >
          {showAdvanced ? 'Simple' : 'Advanced'}
        </AnimatedButton>
      </div>

      {/* Downtime Summary */}
      {totalDowntimeHours > 0 && (
        <div 
          className={`
            p-4 rounded-lg border-2 border-dashed
            ${ANIMATIONS.CLASSES.TRANSITION_SMOOTH}
            animate-in fade-in slide-in-from-top-1 duration-300
          `}
          style={{
            borderColor: downtimeImpactLevel === 'critical' ? '#ef4444' : 
                        downtimeImpactLevel === 'high' ? '#f97316' :
                        downtimeImpactLevel === 'medium' ? '#eab308' : '#22c55e'
          }}
        >
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-primary-100 rounded-lg">
                <Clock className="h-5 w-5 text-primary-600" />
              </div>
              <div>
                <h4 className="font-semibold text-gray-900">
                  Total Downtime: {totalDowntimeDays > 0 && `${totalDowntimeDays}d `}{remainingHours}h
                </h4>
                <p className="text-sm text-gray-600">
                  {DOWNTIME_CATEGORIES.find(c => c.value === downtimeCategory)?.icon} 
                  {DOWNTIME_CATEGORIES.find(c => c.value === downtimeCategory)?.label}
                  {'  '}
                  {IMPACT_LEVELS.find(i => i.value === downtimeImpactLevel)?.label}
                </p>
              </div>
            </div>
            
            <div className="text-right">
              <div className="text-2xl font-bold text-primary-600">
                {totalDowntimeHours}h
              </div>
              <div className="text-xs text-gray-500">Total Hours</div>
            </div>
          </div>
        </div>
      )}

      {/* Quick Presets */}
      <div className="space-y-3">
        <div className="flex items-center justify-between">
          <label className="block text-sm font-medium text-gray-700">
            Quick Select
          </label>
          {isProcessing && (
            <div className="flex items-center text-xs text-blue-600 animate-pulse">
              <Clock className="h-3 w-3 mr-1" />
              Processing...
            </div>
          )}
        </div>
        <div className="grid grid-cols-4 sm:grid-cols-7 gap-2">
          {DOWNTIME_PRESETS.map((preset) => (
            <button
              key={preset.id}
              type="button"
              onClick={() => handlePresetSelect(preset)}
              disabled={isProcessing}
              className={`
                px-3 py-2 text-sm font-medium rounded-lg border-2 transition-all duration-200
                ${selectedPreset === preset.id 
                  ? 'animate-pulse scale-110 shadow-lg' 
                  : preset.color
                }
                ${isProcessing 
                  ? 'opacity-60 cursor-not-allowed' 
                  : `${ANIMATIONS.CLASSES.HOVER_SCALE} hover:scale-105`
                }
                focus:outline-none focus:ring-2 focus:ring-primary-500
                disabled:opacity-60 disabled:cursor-not-allowed
              `}
            >
              {preset.label}
            </button>
          ))}
        </div>
      </div>

      {/* Custom Input */}
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Days
          </label>
          <div className="relative">
            <input
              type="number"
              min="0"
              step="1"
              value={downtimeDays}
              onChange={(e) => handleCustomDowntimeChange('downtime_days', parseInt(e.target.value) || 0)}
              className={`
                w-full px-3 py-2 border rounded-lg
                focus:outline-none focus:ring-2 focus:ring-primary-500
                ${errors.downtime_days ? 'border-red-500 animate-shake' : 'border-gray-300'}
                ${ANIMATIONS.CLASSES.TRANSITION_SMOOTH}
              `}
              placeholder="0"
            />
            {errors.downtime_days && (
              <AnimatedError message={errors.downtime_days.message} />
            )}
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Hours
          </label>
          <div className="relative">
            <input
              type="number"
              min="0"
              max="23"
              step="1"
              value={downtimeHours}
              onChange={(e) => handleCustomDowntimeChange('downtime_hours', parseInt(e.target.value) || 0)}
              className={`
                w-full px-3 py-2 border rounded-lg
                focus:outline-none focus:ring-2 focus:ring-primary-500
                ${errors.downtime_hours ? 'border-red-500 animate-shake' : 'border-gray-300'}
                ${ANIMATIONS.CLASSES.TRANSITION_SMOOTH}
              `}
              placeholder="0"
            />
            {errors.downtime_hours && (
              <AnimatedError message={errors.downtime_hours.message} />
            )}
          </div>
        </div>
      </div>

      {/* Advanced Options */}
      {showAdvanced && (
        <div 
          className={`
            space-y-4 p-4 bg-gray-50 rounded-lg border
            animate-in fade-in slide-in-from-top-1 duration-300
          `}
        >
          <h4 className="font-medium text-gray-900 flex items-center">
            <AlertTriangle className="h-4 w-4 mr-2 text-orange-500" />
            Advanced Downtime Tracking
          </h4>

          {/* Start/End Times */}
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Start Time
              </label>
              <input
                type="datetime-local"
                value={downtimeStartTime}
                onChange={(e) => setDowntimeStartTime(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                End Time
              </label>
              <input
                type="datetime-local"
                value={downtimeEndTime}
                onChange={(e) => setDowntimeEndTime(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
          </div>

          {/* Category Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Downtime Category
            </label>
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-2">
              {DOWNTIME_CATEGORIES.map((category) => (
                <button
                  key={category.value}
                  type="button"
                  onClick={() => setValue('downtime_category', category.value)}
                  className={`
                    p-3 rounded-lg border-2 text-center transition-all duration-200
                    ${downtimeCategory === category.value 
                      ? 'border-primary-500 bg-primary-50' 
                      : 'border-gray-200 bg-white hover:border-gray-300'
                    }
                    ${ANIMATIONS.CLASSES.HOVER_SCALE}
                  `}
                >
                  <div className="text-2xl mb-1">{category.icon}</div>
                  <div className="text-xs font-medium">{category.label}</div>
                </button>
              ))}
            </div>
          </div>

          {/* Impact Level */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Impact Level
            </label>
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-2">
              {IMPACT_LEVELS.map((impact) => (
                <button
                  key={impact.value}
                  type="button"
                  onClick={() => setValue('downtime_impact_level', impact.value)}
                  className={`
                    p-3 rounded-lg border-2 text-center transition-all duration-200
                    ${downtimeImpactLevel === impact.value 
                      ? 'border-primary-500 bg-primary-50' 
                      : 'border-gray-200 bg-white hover:border-gray-300'
                    }
                    ${ANIMATIONS.CLASSES.HOVER_SCALE}
                  `}
                >
                  <div className={`text-sm font-medium ${impact.color}`}>
                    {impact.label}
                  </div>
                  <div className="text-xs text-gray-500 mt-1">
                    {impact.description}
                  </div>
                </button>
              ))}
            </div>
          </div>

          {/* Reason */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Reason (Optional)
            </label>
            <textarea
              {...register('downtime_reason')}
              value={downtimeReason}
              onChange={(e) => setValue('downtime_reason', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              placeholder="Describe the reason for downtime..."
              rows={3}
            />
          </div>
        </div>
      )}

      {/* Validation Messages */}
      {errors.downtime_days && (
        <AnimatedError message={errors.downtime_days.message} />
      )}
      {errors.downtime_hours && (
        <AnimatedError message={errors.downtime_hours.message} />
      )}
    </div>
  );
};

export default EnhancedDowntimeSection;

import React, { useState, useCallback } from 'react';
import { useForm, FormProvider, Controller } from 'react-hook-form';
import { Vehicle } from '@/types';
import { MaintenanceTask } from '@/types/maintenance';
import FileUploadWithProgress from '../ui/FileUploadWithProgress';
import { toast } from 'react-toastify';
import { 
  Calendar,
  Truck,
  Clock,
  CheckCircle,
  AlertTriangle,
  Wrench,
  FileText,
  Upload
} from 'lucide-react';
import '../../styles/FileUploadWithProgress.css';

interface EnhancedMaintenanceFormProps {
  onSubmit: (data: Partial<MaintenanceTask>) => void;
  vehicles: Vehicle[];
  initialData?: Partial<MaintenanceTask>;
  isSubmitting: boolean;
}

const EnhancedMaintenanceForm: React.FC<EnhancedMaintenanceFormProps> = ({
  onSubmit,
  vehicles,
  initialData,
  isSubmitting: externalIsSubmitting
}) => {
  // Form states
  const [odometerPhoto, setOdometerPhoto] = useState<File[]>([]);
  const [documents, setDocuments] = useState<File[]>([]);
  
  // Submission states
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState<string | null>(null);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [overallProgress, setOverallProgress] = useState(0);

  const methods = useForm<Partial<MaintenanceTask>>({
    defaultValues: {
      task_type: "general_scheduled_service",
      priority: "medium",
      status: "open",
      estimated_cost: 0,
      parts_required: [],
      start_date: new Date().toISOString().split("T")[0],
      title: [],
      warranty_claimed: false,
      downtime_days: 0,
      downtime_hours: 0,
      service_groups: [
        {
          vendor_id: "",
          tasks: [],
          cost: 0,
          battery_tracking: false,
          tyre_tracking: false,
        },
      ],
      ...initialData,
    },
  });

  const {
    register,
    handleSubmit,
    watch,
    control,
    setValue,
    formState: { errors },
  } = methods;

  const startDate = watch("start_date");
  const endDate = watch("end_date");
  const odometerReading = watch("odometer_reading");
  const downtimeDays = watch("downtime_days");
  const downtimeHours = watch("downtime_hours");
  const status = watch("status");
  const complaintDescription = watch("complaint_description");
  const resolutionSummary = watch("resolution_summary");

  // Handle form submission with upload progress
  const handleFormSubmit = useCallback(async (data: Partial<MaintenanceTask>) => {
    if (isSubmitting || externalIsSubmitting) return;
    
    setSubmitError(null);
    setSubmitSuccess(false);
    setIsSubmitting(true);
    setOverallProgress(0);
    
    try {
      // Validation
      if (!data.start_date) {
        throw new Error('Start date is required');
      }
      if (!data.end_date) {
        throw new Error('End date is required');
      }
      if (!data.odometer_reading) {
        throw new Error('Odometer reading is required');
      }
      
      // Prepare form data with files
      const formData = {
        ...data,
        odometer_image: odometerPhoto,
        attachments: documents,
      };
      
      setOverallProgress(10);
      
      // Simulate upload progress (replace with actual upload logic)
      const uploadProgress = setInterval(() => {
        setOverallProgress(prev => {
          if (prev >= 90) {
            clearInterval(uploadProgress);
            return 90;
          }
          return prev + 10;
        });
      }, 200);
      
      // Submit the form
      await onSubmit(formData);
      
      setOverallProgress(100);
      setSubmitSuccess(true);
      
      console.log(' Task created successfully');
      
      // Show success for 2 seconds then reset
      setTimeout(() => {
        setSubmitSuccess(false);
        setIsSubmitting(false);
        setOverallProgress(0);
        // Reset form or navigate
      }, 2000);
      
    } catch (error) {
      console.error(' Submission error:', error);
      setSubmitError(error instanceof Error ? error.message : 'Failed to create task');
      setIsSubmitting(false);
      setOverallProgress(0);
    }
  }, [isSubmitting, externalIsSubmitting, startDate, endDate, odometerReading, 
      odometerPhoto, documents, onSubmit]);

  return (
    <FormProvider {...methods}>
      <form onSubmit={handleSubmit(handleFormSubmit)} className="enhanced-maintenance-form">
        <div className="form-header">
          <h2 className="form-title">
            <Wrench className="h-6 w-6" />
            Service Details
          </h2>
        </div>
        
        {/* Date fields */}
        <div className="form-section">
          <h3 className="section-title">
            <Calendar className="h-5 w-5" />
            Service Period
          </h3>
          <div className="form-row">
            <div className="form-group">
              <label>Start Date *</label>
              <input 
                type="date" 
                {...register("start_date", { required: "Start date is required" })}
                className={errors.start_date ? 'error' : ''}
              />
              {errors.start_date && (
                <span className="error-text">{errors.start_date.message}</span>
              )}
            </div>
            <div className="form-group">
              <label>End Date *</label>
              <input 
                type="date" 
                {...register("end_date", { required: "End date is required" })}
                className={errors.end_date ? 'error' : ''}
              />
              {errors.end_date && (
                <span className="error-text">{errors.end_date.message}</span>
              )}
            </div>
          </div>
        </div>

        {/* Downtime tracking */}
        <div className="form-section">
          <h3 className="section-title">
            <Clock className="h-5 w-5" />
            Downtime Tracking
          </h3>
          <div className="form-row">
            <div className="form-group">
              <label>Days</label>
              <input 
                type="number" 
                {...register("downtime_days", { min: 0 })}
                min="0"
              />
            </div>
            <div className="form-group">
              <label>Hours</label>
              <input 
                type="number" 
                {...register("downtime_hours", { min: 0, max: 23 })}
                min="0"
                max="23"
              />
            </div>
          </div>
        </div>

        {/* Complaint & Resolution */}
        <div className="form-section">
          <h3 className="section-title">
            <AlertTriangle className="h-5 w-5" />
            Issue Details
          </h3>
          <div className="form-group">
            <label>Complaint Description</label>
            <textarea 
              {...register("complaint_description")}
              rows={4}
              placeholder="Detailed description of the complaint or issue..."
            />
          </div>

          <div className="form-group">
            <label>Resolution Summary</label>
            <textarea 
              {...register("resolution_summary")}
              rows={4}
              placeholder="Summary of the resolution or fix applied..."
            />
          </div>
        </div>

        {/* Odometer */}
        <div className="form-section">
          <h3 className="section-title">
            <Truck className="h-5 w-5" />
            Vehicle Information
          </h3>
          <div className="form-group">
            <label>Odometer Reading *</label>
            <input 
              type="number" 
              {...register("odometer_reading", { required: "Odometer reading is required" })}
              placeholder="Enter odometer reading"
              className={errors.odometer_reading ? 'error' : ''}
            />
            {errors.odometer_reading && (
              <span className="error-text">{errors.odometer_reading.message}</span>
            )}
          </div>
        </div>

        {/* File Uploads with Individual Progress */}
        <div className="form-section">
          <h3 className="section-title">
            <Upload className="h-5 w-5" />
            Document Upload
          </h3>
          
          <FileUploadWithProgress
            id="odometerPhoto"
            label="Odometer Photo"
            accept="image/jpeg,image/jpg,image/png"
            multiple={false}
            compress={true}
            maxSize={5 * 1024 * 1024}
            onFilesChange={setOdometerPhoto}
            helperText="Upload a clear photo of the odometer reading"
          />

          <FileUploadWithProgress
            id="documents"
            label="Supporting Documents"
            accept=".jpg,.jpeg,.png,.pdf"
            multiple={true}
            compress={false}
            maxSize={10 * 1024 * 1024}
            onFilesChange={setDocuments}
            helperText="Upload warranty cards, bills, or other relevant documents"
          />
        </div>

        {/* Status */}
        <div className="form-section">
          <h3 className="section-title">
            <CheckCircle className="h-5 w-5" />
            Task Status
          </h3>
          <div className="form-group">
            <label>Status *</label>
            <select {...register("status", { required: "Status is required" })}>
              <option value="open">Open</option>
              <option value="in_progress">In Progress</option>
              <option value="completed">Completed</option>
              <option value="closed">Closed</option>
            </select>
          </div>
        </div>

        {/* Error Message */}
        {submitError && (
          <div className="alert alert-error">
            <AlertTriangle className="h-5 w-5" />
            {submitError}
          </div>
        )}

        {/* Success Message */}
        {submitSuccess && (
          <div className="alert alert-success">
            <CheckCircle className="h-5 w-5" />
            Task created successfully!
          </div>
        )}

        {/* Overall Progress Bar (during submission) */}
        {isSubmitting && (
          <div className="overall-progress">
            <div className="progress-bar-container">
              <div 
                className="progress-bar-fill" 
                style={{ width: `${overallProgress}%` }}
              />
            </div>
            <div className="progress-text">
              Uploading... {overallProgress}%
            </div>
          </div>
        )}

        {/* Submit Button */}
        <button 
          type="submit" 
          disabled={isSubmitting || externalIsSubmitting || submitSuccess}
          className={`create-task-btn ${submitSuccess ? 'success' : ''}`}
        >
          {submitSuccess ? (
            <>
              <CheckCircle className="h-5 w-5" />
              Task Created!
            </>
          ) : isSubmitting ? (
            <>
              <div className="spinner" />
              Creating Task... {overallProgress}%
            </>
          ) : (
            <>
              <Wrench className="h-5 w-5" />
              Create Task
            </>
          )}
        </button>
      </form>
    </FormProvider>
  );
};

export default EnhancedMaintenanceForm;

import React, { useState, useEffect } from "react";
import { MaintenanceTask } from "@/types/maintenance";
import { Vehicle } from "@/types";
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameDay, isToday, isPast, addMonths, subMonths } from "date-fns";
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon, Clock, AlertTriangle, CheckCircle, XCircle } from "lucide-react";
import Button from "../ui/Button";
import { useNavigate } from "react-router-dom";

interface MaintenanceCalendarProps {
  tasks: MaintenanceTask[];
  vehicles: Vehicle[];
}

interface CalendarEvent {
  id: string;
  title: string;
  date: Date;
  type: 'start' | 'end' | 'due' | 'overdue';
  status: string;
  priority: string;
  vehicleId: string;
  vehicleRegistration: string;
  task: MaintenanceTask;
}

const MaintenanceCalendar: React.FC<MaintenanceCalendarProps> = ({ tasks, vehicles }) => {
  const navigate = useNavigate();
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [events, setEvents] = useState<CalendarEvent[]>([]);

  // Create a map of vehicles for quick lookup
  const vehicleMap = new Map(vehicles.map(v => [v.id, v]));

  // Generate calendar events from maintenance tasks
  useEffect(() => {
    const calendarEvents: CalendarEvent[] = [];

    tasks.forEach(task => {
      const vehicle = vehicleMap.get(task.vehicle_id);
      if (!vehicle) return;

      const vehicleRegistration = vehicle.registration_number;

      // Add start date event
      if (task.start_date) {
        const startDate = new Date(task.start_date);
        calendarEvents.push({
          id: `${task.id}-start`,
          title: `Start: ${task.task_type}`,
          date: startDate,
          type: 'start',
          status: task.status,
          priority: task.priority,
          vehicleId: task.vehicle_id,
          vehicleRegistration,
          task,
        });
      }

      // Add end date event
      if (task.end_date) {
        const endDate = new Date(task.end_date);
        calendarEvents.push({
          id: `${task.id}-end`,
          title: `End: ${task.task_type}`,
          date: endDate,
          type: 'end',
          status: task.status,
          priority: task.priority,
          vehicleId: task.vehicle_id,
          vehicleRegistration,
          task,
        });
      }

      // Add next service due event
      if (task.next_service_due?.date) {
        const dueDate = new Date(task.next_service_due.date);
        const isOverdue = isPast(dueDate) && task.status !== 'resolved';
        
        calendarEvents.push({
          id: `${task.id}-due`,
          title: `Due: ${task.task_type}`,
          date: dueDate,
          type: isOverdue ? 'overdue' : 'due',
          status: task.status,
          priority: task.priority,
          vehicleId: task.vehicle_id,
          vehicleRegistration,
          task,
        });
      }
    });

    setEvents(calendarEvents);
  }, [tasks, vehicleMap]);

  // Get events for a specific date
  const getEventsForDate = (date: Date): CalendarEvent[] => {
    return events.filter(event => isSameDay(event.date, date));
  };

  // Get events for selected date
  const selectedDateEvents = selectedDate ? getEventsForDate(selectedDate) : [];

  // Calendar navigation
  const goToPreviousMonth = () => {
    setCurrentDate(subMonths(currentDate, 1));
  };

  const goToNextMonth = () => {
    setCurrentDate(addMonths(currentDate, 1));
  };

  const goToToday = () => {
    setCurrentDate(new Date());
    setSelectedDate(new Date());
  };

  // Generate calendar days
  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(currentDate);
  const calendarDays = eachDayOfInterval({ start: monthStart, end: monthEnd });

  // Get event type styling
  const getEventTypeStyle = (type: CalendarEvent['type']) => {
    switch (type) {
      case 'start':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'end':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'due':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'overdue':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  // Get priority styling
  const getPriorityStyle = (priority: string) => {
    switch (priority) {
      case 'critical':
        return 'border-l-4 border-red-500';
      case 'high':
        return 'border-l-4 border-orange-500';
      case 'medium':
        return 'border-l-4 border-yellow-500';
      case 'low':
        return 'border-l-4 border-green-500';
      default:
        return 'border-l-4 border-gray-500';
    }
  };

  // Get status icon
  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'resolved':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'in_progress':
        return <Clock className="h-4 w-4 text-blue-500" />;
      case 'escalated':
        return <AlertTriangle className="h-4 w-4 text-red-500" />;
      case 'open':
        return <XCircle className="h-4 w-4 text-gray-500" />;
      default:
        return <Clock className="h-4 w-4 text-gray-500" />;
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm p-6">
      {/* Calendar Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-4">
          <CalendarIcon className="h-6 w-6 text-primary-500" />
          <h2 className="text-xl font-semibold text-gray-900">
            Maintenance Calendar
          </h2>
        </div>
        
        <div className="flex items-center space-x-2">
          <Button
            variant="outline"
            inputSize="sm"
            onClick={goToPreviousMonth}
            icon={<ChevronLeft className="h-4 w-4" />}
          >
            Previous
          </Button>
          
          <Button
            variant="outline"
            inputSize="sm"
            onClick={goToToday}
          >
            Today
          </Button>
          
          <Button
            variant="outline"
            inputSize="sm"
            onClick={goToNextMonth}
            icon={<ChevronRight className="h-4 w-4" />}
          >
            Next
          </Button>
        </div>
      </div>

      {/* Month/Year Display */}
      <div className="text-center mb-6">
        <h3 className="text-2xl font-bold text-gray-900">
          {format(currentDate, 'MMMM yyyy')}
        </h3>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
        {/* Calendar Grid */}
        <div className="xl:col-span-2">
          <div className="grid grid-cols-7 gap-1 mb-2">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="p-2 text-center text-sm font-medium text-gray-500">
                {day}
              </div>
            ))}
          </div>
          
          <div className="grid grid-cols-7 gap-1">
            {calendarDays.map(day => {
              const dayEvents = getEventsForDate(day);
              const isCurrentDay = isToday(day);
              const isSelected = selectedDate && isSameDay(day, selectedDate);
              
              return (
                <div
                  key={day.toISOString()}
                  className={`
                    min-h-[80px] sm:min-h-[100px] p-1 sm:p-2 border border-gray-200 cursor-pointer hover:bg-gray-50
                    ${isCurrentDay ? 'bg-primary-50 border-primary-200' : ''}
                    ${isSelected ? 'bg-primary-100 border-primary-300' : ''}
                    ${isPast(day) && !isCurrentDay ? 'bg-gray-50' : ''}
                  `}
                  onClick={() => setSelectedDate(day)}
                >
                  <div className="flex items-center justify-between mb-1">
                    <span className={`
                      text-sm font-medium
                      ${isCurrentDay ? 'text-primary-600' : 'text-gray-900'}
                      ${isSelected ? 'text-primary-700' : ''}
                    `}>
                      {format(day, 'd')}
                    </span>
                    {dayEvents.length > 0 && (
                      <span className="text-xs bg-primary-500 text-white rounded-full w-5 h-5 flex items-center justify-center">
                        {dayEvents.length}
                      </span>
                    )}
                  </div>
                  
                  <div className="space-y-1">
                    {dayEvents.slice(0, 2).map(event => (
                      <div
                        key={event.id}
                        className={`
                          text-xs p-1 rounded border
                          ${getEventTypeStyle(event.type)}
                          ${getPriorityStyle(event.priority)}
                        `}
                        title={`${event.title} - ${event.vehicleRegistration}`}
                      >
                        <div className="truncate">{event.title}</div>
                        <div className="text-xs opacity-75 truncate">
                          {event.vehicleRegistration}
                        </div>
                      </div>
                    ))}
                    {dayEvents.length > 2 && (
                      <div className="text-xs text-gray-500">
                        +{dayEvents.length - 2} more
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Selected Date Events */}
        <div className="xl:col-span-1">
          <div className="bg-gray-50 rounded-lg p-4">
            <h4 className="font-semibold text-gray-900 mb-4">
              {selectedDate ? format(selectedDate, 'EEEE, MMMM d, yyyy') : 'Select a date'}
            </h4>
            
            {selectedDateEvents.length > 0 ? (
              <div className="space-y-3">
                {selectedDateEvents.map(event => (
                  <div
                    key={event.id}
                    className={`
                      bg-white rounded-lg p-3 border cursor-pointer hover:shadow-md transition-shadow
                      ${getPriorityStyle(event.priority)}
                    `}
                    onClick={() => navigate(`/maintenance/${event.task.id}`)}
                  >
                    <div className="flex items-start justify-between mb-2">
                      <div className="flex items-center space-x-2">
                        {getStatusIcon(event.status)}
                        <span className="text-sm font-medium text-gray-900">
                          {event.title}
                        </span>
                      </div>
                      <span className={`
                        text-xs px-2 py-1 rounded-full
                        ${getEventTypeStyle(event.type)}
                      `}>
                        {event.type}
                      </span>
                    </div>
                    
                    <div className="text-sm text-gray-600 mb-1">
                      Vehicle: {event.vehicleRegistration}
                    </div>
                    
                    <div className="text-sm text-gray-500">
                      Priority: <span className="capitalize">{event.priority}</span>
                    </div>
                    
                    <div className="text-sm text-gray-500">
                      Status: <span className="capitalize">{event.status.replace('_', ' ')}</span>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center text-gray-500 py-8">
                {selectedDate ? 'No events on this date' : 'Click on a date to view events'}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

