export default SmartServiceGroupItem;

import React, { useState, useEffect } from 'react';
import { 
  generatePDF, 
  generateWeeklyComparisonExcel, 
  generateTripSummaryExcel, 
  generateVehicleUtilizationExcel, 
  generateDriverPerformanceExcel, 
  generateMonthlyComparisonExcel 
} from '../../utils/reportGenerators';
import {
  fetchWeeklyComparisonData,
  fetchMonthlyComparisonData,
  fetchTripSummaryData,
  fetchVehicleUtilizationData,
  fetchDriverPerformanceData,
  WeeklyComparisonData,
  MonthlyComparisonData,
  TripSummaryData,
  VehicleUtilizationData,
  DriverPerformanceData
} from '../../utils/reportDataFetchers';

// Import report templates
import { WeeklyComparisonReport } from './templates/WeeklyComparisonReport';
import { MonthlyComparisonReport } from './templates/MonthlyComparisonReport';
import { TripSummaryReport } from './templates/TripSummaryReport';
import { VehicleUtilizationReport } from './templates/VehicleUtilizationReport';
import { DriverPerformanceReport } from './templates/DriverPerformanceReport';

import { Download, FileText, Table, X, Loader2, Calendar, Filter } from 'lucide-react';

export type ReportType = 
  | 'weekly-comparison'
  | 'monthly-comparison'
  | 'trip-summary'
  | 'vehicle-utilization'
  | 'driver-performance';

interface ReportGeneratorProps {
  reportType: ReportType;
  onClose: () => void;
  initialDateRange?: {
    start: string;
    end: string;
  };
}

interface DateRange {
  start: string;
  end: string;
}

export const ReportGenerator: React.FC<ReportGeneratorProps> = ({
  reportType,
  onClose,
  initialDateRange
}) => {
  const [loading, setLoading] = useState(false);
  const [reportData, setReportData] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);
  const [dateRange, setDateRange] = useState<DateRange>(
    initialDateRange || {
      start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
      end: new Date().toISOString().split('T')[0]
    }
  );
  const [customDateRange, setCustomDateRange] = useState(false);

  // Report type configurations
  const reportConfigs = {
    'weekly-comparison': {
      title: 'Weekly Comparison Report',
      description: 'Compare current week performance with previous week',
      icon: <Calendar className="w-5 h-5" />,
      color: 'bg-green-500'
    },
    'monthly-comparison': {
      title: 'Monthly Comparison Report',
      description: 'Monthly performance analysis and trends',
      icon: <Calendar className="w-5 h-5" />,
      color: 'bg-blue-500'
    },
    'trip-summary': {
      title: 'Trip Summary Report',
      description: 'Detailed trip information and statistics',
      icon: <FileText className="w-5 h-5" />,
      color: 'bg-purple-500'
    },
    'vehicle-utilization': {
      title: 'Vehicle Utilization Report',
      description: 'Vehicle usage patterns and efficiency metrics',
      icon: <Table className="w-5 h-5" />,
      color: 'bg-orange-500'
    },
    'driver-performance': {
      title: 'Driver Performance Report',
      description: 'Driver performance metrics and safety analysis',
      icon: <Table className="w-5 h-5" />,
      color: 'bg-indigo-500'
    }
  };

  const config = reportConfigs[reportType];

  useEffect(() => {
    fetchReportData();
  }, [reportType, dateRange]);

  const fetchReportData = async () => {
    setLoading(true);
    setError(null);
    
    try {
      let data: any;
      
      switch (reportType) {
        case 'weekly-comparison': {
          const currentWeek = getWeekNumber(new Date());
          data = await fetchWeeklyComparisonData(currentWeek);
          break;
        }
          
        case 'monthly-comparison': {
          const currentMonth = new Date().getMonth() + 1;
          data = await fetchMonthlyComparisonData(currentMonth);
          break;
        }
          
        case 'trip-summary':
          data = await fetchTripSummaryData(dateRange);
          break;
          
        case 'vehicle-utilization':
          data = await fetchVehicleUtilizationData('Current Month');
          break;
          
        case 'driver-performance':
          data = await fetchDriverPerformanceData('Current Month');
          break;
          
        default:
          throw new Error(`Unknown report type: ${reportType}`);
      }
      
      setReportData(data);
    } catch (err) {
      console.error('Error fetching report data:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch report data');
    } finally {
      setLoading(false);
    }
  };

  const getWeekNumber = (date: Date): number => {
    const firstDayOfYear = new Date(date.getFullYear(), 0, 1);
    const pastDaysOfYear = (date.getTime() - firstDayOfYear.getTime()) / 86400000;
    return Math.ceil((pastDaysOfYear + firstDayOfYear.getDay() + 1) / 7);
  };

  const handleDownloadPDF = async () => {
    try {
      setLoading(true);
      const fileName = `${reportType.replace('-', '_')}_${new Date().getTime()}`;
      await generatePDF('report-content', fileName, {
        orientation: 'portrait',
        format: 'a4',
        margin: 20,
        scale: 2
      });
    } catch (err) {
      console.error('Error generating PDF:', err);
      setError('Failed to generate PDF');
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadExcel = () => {
    try {
      const fileName = `${reportType.replace('-', '_')}_${new Date().getTime()}`;
      
      switch (reportType) {
        case 'weekly-comparison':
          generateWeeklyComparisonExcel(reportData);
          break;
        case 'monthly-comparison':
          generateMonthlyComparisonExcel(reportData);
          break;
        case 'trip-summary':
          generateTripSummaryExcel(reportData);
          break;
        case 'vehicle-utilization':
          generateVehicleUtilizationExcel(reportData);
          break;
        case 'driver-performance':
          generateDriverPerformanceExcel(reportData);
          break;
        default:
          throw new Error(`Unknown report type: ${reportType}`);
      }
    } catch (err) {
      console.error('Error generating Excel:', err);
      setError('Failed to generate Excel file');
    }
  };

  const handleDateRangeChange = (field: 'start' | 'end', value: string) => {
    setDateRange(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const renderReport = () => {
    if (!reportData) return null;

    switch (reportType) {
      case 'weekly-comparison':
        return <WeeklyComparisonReport data={reportData} />;
      case 'monthly-comparison':
        return <MonthlyComparisonReport data={reportData} />;
      case 'trip-summary':
        return <TripSummaryReport data={reportData} />;
      case 'vehicle-utilization':
        return <VehicleUtilizationReport data={reportData} />;
      case 'driver-performance':
        return <DriverPerformanceReport data={reportData} />;
      default:
        return null;
    }
  };

  const needsDateRange = ['trip-summary'].includes(reportType);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-7xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b bg-gray-50">
          <div className="flex items-center gap-3">
            <div className={`p-2 rounded-lg ${config.color} text-white`}>
              {config.icon}
            </div>
            <div>
              <h2 className="text-xl font-semibold text-gray-900">{config.title}</h2>
              <p className="text-sm text-gray-600">{config.description}</p>
            </div>
          </div>
          
          <div className="flex items-center gap-3">
            {/* Date Range Selector for applicable reports */}
            {needsDateRange && (
              <div className="flex items-center gap-2">
                <Filter className="w-4 h-4 text-gray-500" />
                <div className="flex items-center gap-2">
                  <input
                    type="date"
                    value={dateRange.start}
                    onChange={(e) => handleDateRangeChange('start', e.target.value)}
                    className="px-3 py-1 border border-gray-300 rounded text-sm"
                  />
                  <span className="text-gray-500">to</span>
                  <input
                    type="date"
                    value={dateRange.end}
                    onChange={(e) => handleDateRangeChange('end', e.target.value)}
                    className="px-3 py-1 border border-gray-300 rounded text-sm"
                  />
                </div>
              </div>
            )}
            
            {/* Action Buttons */}
            <button
              onClick={handleDownloadPDF}
              disabled={loading || !reportData}
              className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? <Loader2 className="w-4 h-4 animate-spin" /> : <Download className="w-4 h-4" />}
              PDF
            </button>
            
            <button
              onClick={handleDownloadExcel}
              disabled={loading || !reportData}
              className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? <Loader2 className="w-4 h-4 animate-spin" /> : <Table className="w-4 h-4" />}
              Excel
            </button>
            
            <button
              onClick={onClose}
              className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg"
            >
              <X className="w-5 h-5" />
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="overflow-y-auto max-h-[calc(90vh-120px)]">
          {loading && !reportData ? (
            <div className="flex items-center justify-center py-20">
              <div className="text-center">
                <Loader2 className="w-8 h-8 animate-spin mx-auto mb-4 text-green-600" />
                <p className="text-gray-600">Generating report...</p>
              </div>
            </div>
          ) : error ? (
            <div className="flex items-center justify-center py-20">
              <div className="text-center">
                <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <X className="w-8 h-8 text-red-600" />
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Error Loading Report</h3>
                <p className="text-gray-600 mb-4">{error}</p>
                <button
                  onClick={fetchReportData}
                  className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                >
                  Retry
                </button>
              </div>
            </div>
          ) : (
            <div id="report-content" className="p-6">
              {renderReport()}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

import { getWarehouse, getDestination, getDestinationByAnyId } from '../../utils/storage';
import { Trip, Vehicle, Driver } from '@/types';
import { Truck, User, Calendar, LocateFixed, Fuel, MapPin, IndianRupee, ArrowRight, Edit, Camera, RefreshCw } from 'lucide-react';
import React, { useState, useEffect, useRef, memo } from 'react';
import { parseISO, isValid, format } from 'date-fns';
import { truncateString } from '../../utils/format';
import { uploadFilesAndGetPublicUrls } from '../../utils/supabaseStorage';
import { toast } from 'react-toastify';
import config from '../../utils/env';
import SearchHighlightedText from './SearchHighlightedText';

interface TripCardProps {
  trip: Trip;
  vehicle: Vehicle | undefined;
  driver: Driver | undefined;
  onClick?: () => void;
  onPnlClick?: (e: React.MouseEvent, trip: Trip) => void;
  onEditClick?: (trip: Trip) => void;
  searchTerm?: string;
}

const TripCard: React.FC<TripCardProps> = memo(({ trip, vehicle, driver, onClick, onPnlClick, onEditClick, searchTerm }) => {
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [warehouseData, setWarehouseData] = useState<any>(null);
  const [destinationData, setDestinationData] = useState<any[]>([]);
  const [loadingError, setLoadingError] = useState<string | null>(null);

  // Fetch warehouse and destinations data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoadingError(null);
        
        if (trip.warehouse_id) {
          try {
            const warehouse = await getWarehouse(trip.warehouse_id);
            setWarehouseData(warehouse);
          } catch (error) {
            console.error('Error fetching warehouse:', error);
            // Set loading error for warehouse failures
            if (error instanceof TypeError && error.message.includes('Failed to fetch')) {
              setLoadingError('Unable to load warehouse data due to connection issues');
            } else {
              // Only set error if warehouse_id exists but data is null
              if (!warehouseData) {
                setLoadingError('Unable to load warehouse data');
              }
              setLoadingError('Unable to load warehouse data due to connection issues');
            }
          }
        }
        
        if (Array.isArray(trip.destinations) && trip.destinations.length > 0) {
          try {
            const destinations = await Promise.all(
              trip.destinations.map(async (id) => {
                try {
                  return await getDestinationByAnyId(id);
                } catch (error) {
                  if (config.isDev) console.warn(`Destination ${id} not found or error fetching:`, error);
                  return null;
                }
              })
            );
            const validDestinations = destinations.filter(d => d !== null);
            setDestinationData(validDestinations);
            
            // If we have destination IDs but no valid destinations loaded, show error
            if (validDestinations.length === 0) {
              setLoadingError('Unable to load trip destinations');
            }
          } catch (error) {
            console.error('Error fetching destinations:', error);
            setLoadingError('Error loading trip destinations');
            if (error instanceof TypeError && error.message.includes('Failed to fetch')) {
              setLoadingError('Unable to load some trip locations');
            }
          }
        }
      } catch (error) {
        console.error('Error in fetchData:', error);
        setLoadingError('Failed to load trip details');
        if (error instanceof TypeError && error.message.includes('Failed to fetch')) {
          setLoadingError('Unable to load trip details');
        }
      }
    };
    
    fetchData();
  }, [trip.warehouse_id, trip.destinations]); // Remove warehouseData dependency to prevent loops
  
  const distance = trip.end_km - trip.start_km;
  
  // Parse and validate the date
  const tripStartDate = trip.trip_start_date ? parseISO(trip.trip_start_date) : null;
  const formattedDate = tripStartDate && isValid(tripStartDate) 
    ? format(tripStartDate, 'dd MMM yyyy')
    : 'Invalid Date';

  // Calculate total expenses consistently with other parts of the system
  const totalExpenses = (trip.total_fuel_cost || 0) + 
                       (trip.unloading_expense || 0) + 
                       (trip.driver_expense || 0) + 
                       (trip.road_rto_expense || 0) + 
                       (trip.miscellaneous_expense || 0);
  
  // Determine profit status color
  const getProfitStatusColor = () => {
    if (!trip.profit_status) return '';
    
    switch (trip.profit_status) {
      case 'profit': return 'text-green-600';
      case 'loss': return 'text-red-600';
      case 'neutral': return 'text-gray-600';
      default: return '';
    }
  };

  const handleCameraClick = () => {
    if (fileInputRef.current && !isUploading) {
      fileInputRef.current.click();
    }
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      const uploadedUrls = await uploadFilesAndGetPublicUrls(
        'trip-docs',
        `${trip.id}/attachments`,
        [file]
      );
      
      if (uploadedUrls.length > 0) {
        toast.success('Image uploaded successfully');
      } else {
        throw new Error('No URLs returned from upload');
      }
    } catch (error) {
      console.error('Error uploading image:', error);
      toast.error('Failed to upload image');
    } finally {
      setIsUploading(false);
      // Reset the file input
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  return (
    <div 
      className="card p-4 cursor-pointer hover:bg-gray-50 transition-colors animate-fade-in"
      onClick={onClick}
    >
      <div className="flex justify-between items-start mb-2">
        <div className="flex items-center gap-2">
          {trip.refueling_done ? (
            <div className="bg-accent-100 text-accent-700 p-1.5 rounded-full">
              <Fuel className="h-5 w-5" />
            </div>
          ) : (
            <div className="bg-gray-100 text-gray-600 p-1.5 rounded-full">
              <LocateFixed className="h-5 w-5" />
            </div>
          )}
          <div>
            <p className="text-base text-gray-900 font-medium">
              <SearchHighlightedText 
                text={trip.trip_serial_number} 
                searchTerm={searchTerm || ''} 
              />
            </p>
            <p className="text-sm text-gray-500">{formattedDate}</p>
          </div>
        </div>
        
        <div className="flex items-center gap-2">
          {/* P&L Button */}
          <button 
            className="p-1.5 rounded-full bg-gray-100 text-gray-600 hover:bg-primary-50 hover:text-primary-600 transition-colors"
            onClick={(e) => {
              e.stopPropagation();
              if (onPnlClick) onPnlClick(e, trip);
            }}
            title="Profit & Loss"
          >
            <IndianRupee className="h-4 w-4" />
          </button>
          
          {/* Camera Button */}
          <button 
            className="p-1.5 rounded-full bg-gray-100 text-gray-600 hover:bg-primary-50 hover:text-primary-600 transition-colors disabled:opacity-50"
            onClick={(e) => {
              e.stopPropagation();
              handleCameraClick();
            }}
            title="Upload Image"
            disabled={isUploading}
          >
            {isUploading ? (
              <RefreshCw className="h-4 w-4 animate-spin" />
            ) : (
              <Camera className="h-4 w-4" />
            )}
          </button>
          
          {/* Edit Button */}
          {onEditClick && (
            <button 
              className="p-1.5 rounded-full bg-gray-100 text-gray-600 hover:bg-blue-50 hover:text-blue-600 transition-colors"
              onClick={(e) => {
                e.stopPropagation();
                onEditClick(trip);
              }}
              title="Edit Trip"
            >
              <Edit className="h-4 w-4" />
            </button>
          )}
          
          {/* Hidden File Input */}
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            onChange={handleFileUpload}
            className="hidden"
          />
        </div>
      </div>
      
      <div className="space-y-2">
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center gap-2">
            <Truck className="h-4 w-4 text-gray-400" />
            <span className="text-gray-600">
              <SearchHighlightedText 
                text={vehicle ? vehicle.registration_number : 'Unknown Vehicle'} 
                searchTerm={searchTerm || ''} 
              />
            </span>
          </div>
          <div className="flex items-center gap-2">
            <User className="h-4 w-4 text-gray-400" />
            <span className="text-gray-600">
              <SearchHighlightedText 
                text={driver ? driver.name : 'Unknown Driver'} 
                searchTerm={searchTerm || ''} 
              />
            </span>
          </div>
        </div>

        {/* Route - Use saved destination_display for efficiency */}
        {trip.destination_display ? (
          <div className="flex items-center gap-2 text-sm">
            <MapPin className="h-4 w-4 text-gray-400" />
            <span className="text-gray-600 truncate">{trip.destination_display}</span>
          </div>
        ) : loadingError ? (
          <div className="flex items-center gap-2 text-sm">
            <MapPin className="h-4 w-4 text-gray-400" />
            <span className="text-gray-500 italic">{loadingError}</span>
          </div>
        ) : warehouseData && destinationData.length > 0 ? (
          // Fallback for older trips without destination_display
          <div className="flex items-center gap-2 text-sm">
            <MapPin className="h-4 w-4 text-gray-400" />
            <div className="flex items-center gap-1 text-gray-600 overflow-hidden">
              <span className="truncate max-w-[100px]">{warehouseData.name}</span>
              <ArrowRight className="h-3 w-3 flex-shrink-0" />
              <span className="truncate max-w-[100px]">
                {destinationData[0]?.name}
              </span>
              {destinationData.length > 1 && (
                <span className="text-xs bg-gray-100 px-1.5 py-0.5 rounded">
                  +{destinationData.length - 1}
                </span>
              )}
            </div>
          </div>
        ) : (
          // Show loading state or fallback when data is not available
          <div className="flex items-center gap-2 text-sm">
            <MapPin className="h-4 w-4 text-gray-400" />
            <span className="text-gray-500 italic">No trip locations found</span>
          </div>
        )}
      </div>
      
      <div className="mt-3 pt-3 border-t border-gray-100">
        <div className="grid grid-cols-4 gap-2 text-sm">
          <div>
            <span className="text-gray-500 block">Distance</span>
            <span className="font-medium text-primary-600">
              {distance.toLocaleString()} km
            </span>
          </div>
          
          <div>
            <span className="text-gray-500 block">Mileage</span>
            <span className={`font-medium ${
              trip.calculated_kmpl ? 'text-success-600' : 'text-gray-400'
            }`}>
              {trip.calculated_kmpl ? `${trip.calculated_kmpl.toFixed(1)} km/L` : '-'}
            </span>
          </div>
          
          <div>
            <span className="text-gray-500 block">Fuel</span>
            <span className="font-medium text-gray-900">
              {trip.fuel_quantity ? `${trip.fuel_quantity}L` : '-'}
            </span>
          </div>
          
          <div>
            <span className="text-gray-500 block">Expenses</span>
            <span className="font-medium text-gray-900">
              {totalExpenses.toLocaleString()}
            </span>
          </div>
        </div>

        <div className="mt-2 flex flex-wrap items-center gap-2">
          {trip.route_deviation && (() => {
            // Recalculate deviation for return trips with wrong values
            let displayDeviation = trip.route_deviation;
            if (trip.is_return_trip && Math.abs(trip.route_deviation) > 100 && trip.start_km && trip.end_km) {
              const actualDistance = trip.end_km - trip.start_km;
              const impliedStandardDistance = actualDistance / (1 + trip.route_deviation / 100);
              const correctStandardDistance = impliedStandardDistance * 2;
              displayDeviation = ((actualDistance - correctStandardDistance) / correctStandardDistance) * 100;
            }
            
            return (
              <div className={`text-xs px-2 py-1 rounded-full inline-flex items-center gap-1 ${
                Math.abs(displayDeviation) > 15
                  ? 'bg-error-50 text-error-700'
                  : Math.abs(displayDeviation) > 5
                  ? 'bg-warning-50 text-warning-700'
                  : 'bg-success-50 text-success-700'
              }`}>
                <span>Deviation: {displayDeviation > 0 ? '+' : ''}{displayDeviation.toFixed(1)}%</span>
              </div>
            );
          })()}
          
          {/* P&L Status Badge */}
          {trip.profit_status && (
            <div className={`text-xs px-2 py-1 rounded-full inline-flex items-center gap-1 ${
              trip.profit_status === 'profit'
                ? 'bg-success-50 text-success-700'
                : trip.profit_status === 'loss'
                ? 'bg-error-50 text-error-700'
                : 'bg-gray-50 text-gray-700'
            }`}>
              <span>
                {trip.profit_status === 'profit' ? 'Profit' : 
                 trip.profit_status === 'loss' ? 'Loss' : 'Break-even'}
                {trip.net_profit !== undefined && `: ${Math.abs(trip.net_profit).toLocaleString()}`}
              </span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}, (prevProps, nextProps) => {
  // Custom comparison function for memo
  return (
    prevProps.trip.id === nextProps.trip.id &&
    prevProps.trip.updated_at === nextProps.trip.updated_at &&
    prevProps.vehicle?.id === nextProps.vehicle?.id &&
    prevProps.driver?.id === nextProps.driver?.id
  );
});

TripCard.displayName = 'TripCard';

export default TripCard;

import React, { useMemo, useState, useEffect } from 'react';
import { Trip, Vehicle, Driver, Warehouse } from '@/types';
import { format, parseISO, isValid, differenceInDays } from 'date-fns';
import { 
  MapPin, User, Truck, Calendar, Fuel, DollarSign, 
  Edit2, Eye, TrendingUp, AlertTriangle, Clock, 
  Building2, Package, Navigation, ChevronRight,
  Activity, Target, IndianRupee, ArrowRight
} from 'lucide-react';
import { getWarehouse, getDestinationByAnyId } from '../../utils/storage';

interface TripListViewProps {
  trips: Trip[];
  vehicles: Vehicle[];
  drivers: Driver[];
  warehouses?: Warehouse[];
  onSelectTrip: (trip: Trip) => void;
  onPnlClick?: (e: React.MouseEvent, trip: Trip) => void;
  onEditTrip?: (trip: Trip) => void;
}

// Component to handle destination loading for individual trips
const TripDestinationDisplay: React.FC<{ trip: Trip }> = ({ trip }) => {
  const [warehouseData, setWarehouseData] = useState<any>(null);
  const [destinationData, setDestinationData] = useState<any[]>([]);
  const [loadingError, setLoadingError] = useState<string | null>(null);

  // Fetch warehouse and destinations data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoadingError(null);
        
        if (trip.warehouse_id) {
          try {
            const warehouse = await getWarehouse(trip.warehouse_id);
            setWarehouseData(warehouse);
          } catch (error) {
            console.error('Error fetching warehouse:', error);
            if (error instanceof TypeError && error.message.includes('Failed to fetch')) {
              setLoadingError('Unable to load warehouse data due to connection issues');
            } else {
              if (!warehouseData) {
                setLoadingError('Unable to load warehouse data');
              }
            }
          }
        }
        
        if (Array.isArray(trip.destinations) && trip.destinations.length > 0) {
          try {
            const destinations = await Promise.all(
              trip.destinations.map(async (id) => {
                try {
                  return await getDestinationByAnyId(id);
                } catch (error) {
                  console.warn(`Destination ${id} not found or error fetching:`, error);
                  return null;
                }
              })
            );
            const validDestinations = destinations.filter(d => d !== null);
            setDestinationData(validDestinations);
            
            if (validDestinations.length === 0) {
              setLoadingError('Unable to load trip destinations');
            }
          } catch (error) {
            console.error('Error fetching destinations:', error);
            setLoadingError('Error loading trip destinations');
            if (error instanceof TypeError && error.message.includes('Failed to fetch')) {
              setLoadingError('Unable to load some trip locations');
            }
          }
        }
      } catch (error) {
        console.error('Error in fetchData:', error);
        setLoadingError('Failed to load trip details');
        if (error instanceof TypeError && error.message.includes('Failed to fetch')) {
          setLoadingError('Unable to load trip details');
        }
      }
    };
    
    fetchData();
  }, [trip.warehouse_id, trip.destinations]);

  // Use saved destination_display for efficiency
  if (trip.destination_display) {
    return (
      <div className="flex items-center gap-2 text-sm">
        <MapPin className="h-4 w-4 text-gray-400" />
        <span className="text-gray-600 truncate">{trip.destination_display}</span>
      </div>
    );
  }

  if (loadingError) {
    return (
      <div className="flex items-center gap-2 text-sm">
        <MapPin className="h-4 w-4 text-gray-400" />
        <span className="text-gray-500 italic">{loadingError}</span>
      </div>
    );
  }

  if (warehouseData && destinationData.length > 0) {
    // Fallback for older trips without destination_display
    return (
      <div className="flex items-center gap-2 text-sm">
        <MapPin className="h-4 w-4 text-gray-400" />
        <div className="flex items-center gap-1 text-gray-600 overflow-hidden">
          <span className="truncate max-w-[100px]">{warehouseData.name}</span>
          <ArrowRight className="h-3 w-3 flex-shrink-0" />
          <span className="truncate max-w-[100px]">
            {destinationData[0]?.name}
          </span>
          {destinationData.length > 1 && (
            <span className="text-xs bg-gray-100 px-1.5 py-0.5 rounded">
              +{destinationData.length - 1}
            </span>
          )}
        </div>
      </div>
    );
  }

  // Show loading state or fallback when data is not available
  return (
    <div className="flex items-center gap-2 text-sm">
      <MapPin className="h-4 w-4 text-gray-400" />
      <span className="text-gray-500 italic">No trip locations found</span>
    </div>
  );
};

const TripListView: React.FC<TripListViewProps> = ({ 
  trips, 
  vehicles, 
  drivers,
  warehouses = [],
  onSelectTrip,
  onPnlClick,
  onEditTrip
}) => {
  const [expandedTrip, setExpandedTrip] = useState<string | null>(null);
  
  // Create lookup maps
  const vehiclesMap = useMemo(() => 
    new Map(vehicles.map(v => [v.id, v])), [vehicles]);
  
  const driversMap = useMemo(() => 
    new Map(drivers.map(d => [d.id, d])), [drivers]);
  
  const warehousesMap = useMemo(() => 
    new Map(warehouses.map(w => [w.id, w])), [warehouses]);
  
  const displayTrips = Array.isArray(trips) ? trips : [];
  
  // Calculate performance metrics
  const getPerformanceMetrics = (trip: Trip) => {
    const expectedMileage = 15; // Default expected
    const mileageStatus = trip.calculated_kmpl 
      ? trip.calculated_kmpl >= expectedMileage ? 'good' : 'poor' 
      : 'unknown';
    
    const deviationStatus = trip.route_deviation 
      ? Math.abs(trip.route_deviation) <= 5 ? 'normal' : 
        Math.abs(trip.route_deviation) <= 10 ? 'moderate' : 'high'
      : 'normal';
    
    const costPerKm = trip.total_expenses && trip.total_distance 
      ? (trip.total_expenses / trip.total_distance).toFixed(2)
      : null;
    
    return { mileageStatus, deviationStatus, costPerKm };
  };
  
  return (
    <div className="space-y-3">
      {displayTrips.length > 0 ? (
        displayTrips.map(trip => {
          const vehicle = vehiclesMap.get(trip.vehicle_id);
          const driver = driversMap.get(trip.driver_id);
          const warehouse = warehousesMap.get(trip.warehouse_id);
          const isExpanded = expandedTrip === trip.id;
          const metrics = getPerformanceMetrics(trip);
          
          const dateString = trip.trip_end_date || trip.trip_start_date || trip.created_at;
          const tripDate = dateString ? parseISO(dateString) : null;
          const formattedDate = tripDate && isValid(tripDate) 
            ? format(tripDate, 'dd MMM yyyy')
            : '-';
          
          const daysAgo = tripDate ? differenceInDays(new Date(), tripDate) : null;
          const distance = trip.end_km && trip.start_km ? trip.end_km - trip.start_km : 0;
          
          return (
            <div 
              key={trip.id}
              className="bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-all duration-200"
            >
              {/* Main Content Row */}
              <div 
                className="p-4 cursor-pointer"
                onClick={() => onSelectTrip(trip)}
              >
                <div className="flex items-start justify-between gap-4">
                  {/* Left Section - Trip Info */}
                  <div className="flex-1 space-y-3">
                    {/* Header Row */}
                    <div className="flex items-start justify-between">
                      <div className="flex items-center gap-3">
                        <div className="p-2 bg-primary-50 rounded-lg">
                          <Navigation className="h-5 w-5 text-primary-600" />
                        </div>
                        <div>
                          <h3 className="font-semibold text-gray-900 text-sm">
                            {trip.trip_serial_number}
                          </h3>
                          <div className="flex items-center gap-2 mt-1 text-xs text-gray-500">
                            <Calendar className="h-3 w-3" />
                            <span>{formattedDate}</span>
                            {daysAgo !== null && (
                              <span className="text-gray-400">({daysAgo} days ago)</span>
                            )}
                          </div>
                        </div>
                      </div>
                      
                      {/* Status Badges */}
                      <div className="flex items-center gap-2">
                        {trip.refueling_done && (
                          <span className="px-2 py-1 bg-green-50 text-green-700 text-xs rounded-full font-medium">
                            Refueled
                          </span>
                        )}
                        {metrics.deviationStatus === 'high' && (
                          <span className="px-2 py-1 bg-red-50 text-red-700 text-xs rounded-full font-medium">
                            High Deviation
                          </span>
                        )}
                        {metrics.mileageStatus === 'good' && (
                          <span className="px-2 py-1 bg-emerald-50 text-emerald-700 text-xs rounded-full font-medium">
                            Good Mileage
                          </span>
                        )}
                      </div>
                    </div>
                    
                    {/* Key Details Grid */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                      {/* Vehicle */}
                      <div className="flex items-center gap-2">
                        <Truck className="h-4 w-4 text-gray-400" />
                        <div>
                          <p className="text-xs text-gray-500">Vehicle</p>
                          <p className="text-sm font-medium text-gray-900">
                            {vehicle?.registration_number || '-'}
                          </p>
                        </div>
                      </div>
                      
                      {/* Driver */}
                      <div className="flex items-center gap-2">
                        <User className="h-4 w-4 text-gray-400" />
                        <div>
                          <p className="text-xs text-gray-500">Driver</p>
                          <p className="text-sm font-medium text-gray-900">
                            {driver?.name || '-'}
                          </p>
                        </div>
                      </div>
                      
                      {/* Distance */}
                      <div className="flex items-center gap-2">
                        <Activity className="h-4 w-4 text-gray-400" />
                        <div>
                          <p className="text-xs text-gray-500">Distance</p>
                          <p className="text-sm font-medium text-gray-900">
                            {distance > 0 ? `${distance.toFixed(1)} km` : '-'}
                          </p>
                        </div>
                      </div>
                      
                      {/* Expenses */}
                      <div className="flex items-center gap-2">
                        <IndianRupee className="h-4 w-4 text-gray-400" />
                        <div>
                          <p className="text-xs text-gray-500">Total Expenses</p>
                          <p className="text-sm font-medium text-gray-900">
                            {trip.total_expenses ? `${trip.total_expenses.toLocaleString()}` : '-'}
                          </p>
                        </div>
                      </div>
                    </div>
                    
                    {/* Route Info */}
                    <TripDestinationDisplay trip={trip} />
                  </div>
                  
                  {/* Right Section - Metrics & Actions */}
                  <div className="flex flex-col items-end gap-3">
                    {/* Performance Indicators */}
                    <div className="grid grid-cols-2 gap-2 text-right">
                      {/* Mileage */}
                      {trip.calculated_kmpl && (
                        <div className={`px-3 py-2 rounded-lg ${
                          metrics.mileageStatus === 'good' 
                            ? 'bg-green-50 border border-green-200' 
                            : 'bg-red-50 border border-red-200'
                        }`}>
                          <p className="text-xs text-gray-600">Mileage</p>
                          <p className={`text-sm font-bold ${
                            metrics.mileageStatus === 'good' ? 'text-green-700' : 'text-red-700'
                          }`}>
                            {trip.calculated_kmpl.toFixed(2)} km/L
                          </p>
                        </div>
                      )}
                      
                      {/* Cost per KM */}
                      {metrics.costPerKm && (
                        <div className="px-3 py-2 bg-gray-50 rounded-lg border border-gray-200">
                          <p className="text-xs text-gray-600">Cost/km</p>
                          <p className="text-sm font-bold text-gray-900">
                            {metrics.costPerKm}
                          </p>
                        </div>
                      )}
                    </div>
                    
                    {/* Action Buttons */}
                    <div className="flex items-center gap-1">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          setExpandedTrip(isExpanded ? null : trip.id);
                        }}
                        className="p-2 text-gray-500 hover:text-primary-600 hover:bg-primary-50 rounded-lg transition-colors"
                        title="View Details"
                      >
                        <Eye className="h-4 w-4" />
                      </button>
                      
                      {onEditTrip && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            onEditTrip(trip);
                          }}
                          className="p-2 text-gray-500 hover:text-primary-600 hover:bg-primary-50 rounded-lg transition-colors"
                          title="Edit Trip"
                        >
                          <Edit2 className="h-4 w-4" />
                        </button>
                      )}
                      
                      {onPnlClick && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            onPnlClick(e, trip);
                          }}
                          className="p-2 text-gray-500 hover:text-green-600 hover:bg-green-50 rounded-lg transition-colors"
                          title="P&L Analysis"
                        >
                          <DollarSign className="h-4 w-4" />
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              </div>
              
              {/* Expanded Details */}
              {isExpanded && (
                <div className="border-t border-gray-100 px-4 py-3 bg-gray-50">
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                    {/* KM Readings */}
                    <div>
                      <p className="text-xs text-gray-500 mb-1">KM Readings</p>
                      <p className="font-medium">
                        {trip.start_km?.toLocaleString() || '-'}  {trip.end_km?.toLocaleString() || '-'}
                      </p>
                    </div>
                    
                    {/* Fuel Details */}
                    {trip.fuel_quantity && (
                      <div>
                        <p className="text-xs text-gray-500 mb-1">Fuel</p>
                        <p className="font-medium">
                          {trip.fuel_quantity.toFixed(1)}L @ {trip.refueling_rate || '-'}/L
                        </p>
                      </div>
                    )}
                    
                    {/* Warehouse */}
                    {warehouse && (
                      <div>
                        <p className="text-xs text-gray-500 mb-1">Warehouse</p>
                        <p className="font-medium flex items-center gap-1">
                          <Building2 className="h-3 w-3" />
                          {warehouse.name}
                        </p>
                      </div>
                    )}
                    
                    {/* Deviation */}
                    {trip.route_deviation && (
                      <div>
                        <p className="text-xs text-gray-500 mb-1">Route Deviation</p>
                        <p className={`font-medium ${
                          Math.abs(trip.route_deviation) > 8 ? 'text-red-600' : 'text-gray-900'
                        }`}>
                          {trip.route_deviation > 0 ? '+' : ''}{trip.route_deviation.toFixed(1)}%
                        </p>
                      </div>
                    )}
                    
                    {/* Toll & Other Expenses */}
                    {trip.toll_amount && (
                      <div>
