                                    docPaths={vehicles.find(v => v.id === vehicle.id)?.puc_document_url ? [vehicles.find(v => v.id === vehicle.id)!.puc_document_url!] : null}
                                    preferredFormat="short"
                                  />
                                </td>
                              )}
                              
