                          if (value >= 100000) return `${(value/100000).toFixed(1)}L`;
                          if (value >= 1000) return `${(value/1000).toFixed(0)}k`;
                          return `${value}`;
                        }} 
                        tick={{ fontSize: 11 }}
                        axisLine={false}
