            <LoginForm showPassword={showPassword} setShowPassword={setShowPassword} />

            {/* Links */}
            <div className="mt-6 text-center text-sm space-y-2">
              <Link
                to="/reset-password"
                className="block text-emerald-600 hover:text-emerald-700 font-medium transition-colors"
              >
                Forgot password?
              </Link>
              <p className="text-gray-500 hover:text-gray-700 font-medium transition-colors">
                Don't have an account?{' '}
                <Link to="/register" className="text-emerald-600 hover:text-emerald-700 font-medium transition-colors">
                  Register your organization
                </Link>
              </p>
            </div>

            {/* Security Badge */}
            <div className="mt-6 pt-6 border-t border-gray-100">
              <div className="flex items-center justify-center gap-2 text-gray-400">
                <Lock className="w-4 h-4" />
                <span className="text-xs font-medium">
                  Your credentials are encrypted and secure
                </span>
              </div>
            </div>
          </div>

          {/* Footer */}
          <p className="text-center text-xs text-gray-400 mt-6">
            &copy; {new Date().getFullYear()} Auto Vital Solution. All rights reserved. &bull; Built with &#10084;&#65039;
          </p>
          
          {/* Decorative Clickable Truck Icon */}
          <button
            onClick={() => console.log('Truck icon clicked - add your custom action here')}
            className="fixed bottom-8 right-8 group cursor-pointer"
            aria-label="Fleet icon"
          >
            <div className="bg-gradient-to-br from-emerald-500 to-emerald-600 p-4 rounded-2xl shadow-lg shadow-emerald-500/30 hover:shadow-emerald-500/50 hover:scale-110 transition-all duration-300 border border-emerald-400/20">
              <Truck className="w-7 h-7 text-white" strokeWidth={2.5} fill="white" />
            </div>
            <div className="absolute -bottom-8 right-0 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
              <span className="text-xs text-gray-500 font-medium whitespace-nowrap">Fleet Manager</span>
            </div>
          </button>
        </div>
      </div>
    </>
  );
};

export default LoginPage;

import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate, useLocation } from "react-router-dom";
import Layout from "../components/layout/Layout";
import { MaintenanceTask, Vehicle } from "@/types";
import {
  getDateRangeForFilter,
  getMaintenanceMetricsWithComparison,
} from "../utils/maintenanceAnalytics";
import { getTasks } from "../utils/maintenanceStorage";
import { getVehicles } from "../utils/storage";
import { PlusCircle, BarChart3, Wrench, Calendar } from "lucide-react";
import Button from "../components/ui/Button";
import KPIPanel from "../components/maintenance/KPIPanel";
import MaintenanceCalendar from "../components/maintenance/MaintenanceCalendar";
import MaintenanceTaskList from "../components/maintenance/MaintenanceTaskList";
import { useQuery } from "@tanstack/react-query";
const MaintenancePage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { t } = useTranslation();
  const [dateRangeFilter] = useState("allTime");
  const [customDateRange, setCustomDateRange] = useState({
    start: "",
    end: "",
  });
  const [viewMode, setViewMode] = useState<"calendar" | "list">("calendar");
  const [metrics, setMetrics] = useState<any>({
    totalTasks: 0,
    pendingTasks: 0,
    completedTasksThisMonth: 0,
    averageCompletionTime: 0,
    averageCost: 0,
    totalExpenditure: 0,
    monthlyExpenditure: [],
    expenditureByVehicle: [],
    expenditureByVendor: [],
    taskTypeDistribution: [],
    vehicleDowntime: [],
    kmBetweenMaintenance: [],
    previousPeriodComparison: {
      totalTasks: 0,
      totalExpenditure: 0,
      percentChange: 0,
    },
  });
  // Initialize custom date range values and handle action=new
  useEffect(() => {
    const today = new Date();
    setCustomDateRange({
      start: "2020-01-01",
      end: today.toISOString().split("T")[0],
    });
    // Check query parameters for action=new
    const searchParams = new URLSearchParams(location.search);
    const action = searchParams.get("action");
    if (action === "new") {
      // Navigate to new maintenance task page
      navigate("/maintenance/new");
      // Clear query params
      const newUrl = window.location.pathname;
      window.history.replaceState({}, document.title, newUrl);
    }
  }, [location.search, navigate]);
  // Use React Query to fetch tasks
  const { data: tasks, isLoading: tasksLoading } = useQuery({
    queryKey: ["maintenanceTasks"],
    queryFn: getTasks,
    staleTime: 30 * 1000, // 30 seconds
  });
  // Use React Query to fetch vehicles
  const { data: vehicles, isLoading: vehiclesLoading } = useQuery({
    queryKey: ["vehicles"],
    queryFn: getVehicles,
    staleTime: 30 * 1000, // 30 seconds
  });
  // Calculate metrics whenever date range changes or data is loaded
  useEffect(() => {
    if (!tasksLoading && !vehiclesLoading && tasks && vehicles) {
      calculateMetrics(tasks, vehicles, dateRangeFilter);
    }
  }, [
    dateRangeFilter,
    customDateRange,
    tasks,
    vehicles,
    tasksLoading,
    vehiclesLoading,
  ]);
  const calculateMetrics = async (
    tasksData: MaintenanceTask[],
    vehiclesData: Vehicle[],
    filter: string,
  ) => {
    try {
      const dateRange = getDateRangeForFilter(
        filter,
        customDateRange.start,
        customDateRange.end,
      );
      // Get metrics with comparison to previous period
      const metricsData = await getMaintenanceMetricsWithComparison(
        dateRange,
        tasksData,
        vehiclesData,
      );
      setMetrics(metricsData);
    } catch (error) {
      console.error("Error calculating maintenance metrics:", error);
    }
  };
  const handleViewTask = (task: MaintenanceTask) => {
    navigate(`/maintenance/${task.id}?mode=view`, { state: { task, mode: 'view' } });
  };

  const handleEditTask = (task: MaintenanceTask) => {
    navigate(`/maintenance/${task.id}`, { state: { task, mode: 'edit' } });
  };

  const loading = tasksLoading || vehiclesLoading;
  return (
    <Layout>
      {/* Page Header */}
      <div className="rounded-xl border bg-white dark:bg-white px-4 py-3 shadow-sm mb-6">
        <div className="flex items-center group">
          <Wrench className="h-5 w-5 mr-2 text-gray-500 dark:text-gray-400 group-hover:text-primary-600 transition" />
          <h1 className="text-2xl font-display font-semibold tracking-tight-plus text-gray-900 dark:text-gray-100">
            {t("maintenance.title")}
          </h1>
        </div>
        <p className="text-sm font-sans text-gray-500 dark:text-gray-400 mt-1 ml-7">
          {t("maintenance.description")}
        </p>
        <div className="mt-4 flex flex-wrap gap-2">
          <Button
            onClick={() => navigate("/maintenance/new")}
            icon={<PlusCircle className="h-4 w-4" />}
            inputSize="sm"
          >
            {t("maintenance.newTask")}
          </Button>
          <Button
            onClick={() => navigate("/parts-health-analytics")}
            icon={<BarChart3 className="h-4 w-4" />}
            variant="outline"
            inputSize="sm"
          >
            {t("maintenance.partsHealthAnalytics")}
          </Button>
          {/* View Toggle */}
          <div className="flex border border-gray-200 rounded-lg overflow-hidden">
            <Button
              onClick={() => setViewMode("calendar")}
              variant={viewMode === "calendar" ? "default" : "outline"}
              inputSize="sm"
              icon={<Calendar className="h-4 w-4" />}
              className="rounded-none border-0"
            >
              {t("maintenance.calendar")}
            </Button>
            <Button
              onClick={() => setViewMode("list")}
              variant={viewMode === "list" ? "default" : "outline"}
              inputSize="sm"
              icon={<Wrench className="h-4 w-4" />}
              className="rounded-none border-0"
            >
              Task List
            </Button>
          </div>
        </div>
      </div>
      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
          <p className="ml-3 font-sans text-gray-600">
            Loading maintenance analytics...
          </p>
        </div>
      ) : (
        <div className="space-y-6">
          {/* KPI Panel */}
          <KPIPanel
            totalTasks={metrics.totalTasks}
            pendingTasks={metrics.pendingTasks}
            averageCompletionTime={metrics.averageCompletionTime}
            completedTasksThisMonth={metrics.completedTasksThisMonth}
            averageCost={metrics.averageCost}
            totalExpenditure={metrics.totalExpenditure}
            previousPeriodComparison={metrics.previousPeriodComparison}
          />
          
          {/* View Content */}
          {viewMode === "calendar" ? (
            <MaintenanceCalendar
              tasks={tasks || []}
              vehicles={vehicles || []}
            />
          ) : (
            <MaintenanceTaskList
              tasks={tasks || []}
              vehicles={vehicles || []}
              onViewTask={handleViewTask}
              onEditTask={handleEditTask}
            />
          )}
        </div>
      )}
    </Layout>
  );
};
export default MaintenancePage;

import React, { useState, useRef, useEffect, useMemo } from 'react';
import { Navigate } from 'react-router-dom';
import Layout from '../components/layout/Layout';
import MediaCard from '../components/HeroFeed/MediaCard';
import { usePermissions } from '../hooks/usePermissions';
import { useHeroFeed, useKPICards } from '../hooks/useHeroFeed';
import LoadingScreen from '../components/LoadingScreen';
import { RefreshCw, Sparkles, Play } from 'lucide-react';

// Fleet-relevant YouTube videos
const FLEET_VIDEOS = [
  { id: 'L2ZM0j0KDr4', title: 'Truck Maintenance Tips' },
  { id: 'nUHjUoT2QwA', title: 'Fuel Saving Techniques' },
  { id: 'zWOADa2rKHo', title: 'Driver Safety Training' },
  { id: 'B1J6Ou4q8vE', title: 'Fleet GPS Tracking Guide' },
  { id: 'sFYo6-nVHd8', title: 'Commercial Vehicle Inspection' },
  { id: 'kWTozGbRrCs', title: 'Route Optimization Tips' },
  { id: 'p3jiXMXK2Cs', title: 'ELD Compliance Guide' },
  { id: 'TK5_kEBx-q4', title: 'Winter Driving Safety' }
];

const NotificationsPage: React.FC = () => {
  const { permissions, loading: permissionsLoading } = usePermissions();
  const [selectedFilters, setSelectedFilters] = useState<string[]>(['all']);
  const [playingVideos, setPlayingVideos] = useState<Set<string>>(new Set());
  const videoRefs = useRef<{ [key: string]: HTMLDivElement }>({});

  const feedKinds = useMemo(() => {
    if (selectedFilters.includes('all')) {
      return [];
    }
    return selectedFilters.filter(kind => kind !== 'media');
  }, [selectedFilters]);

  const { 
    data, 
    fetchNextPage, 
    hasNextPage, 
    isFetchingNextPage, 
    isLoading, 
    refetch 
  } = useHeroFeed({
    kinds: feedKinds,
    limit: 20
  });

  const { data: kpiCards, refetch: refetchKPIs } = useKPICards();

  const events = data?.pages.flat() || [];

  // Count events by type
  const getCounts = () => {
    const counts = {
      ai_alert: 0,
      vehicle_doc: 0,
      maintenance: 0,
      trip: 0,
      kpi: 0,
      vehicle_activity: 0,
      activity: 0
    };
    
    events.forEach((event: any) => {
      if (Object.prototype.hasOwnProperty.call(counts, event.kind)) {
        counts[event.kind as keyof typeof counts]++;
      }
    });
    
    return counts;
  };

  const counts = getCounts();

  const mediaCards = useMemo(() => {
    if (!kpiCards) {
      return [];
    }
    return kpiCards.filter(card => {
      const type = card.kpi_payload?.type;
      return type === 'youtube' || type === 'image' || type === 'playlist';
    });
  }, [kpiCards]);

  const mediaCount = mediaCards.length;
  const isMediaOnly = selectedFilters.length === 1 && selectedFilters[0] === 'media';

  const filteredEvents = useMemo(() => {
    if (isMediaOnly) {
      return [];
    }
    if (feedKinds.length === 0) {
      return events;
    }
    return events.filter((event: any) => feedKinds.includes(event.kind));
  }, [events, feedKinds, isMediaOnly]);

  // Intersection Observer for autoplay
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const videoId = entry.target.getAttribute('data-video-id');
          if (!videoId) return;
          
          if (entry.isIntersecting && entry.intersectionRatio > 0.5) {
            // Auto-play when more than 50% visible
            setPlayingVideos(prev => new Set([...prev, videoId]));
          } else {
            // Stop playing when less than 50% visible
            setPlayingVideos(prev => {
              const newSet = new Set(prev);
              newSet.delete(videoId);
              return newSet;
            });
          }
        });
      },
      { 
        threshold: [0, 0.5, 1], // Multiple thresholds for better detection
        rootMargin: '0px'
      }
    );

    // Delay observation to ensure elements are rendered
    setTimeout(() => {
      Object.values(videoRefs.current).forEach(ref => {
        if (ref) observer.observe(ref);
      });
    }, 100);

    return () => {
      observer.disconnect();
    };
  }, [events, selectedFilters]); // Re-run when events change

  // Handle loading and permissions after all hooks
  if (permissionsLoading) {
    return <LoadingScreen isLoading={true} />;
  }

  if (!permissions?.canAccessAlerts) {
    return <Navigate to="/vehicles" replace />;
  }

  const handleFilterClick = (filterId: string) => {
    if (filterId === 'all') {
      setSelectedFilters(['all']);
    } else {
      const newFilters = selectedFilters.includes(filterId)
        ? selectedFilters.filter(f => f !== filterId)
        : [...selectedFilters.filter(f => f !== 'all'), filterId];
      
      setSelectedFilters(newFilters.length === 0 ? ['all'] : newFilters);
    }
    // Force refetch when changing filters
    setTimeout(() => {
      refetch();
      refetchKPIs();
    }, 100);
  };

  // AI Summary Component
  const AISummary = ({ index }: { index: number }) => {
    const summaries = [
      "Fleet utilization improved by 12% this week. Focus on Route CG04 vehicles for maintenance scheduling.",
      "3 vehicles showing consistent route deviations. Consider driver training for efficiency improvement.",
      "Fuel costs trending upward. Recommend reviewing driver behavior reports for optimization opportunities.",
      "Document renewals clustered in Q2. Start renewal process 60 days in advance to avoid downtime."
    ];
    
    return (
      <div className="bg-gradient-to-r from-blue-50 to-purple-50 border border-blue-200 rounded-lg p-6 my-6">
        <div className="flex items-start gap-3">
          <Sparkles className="w-5 h-5 text-blue-600 mt-1" />
          <div>
            <h3 className="font-semibold text-gray-900 mb-2">AVS Intelligence Summary</h3>
            <p className="text-gray-700">{summaries[index % summaries.length]}</p>
          </div>
        </div>
      </div>
    );
  };

  // Compact KPI display
  const CompactKPIs = () => (
    <div className="grid grid-cols-5 gap-2 mb-4 opacity-75">
      {[
        { label: 'Utilization', value: '0.1%' },
        { label: 'Mileage', value: '6.8 km/L' },
        { label: 'Load/Trip', value: '3,924 kg' },
        { label: 'Cost/KM', value: 'Rs 10.21' },
        { label: 'Distance', value: '12,450 km' }
      ].map((kpi, i) => (
        <div key={i} className="bg-white p-2 rounded text-center border border-gray-200">
          <p className="text-xs text-gray-600">{kpi.label}</p>
          <p className="text-sm font-semibold">{kpi.value}</p>
        </div>
      ))}
    </div>
  );

  // Video Reel Component
  const VideoReel = ({ videoId, index }: any) => {
    const isPlaying = playingVideos.has(videoId);
    const videoIndex = index % FLEET_VIDEOS.length;
    const video = FLEET_VIDEOS[videoIndex];
    
    return (
      <div 
        ref={el => {
          if (el) {
            videoRefs.current[videoId] = el;
          } else {
            delete videoRefs.current[videoId];
          }
        }}
        data-video-id={videoId}
        className="relative rounded-lg overflow-hidden bg-black video-reel-container"
        style={{ aspectRatio: '16/9', maxHeight: '400px' }}
      >
        {isPlaying ? (
          <iframe
            className="w-full h-full"
            src={`https://www.youtube.com/embed/${video.id}?autoplay=1&mute=1&controls=1&loop=1&playlist=${video.id}&playsinline=1`}
            title={video.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
            allowFullScreen
            loading="lazy"
          />
        ) : (
          <div 
            className="relative cursor-pointer group h-full"
            onClick={() => setPlayingVideos(new Set([videoId]))}
          >
            <img
              src={`https://img.youtube.com/vi/${video.id}/hqdefault.jpg`}
              alt={video.title}
              className="w-full h-full object-cover"
              loading="lazy"
              onError={(e) => {
                e.currentTarget.src = `https://img.youtube.com/vi/${video.id}/default.jpg`;
              }}
            />
            <div className="absolute inset-0 bg-black bg-opacity-30 flex items-center justify-center hover:bg-opacity-40 transition-all">
              <div className="bg-white/90 rounded-full p-3 hover:scale-110 transition-transform">
                <Play className="w-6 h-6 text-black" />
              </div>
            </div>
            <div className="absolute bottom-0 left-0 right-0 p-3 bg-gradient-to-t from-black/80 to-transparent">
              <p className="text-white font-medium text-sm">{video.title}</p>
            </div>
          </div>
        )}
      </div>
    );
  };

  const renderEventCard = (event: any, index: number) => {
    // Insert AI summary every 8 events (check first to avoid conflict with video reel)
    if (index > 0 && index % 8 === 0) {
      return (
        <React.Fragment key={`fragment-${event.id}`}>
          <AISummary index={index / 8} />
          {renderOriginalEventCard(event)}
        </React.Fragment>
      );
    }

    // Insert video reel every 4 events
    if (index > 0 && index % 4 === 0) {
      return (
        <React.Fragment key={`fragment-${event.id}`}>
          <VideoReel 
            videoId={`video-${index}`} 
            index={index}
          />
          {renderOriginalEventCard(event)}
        </React.Fragment>
      );
    }

    return renderOriginalEventCard(event);
  };

  const renderOriginalEventCard = (event: any) => {
    // Your existing event card rendering logic here
    const getPriorityStyles = (priority: string) => {
      switch (priority) {
        case 'danger': return 'border-red-200 bg-red-50';
        case 'warn': return 'border-yellow-200 bg-yellow-50';
        default: return 'border-gray-200 bg-white';
      }
    };

    return (
      <div key={event.id} className={`p-4 rounded-lg border ${getPriorityStyles(event.priority)}`}>
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h3 className="font-semibold text-gray-900">{event.title}</h3>
            <p className="text-sm text-gray-600 mt-1">{event.description}</p>
            <p className="text-xs text-gray-500 mt-2">
              {new Date(event.event_time).toLocaleString()}
            </p>
          </div>
          {event.kind === 'vehicle_doc' && (
            <button className="px-3 py-1 bg-blue-600 text-white rounded text-sm hover:bg-blue-700">
              Send Reminder
            </button>
          )}
        </div>
      </div>
    );
  };

  return (
    <Layout>
      <div className="max-w-4xl mx-auto px-4 py-6">
        {/* Compact KPIs */}
        <CompactKPIs />
        
        {/* Header */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Fleet Activity Feed</h1>
          <button
            onClick={() => refetch()}
            className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            <RefreshCw className="w-4 h-4" />
            Refresh
          </button>
        </div>

        {/* Stats Grid - Updated to show real counts */}
        <div className="grid grid-cols-3 md:grid-cols-6 gap-2 mb-4">
          <div className="bg-white p-3 rounded-lg border">
            <span className="text-lg font-bold">{counts.ai_alert}</span>
            <p className="text-xs text-gray-600">AI Alerts</p>
          </div>
          <div className="bg-white p-3 rounded-lg border">
            <span className="text-lg font-bold">{counts.vehicle_doc}</span>
            <p className="text-xs text-gray-600">Documents</p>
          </div>
          <div className="bg-white p-3 rounded-lg border">
            <span className="text-lg font-bold">{counts.maintenance}</span>
            <p className="text-xs text-gray-600">Maintenance</p>
          </div>
          <div className="bg-white p-3 rounded-lg border">
            <span className="text-lg font-bold">{counts.trip}</span>
            <p className="text-xs text-gray-600">Trips</p>
          </div>
          <div className="bg-white p-3 rounded-lg border">
            <span className="text-lg font-bold">{counts.kpi}</span>
            <p className="text-xs text-gray-600">KPIs</p>
          </div>
          <div className="bg-white p-3 rounded-lg border">
            <span className="text-lg font-bold">{mediaCount}</span>
            <p className="text-xs text-gray-600">Media</p>
          </div>
        </div>

        {/* Filters */}
        <div className="flex flex-wrap gap-2 mb-6">
          {['all', 'ai_alert', 'vehicle_doc', 'maintenance', 'trip', 'kpi', 'media'].map(filter => (
            <button
              key={filter}
              onClick={() => handleFilterClick(filter)}
              className={`px-4 py-2 rounded-full text-sm font-medium transition-colors ${
                selectedFilters.includes(filter)
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              {filter === 'all' ? 'All' : filter.split('_').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ')}
            </button>
          ))}
        </div>

        {/* Feed with mixed content */}
        <div className="space-y-4">
          {isLoading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
              <p className="mt-4 text-gray-600">Loading feed...</p>
            </div>
          ) : isMediaOnly ? (
            mediaCards.length > 0 || FLEET_VIDEOS.length > 0 ? (
              <div className="space-y-4">
                {mediaCards.map((card, index) => (
                  <MediaCard key={`media-card-${card.id ?? index}`} card={card} />
                ))}
                {FLEET_VIDEOS.map((video, index) => (
                  <VideoReel
                    key={`media-reel-${video.id}`}
                    videoId={`media-${video.id}`}
                    index={index}
                  />
                ))}
              </div>
            ) : (
              <div className="text-center py-12 bg-gray-50 rounded-lg">
                <p className="text-gray-600">No media to display</p>
              </div>
            )
          ) : filteredEvents.length > 0 ? (
            <>
              {filteredEvents.map((event, index) => renderEventCard(event, index))}
              
              {hasNextPage && (
                <div className="text-center pt-4">
                  <button
                    onClick={() => fetchNextPage()}
                    disabled={isFetchingNextPage}
                    className="px-6 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200"
                  >
                    {isFetchingNextPage ? 'Loading...' : 'Load More'}
                  </button>
                </div>
              )}
            </>
          ) : (
            <div className="text-center py-12 bg-gray-50 rounded-lg">
              <p className="text-gray-600">No events to display</p>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
};

export default NotificationsPage;

import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate, Navigate } from 'react-router-dom';
import Layout from '../components/layout/Layout';
import { usePermissions } from '../hooks/usePermissions';
import LoadingScreen from '../components/LoadingScreen';
import { Trip, Vehicle, Driver, Warehouse } from '@/types';
import { getTrips, getVehicles, getWarehouses } from '../utils/storage';
import { getDrivers } from '../utils/api/drivers';
import { 
  Calendar, 
  ChevronDown, 
  Filter, 
  ChevronLeft, 
  ChevronRight, 
  X, 
  RefreshCw, 
  Search, 
  Download, 
  IndianRupee,
  TrendingUp,
  TrendingDown,
  DollarSign,
  BarChart3,
  BarChart2,
  Target,
  Eye,
  Printer,
  ArrowUpDown,
  MoreHorizontal,
  PieChart,
  LineChart,
  Activity
} from 'lucide-react';
import Button from '../components/ui/Button';
import Input from '../components/ui/Input';
import Select from '../components/ui/Select';
import { format, subDays, startOfWeek, endOfWeek, startOfMonth, endOfMonth, 
         startOfYear, endOfYear, subWeeks, subMonths, subYears, parseISO } from 'date-fns';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import { toast } from 'react-toastify';
import { 
  LineChart as RechartsLineChart, 
  Line, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer,
  PieChart as RechartsPieChart,
  Pie,
  Cell,
  BarChart as RechartsBarChart,
  Bar,
  Area,
  AreaChart
} from 'recharts';

interface PnLSummary {
  totalTrips: number;
  totalIncome: number;
  totalExpense: number;
  netProfit: number;
  profitMargin: number;
  avgCostPerKm: number;
  profitableTrips: number;
  lossTrips: number;
  previousPeriodIncome?: number;
  previousPeriodExpense?: number;
  previousPeriodProfit?: number;
  incomeGrowth?: number;
  expenseGrowth?: number;
  profitGrowth?: number;
}

interface ChartData {
  name: string;
  value: number;
  color?: string;
}

interface TrendData {
  date: string;
  income: number;
  expense: number;
  profit: number;
  trips: number;
}

interface TripSummaryMetrics {
  totalExpenses: number;
  avgDistance: number;
  tripCount: number;
  meanMileage: number;
  topDriver: {
    id: string;
    name: string;
    totalDistance: number;
    tripCount: number;
  } | null;
  topVehicle: {
    id: string;
    registrationNumber: string;
    tripCount: number;
  } | null;
}

interface DatePreset {
  label: string;
  getValue: () => { startDate: Date; endDate: Date };
}

const TripPnlReportsPage: React.FC = () => {
  const navigate = useNavigate();
  const { permissions, loading: permissionsLoading } = usePermissions();
  
  // Add error state for debugging
  const [error, setError] = useState<string | null>(null);
  const [debugInfo, setDebugInfo] = useState<any>(null);

  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [trips, setTrips] = useState<Trip[]>([]);
  const [drivers, setDrivers] = useState<Driver[]>([]);
  const [warehouses, setWarehouses] = useState<Warehouse[]>([]);
  const [loading, setLoading] = useState(true);

  // Filter state
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedVehicle, setSelectedVehicle] = useState('');
  const [selectedDriver, setSelectedDriver] = useState('');
  const [selectedWarehouse, setSelectedWarehouse] = useState('');
  const [selectedProfitStatus, setSelectedProfitStatus] = useState('');
  const [selectedDatePreset, setSelectedDatePreset] = useState('alltime');
  const [customStartDate, setCustomStartDate] = useState('');
  const [customEndDate, setCustomEndDate] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  
  // Enhanced UI state
  const [showCharts, setShowCharts] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [sortField, setSortField] = useState<keyof Trip>('trip_start_date');
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('desc');
  const [selectedTrip, setSelectedTrip] = useState<Trip | null>(null);
  const [showTripModal, setShowTripModal] = useState(false);

  // Permission checks will be moved after all hooks

  // ...rest of component logic

  const datePresetOptions: DatePreset[] = [
    {
      label: 'Last 7 days',
      getValue: () => ({
        startDate: subDays(new Date(), 7),
        endDate: new Date()
      })
    },
    {
      label: 'Last 30 days',
      getValue: () => ({
        startDate: subDays(new Date(), 30),
        endDate: new Date()
      })
    },
    {
      label: 'This week',
      getValue: () => ({
        startDate: startOfWeek(new Date()),
        endDate: endOfWeek(new Date())
      })
    },
    {
      label: 'Last week',
      getValue: () => ({
        startDate: startOfWeek(subWeeks(new Date(), 1)),
        endDate: endOfWeek(subWeeks(new Date(), 1))
      })
    },
    {
      label: 'This month',
      getValue: () => ({
        startDate: startOfMonth(new Date()),
        endDate: endOfMonth(new Date())
      })
    },
    {
      label: 'Last month',
      getValue: () => ({
        startDate: startOfMonth(subMonths(new Date(), 1)),
        endDate: endOfMonth(subMonths(new Date(), 1))
      })
    },
    {
      label: 'This year',
      getValue: () => ({
        startDate: startOfYear(new Date()),
        endDate: endOfYear(new Date())
      })
    },
    {
      label: 'Last year',
      getValue: () => ({
        startDate: startOfYear(subYears(new Date(), 1)),
        endDate: endOfYear(subYears(new Date(), 1))
      })
    },
    {
      label: 'All Time',
      getValue: () => ({
        startDate: new Date('2020-01-01'),
        endDate: new Date()
      })
    }
  ];

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      setDebugInfo(null);
      
      console.log('Loading PNL Reports data...');

      const [tripsData, vehiclesData, driversData, warehousesData] = await Promise.all([
        getTrips(),
        getVehicles(),
        getDrivers(),
        getWarehouses()
      ]);
      
      console.log('Data loaded successfully:', {
        trips: tripsData.length,
        vehicles: vehiclesData.length,
        drivers: driversData.length,
        warehouses: warehousesData.length
      });
      
      setTrips(tripsData);
      setVehicles(vehiclesData);
      setDrivers(driversData);
      setWarehouses(warehousesData);
    } catch (error) {
      console.error("Error fetching PNL Reports data:", error);
      setError(error instanceof Error ? error.message : 'Unknown error occurred');
      setDebugInfo({
        error: error,
        timestamp: new Date().toISOString(),
        userAgent: navigator.userAgent,
        url: window.location.href
      });
      toast.error("Failed to load data");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const tripSummaryMetrics = useMemo((): TripSummaryMetrics => {
    const tripsArray = Array.isArray(trips) ? trips : [];
    const totalTrips = tripsArray.length;
    const totalDistance = tripsArray.reduce((sum, trip) => sum + (trip.end_km - trip.start_km), 0);
    const totalExpenses = tripsArray.reduce((sum, trip) => sum + (trip.total_expense || 0), 0);
    const avgDistance = totalTrips > 0 ? totalDistance / totalTrips : 0;
    
    // Calculate mean mileage from trips with valid kmpl
    const tripsWithMileage = tripsArray.filter(trip => trip.calculated_kmpl);
    const meanMileage = tripsWithMileage.length > 0 
      ? tripsWithMileage.reduce((sum, trip) => sum + (trip.calculated_kmpl || 0), 0) / tripsWithMileage.length 
      : 0;
    
    // Find top driver by trip count
    const driverStats: Record<string, { tripCount: number; totalDistance: number; name: string }> = {};
    tripsArray.forEach(trip => {
      if (trip.driver_id) {
        if (!driverStats[trip.driver_id]) {
          const driver = drivers.find(d => d.id === trip.driver_id);
          driverStats[trip.driver_id] = {
            tripCount: 0,
            totalDistance: 0,
            name: driver?.name || 'Unknown'
          };
        }
        driverStats[trip.driver_id].tripCount++;
        driverStats[trip.driver_id].totalDistance += (trip.end_km - trip.start_km);
      }
    });
    
    const topDriver = Object.entries(driverStats)
      .sort((a, b) => b[1].tripCount - a[1].tripCount)[0];
    
    // Find top vehicle by trip count
    const vehicleStats: Record<string, { tripCount: number; registrationNumber: string }> = {};
    tripsArray.forEach(trip => {
      if (trip.vehicle_id) {
        if (!vehicleStats[trip.vehicle_id]) {
          const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
          vehicleStats[trip.vehicle_id] = {
            tripCount: 0,
            registrationNumber: vehicle?.registration_number || 'Unknown'
          };
        }
        vehicleStats[trip.vehicle_id].tripCount++;
      }
    });
    
    const topVehicle = Object.entries(vehicleStats)
      .sort((a, b) => b[1].tripCount - a[1].tripCount)[0];
    
    return {
      totalExpenses,
      avgDistance,
      tripCount: totalTrips,
      meanMileage,
      topDriver: topDriver ? {
        id: topDriver[0],
        name: topDriver[1].name,
        totalDistance: topDriver[1].totalDistance,
        tripCount: topDriver[1].tripCount
      } : null,
      topVehicle: topVehicle ? {
        id: topVehicle[0],
        registrationNumber: topVehicle[1].registrationNumber,
        tripCount: topVehicle[1].tripCount
      } : null
    };
  }, [trips, vehicles, drivers]);

  const dateRange = useMemo(() => {
    if (selectedDatePreset === 'custom') {
      return {
        startDate: customStartDate ? parseISO(customStartDate) : subDays(new Date(), 30),
        endDate: customEndDate ? parseISO(customEndDate) : new Date()
      };
    } else {
      const preset = datePresetOptions.find(option => option.label.toLowerCase().replace(/\s+/g, '') === selectedDatePreset);
      return preset ? preset.getValue() : datePresetOptions[1].getValue();
    }
  }, [selectedDatePreset, customStartDate, customEndDate]);

  // Filter trips based on current filters
  const filteredTrips = useMemo(() => {
    try {
    const filtered = trips.filter(trip => {
      // Date range filter
      const tripDate = parseISO(trip.trip_start_date);
      if (tripDate < dateRange.startDate || tripDate > dateRange.endDate) {
        return false;
      }

      // Search filter
      if (
        searchTerm &&
        !(trip.trip_serial_number || "").toLowerCase().includes(searchTerm.toLowerCase())
      ) {
        return false;
      }

      // Vehicle filter
      if (selectedVehicle && trip.vehicle_id !== selectedVehicle) {
        return false;
      }

      // Driver filter
      if (selectedDriver && trip.driver_id !== selectedDriver) {
        return false;
      }

      // Warehouse filter
      if (selectedWarehouse && trip.warehouse_id !== selectedWarehouse) {
        return false;
      }

      // Profit status filter
      if (selectedProfitStatus && trip.profit_status !== selectedProfitStatus) {
        return false;
      }

      return true;
    });
    
    // Recalculate financial values for each filtered trip
    return filtered.map(trip => {
      // Calculate total expense from individual components
      const calculatedTotalExpense = (
        Number(trip.total_fuel_cost || 0) +
        Number(trip.unloading_expense || 0) +
        Number(trip.driver_expense || 0) +
        Number(trip.road_rto_expense || 0) +
        Number(trip.miscellaneous_expense || 0) +
        Number(trip.breakdown_expense || 0)
      );
      
      // Calculate income (skip if no income data)
      const income = Number(trip.income_amount) || 0;
      
      // Calculate net profit (only if income exists)
      const calculatedNetProfit = income > 0 ? income - calculatedTotalExpense : -calculatedTotalExpense;
      
      // Calculate cost per km
      const distance = (Number(trip.end_km) || 0) - (Number(trip.start_km) || 0);
      const calculatedCostPerKm = distance > 0 ? calculatedTotalExpense / distance : 0;
      
      // Determine profit status
      let calculatedProfitStatus: 'profit' | 'loss' | 'neutral';
      if (income > 0) {
        if (calculatedNetProfit > 0) {
          calculatedProfitStatus = 'profit';
        } else if (calculatedNetProfit < 0) {
          calculatedProfitStatus = 'loss';
        } else {
          calculatedProfitStatus = 'neutral';
        }
      } else {
        // If no income, default to loss since we have expenses
        calculatedProfitStatus = calculatedTotalExpense > 0 ? 'loss' : 'neutral';
      }
      
      // Return trip with recalculated financial values
      return {
        ...trip,
        total_expense: calculatedTotalExpense,
        net_profit: calculatedNetProfit,
        cost_per_km: calculatedCostPerKm,
        profit_status: calculatedProfitStatus
      };
    });
    
    return filtered;
    } catch (error) {
      console.error('Error filtering trips:', error);
      return [];
    }
  }, [trips, dateRange, searchTerm, selectedVehicle, selectedDriver, selectedWarehouse, selectedProfitStatus]);

  // Calculate P&L summary directly from filtered trips
  const pnlSummary = useMemo((): PnLSummary => {
    const summary = filteredTrips.reduce((acc, trip) => {
      const income = Number(trip.income_amount) || 0;
      
      // Calculate total expense from individual components using total_fuel_cost
      const calculatedTotalExpense = (
        Number(trip.total_fuel_cost || 0) +
        Number(trip.unloading_expense || 0) +
        Number(trip.driver_expense || 0) +
        Number(trip.road_rto_expense || 0) +
        Number(trip.miscellaneous_expense || 0) +
        Number(trip.breakdown_expense || 0)
      );
      
      // Calculate profit from income minus calculated expenses
      const calculatedProfit = income - calculatedTotalExpense;
      
      // Calculate cost per km from calculated expenses
      const distance = (Number(trip.end_km) || 0) - (Number(trip.start_km) || 0);
      const calculatedCostPerKm = distance > 0 ? calculatedTotalExpense / distance : 0;

      acc.totalIncome += income;
      acc.totalExpense += calculatedTotalExpense;
      acc.netProfit += calculatedProfit;
      acc.avgCostPerKm += calculatedCostPerKm;

      // Determine profit status based on calculated profit
      if (calculatedProfit > 0) {
        acc.profitableTrips++;
      } else if (calculatedProfit < 0) {
        acc.lossTrips++;
      }

      return acc;
    }, {
      totalTrips: filteredTrips.length,
      totalIncome: 0,
      totalExpense: 0,
      netProfit: 0,
      profitMargin: 0,
      avgCostPerKm: 0,
      profitableTrips: 0,
      lossTrips: 0
    });

    summary.avgCostPerKm = filteredTrips.length > 0 ? summary.avgCostPerKm / filteredTrips.length : 0;
    summary.profitMargin = summary.totalIncome > 0 ? (summary.netProfit / summary.totalIncome) * 100 : 0;

    return summary;
  }, [filteredTrips]);

  // Prepare chart data
  const chartData = useMemo(() => {
    try {
      // Profit/Loss distribution pie chart
      const profitLossData: ChartData[] = [
        { name: 'Profitable Trips', value: pnlSummary.profitableTrips, color: '#10B981' },
        { name: 'Loss Trips', value: pnlSummary.lossTrips, color: '#EF4444' }
      ];

    // Monthly trend data
    const monthlyTrend: TrendData[] = [];
    const monthlyData = new Map<string, { income: number; expense: number; profit: number; trips: number }>();
    
    filteredTrips.forEach(trip => {
      const month = format(parseISO(trip.trip_start_date), 'MMM yyyy');
      const income = Number(trip.income_amount) || 0;
      const expense = Number(trip.total_expense) || 0;
      const profit = income - expense;
      
      if (!monthlyData.has(month)) {
        monthlyData.set(month, { income: 0, expense: 0, profit: 0, trips: 0 });
      }
      
      const data = monthlyData.get(month)!;
      data.income += income;
      data.expense += expense;
      data.profit += profit;
      data.trips += 1;
    });

    monthlyData.forEach((data, month) => {
      monthlyTrend.push({
        date: month,
        income: data.income,
        expense: data.expense,
        profit: data.profit,
        trips: data.trips
      });
    });

    // Vehicle performance data
    const vehiclePerformance: ChartData[] = [];
    const vehicleData = new Map<string, { profit: number; trips: number }>();
    
    filteredTrips.forEach(trip => {
      const vehicleId = trip.vehicle_id;
      const vehicleName = getVehicleName(vehicleId);
      const income = Number(trip.income_amount) || 0;
      const expense = Number(trip.total_expense) || 0;
      const profit = income - expense;
      
      if (!vehicleData.has(vehicleId)) {
        vehicleData.set(vehicleId, { profit: 0, trips: 0 });
      }
      
      const data = vehicleData.get(vehicleId)!;
      data.profit += profit;
      data.trips += 1;
    });

    vehicleData.forEach((data, vehicleId) => {
      const vehicleName = getVehicleName(vehicleId);
      vehiclePerformance.push({
        name: vehicleName,
        value: data.profit,
        color: data.profit >= 0 ? '#10B981' : '#EF4444'
      });
    });

      return {
        profitLossData,
        monthlyTrend: monthlyTrend.sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime()),
        vehiclePerformance: vehiclePerformance.slice(0, 10) // Top 10 vehicles
      };
    } catch (error) {
      console.error('Error preparing chart data:', error);
      // Return empty data structure to prevent crashes
      return {
        profitLossData: [],
        monthlyTrend: [],
        vehiclePerformance: []
      };
    }
  }, [filteredTrips, pnlSummary]);

  // Pagination
  const paginatedTrips = useMemo(() => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return filteredTrips.slice(startIndex, endIndex);
  }, [filteredTrips, currentPage, itemsPerPage]);

  // Sorting
  const sortedTrips = useMemo(() => {
    return [...paginatedTrips].sort((a, b) => {
      const aValue = a[sortField];
      const bValue = b[sortField];
      
      if (aValue < bValue) return sortDirection === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortDirection === 'asc' ? 1 : -1;
      return 0;
    });
  }, [paginatedTrips, sortField, sortDirection]);

  //  PERMISSION CHECKS AFTER ALL HOOKS
  if (permissionsLoading) {
    return <LoadingScreen isLoading={true} />;
  }

  // Redirect non-owner users - FIXED: use correct permission property
  if (!permissions?.canAccessReports) {
    return <Navigate to="/vehicles" replace />;
  }

  const clearFilters = () => {
    setSearchTerm('');
    setSelectedVehicle('');
    setSelectedDriver('');
    setSelectedWarehouse('');
    setSelectedProfitStatus('');
    setSelectedDatePreset('alltime');
    setCustomStartDate('');
    setCustomEndDate('');
    setCurrentPage(1);
  };

  const handleSort = (field: keyof Trip) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  const handleItemsPerPageChange = (items: number) => {
    setItemsPerPage(items);
    setCurrentPage(1);
  };

  const handleTripClick = (trip: Trip) => {
    setSelectedTrip(trip);
    setShowTripModal(true);
  };

  const printReport = () => {
    window.print();
  };

  const exportToExcel = () => {
    const exportData = filteredTrips.map(trip => {
      const vehicle = vehicles.find(v => v.id === trip.vehicle_id);
      const driver = drivers.find(d => d.id === trip.driver_id);
      const warehouse = warehouses.find(w => w.id === trip.warehouse_id);

      return {
        'Trip ID': trip.trip_serial_number,
        'Date': format(parseISO(trip.trip_start_date), 'dd/MM/yyyy'),
        'Vehicle': vehicle?.registration_number || 'N/A',
        'Driver': driver?.name || 'N/A',
        'Warehouse': warehouse?.name || 'N/A',
        'Income ()': trip.income_amount || 0,
        'Total Expense ()': trip.total_expense || 0,
        'Net Profit ()': trip.net_profit || 0,
        'Cost per KM ()': trip.cost_per_km || 0,
        'Profit Status': trip.profit_status || 'N/A',
        'Billing Type': trip.billing_type || 'N/A',
        'Freight Rate': trip.freight_rate || 0,
        'Distance (KM)': (trip.end_km || 0) - (trip.start_km || 0)
      };
    });

    const worksheet = XLSX.utils.json_to_sheet(exportData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Trip P&L Report');
    
    const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
    const data = new Blob([excelBuffer], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });
    
    const fileName = `trip-pnl-report-${format(new Date(), 'yyyy-MM-dd')}.xlsx`;
    saveAs(data, fileName);
    
    toast.success('Report exported successfully');
  };

  const getVehicleName = (vehicleId: string) => {
    const vehicle = vehicles.find(v => v.id === vehicleId);
    return vehicle?.registration_number || 'N/A';
  };

  const getDriverName = (driverId: string) => {
    const driver = drivers.find(d => d.id === driverId);
    return driver?.name || 'N/A';
  };

  const getWarehouseName = (warehouseId: string) => {
    const warehouse = warehouses.find(w => w.id === warehouseId);
    return warehouse?.name || 'N/A';
  };

  const getProfitStatusColor = (status: string) => {
    switch (status) {
      case 'profit':
        return 'text-green-600 bg-green-50';
      case 'loss':
        return 'text-red-600 bg-red-50';
      case 'neutral':
        return 'text-gray-600 bg-gray-50';
      default:
        return 'text-gray-600 bg-gray-50';
    }
  };

  if (loading) {
    return (
      <Layout title="Trip P&L Report" subtitle="Analyze profitability of trips">
        <div className="flex items-center justify-center h-64">
          <RefreshCw className="h-8 w-8 animate-spin text-blue-600" />
        </div>
      </Layout>
    );
  }

  // Show error state if there's an error
  if (error) {
    return (
      <Layout title="Trip P&L Report" subtitle="Analyze profitability of trips">
        <div className="max-w-2xl mx-auto">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6">
            <div className="flex items-center mb-4">
              <AlertTriangle className="h-6 w-6 text-red-600 mr-2" />
              <h3 className="text-lg font-semibold text-red-800">Error Loading PNL Reports</h3>
            </div>
            <p className="text-red-700 mb-4">{error}</p>
            
            {debugInfo && (
              <details className="mb-4">
                <summary className="cursor-pointer text-sm font-medium text-red-600 mb-2">
                  Debug Information
                </summary>
                <pre className="text-xs bg-red-100 p-3 rounded overflow-auto max-h-40">
                  {JSON.stringify(debugInfo, null, 2)}
                </pre>
              </details>
            )}
            
            <div className="flex gap-3">
              <Button
                onClick={() => {
                  setError(null);
                  setDebugInfo(null);
                  fetchData();
                }}
                icon={<RefreshCw className="h-4 w-4" />}
              >
                Try Again
              </Button>
              <Button
                variant="outline"
                onClick={() => navigate('/trips')}
                icon={<ChevronLeft className="h-4 w-4" />}
              >
                Back to Trips
              </Button>
            </div>
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout
      title="Trip P&L Report"
      subtitle="Analyze profitability of trips"
      actions={
        <div className="flex gap-3">
          <Button
            variant="outline"
            onClick={() => setShowCharts(!showCharts)}
            icon={showCharts ? <BarChart3 className="h-4 w-4" /> : <LineChart className="h-4 w-4" />}
          >
            {showCharts ? 'Hide Charts' : 'Show Charts'}
          </Button>
          <Button
            variant="outline"
            onClick={printReport}
            icon={<Printer className="h-4 w-4" />}
            disabled={filteredTrips.length === 0}
          >
            Print
          </Button>
          <Button
            variant="outline"
            onClick={exportToExcel}
            icon={<Download className="h-4 w-4" />}
            disabled={filteredTrips.length === 0}
          >
            Export
          </Button>
          <Button
            variant="outline"
            onClick={() => navigate('/trips')}
            icon={<ChevronLeft className="h-4 w-4" />}
          >
            Back to Trips
          </Button>
        </div>
      }
    >
      <div className="space-y-6">
        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600 mb-1">Total Income</p>
                <p className="text-2xl font-bold text-gray-900 flex items-center mb-2">
                  <IndianRupee className="h-5 w-5 mr-1" />
                  {pnlSummary.totalIncome.toLocaleString('en-IN')}
                </p>
                <div className="flex items-center gap-2">
                  <div className="flex items-center text-green-600">
                    <TrendingUp className="h-4 w-4 mr-1" />
                    <span className="text-sm font-medium">+12.5%</span>
                  </div>
                  <span className="text-xs text-gray-500">vs last month</span>
                </div>
              </div>
              <div className="p-3 bg-green-50 rounded-lg">
                <TrendingUp className="h-6 w-6 text-green-600" />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600 mb-1">Total Expense</p>
                <p className="text-2xl font-bold text-gray-900 flex items-center mb-2">
                  <IndianRupee className="h-5 w-5 mr-1" />
                  {pnlSummary.totalExpense.toLocaleString('en-IN')}
                </p>
                <div className="flex items-center gap-2">
                  <div className="flex items-center text-red-600">
                    <TrendingUp className="h-4 w-4 mr-1" />
                    <span className="text-sm font-medium">+8.2%</span>
                  </div>
                  <span className="text-xs text-gray-500">vs last month</span>
                </div>
              </div>
              <div className="p-3 bg-red-50 rounded-lg">
                <TrendingDown className="h-6 w-6 text-red-600" />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600 mb-1">Net Profit</p>
                <p className={`text-2xl font-bold flex items-center mb-2 ${
                  pnlSummary.netProfit >= 0 ? 'text-green-600' : 'text-red-600'
                }`}>
                  <IndianRupee className="h-5 w-5 mr-1" />
                  {pnlSummary.netProfit.toLocaleString('en-IN')}
                </p>
                <div className="flex items-center gap-2">
                  <div className={`flex items-center ${pnlSummary.netProfit >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                    <TrendingUp className="h-4 w-4 mr-1" />
                    <span className="text-sm font-medium">{pnlSummary.profitMargin.toFixed(1)}%</span>
                  </div>
                  <span className="text-xs text-gray-500">margin</span>
                </div>
              </div>
              <div className={`p-3 rounded-lg ${
                pnlSummary.netProfit >= 0 ? 'bg-green-50' : 'bg-red-50'
              }`}>
                <DollarSign className={`h-6 w-6 ${
                  pnlSummary.netProfit >= 0 ? 'text-green-600' : 'text-red-600'
                }`} />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
            <div className="flex items-center justify-between">
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-600 mb-1">Total Trips</p>
                <p className="text-2xl font-bold text-gray-900 mb-2">{pnlSummary.totalTrips}</p>
                <div className="flex items-center gap-2">
                  <div className="flex items-center text-blue-600">
                    <Activity className="h-4 w-4 mr-1" />
                    <span className="text-sm font-medium">
                      {((pnlSummary.profitableTrips / pnlSummary.totalTrips) * 100).toFixed(1)}%
                    </span>
                  </div>
                  <span className="text-xs text-gray-500">success rate</span>
                </div>
              </div>
              <div className="p-3 bg-blue-50 rounded-lg">
                <BarChart3 className="h-6 w-6 text-blue-600" />
              </div>
            </div>
          </div>
        </div>

        {/* Charts Section */}
        {showCharts && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Monthly Trend Chart */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                  <LineChart className="h-5 w-5 mr-2 text-blue-600" />
                  Monthly Trend
                </h3>
              </div>
              <div className="h-80">
                <ResponsiveContainer width="100%" height="100%">
                  <AreaChart data={chartData.monthlyTrend}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip 
                      formatter={(value: number, name: string) => [
                        `${value.toLocaleString('en-IN')}`, 
                        name.charAt(0).toUpperCase() + name.slice(1)
                      ]}
                    />
                    <Area 
                      type="monotone" 
                      dataKey="income" 
                      stackId="1" 
                      stroke="#10B981" 
                      fill="#10B981" 
                      fillOpacity={0.6}
                    />
                    <Area 
                      type="monotone" 
                      dataKey="expense" 
                      stackId="2" 
                      stroke="#EF4444" 
                      fill="#EF4444" 
                      fillOpacity={0.6}
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Profit/Loss Distribution */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                  <PieChart className="h-5 w-5 mr-2 text-green-600" />
                  Trip Distribution
                </h3>
              </div>
              <div className="h-80">
                <ResponsiveContainer width="100%" height="100%">
                  <RechartsPieChart>
                    <Pie
                      data={chartData.profitLossData}
                      cx="50%"
                      cy="50%"
                      labelLine={false}
                      label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                    >
                      {chartData.profitLossData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip />
                  </RechartsPieChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Vehicle Performance */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 lg:col-span-2">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                  <BarChart3 className="h-5 w-5 mr-2 text-purple-600" />
                  Vehicle Performance
                </h3>
              </div>
              <div className="h-80">
                <ResponsiveContainer width="100%" height="100%">
                  <RechartsBarChart data={chartData.vehiclePerformance}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip 
                      formatter={(value: number) => [`${value.toLocaleString('en-IN')}`, 'Profit']}
                    />
                    <Bar dataKey="value" fill="#8884d8">
                      {chartData.vehiclePerformance.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Bar>
                  </RechartsBarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        )}

        {/* Filters */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center border-l-2 border-blue-500 pl-2">
              <Filter className="h-5 w-5 mr-2" />
              Filters
            </h3>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                inputSize="sm"
                onClick={clearFilters}
                icon={<X className="h-4 w-4" />}
              >
                Clear All
              </Button>
              <Button
                variant="outline"
                inputSize="sm"
                onClick={() => setShowFilters(!showFilters)}
                icon={<ChevronDown className={`h-4 w-4 transition-transform ${showFilters ? 'rotate-180' : ''}`} />}
              >
                {showFilters ? 'Hide' : 'Show'} Filters
              </Button>
            </div>
          </div>

          {/* Quick Filter Chips */}
          <div className="mb-4">
            <div className="flex flex-wrap gap-2">
              <span className="text-sm font-medium text-gray-700 mr-2">Quick Filters:</span>
              <button
                onClick={() => setSelectedProfitStatus('profit')}
                className={`px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                  selectedProfitStatus === 'profit'
                    ? 'bg-green-100 text-green-800 border border-green-200'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                Profitable Only
              </button>
              <button
                onClick={() => setSelectedProfitStatus('loss')}
                className={`px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                  selectedProfitStatus === 'loss'
                    ? 'bg-red-100 text-red-800 border border-red-200'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                Loss Only
              </button>
              <button
                onClick={() => setSelectedDatePreset('last7days')}
                className={`px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                  selectedDatePreset === 'last7days'
                    ? 'bg-blue-100 text-blue-800 border border-blue-200'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                Last 7 Days
              </button>
              <button
                onClick={() => setSelectedDatePreset('last30days')}
                className={`px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                  selectedDatePreset === 'last30days'
                    ? 'bg-blue-100 text-blue-800 border border-blue-200'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                Last 30 Days
              </button>
              <button
                onClick={() => setSelectedDatePreset('thismonth')}
                className={`px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                  selectedDatePreset === 'thismonth'
                    ? 'bg-blue-100 text-blue-800 border border-blue-200'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                This Month
              </button>
            </div>
          </div>

          {showFilters && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Search Trip ID
                </label>
                <Input
                  type="text"
                  placeholder="Enter trip ID..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  icon={<Search className="h-4 w-4" />}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Date Range
                </label>
                <Select
                  value={selectedDatePreset}
                  onChange={(e) => setSelectedDatePreset(e.target.value)}
                  options={[
                    ...datePresetOptions.map(option => ({
                      value: option.label.toLowerCase().replace(/\s+/g, ''),
                      label: option.label
                    })),
                    { value: 'custom', label: 'Custom Range' }
                  ]}
                />
              </div>

              {selectedDatePreset === 'custom' && (
                <>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Start Date
                    </label>
                    <Input
                      type="date"
                      value={customStartDate}
                      onChange={(e) => setCustomStartDate(e.target.value)}
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      End Date
                    </label>
                    <Input
                      type="date"
                      value={customEndDate}
                      onChange={(e) => setCustomEndDate(e.target.value)}
                    />
                  </div>
                </>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Vehicle
                </label>
                <Select
                  value={selectedVehicle}
                  onChange={(e) => setSelectedVehicle(e.target.value)}
                  options={[
                    { value: '', label: 'All Vehicles' },
                    ...vehicles.map(vehicle => ({
                      value: vehicle.id,
                      label: vehicle.registration_number
                    }))
                  ]}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Driver
                </label>
                <Select
                  value={selectedDriver}
                  onChange={(e) => setSelectedDriver(e.target.value)}
                  options={[
                    { value: '', label: 'All Drivers' },
                    ...drivers.map(driver => ({
                      value: driver.id,
                      label: driver.name
                    }))
                  ]}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Warehouse
                </label>
                <Select
                  value={selectedWarehouse}
                  onChange={(e) => setSelectedWarehouse(e.target.value)}
                  options={[
                    { value: '', label: 'All Warehouses' },
                    ...warehouses.map(warehouse => ({
                      value: warehouse.id,
                      label: warehouse.name
                    }))
                  ]}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Profit Status
                </label>
                <Select
                  value={selectedProfitStatus}
                  onChange={(e) => setSelectedProfitStatus(e.target.value)}
                  options={[
                    { value: '', label: 'All Status' },
                    { value: 'profit', label: 'Profit' },
                    { value: 'loss', label: 'Loss' },
                    { value: 'neutral', label: 'Neutral' }
                  ]}
                />
              </div>
            </div>
          )}
        </div>

        {/* Trips Table */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold text-gray-900">
                Trip Details ({filteredTrips.length} trips)
              </h3>
              <div className="flex items-center gap-4">
                <div className="flex items-center gap-2">
                  <label className="text-sm text-gray-600">Show:</label>
                  <Select
                    value={itemsPerPage.toString()}
                    onChange={(e) => handleItemsPerPageChange(Number(e.target.value))}
                    options={[
                      { value: '10', label: '10' },
                      { value: '25', label: '25' },
                      { value: '50', label: '50' },
                      { value: '100', label: '100' }
                    ]}
                    inputSize="sm"
                  />
                </div>
              </div>
            </div>
          </div>

          {filteredTrips.length === 0 ? (
            <div className="p-12 text-center">
              <Target className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No trips found</h3>
              <p className="text-gray-600 mb-4">Try adjusting your filters to see more results.</p>
              <Button variant="outline" onClick={clearFilters}>
                Clear Filters
              </Button>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th 
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                      onClick={() => handleSort('trip_serial_number')}
                    >
                      <div className="flex items-center gap-1">
                        Trip Details
                        <ArrowUpDown className="h-3 w-3" />
                      </div>
                    </th>
                    <th 
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                      onClick={() => handleSort('vehicle_id')}
                    >
                      <div className="flex items-center gap-1">
                        Vehicle & Driver
                        <ArrowUpDown className="h-3 w-3" />
                      </div>
                    </th>
                    <th 
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                      onClick={() => handleSort('income_amount')}
                    >
                      <div className="flex items-center gap-1">
                        Financial Summary
                        <ArrowUpDown className="h-3 w-3" />
                      </div>
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {sortedTrips.map((trip) => (
                    <tr key={trip.id} className="hover:bg-gray-50 cursor-pointer" onClick={() => handleTripClick(trip)}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {trip.trip_serial_number}
                          </div>
                          <div className="text-sm text-gray-500">
                            {format(parseISO(trip.trip_start_date), 'dd MMM yyyy')}
                          </div>
                          <div className="text-sm text-gray-500">
                            {((trip.end_km || 0) - (trip.start_km || 0))} km
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {getVehicleName(trip.vehicle_id)}
                          </div>
                          <div className="text-sm text-gray-500">
                            {getDriverName(trip.driver_id)}
                          </div>
                          <div className="text-sm text-gray-500">
                            {getWarehouseName(trip.warehouse_id)}
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="space-y-1">
                          <div className="flex justify-between text-sm">
                            <span className="text-gray-600">Income:</span>
                            <span className="font-medium text-green-600">
                              {(trip.income_amount || 0).toLocaleString('en-IN')}
                            </span>
                          </div>
                          <div className="flex justify-between text-sm">
                            <span className="text-gray-600">Expense:</span>
                            <span className="font-medium text-red-600">
                              {(trip.total_expense || 0).toLocaleString('en-IN')}
                            </span>
                          </div>
                          <div className="flex justify-between text-sm font-semibold border-t pt-1">
                            <span className="text-gray-900">Profit:</span>
                            <span className={
                              (trip.net_profit || 0) >= 0 ? 'text-green-600' : 'text-red-600'
                            }>
                              {(trip.net_profit || 0).toLocaleString('en-IN')}
                            </span>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                          getProfitStatusColor(trip.profit_status || '')
                        }`}>
                          {trip.profit_status || 'N/A'}
                        </span>
                        {trip.billing_type && (
                          <div className="text-xs text-gray-500 mt-1">
                            {trip.billing_type}
                          </div>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center gap-2">
                          <Button
                            variant="outline"
                            inputSize="sm"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleTripClick(trip);
                            }}
                            icon={<Eye className="h-3 w-3" />}
                          >
                            View
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
          
          {/* Pagination */}
          {filteredTrips.length > 0 && (
            <div className="px-6 py-4 border-t border-gray-200">
              <div className="flex items-center justify-between">
                <div className="text-sm text-gray-700">
                  Showing {((currentPage - 1) * itemsPerPage) + 1} to {Math.min(currentPage * itemsPerPage, filteredTrips.length)} of {filteredTrips.length} results
                </div>
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    inputSize="sm"
                    onClick={() => handlePageChange(currentPage - 1)}
                    disabled={currentPage === 1}
                    icon={<ChevronLeft className="h-4 w-4" />}
                  >
                    Previous
                  </Button>
                  
                  <div className="flex items-center gap-1">
                    {Array.from({ length: Math.ceil(filteredTrips.length / itemsPerPage) }, (_, i) => i + 1)
                      .filter(page => 
                        page === 1 || 
                        page === Math.ceil(filteredTrips.length / itemsPerPage) ||
                        Math.abs(page - currentPage) <= 2
                      )
                      .map((page, index, array) => (
                        <React.Fragment key={page}>
                          {index > 0 && array[index - 1] !== page - 1 && (
                            <span className="px-2 text-gray-500">...</span>
                          )}
                          <Button
                            variant={currentPage === page ? "primary" : "outline"}
                            inputSize="sm"
                            onClick={() => handlePageChange(page)}
                            className="min-w-[40px]"
                          >
                            {page}
                          </Button>
                        </React.Fragment>
                      ))
                    }
                  </div>
                  
                  <Button
                    variant="outline"
                    inputSize="sm"
                    onClick={() => handlePageChange(currentPage + 1)}
                    disabled={currentPage === Math.ceil(filteredTrips.length / itemsPerPage)}
                    icon={<ChevronRight className="h-4 w-4" />}
                  >
                    Next
                  </Button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Trip Detail Modal */}
      {showTripModal && selectedTrip && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-xl font-semibold text-gray-900">
                  Trip Details - {selectedTrip.trip_serial_number}
                </h3>
                <Button
                  variant="outline"
                  inputSize="sm"
                  onClick={() => setShowTripModal(false)}
                  icon={<X className="h-4 w-4" />}
                >
                  Close
                </Button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Trip Information */}
                <div className="space-y-4">
                  <h4 className="font-medium text-gray-900 border-b pb-2">Trip Information</h4>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Trip ID:</span>
                      <span className="font-medium">{selectedTrip.trip_serial_number}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Date:</span>
                      <span className="font-medium">{format(parseISO(selectedTrip.trip_start_date), 'dd MMM yyyy')}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Distance:</span>
                      <span className="font-medium">{((selectedTrip.end_km || 0) - (selectedTrip.start_km || 0))} km</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Vehicle:</span>
                      <span className="font-medium">{getVehicleName(selectedTrip.vehicle_id)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Driver:</span>
                      <span className="font-medium">{getDriverName(selectedTrip.driver_id)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Warehouse:</span>
                      <span className="font-medium">{getWarehouseName(selectedTrip.warehouse_id)}</span>
                    </div>
                  </div>
                </div>

                {/* Financial Summary */}
                <div className="space-y-4">
                  <h4 className="font-medium text-gray-900 border-b pb-2">Financial Summary</h4>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Income:</span>
                      <span className="font-medium text-green-600">
                        {(selectedTrip.income_amount || 0).toLocaleString('en-IN')}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Total Expense:</span>
                      <span className="font-medium text-red-600">
                        {(selectedTrip.total_expense || 0).toLocaleString('en-IN')}
                      </span>
                    </div>
                    <div className="flex justify-between border-t pt-2">
                      <span className="text-gray-900 font-medium">Net Profit:</span>
                      <span className={`font-bold ${
                        (selectedTrip.net_profit || 0) >= 0 ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {(selectedTrip.net_profit || 0).toLocaleString('en-IN')}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Cost per KM:</span>
                      <span className="font-medium">
                        {(selectedTrip.cost_per_km || 0).toFixed(2)}/km
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Billing Type:</span>
                      <span className="font-medium">{selectedTrip.billing_type || 'N/A'}</span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Expense Breakdown */}
              <div className="mt-6">
                <h4 className="font-medium text-gray-900 border-b pb-2 mb-4">Expense Breakdown</h4>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                  <div className="p-3 bg-gray-50 rounded-lg">
                    <p className="text-xs text-gray-500">Fuel Cost</p>
                    <p className="font-medium">{(selectedTrip.total_fuel_cost || 0).toLocaleString('en-IN')}</p>
                  </div>
                  <div className="p-3 bg-gray-50 rounded-lg">
                    <p className="text-xs text-gray-500">Driver Expense</p>
                    <p className="font-medium">{(selectedTrip.driver_expense || 0).toLocaleString('en-IN')}</p>
                  </div>
                  <div className="p-3 bg-gray-50 rounded-lg">
                    <p className="text-xs text-gray-500">Unloading</p>
                    <p className="font-medium">{(selectedTrip.unloading_expense || 0).toLocaleString('en-IN')}</p>
                  </div>
                  <div className="p-3 bg-gray-50 rounded-lg">
                    <p className="text-xs text-gray-500">Road/RTO</p>
                    <p className="font-medium">{(selectedTrip.road_rto_expense || 0).toLocaleString('en-IN')}</p>
                  </div>
                  <div className="p-3 bg-gray-50 rounded-lg">
                    <p className="text-xs text-gray-500">Miscellaneous</p>
                    <p className="font-medium">{(selectedTrip.miscellaneous_expense || 0).toLocaleString('en-IN')}</p>
                  </div>
                  <div className="p-3 bg-gray-50 rounded-lg">
                    <p className="text-xs text-gray-500">Breakdown</p>
                    <p className="font-medium">{(selectedTrip.breakdown_expense || 0).toLocaleString('en-IN')}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default TripPnlReportsPage;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../../components/layout/Layout'; 
import TripsTable from '../../components/admin/TripsTable';
import TripsSummary from '../../components/admin/TripsSummary';
import ExportOptionsModal, { ExportOptions } from '../../components/admin/ExportOptionsModal';
import { Trip, Vehicle, Driver, Warehouse, Destination } from '@/types';
import { getTrips, getVehicles, getWarehouses, getDestinations, updateTrip } from '../../utils/storage';
import { getDrivers } from '../../utils/api/drivers';
import { generateCSV, downloadCSV, parseCSV } from '../../utils/csvParser';
import { supabase } from '../../utils/supabaseClient';
import { forceDataRefresh, testDatabaseConnection, clearCacheAndRefresh } from '../../utils/forceDataRefresh';
import { format, subDays, startOfWeek, endOfWeek, startOfMonth, endOfMonth, 
         startOfYear, endOfYear, subWeeks, subMonths, subYears } from 'date-fns';
import Button from '../../components/ui/Button';
import Input from '../../components/ui/Input';
import Select from '../../components/ui/Select';
import { Calendar, ChevronDown, Filter, ChevronLeft, ChevronRight, X, RefreshCw, Search, FileText, Download, ChevronUp, Upload } from 'lucide-react';
import UnifiedSearchBar from '../../components/trips/UnifiedSearchBar';
import { comprehensiveSearchTrips } from '../../utils/tripSearch';
import * as XLSX from 'xlsx';
import { toast } from 'react-toastify';
import { fixAllExistingMileage, fixMileageForSpecificVehicle } from '../../utils/fixExistingMileage';

interface TripSummaryMetrics {
  totalExpenses: number;
  avgDistance: number;
  tripCount: number;
  meanMileage: number;
  topDriver: {
    id: string;
    name: string;
    totalDistance: number;
    tripCount: number;
  } | null;
  topVehicle: {
    id: string;
    registrationNumber: string;
    tripCount: number;
  } | null;
}

const AdminTripsPage: React.FC = () => {
  const navigate = useNavigate();
  const [trips, setTrips] = useState<Trip[]>([]);
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [drivers, setDrivers] = useState<Driver[]>([]);
  const [warehouses, setWarehouses] = useState<Warehouse[]>([]);
  const [destinations, setDestinations] = useState<Destination[]>([]);
  const [showExportModal, setShowExportModal] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  const [loading, setLoading] = useState(true);
  const [summaryLoading, setSummaryLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [fixingMileage, setFixingMileage] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [searchResult, setSearchResult] = useState<any>(null);
  const importInputRef = useRef<HTMLInputElement | null>(null);

  const vehiclesMap = useMemo(() => new Map(vehicles.map(v => [v.id, v])), [vehicles]);
  const driversMap = useMemo(() => new Map(drivers.map(d => [d.id, d])), [drivers]);
  const destinationsMap = useMemo(() => new Map(destinations.map(d => [d.id, d.name])), [destinations]);
  
  // Date preset state
  const [datePreset, setDatePreset] = useState('last30');

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const tripsPerPage = 50; // 50 trips per page

  const [summaryMetrics, setSummaryMetrics] = useState<TripSummaryMetrics>({
    totalExpenses: 0,
    avgDistance: 0,
    tripCount: 0,
    meanMileage: 0,
    topDriver: null,
    topVehicle: null
  });

  // Filter state
  const [filters, setFilters] = useState({
    dateRange: {
      start: format(subDays(new Date(), 30), 'yyyy-MM-dd'),
      end: format(new Date(), 'yyyy-MM-dd')
    },
    vehicleId: '',
    driverId: '',
    warehouseId: '',
    tripType: '',
    search: ''
  });

  // Date preset options
  const datePresetOptions = [
    { value: 'today', label: 'Today' },
    { value: 'yesterday', label: 'Yesterday' },
    { value: 'thisWeek', label: 'This Week' },
    { value: 'lastWeek', label: 'Last Week' },
    { value: 'thisMonth', label: 'This Month' },
    { value: 'lastMonth', label: 'Last Month' },
    { value: 'thisYear', label: 'This Year' },
    { value: 'lastYear', label: 'Last Year' },
    { value: 'last7', label: 'Last 7 Days' },
    { value: 'last30', label: 'Last 30 Days' },
    { value: 'allTime', label: 'All Time' },
    { value: 'custom', label: 'Custom Range' }
  ];

  // Handle date preset changes
  useEffect(() => {
    const today = new Date();
    let start: Date, end: Date;
    
    switch (datePreset) {
      case 'today':
        start = end = today;
        break;
      case 'yesterday':
        start = end = subDays(today, 1);
        break;
      case 'thisWeek':
        start = startOfWeek(today);
        end = endOfWeek(today);
        break;
      case 'lastWeek':
        start = startOfWeek(subWeeks(today, 1));
        end = endOfWeek(subWeeks(today, 1));
        break;
      case 'thisMonth':
        start = startOfMonth(today);
        end = endOfMonth(today);
        break;
      case 'lastMonth':
        start = startOfMonth(subMonths(today, 1));
        end = endOfMonth(subMonths(today, 1));
        break;
      case 'thisYear':
        start = startOfYear(today);
        end = endOfYear(today);
        break;
      case 'lastYear':
        start = startOfYear(subYears(today, 1));
        end = endOfYear(subYears(today, 1));
        break;
      case 'last7':
        start = subDays(today, 6);
        end = today;
        break;
      case 'last30':
        start = subDays(today, 29);
        end = today;
        break;
      case 'allTime':
        start = new Date('2020-01-01');
        end = today;
        break;
      case 'custom':
        // Don't update dates for custom
        return;
      default:
        start = subDays(today, 29);
        end = today;
    }
    
    setFilters(prev => ({
      ...prev,
      dateRange: {
        start: format(start, 'yyyy-MM-dd'),
        end: format(end, 'yyyy-MM-dd')
      }
    }));
  }, [datePreset]);

  // Fetch data on load
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setSummaryLoading(true);

        const [tripsData, vehiclesData, driversData, warehousesData, destinationsData] = await Promise.all([
          getTrips(),
          getVehicles(),
          getDrivers(),
          getWarehouses(),
          getDestinations()
        ]);
        setTrips(tripsData);
        setVehicles(vehiclesData);
        setDrivers(driversData);
        setWarehouses(warehousesData);
        setDestinations(destinationsData);

        // Fetch summary metrics
        await fetchSummaryMetrics();
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
        setSummaryLoading(false);
      }
    };
    fetchData();
  }, []);

  // Fetch summary metrics when filters change
  useEffect(() => {
    fetchSummaryMetrics();
    // Reset to first page when filters change
    setCurrentPage(1);
  }, [filters]);

  // Enhanced refresh data function with force refresh capability
  const refreshData = async (forceRefresh = false) => {
    try {
      setRefreshing(true);
      
      if (forceRefresh) {
        console.log(' Using force refresh...');
        const result = await forceDataRefresh();
        
        if (result.success) {
          // Get fresh data after force refresh
          const [tripsData, vehiclesData, driversData, warehousesData, destinationsData] = await Promise.all([
            getTrips(),
            getVehicles(),
            getDrivers(),
            getWarehouses(),
            getDestinations()
          ]);
          setTrips(tripsData);
          setVehicles(vehiclesData);
          setDrivers(driversData);
          setWarehouses(warehousesData);
          setDestinations(destinationsData);
          
          await fetchSummaryMetrics();
          toast.success(`Force refresh completed: ${result.tripsCount} trips, ${result.vehiclesCount} vehicles, ${result.driversCount} drivers`);
        } else {
          toast.error(`Force refresh failed: ${result.error}`);
        }
      } else {
        // Normal refresh
        const [tripsData, vehiclesData, driversData, warehousesData, destinationsData] = await Promise.all([
          getTrips(),
          getVehicles(),
          getDrivers(),
          getWarehouses(),
          getDestinations()
        ]);
        setTrips(tripsData);
        setVehicles(vehiclesData);
        setDrivers(driversData);
        setWarehouses(warehousesData);
        setDestinations(destinationsData);
        
        await fetchSummaryMetrics();
        toast.success("Data refreshed successfully");
      }
    } catch (error) {
      console.error("Error refreshing data:", error);
      toast.error("Failed to refresh data");
    } finally {
      setRefreshing(false);
    }
  };

  // Test database connection
  const testConnection = async () => {
    const isConnected = await testDatabaseConnection();
    if (isConnected) {
      toast.success("Database connection is working");
    } else {
      toast.error("Database connection failed - check console for details");
    }
  };

  // Clear cache and force refresh
  const clearCacheAndForceRefresh = async () => {
    const result = await clearCacheAndRefresh();
    if (result.success) {
      // Update local state
      const [tripsData, vehiclesData, driversData, warehousesData, destinationsData] = await Promise.all([
        getTrips(),
        getVehicles(),
        getDrivers(),
        getWarehouses(),
        getDestinations()
      ]);
      setTrips(tripsData);
      setVehicles(vehiclesData);
      setDrivers(driversData);
      setWarehouses(warehousesData);
      setDestinations(destinationsData);
      
      await fetchSummaryMetrics();
    }
  };

  // Fix mileage calculations for all trips
  const handleFixMileage = async () => {
    const confirmed = window.confirm(
      'This will recalculate mileage for all existing trips using the tank-to-tank method. ' +
      'This may take a few minutes. Do you want to continue?'
    );
    
    if (!confirmed) return;

    setFixingMileage(true);
    try {
      const result = await fixAllExistingMileage();
      if (result.success) {
        toast.success(result.message);
        // Refresh the data to show updated mileage
        await refreshData();
      } else {
        toast.error(result.message);
      }
    } catch (error) {
      console.error('Error fixing mileage:', error);
      toast.error('Failed to fix mileage calculations');
    } finally {
      setFixingMileage(false);
    }
  };

  // Handle smart search with field selection
  const handleSmartSearch = async (searchTerm: string, searchField?: string) => {
    if (searchTerm.length < 2) {
      // Clear search if term is too short
      setSearchResult(null);
      return;
    }
    
    setIsSearching(true);
    try {
      const result = await comprehensiveSearchTrips(
        searchTerm,
        searchField,
        trips,
        vehicles,
        drivers,
        warehouses,
        { page: currentPage, limit: tripsPerPage }
      );
      
      setSearchResult(result);
      
      // Show appropriate message based on field
      if (result.totalCount === 0) {
        toast.info(`No trips found matching "${searchTerm}"${searchField ? ` in ${searchField}` : ''}`);
      } else {
        const fieldName = searchField ? ` in ${searchField}` : ' in all fields';
        toast.success(`Found ${result.totalCount} trips${fieldName}`);
      }
    } catch (error) {
      console.error('Smart search error:', error);
      toast.error('Search failed. Please try again.');
    } finally {
      setIsSearching(false);
    }
  };

  // Filter trips based on current filters
  const filteredTrips = useMemo(() => {
    // If we have search results, use them instead of basic filtering
    if (searchResult && searchResult.trips) {
      return searchResult.trips;
    }
    
    return trips.filter(trip => {
      // Search filter
      if (filters.search) {
        const vehicle = vehiclesMap.get(trip.vehicle_id);
        const driver = driversMap.get(trip.driver_id);
        
        const searchTerm = filters.search.toLowerCase();
        const searchFields = [
          trip.trip_serial_number,
          vehicle?.registration_number,
          driver?.name,
        ].map(field => field?.toLowerCase());
        
        if (!searchFields.some(field => field?.includes(searchTerm))) {
          return false;
        }
      }

      // Date range filter
      if (filters.dateRange.start) {
        const startDate = new Date(filters.dateRange.start);
        const tripStartDate = new Date(trip.trip_start_date);
        if (tripStartDate < startDate) {
          return false;
        }
      }
      
      if (filters.dateRange.end) {
        const endDate = new Date(filters.dateRange.end);
        endDate.setHours(23, 59, 59, 999); // End of day
        const tripEndDate = new Date(trip.trip_end_date);
        if (tripEndDate > endDate) {
          return false;
        }
      }

      // Vehicle filter
      if (filters.vehicleId && trip.vehicle_id !== filters.vehicleId) {
        return false;
      }

      // Driver filter
      if (filters.driverId && trip.driver_id !== filters.driverId) {
        return false;
      }

      // Warehouse filter
      if (filters.warehouseId && trip.warehouse_id !== filters.warehouseId) {
        return false;
      }

      // Trip type filter
      if (filters.tripType) {
        if (filters.tripType === 'two_way' && !(Array.isArray(trip.destinations) && trip.destinations.length > 1)) {
          return false;
        }
        if (filters.tripType === 'one_way' && !(Array.isArray(trip.destinations) && trip.destinations.length === 1)) {
          return false;
        }
      }

      return true;
    });
  }, [trips, vehiclesMap, driversMap, filters, searchResult]);

  // Calculate pagination
  const indexOfLastTrip = currentPage * tripsPerPage;
  const indexOfFirstTrip = indexOfLastTrip - tripsPerPage;
  const currentTrips = filteredTrips.slice(indexOfFirstTrip, indexOfLastTrip);
  const totalPages = Math.ceil(filteredTrips.length / tripsPerPage);
  
  // Go to a specific page
  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);

  // Previous page
  const goToPreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };
  
  // Next page
  const goToNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const fetchSummaryMetrics = async () => {
    try {
      setSummaryLoading(true);
      
      // Ensure dates are properly formatted
      const startDate = filters.dateRange.start ? 
        new Date(filters.dateRange.start).toISOString() : null;
      const endDate = filters.dateRange.end ? 
        new Date(filters.dateRange.end + 'T23:59:59').toISOString() : null;
      
      const { data, error } = await supabase.rpc('get_trip_summary_metrics', {
        start_date: startDate,
        end_date: endDate,
        p_vehicle_id: filters.vehicleId || null,
        p_driver_id: filters.driverId || null,
        p_warehouse_id: filters.warehouseId || null,
        p_trip_type: filters.tripType || null
      });
      
      if (error) {
        console.error("Error fetching summary metrics:", error);
        // Fallback to local calculation
        calculateMetricsLocally();
      } else if (data) {
        // Handle both array and object responses
        const metricsData = Array.isArray(data) ? data[0] : data;
        
        if (metricsData) {
          setSummaryMetrics({
            totalExpenses: metricsData.total_expenses || 0,
            avgDistance: metricsData.avg_distance || 0,
            tripCount: metricsData.trip_count || 0,
            meanMileage: metricsData.mean_mileage || 0,
            topDriver: metricsData.top_driver ? {
              id: metricsData.top_driver.id,
              name: metricsData.top_driver.name,
              totalDistance: metricsData.top_driver.totalDistance || 0,
              tripCount: metricsData.top_driver.tripCount || 0
            } : null,
            topVehicle: metricsData.top_vehicle ? {
              id: metricsData.top_vehicle.id,
              registrationNumber: metricsData.top_vehicle.registrationNumber,
              tripCount: metricsData.top_vehicle.tripCount || 0
            } : null
          });
        } else {
          calculateMetricsLocally();
        }
      }
    } catch (error) {
      console.error("Exception fetching summary metrics:", error);
      calculateMetricsLocally();
    } finally {
      setSummaryLoading(false);
    }
  };

  const calculateMetricsLocally = () => {
    const filtered = filteredTrips;
    
    // Calculate total expenses
    const totalExpenses = filtered.reduce((sum, trip) => {
      const fuel = trip.total_fuel_cost || 0;
      const road = trip.total_road_expenses || 0;
      const unloading = trip.unloading_expense || 0;
      const driver = trip.driver_expense || 0;
      const rto = trip.road_rto_expense || 0;
      const breakdown = trip.breakdown_expense || 0;
      const misc = trip.miscellaneous_expense || 0;
      return sum + fuel + road + unloading + driver + rto + breakdown + misc;
    }, 0);
    
    // Calculate average distance
    const totalDistance = filtered.reduce((sum, trip) => 
      sum + (trip.end_km - trip.start_km), 0
    );
    const avgDistance = filtered.length > 0 ? totalDistance / filtered.length : 0;
    
    // Calculate mean mileage (only from trips with valid mileage)
    const tripsWithMileage = filtered.filter(trip => 
      trip.calculated_kmpl && trip.calculated_kmpl > 0
    );
    const meanMileage = tripsWithMileage.length > 0 ?
      tripsWithMileage.reduce((sum, trip) => sum + (trip.calculated_kmpl || 0), 0) / tripsWithMileage.length : 0;
    
    // Calculate top driver
    const driverStats: Record<string, any> = {};
    filtered.forEach(trip => {
      if (trip.driver_id) {
        if (!driverStats[trip.driver_id]) {
          const driver = driversMap.get(trip.driver_id);
          driverStats[trip.driver_id] = {
            id: trip.driver_id,
            name: driver?.name || 'Unknown',
            tripCount: 0,
            totalDistance: 0
          };
        }
        driverStats[trip.driver_id].tripCount++;
        driverStats[trip.driver_id].totalDistance += (trip.end_km - trip.start_km);
      }
    });
    
    // Calculate top vehicle
    const vehicleStats: Record<string, any> = {};
    filtered.forEach(trip => {
      if (trip.vehicle_id) {
        if (!vehicleStats[trip.vehicle_id]) {
          const vehicle = vehiclesMap.get(trip.vehicle_id);
          vehicleStats[trip.vehicle_id] = {
            id: trip.vehicle_id,
            registrationNumber: vehicle?.registration_number || 'Unknown',
            tripCount: 0
          };
        }
        vehicleStats[trip.vehicle_id].tripCount++;
      }
    });
    
    const topDriver = Object.values(driverStats)
      .sort((a, b) => b.tripCount - a.tripCount)[0] || null;
      
    const topVehicle = Object.values(vehicleStats)
      .sort((a, b) => b.tripCount - a.tripCount)[0] || null;
    
    setSummaryMetrics({
      totalExpenses,
      avgDistance,
      tripCount: filtered.length,
      meanMileage,
      topDriver,
      topVehicle
    });
  };

  const handleUpdateTrip = async (tripId: string, updates: Partial<Trip>) => {
    const updatedTrip = await updateTrip(tripId, updates);
    if (updatedTrip) {
      setTrips(prev => 
        prev.map(trip => 
          trip.id === tripId ? updatedTrip : trip
        )
      );
      // Refresh summary metrics after update
      fetchSummaryMetrics();
    }
  };

  const handleDeleteTrip = async (tripId: string) => {
    try {
      // Delete the trip from Supabase
      const { error } = await supabase
        .from('trips')
        .delete()
        .eq('id', tripId);

      if (error) {
        throw new Error(`Failed to delete trip: ${error.message}`);
      }

      // If successful, update the trips list
      setTrips(prev => prev.filter(trip => trip.id !== tripId));
      toast.success('Trip deleted successfully');
      
      // Refresh summary metrics
      fetchSummaryMetrics();
      
      // If the last trip on the page was deleted, go to previous page
      if (currentTrips.length === 1 && currentPage > 1) {
        setCurrentPage(currentPage - 1);
      }
    } catch (error) {
      console.error("Error deleting trip:", error);
      toast.error(`Error deleting trip: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  };

  const handleFiltersChange = (newFilters: Partial<typeof filters>) => {
    if (newFilters.dateRange && datePreset !== 'custom') {
      // If manually changing dates, switch to custom preset
      setDatePreset('custom');
    }
    
    setFilters(prev => ({
      ...prev,
      ...newFilters
    }));
  };
  
  const clearFilters = () => {
    setFilters({
      dateRange: {
        start: format(subDays(new Date(), 30), 'yyyy-MM-dd'),
        end: format(new Date(), 'yyyy-MM-dd')
      },
      vehicleId: '',
      driverId: '',
      warehouseId: '',
      tripType: '',
      search: ''
    });
    setDatePreset('last30');
    setSearchResult(null); // Clear search results
  };

  const handleExport = async () => {
    try {
      // Use filtered trips from the current view
      const tripsToExport = filteredTrips.length > 0 ? filteredTrips : trips;
      
      if (tripsToExport.length === 0) {
        toast.error('No trips available to export');
        return;
      }
      
      // Get destinations for name mapping
      const destinations = await getDestinations();
      const destinationMap = new Map(destinations.map(d => [d.id, d.name]));
      
      console.log('Exporting trips:', tripsToExport.length);
      
      const exportData = tripsToExport.map(trip => {
        const vehicle = vehiclesMap.get(trip.vehicle_id);
        const driver = driversMap.get(trip.driver_id);
        const warehouse = warehouses.find(w => w.id === trip.warehouse_id);
        
        // Calculate distance
        const distance = (trip.end_km || 0) - (trip.start_km || 0);
        
        // Handle fuel calculations
        let fuelCost = trip.total_fuel_cost || 0;
        let fuelRatePerLiter = trip.fuel_rate_per_liter || 0;
        
        // Calculate fuel rate if we have cost and quantity
        if (fuelCost > 0 && trip.fuel_quantity > 0 && !fuelRatePerLiter) {
          fuelRatePerLiter = fuelCost / trip.fuel_quantity;
        }
        
        // If still no fuel rate but we have quantity, use default rate
        if (trip.fuel_quantity > 0 && !fuelRatePerLiter && fuelCost === 0) {
          fuelRatePerLiter = 93.33; // Average diesel price
          fuelCost = trip.fuel_quantity * fuelRatePerLiter;
        }
        
        // Calculate PROPER total expense INCLUDING all components
        const totalExpense = 
          (fuelCost || 0) +
          (trip.total_road_expenses || 0) +
          (trip.unloading_expense || 0) +
          (trip.driver_expense || 0) +
          (trip.road_rto_expense || 0) +
          (trip.breakdown_expense || 0) +
          (trip.miscellaneous_expense || 0);
        
        // Convert destination IDs to names
        let destinationNames = '';
        if (trip.destinations && Array.isArray(trip.destinations)) {
          destinationNames = trip.destinations
            .map(destId => {
              // Check if it's already a name or needs mapping
              if (typeof destId === 'string' && destId.includes('-')) {
                // It's a UUID, map it
                return destinationMap.get(destId) || 'Unknown';
              }
              return destId; // It's already a name
            })
            .join(', ');
        } else if (trip.destinations) {
          destinationNames = destinationMap.get(trip.destinations) || trip.destinations;
        }
        
        // Determine trip type correctly based on short_trip flag
        let tripType = 'One Way';
        if (trip.short_trip === true) {
          tripType = 'Return Trip';
        } else if (trip.return_trip === true) {
          tripType = 'Return Trip';
        } else if (Array.isArray(trip.destinations) && trip.destinations.length > 1) {
          tripType = 'Multi-Stop';
        }
        
        // Calculate mileage if not present
        let mileage = trip.calculated_kmpl || 0;
        if (!mileage && trip.fuel_quantity > 0) {
          mileage = distance / trip.fuel_quantity;
        }
        
        return {
          'TRIP ID': trip.trip_serial_number || '',
          'START DATE': trip.trip_start_date ? 
            new Date(trip.trip_start_date).toLocaleDateString('en-GB') : '',
          'END DATE': trip.trip_end_date ? 
            new Date(trip.trip_end_date).toLocaleDateString('en-GB') : '',
          'VEHICLE': vehicle?.registration_number || '',
          'DRIVER': driver?.name || '',
          'SOURCE WAREHOUSE': warehouse?.name || '',
          'DESTINATIONS': destinationNames || 'N/A',
          'START KM': trip.start_km || 0,
          'END KM': trip.end_km || 0,
          'DISTANCE (KM)': distance,
          'FUEL QUANTITY (L)': parseFloat(trip.fuel_quantity || 0).toFixed(2),
          'FUEL RATE (/L)': parseFloat(fuelRatePerLiter || 0).toFixed(2),
          'FUEL COST ()': parseFloat(fuelCost || 0).toFixed(2),
          'MILEAGE (km/L)': parseFloat(mileage || 0).toFixed(2),
          'ROAD EXPENSES ()': parseFloat(trip.total_road_expenses || 0).toFixed(2),
          'UNLOADING EXPENSE ()': parseFloat(trip.unloading_expense || 0).toFixed(2),
          'DRIVER EXPENSE ()': parseFloat(trip.driver_expense || 0).toFixed(2),
          'RTO EXPENSE ()': parseFloat(trip.road_rto_expense || 0).toFixed(2),
          'BREAKDOWN EXPENSE ()': parseFloat(trip.breakdown_expense || 0).toFixed(2),
          'MISC EXPENSE ()': parseFloat(trip.miscellaneous_expense || 0).toFixed(2),
          'TOTAL EXPENSE ()': parseFloat(totalExpense).toFixed(2),
          'TRIP TYPE': tripType,
          'BILLING TYPE': trip.billing_type || 'N/A',
          'INCOME ()': parseFloat(trip.income_amount || 0).toFixed(2),
          'NET PROFIT/LOSS ()': parseFloat((trip.income_amount || 0) - totalExpense).toFixed(2)
        };
      });
      
      // Create Excel workbook
      const worksheet = XLSX.utils.json_to_sheet(exportData);
      
      // Set column widths for better readability
      const colWidths = [
        { wch: 15 }, // Trip ID
        { wch: 12 }, // Start Date
        { wch: 12 }, // End Date
        { wch: 15 }, // Vehicle
        { wch: 25 }, // Driver
        { wch: 25 }, // Source Warehouse
        { wch: 35 }, // Destinations
        { wch: 10 }, // Start KM
        { wch: 10 }, // End KM
        { wch: 12 }, // Distance
        { wch: 14 }, // Fuel Quantity
        { wch: 12 }, // Fuel Rate
        { wch: 12 }, // Fuel Cost
        { wch: 12 }, // Mileage
        { wch: 14 }, // Road Expenses
        { wch: 16 }, // Unloading
        { wch: 14 }, // Driver Expense
        { wch: 12 }, // RTO
        { wch: 14 }, // Breakdown
        { wch: 12 }, // Misc
        { wch: 14 }, // Total Expense
        { wch: 12 }, // Trip Type
        { wch: 12 }, // Billing Type
        { wch: 12 }, // Income
        { wch: 16 }, // Net Profit/Loss
      ];
      worksheet['!cols'] = colWidths;
      
      const workbook = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(workbook, worksheet, 'Trips Report');
      
      const fileName = `trips-export-${format(new Date(), 'yyyy-MM-dd-HHmmss')}.xlsx`;
      XLSX.writeFile(workbook, fileName);
      
      toast.success(`Successfully exported ${exportData.length} trips!`);
      
    } catch (error) {
      console.error('Export failed:', error);
      toast.error('Failed to export trips. Please check console for details.');
    }
  };

  const handleImport = async (file: File) => {
    try {
      const data = await parseCSV(file);
      // Process imported data
      toast.info(`Imported ${data.length} trips. Processing...`);
      // You'd normally process these and add to the database
      // For now, we're just showing a success message
      toast.success(`Successfully processed ${data.length} trips`);
    } catch (error) {
      console.error('Error importing file:', error);
      toast.error('Failed to import trips');
    }
  };

  const handleImportInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      handleImport(file);
      event.target.value = '';
    }
  };

  const getActiveFiltersText = () => {
    const parts = [];
    
    if (filters.vehicleId) {
      const vehicle = vehiclesMap.get(filters.vehicleId);
      parts.push(`Vehicle: ${vehicle?.registration_number || 'Unknown'}`);
    } else {
      parts.push('Vehicle: All');
    }
    
    if (filters.driverId) {
      const driver = driversMap.get(filters.driverId);
      parts.push(`Driver: ${driver?.name || 'Unknown'}`);
    } else {
      parts.push('Driver: All');
    }
    
    if (filters.dateRange.start && filters.dateRange.end) {
      parts.push(`Dates: ${format(new Date(filters.dateRange.start), 'dd MMM yyyy')} - ${format(new Date(filters.dateRange.end), 'dd MMM yyyy')}`);
    }
    
    return parts.join(', ');
  };

  return (
    <Layout>
      {/* Page Header */}
      <div className="rounded-xl border bg-white dark:bg-white px-4 py-3 shadow-sm mb-6">
        <div className="flex items-center group">
          <FileText className="h-5 w-5 mr-2 text-gray-500 dark:text-gray-400 group-hover:text-primary-600 transition" />
          <h1 className="text-2xl font-semibold text-gray-900 dark:text-gray-100">Trip Management</h1>
        </div>
        <p className="text-sm text-gray-500 dark:text-gray-400 mt-1 ml-7">View and manage all trip records</p>
      </div>

      {/* Simplified Filters Section */}
      {!loading && (
        <div className="bg-white rounded-lg shadow-sm mb-6">
          <div className="px-4 py-3 border-b border-gray-100">
            <div className="flex justify-between items-center">
              <button
                onClick={() => setShowFilters(!showFilters)}
                className="flex items-center gap-2 text-gray-700 hover:text-blue-600 transition-colors font-medium"
              >
                <Filter className="h-5 w-5" />
                <span>Filters</span>
                {showFilters ? 
                  <ChevronUp className="h-4 w-4" /> : 
                  <ChevronDown className="h-4 w-4" />
                }
              </button>
              
              <div className="flex items-center gap-2">
                {/* Active filter count badge */}
                {(filters.search || filters.vehicleId || filters.driverId || filters.warehouseId || filters.tripType) && (
                  <span className="px-2 py-1 text-xs font-medium bg-blue-100 text-blue-700 rounded-full">
                    {[filters.search, filters.vehicleId, filters.driverId, filters.warehouseId, filters.tripType]
                      .filter(Boolean).length} active
                  </span>
                )}
                
                <button
                  onClick={clearFilters}
                  className="px-3 py-1.5 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors inline-flex items-center gap-1"
                >
                  <X className="h-4 w-4" />
                  Clear
                </button>
                
                <button
                  onClick={() => refreshData(false)}
                  disabled={refreshing}
                  className="px-3 py-1.5 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors inline-flex items-center gap-1 disabled:opacity-50"
                >
                  <RefreshCw className={`h-4 w-4 ${refreshing ? 'animate-spin' : ''}`} />
                  Refresh
                </button>
                
                <input
                  ref={importInputRef}
                  type="file"
                  accept=".csv,.xlsx"
                  onChange={handleImportInputChange}
                  className="hidden"
                  aria-label="Import trips file"
                />
                <button
                  onClick={() => importInputRef.current?.click()}
                  className="px-3 py-1.5 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors inline-flex items-center gap-1"
                >
                  <Upload className="h-4 w-4" />
                  Import
                </button>
                <button
                  onClick={() => handleExport()}
                  className="px-3 py-1.5 text-sm font-medium text-white bg-blue-600 border border-blue-600 rounded-lg hover:bg-blue-700 transition-colors inline-flex items-center gap-1"
                >
                  <Download className="h-4 w-4" />
                  Export
                </button>
              </div>
            </div>
          </div>
          
          {showFilters && (
            <div className="p-3">
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-6 gap-3">
                {/* Compact Search */}
                <div className="sm:col-span-2 lg:col-span-2 xl:col-span-2">
                  <label className="block text-xs font-medium text-gray-600 mb-1">
                    Search
                  </label>
                  <div className="relative">
                    <div className="flex items-center bg-white border border-gray-300 rounded-lg focus-within:border-blue-500 focus-within:ring-1 focus-within:ring-blue-500 transition-all">
                      {/* Search Icon */}
                      <div className="pl-3 pr-2">
                        {isSearching ? (
                          <div className="h-4 w-4 border-2 border-gray-300 border-t-blue-500 rounded-full animate-spin"></div>
                        ) : (
                          <Search className="h-4 w-4 text-gray-400" />
                        )}
                      </div>
                      
                      {/* Input Field */}
                      <input
                        type="text"
                        value={filters.search}
                        onChange={(e) => handleFiltersChange({ search: e.target.value })}
                        onKeyDown={(e) => {
                          if (e.key === 'Enter' && filters.search.length >= 2) {
                            handleSmartSearch(filters.search);
                          }
                        }}
                        placeholder="Search trips, vehicles, drivers..."
                        className="flex-1 py-2 px-2 text-sm bg-transparent outline-none placeholder-gray-400"
                        autoComplete="off"
                      />
                      
                      {/* Quick Search Button */}
                      {filters.search.length >= 2 && (
                        <button
                          onClick={() => handleSmartSearch(filters.search)}
                          className="px-3 py-1 mr-2 text-xs font-medium text-blue-600 bg-blue-50 border border-blue-200 rounded-md hover:bg-blue-100 transition-colors"
                        >
                          Search
                        </button>
                      )}
                      
                      {/* Clear Button */}
                      {filters.search && (
                        <button
                          onClick={() => {
                            handleFiltersChange({ search: '' });
                            setSearchResult(null);
                          }}
                          className="p-1 mr-2 text-gray-400 hover:text-gray-600 transition-colors"
                        >
                          <X className="h-4 w-4" />
                        </button>
                      )}
                    </div>
                    
                    {/* Search Results Indicator */}
                    {searchResult && (
                      <div className="absolute top-full left-0 right-0 mt-1 px-3 py-1 bg-blue-50 border border-blue-200 rounded-md text-xs text-blue-700">
                        Found {searchResult.totalCount} results
                        {searchResult.matchedFields && searchResult.matchedFields.length > 0 && (
                          <span className="ml-2">in: {searchResult.matchedFields.join(', ')}</span>
                        )}
                      </div>
                    )}
                  </div>
                </div>
                
                {/* Date Range */}
                <div>
                  <label className="block text-xs font-medium text-gray-600 mb-1">
                    From Date
                  </label>
                  <input
                    type="date"
                    value={filters.dateRange.start}
                    onChange={(e) => handleFiltersChange({ 
                      dateRange: { ...filters.dateRange, start: e.target.value }
                    })}
                    className="w-full px-2 py-1.5 text-sm border border-gray-300 rounded-md focus:ring-1 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                
                <div>
                  <label className="block text-xs font-medium text-gray-600 mb-1">
                    To Date
                  </label>
                  <input
                    type="date"
                    value={filters.dateRange.end}
                    onChange={(e) => handleFiltersChange({ 
                      dateRange: { ...filters.dateRange, end: e.target.value }
                    })}
                    className="w-full px-2 py-1.5 text-sm border border-gray-300 rounded-md focus:ring-1 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                
                {/* Vehicle Select */}
                <div>
                  <label className="block text-xs font-medium text-gray-600 mb-1">
                    Vehicle
                  </label>
                  <select
                    value={filters.vehicleId}
                    onChange={(e) => handleFiltersChange({ vehicleId: e.target.value })}
                    className="w-full px-2 py-1.5 text-sm border border-gray-300 rounded-md focus:ring-1 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">All Vehicles</option>
                    {vehicles.map(vehicle => (
                      <option key={vehicle.id} value={vehicle.id}>
                        {vehicle.registration_number}
                      </option>
                    ))}
                  </select>
                </div>
                
                {/* Driver Select */}
                <div>
                  <label className="block text-xs font-medium text-gray-600 mb-1">
                    Driver
                  </label>
                  <select
                    value={filters.driverId}
                    onChange={(e) => handleFiltersChange({ driverId: e.target.value })}
                    className="w-full px-2 py-1.5 text-sm border border-gray-300 rounded-md focus:ring-1 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">All Drivers</option>
                    {drivers.map(driver => (
                      <option key={driver.id} value={driver.id}>
                        {driver.name}
                      </option>
                    ))}
                  </select>
                </div>
                
                {/* Warehouse Select */}
                <div>
                  <label className="block text-xs font-medium text-gray-600 mb-1">
                    Warehouse
                  </label>
                  <select
                    value={filters.warehouseId}
                    onChange={(e) => handleFiltersChange({ warehouseId: e.target.value })}
                    className="w-full px-2 py-1.5 text-sm border border-gray-300 rounded-md focus:ring-1 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">All Warehouses</option>
                    {warehouses.map(warehouse => (
                      <option key={warehouse.id} value={warehouse.id}>
                        {warehouse.name}
                      </option>
                    ))}
                  </select>
                </div>
                
                {/* Trip Type Select */}
                <div>
                  <label className="block text-xs font-medium text-gray-600 mb-1">
                    Trip Type
                  </label>
                  <select
                    value={filters.tripType}
                    onChange={(e) => handleFiltersChange({ tripType: e.target.value })}
                    className="w-full px-2 py-1.5 text-sm border border-gray-300 rounded-md focus:ring-1 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">All Types</option>
                    <option value="one_way">One Way</option>
                    <option value="two_way">Two Way</option>
                  </select>
                </div>
              </div>
              
              {/* Active filters summary */}
              {(filters.search || filters.vehicleId || filters.driverId || filters.warehouseId || filters.tripType) && (
                <div className="mt-4 flex items-center gap-2 text-sm">
                  <span className="text-gray-500">Active filters:</span>
                  <div className="flex flex-wrap gap-2">
                    {filters.search && (
                      <span className="px-2 py-1 bg-gray-100 text-gray-700 rounded-full text-xs">
                        Search: {filters.search}
                      </span>
                    )}
                    {filters.vehicleId && (
                      <span className="px-2 py-1 bg-gray-100 text-gray-700 rounded-full text-xs">
                        Vehicle: {vehicles.find(v => v.id === filters.vehicleId)?.registration_number}
                      </span>
                    )}
                    {filters.driverId && (
                      <span className="px-2 py-1 bg-gray-100 text-gray-700 rounded-full text-xs">
                        Driver: {drivers.find(d => d.id === filters.driverId)?.name}
                      </span>
                    )}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* Summary Metrics */}
      <TripsSummary
        trips={trips}
        vehicles={vehicles}
        drivers={drivers}
        loading={summaryLoading}
        metrics={summaryMetrics}
      />

      {/* Trip Table */}
      <TripsTable
        trips={currentTrips}
        vehicles={vehicles}
        drivers={drivers}
        onUpdateTrip={handleUpdateTrip}
        onDeleteTrip={handleDeleteTrip}
      />

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="flex items-center justify-between border-t border-gray-200 bg-white px-4 py-3 sm:px-6 rounded-lg shadow-sm mt-4">
          <div className="hidden sm:flex sm:flex-1 sm:items-center sm:justify-between">
            <div>
              <p className="text-sm text-gray-700">
                Showing <span className="font-medium">{indexOfFirstTrip + 1}</span> to{' '}
                <span className="font-medium">
                  {Math.min(indexOfLastTrip, filteredTrips.length)}
                </span>{' '}
                of <span className="font-medium">{filteredTrips.length}</span> results
              </p>
            </div>
            <div>
              <nav className="isolate inline-flex -space-x-px rounded-md shadow-sm" aria-label="Pagination">
                <button
                  onClick={goToPreviousPage}
                  className={`relative inline-flex items-center rounded-l-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 ${
                    currentPage === 1 ? 'cursor-not-allowed opacity-50' : ''
                  }`}
                  disabled={currentPage === 1}
                >
                  <ChevronLeft className="h-5 w-5" />
                </button>
                
                {/* Page numbers */}
                {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                  // Simple pagination logic that shows 5 pages at most
                  let pageNum = i + 1;
                  if (totalPages > 5) {
                    if (currentPage <= 3) {
                      pageNum = i + 1;
                    } else if (currentPage >= totalPages - 2) {
                      pageNum = totalPages - 4 + i;
                    } else {
                      pageNum = currentPage - 2 + i;
                    }
                  }
                  
                  return (
                    <button
                      key={pageNum}
                      onClick={() => paginate(pageNum)}
                      className={`relative inline-flex items-center px-4 py-2 text-sm font-semibold ${
                        currentPage === pageNum
                          ? 'z-10 bg-primary-600 text-white focus:z-20 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-primary-600'
                          : 'text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0'
                      }`}
                    >
                      {pageNum}
                    </button>
                  );
                })}
                
                <button
                  onClick={goToNextPage}
                  className={`relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 ${
                    currentPage === totalPages ? 'cursor-not-allowed opacity-50' : ''
                  }`}
                  disabled={currentPage === totalPages}
                >
                  <ChevronRight className="h-5 w-5" />
                </button>
              </nav>
            </div>
          </div>
        </div>
      )}

      {showExportModal && (
        <ExportOptionsModal
          onExport={handleExport}
          onClose={() => setShowExportModal(false)}
          vehicles={vehicles}
          drivers={drivers}
          warehouses={warehouses}
        />
      )}
    </Layout>
  );
};

export default AdminTripsPage;

import React, { useState, useEffect, useRef } from 'react';
import { Building2, Upload, Save, Loader2, ArrowLeft, CheckCircle, AlertCircle, Trash2 } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../../utils/supabaseClient';

interface CompanyData {
  id?: string;
  name: string;
  tagline?: string;
  logo_url?: string;
  contact_email: string;
  contact_phone: string;
  address?: string;
  city?: string;
  state?: string;
  pincode?: string;
  gst_number?: string;
  pan_number?: string;
  bank_name?: string;
  bank_account_number?: string;
  ifsc_code?: string;
}

const indianStates = [
  'Andhra Pradesh', 'Arunachal Pradesh', 'Assam', 'Bihar', 'Chhattisgarh',
  'Goa', 'Gujarat', 'Haryana', 'Himachal Pradesh', 'Jharkhand', 'Karnataka',
  'Kerala', 'Madhya Pradesh', 'Maharashtra', 'Manipur', 'Meghalaya', 'Mizoram',
  'Nagaland', 'Odisha', 'Punjab', 'Rajasthan', 'Sikkim', 'Tamil Nadu',
  'Telangana', 'Tripura', 'Uttar Pradesh', 'Uttarakhand', 'West Bengal'
];

const CompanySettings: React.FC = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef<HTMLInputElement>(null);
  
  // State management
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [successMessage, setSuccessMessage] = useState('');
  
  // File handling
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string>('');
  const [existingLogoUrl, setExistingLogoUrl] = useState<string>('');
  const [oldLogoToDelete, setOldLogoToDelete] = useState<string>('');
  
  // Company data
  const [company, setCompany] = useState<CompanyData>({
    name: '',
    contact_email: '',
    contact_phone: '',
  });

  // Validation functions
  const validateGST = (gst: string): boolean => {
    const gstRegex = /^[0-9]{2}[A-Z]{5}[0-9]{4}[A-Z]{1}[0-9A-Z]{1}[Z]{1}[0-9A-Z]{1}$/;
    return !gst || gstRegex.test(gst);
  };

  const validatePAN = (pan: string): boolean => {
    const panRegex = /^[A-Z]{5}[0-9]{4}[A-Z]{1}$/;
    return !pan || panRegex.test(pan);
  };

  const validatePhone = (phone: string): boolean => {
    const phoneRegex = /^[+91]?[6-9][0-9]{9}$/;
    return !phone || phoneRegex.test(phone.replace(/[\s-]/g, ''));
  };

  const validateEmail = (email: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return !email || emailRegex.test(email);
  };

  const validatePincode = (pincode: string): boolean => {
    const pincodeRegex = /^[0-9]{6}$/;
    return !pincode || pincodeRegex.test(pincode);
  };

  const validateIFSC = (ifsc: string): boolean => {
    const ifscRegex = /^[A-Z]{4}[0-9]{7}$/;
    return !ifsc || ifscRegex.test(ifsc);
  };

  // Load existing company data
  useEffect(() => {
    loadCompanyData();
  }, []);

  const loadCompanyData = async () => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        navigate('/login');
        return;
      }

      if (import.meta.env.MODE === 'development') {
        console.log('Loading company data for user:', user.id);
      }

      const { data, error } = await supabase
        .from('organizations')
        .select('*')
        .eq('owner_id', user.id)
        .order('created_at', { ascending: false })
        .limit(1);

      if (import.meta.env.MODE === 'development') {
        console.log('Company data query result:', { data, error });
      }

      if (error) {
        console.error('Error fetching organization:', error);
        return;
      }

      // Handle the result as an array and take the first element
      const companyData = Array.isArray(data) && data.length > 0 ? data[0] : null;

      if (companyData) {
        if (import.meta.env.MODE === 'development') {
          console.log('Setting company data:', companyData);
        }
        setCompany(companyData);
        setIsEditMode(true);
        setIsEditing(false); // Start in view mode when data exists
        if (companyData.logo_url) {
          setExistingLogoUrl(companyData.logo_url);
          setPreviewUrl(companyData.logo_url);
        }
      } else {
        if (import.meta.env.MODE === 'development') {
          console.log('No company data found, setting up new company');
        }
        setIsEditMode(false);
        setIsEditing(true); // Start in edit mode for new company
      }
    } catch (error) {
      console.error('Error loading company data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle file selection (preview only)
  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    // Validate file
    const validTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/svg+xml'];
    if (!validTypes.includes(file.type)) {
      setErrors({ ...errors, logo: 'Please select a valid image file (JPG, PNG, GIF, SVG)' });
      return;
    }

    if (file.size > 2 * 1024 * 1024) {
      setErrors({ ...errors, logo: 'Image size must be less than 2MB' });
      return;
    }

    // Clear logo errors
    const newErrors = { ...errors };
    delete newErrors.logo;
    setErrors(newErrors);

    // Set for preview
    setSelectedFile(file);
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreviewUrl(reader.result as string);
    };
    reader.readAsDataURL(file);

    // Mark old logo for deletion if exists
    if (existingLogoUrl) {
      setOldLogoToDelete(existingLogoUrl);
    }

    // Automatically enter edit mode when a new logo is selected
    if (isEditMode && !isEditing) {
      setIsEditing(true);
    }
  };

  // Remove selected logo
  const removeLogo = () => {
    setSelectedFile(null);
    setPreviewUrl(existingLogoUrl || '');
    setOldLogoToDelete('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Validate all fields
  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    // Required fields
    if (!company.name?.trim()) {
      newErrors.name = 'Company name is required';
    }
    if (company.name && (company.name.length < 3 || company.name.length > 100)) {
      newErrors.name = 'Company name must be between 3 and 100 characters';
    }

    // Email validation
    if (!validateEmail(company.contact_email)) {
      newErrors.email = 'Invalid email format';
    }

    // Phone validation
    if (!validatePhone(company.contact_phone)) {
      newErrors.phone = 'Invalid phone number (10 digits, starting with 6-9)';
    }

    // GST validation
    if (company.gst_number && !validateGST(company.gst_number)) {
      newErrors.gst = 'Invalid GST format (e.g., 22AAAAA0000A1Z5)';
    }

    // PAN validation
    if (company.pan_number && !validatePAN(company.pan_number)) {
      newErrors.pan = 'Invalid PAN format (e.g., ABCDE1234F)';
    }

    // Pincode validation
    if (company.pincode && !validatePincode(company.pincode)) {
      newErrors.pincode = 'Invalid PIN code (6 digits)';
    }

    // IFSC validation
    if (company.ifsc_code && !validateIFSC(company.ifsc_code)) {
      newErrors.ifsc = 'Invalid IFSC code format';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Upload logo to storage
  const uploadLogo = async (file: File, userId: string): Promise<string | null> => {
    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`;
      const filePath = `${userId}/${fileName}`;

      const { error } = await supabase.storage
        .from('company-logos')
        .upload(filePath, file, {
          cacheControl: '3600',
          upsert: false
        });

      if (error) throw error;

      const { data: { publicUrl } } = supabase.storage
        .from('company-logos')
        .getPublicUrl(filePath);

      return publicUrl;
    } catch (error) {
      console.error('Error uploading logo:', error);
      throw error;
    }
  };

  // Delete old logo from storage
  const deleteOldLogo = async (logoUrl: string, userId: string) => {
    try {
      // Extract file path from URL
      const urlParts = logoUrl.split('/');
      const fileName = urlParts[urlParts.length - 1];
      const filePath = `${userId}/${fileName}`;

      await supabase.storage
        .from('company-logos')
        .remove([filePath]);
    } catch (error) {
      console.error('Error deleting old logo:', error);
      // Non-critical error, continue
    }
  };

  // Save company data
  const handleSave = async () => {
    if (!validateForm()) {
      return;
    }

    setSaving(true);
    setSuccessMessage('');

    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        throw new Error('User not authenticated');
      }

      let finalLogoUrl = existingLogoUrl;

      // Upload new logo if selected
      if (selectedFile) {
        finalLogoUrl = await uploadLogo(selectedFile, user.id) || '';
        
        // Delete old logo after successful upload
        if (oldLogoToDelete) {
          await deleteOldLogo(oldLogoToDelete, user.id);
        }
      }

      // Prepare data
      const companyData = {
        ...company,
        logo_url: finalLogoUrl,
        owner_id: user.id,
        updated_at: new Date().toISOString(),
        logo_updated_at: selectedFile ? new Date().toISOString() : company.logo_url
      };

      // Save to database
      if (isEditMode && company.id) {
        // Update existing record
        const { error } = await supabase
          .from('organizations')
          .update(companyData)
          .eq('id', company.id);

        if (error) throw error;
      } else {
        // Insert new record
        const { data: newData, error } = await supabase
          .from('organizations')
          .insert([companyData])
          .select()
          .single();

        if (error) throw error;
        
        // Update local state with the new ID
        if (newData) {
          setCompany({ ...company, id: newData.id });
          
          //  CRITICAL: Create organization_users record so user can access their organization
          const { error: orgUserError } = await supabase
            .from('organization_users')
            .insert([{
              user_id: user.id,
              organization_id: newData.id,
              role: 'owner'
            }]);

          if (orgUserError) {
            console.error('Error creating organization_users record:', orgUserError);
            // Don't throw error here as the organization was created successfully
          }
        }
        
        setIsEditMode(true);
      }

      // Update local state
      setExistingLogoUrl(finalLogoUrl || '');
      setSelectedFile(null);
      setOldLogoToDelete('');
      setIsEditing(false); // Exit edit mode after successful save
      setSuccessMessage('Company information saved successfully!');

      // Clear success message after 3 seconds
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (error: any) {
      console.error('Error saving company data:', error);
      setErrors({ save: error.message || 'Failed to save company information' });
    } finally {
      setSaving(false);
    }
  };

  // Handle input changes
  const handleInputChange = (field: keyof CompanyData, value: string) => {
    setCompany({ ...company, [field]: value });
    
    // Clear field error on change
    if (errors[field]) {
      const newErrors = { ...errors };
      delete newErrors[field];
      setErrors(newErrors);
    }
  };

  // Handle edit mode toggle
  const handleEditToggle = () => {
    setIsEditing(!isEditing);
    if (!isEditing) {
      // Reset any unsaved changes
      setSelectedFile(null);
      setPreviewUrl(existingLogoUrl || '');
      setOldLogoToDelete('');
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Loader2 className="h-8 w-8 animate-spin text-primary-600" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-6">
      <div className="max-w-4xl mx-auto px-4">
        {/* Header */}
        <div className="mb-6">
          <button
            onClick={() => navigate('/admin')}
            className="flex items-center text-gray-600 hover:text-gray-900 mb-4"
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            <span className="font-sans">Back to Admin</span>
          </button>
          
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Building2 className="h-8 w-8 text-primary-600" />
              <div>
                <h1 className="text-2xl font-display font-bold tracking-tight-plus text-gray-900">
                  {isEditMode ? (isEditing ? 'Edit Company Profile' : 'Company Profile') : 'Setup Company Profile'}
                </h1>
                <p className="text-sm font-sans text-gray-500">
                  {isEditMode ? 'View and manage your organization profile' : 'Set up your organization profile and branding'}
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              {isEditMode && !isEditing && (
                <span className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm font-sans">
                  Profile Active
                </span>
              )}
              {isEditMode && (
                <button
                  onClick={handleEditToggle}
                  className={`px-4 py-2 rounded-lg font-sans font-medium transition-colors ${
                    isEditing 
                      ? 'bg-gray-100 text-gray-700 hover:bg-gray-200' 
                      : 'bg-primary-600 text-white hover:bg-primary-700'
                  }`}
                >
                  {isEditing ? 'Cancel' : 'Edit Profile'}
                </button>
              )}
            </div>
          </div>
        </div>

        {/* Success Message */}
        {successMessage && (
          <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-lg flex items-center">
            <CheckCircle className="h-5 w-5 text-green-600 mr-2" />
            <span className="font-sans text-green-800">{successMessage}</span>
          </div>
        )}

        {/* Error Message */}
        {errors.save && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg flex items-center">
            <AlertCircle className="h-5 w-5 text-red-600 mr-2" />
            <span className="font-sans text-red-800">{errors.save}</span>
          </div>
        )}

        {/* Main Form */}
        <div className="bg-white rounded-lg shadow-sm">
          <div className="p-6 space-y-6">
            {/* Company Information Section */}
            <div>
              <h2 className="text-lg font-display font-semibold tracking-tight-plus mb-4">Company Information</h2>
              
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-sans font-medium text-gray-700 mb-1">
                    Company Name *
                  </label>
                  <input
                    type="text"
                    value={company.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    readOnly={!isEditing && isEditMode}
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                      errors.name ? 'border-red-500' : 'border-gray-300'
                    } ${!isEditing && isEditMode ? 'bg-gray-50 cursor-not-allowed' : ''}`}
                    placeholder="Enter company name"
                    maxLength={100}
                  />
                  {errors.name && (
                    <p className="font-sans text-red-500 text-xs mt-1">{errors.name}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-sans font-medium text-gray-700 mb-1">
                    Tagline
                  </label>
                  <input
                    type="text"
                    value={company.tagline || ''}
                    onChange={(e) => handleInputChange('tagline', e.target.value)}
                    readOnly={!isEditing && isEditMode}
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                      errors.tagline ? 'border-red-500' : 'border-gray-300'
                    } ${!isEditing && isEditMode ? 'bg-gray-50 cursor-not-allowed' : ''}`}
                    placeholder="Your company slogan"
                    maxLength={100}
                  />
                </div>
              </div>
            </div>

            {/* Logo Upload Section */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Company Logo
              </label>
              <div className="flex items-start space-x-6">
                <div className="flex-shrink-0">
                  {previewUrl ? (
                    <img
                      src={previewUrl}
                      alt="Company Logo"
                      className="h-24 w-24 object-contain border-2 border-gray-200 rounded-lg"
                    />
                  ) : (
                    <div className="h-24 w-24 border-2 border-dashed border-gray-300 rounded-lg flex items-center justify-center">
                      <Building2 className="h-8 w-8 text-gray-400" />
                    </div>
                  )}
                </div>
                <div className="flex-grow">
                  <div className="flex space-x-2">
                    <label className="cursor-pointer">
                      <div className="px-4 py-2 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 flex items-center">
                        <Upload className="h-4 w-4 mr-2" />
                        {previewUrl && selectedFile ? 'Change Logo' : 'Upload Logo'}
                      </div>
                      <input
                        ref={fileInputRef}
                        type="file"
                        accept="image/*"
                        onChange={handleFileSelect}
                        className="hidden"
                      />
                    </label>
                    {selectedFile && (
                      <button
                        type="button"
                        onClick={removeLogo}
                        className="px-4 py-2 border border-red-300 text-red-600 rounded-lg hover:bg-red-50 flex items-center"
                      >
                        <Trash2 className="h-4 w-4 mr-2" />
                        Remove
                      </button>
                    )}
                  </div>
                  <p className="text-xs text-gray-500 mt-2">
                    Maximum file size: 2MB. Supported formats: JPG, PNG, GIF, SVG
                  </p>
                  {selectedFile && (
                    <p className="text-xs text-blue-600 mt-1">
                      New logo selected. Will be uploaded when you save.
                    </p>
                  )}
                  {errors.logo && (
                    <p className="text-red-500 text-xs mt-1">{errors.logo}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Contact Information */}
            <div>
              <h2 className="text-lg font-semibold mb-4">Contact Information</h2>
              
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Contact Email
                  </label>
                  <input
                    type="email"
                    value={company.contact_email}
                    onChange={(e) => handleInputChange('contact_email', e.target.value)}
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                      errors.email ? 'border-red-500' : 'border-gray-300'
                    }`}
                    placeholder="contact@company.com"
                  />
                  {errors.email && (
                    <p className="text-red-500 text-xs mt-1">{errors.email}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Contact Phone
                  </label>
                  <input
                    type="tel"
                    value={company.contact_phone}
                    onChange={(e) => handleInputChange('contact_phone', e.target.value)}
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                      errors.phone ? 'border-red-500' : 'border-gray-300'
                    }`}
                    placeholder="+91 98765 43210"
                  />
                  {errors.phone && (
                    <p className="text-red-500 text-xs mt-1">{errors.phone}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Address Information */}
            <div>
              <h2 className="text-lg font-semibold mb-4">Address Information</h2>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Address
                  </label>
                  <textarea
                    value={company.address || ''}
                    onChange={(e) => handleInputChange('address', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    rows={2}
                    placeholder="Enter complete address"
                  />
                </div>

                <div className="grid md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      City
                    </label>
                    <input
                      type="text"
                      value={company.city || ''}
                      onChange={(e) => handleInputChange('city', e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                      placeholder="Enter city"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      State
                    </label>
                    <select
                      value={company.state || ''}
                      onChange={(e) => handleInputChange('state', e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    >
                      <option value="">Select State</option>
                      {indianStates.map(state => (
                        <option key={state} value={state}>{state}</option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      PIN Code
                    </label>
                    <input
                      type="text"
                      value={company.pincode || ''}
                      onChange={(e) => {
                        const value = e.target.value.replace(/\D/g, '');
                        if (value.length <= 6) {
                          handleInputChange('pincode', value);
                        }
                      }}
                      className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                        errors.pincode ? 'border-red-500' : 'border-gray-300'
                      }`}
                      placeholder="123456"
                      maxLength={6}
                    />
                    {errors.pincode && (
                      <p className="text-red-500 text-xs mt-1">{errors.pincode}</p>
                    )}
                  </div>
                </div>
              </div>
            </div>

            {/* Tax Information */}
            <div>
              <h2 className="text-lg font-semibold mb-4">Tax Information</h2>
              
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    GST Number
                  </label>
                  <input
                    type="text"
                    value={company.gst_number || ''}
                    onChange={(e) => handleInputChange('gst_number', e.target.value.toUpperCase())}
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                      errors.gst ? 'border-red-500' : 'border-gray-300'
                    }`}
                    placeholder="22AAAAA0000A1Z5"
                    maxLength={15}
                  />
                  {errors.gst && (
                    <p className="text-red-500 text-xs mt-1">{errors.gst}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    PAN Number
                  </label>
                  <input
                    type="text"
                    value={company.pan_number || ''}
                    onChange={(e) => handleInputChange('pan_number', e.target.value.toUpperCase())}
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                      errors.pan ? 'border-red-500' : 'border-gray-300'
                    }`}
                    placeholder="ABCDE1234F"
                    maxLength={10}
                  />
                  {errors.pan && (
                    <p className="text-red-500 text-xs mt-1">{errors.pan}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Banking Information */}
            <div>
              <h2 className="text-lg font-semibold mb-4">Banking Information</h2>
              
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Bank Name
                  </label>
                  <input
                    type="text"
                    value={company.bank_name || ''}
                    onChange={(e) => handleInputChange('bank_name', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    placeholder="State Bank of India"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Account Number
                  </label>
                  <input
                    type="text"
                    value={company.bank_account_number || ''}
                    onChange={(e) => handleInputChange('bank_account_number', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
                    placeholder="Enter account number"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    IFSC Code
                  </label>
                  <input
                    type="text"
                    value={company.ifsc_code || ''}
                    onChange={(e) => handleInputChange('ifsc_code', e.target.value.toUpperCase())}
                    className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500 ${
                      errors.ifsc ? 'border-red-500' : 'border-gray-300'
                    }`}
                    placeholder="SBIN0001234"
                    maxLength={11}
                  />
                  {errors.ifsc && (
                    <p className="text-red-500 text-xs mt-1">{errors.ifsc}</p>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Action Buttons - Show when editing or creating new */}
          {(isEditing || !isEditMode) && (
            <div className="px-6 py-4 bg-gray-50 border-t flex justify-between items-center rounded-b-lg">
              <button
                type="button"
                onClick={() => {
                  if (isEditing) {
                    handleEditToggle(); // Cancel editing
                  } else {
                    navigate('/admin'); // Cancel creation
                  }
                }}
                className="px-4 py-2 text-gray-700 hover:text-gray-900"
              >
                Cancel
              </button>
              <button
                onClick={handleSave}
                disabled={saving}
                className="px-6 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              >
                {saving ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    {isEditMode ? 'Update Company Profile' : 'Create Company Profile'}
                  </>
                )}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CompanySettings;

import React, { useState, useEffect } from 'react';
import {
  BarChart,
  Bar,
  LineChart,
  Line,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';
import {
  Calendar,
  TrendingUp,
  Users,
  Truck,
  DollarSign,
  Package,
  AlertCircle,
  Download,
  Filter,
  RefreshCw,
  FileText,
  Fuel,
  MapPin,
  Clock,
  Wrench,
  BarChart3,
  PieChart as PieChartIcon,
  FileSpreadsheet,
  Eye,
  ChevronDown,
  ChevronUp,
  Activity
} from 'lucide-react';
import { supabase } from '../../utils/supabaseClient';
import { format, startOfDay, endOfDay, startOfWeek, endOfWeek, startOfMonth, endOfMonth, subDays, subMonths } from 'date-fns';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import Layout from '../../components/layout/Layout';

// Extend jsPDF type for autoTable
declare module 'jspdf' {
  interface jsPDF {
    autoTable: (options: any) => jsPDF;
  }
}

interface ReportMetrics {
  totalRevenue: number;
  totalTrips: number;
  activeVehicles: number;
  activeDrivers: number;
  avgTripDistance: number;
  avgFuelEfficiency: number;
  maintenanceCosts: number;
  pendingMaintenance: number;
}

const CompleteFixedReportingDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'dashboard' | 'reports'>('dashboard');
  const [metrics, setMetrics] = useState<ReportMetrics>({
    totalRevenue: 0,
    totalTrips: 0,
    activeVehicles: 0,
    activeDrivers: 0,
    avgTripDistance: 0,
    avgFuelEfficiency: 0,
    maintenanceCosts: 0,
    pendingMaintenance: 0
  });
  const [chartData, setChartData] = useState<any>({
    tripTrends: [],
    vehicleUtilization: [],
    driverPerformance: [],
    expenseBreakdown: []
  });
  const [dateRange, setDateRange] = useState({
    startDate: startOfMonth(new Date()),
    endDate: endOfMonth(new Date())
  });
  const [selectedDateRange, setSelectedDateRange] = useState('thisMonth');
  const [customStartDate, setCustomStartDate] = useState('');
  const [customEndDate, setCustomEndDate] = useState('');
  const [loading, setLoading] = useState(false);
  const [generatingReport, setGeneratingReport] = useState<string | null>(null);
  const [showAllReports, setShowAllReports] = useState(false);

  // Initialize date range properly
  useEffect(() => {
    updateDateRange(selectedDateRange);
  }, []);

  // Fetch data when date range changes
  useEffect(() => {
    if (activeTab === 'dashboard') {
      fetchDashboardData();
    }
  }, [dateRange, activeTab]);

  const updateDateRange = (rangeType: string) => {
    const now = new Date();
    let start: Date, end: Date;

    switch (rangeType) {
      case 'today':
        start = startOfDay(now);
        end = endOfDay(now);
        break;
      case 'yesterday':
        start = startOfDay(subDays(now, 1));
        end = endOfDay(subDays(now, 1));
        break;
      case 'thisWeek':
        start = startOfWeek(now, { weekStartsOn: 1 });
        end = endOfWeek(now, { weekStartsOn: 1 });
        break;
      case 'lastWeek':
        start = startOfWeek(subDays(now, 7), { weekStartsOn: 1 });
        end = endOfWeek(subDays(now, 7), { weekStartsOn: 1 });
        break;
      case 'thisMonth':
        start = startOfMonth(now);
        end = endOfMonth(now);
        break;
      case 'lastMonth':
        start = startOfMonth(subMonths(now, 1));
        end = endOfMonth(subMonths(now, 1));
        break;
      case 'last30Days':
        start = subDays(now, 30);
        end = now;
        break;
      case 'custom':
        start = customStartDate ? new Date(customStartDate) : startOfMonth(now);
        end = customEndDate ? new Date(customEndDate) : endOfMonth(now);
        break;
      default:
        start = startOfMonth(now);
        end = endOfMonth(now);
    }

    setDateRange({ startDate: start, endDate: end });
    setSelectedDateRange(rangeType);
  };

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      // Fetch all data in parallel
      const [metricsData, trends, utilization, performance, expenses] = await Promise.all([
        fetchMetrics(),
        fetchTripTrends(),
        fetchVehicleUtilization(),
        fetchDriverPerformance(),
        fetchExpenseBreakdown()
      ]);
      console.log('Dashboard data fetched successfully');
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchMetrics = async () => {
    try {
      // Get trips for the selected period
      const { data: trips, error: tripsError } = await supabase
        .from('trips')
        .select('*')
        .gte('created_at', dateRange.startDate.toISOString())
        .lte('created_at', dateRange.endDate.toISOString());

      if (tripsError) {
        console.error('Error fetching trips:', tripsError);
      }

      // Calculate revenue (using a simple formula - adjust based on your business logic)
      const totalRevenue = trips?.reduce((sum, trip) => {
        const distance = (trip.end_km || 0) - (trip.start_km || 0);
        return sum + (distance * 10); // Example: 10 per km
      }, 0) || 0;

      const totalDistance = trips?.reduce((sum, trip) => 
        sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0) || 0;
      const avgDistance = trips?.length ? totalDistance / trips.length : 0;

      // Get active vehicles
      const { count: vehicleCount } = await supabase
        .from('vehicles')
        .select('*', { count: 'exact', head: true })
        .eq('status', 'active');

      // Get active drivers
      const { count: driverCount } = await supabase
        .from('drivers')
        .select('*', { count: 'exact', head: true })
        .eq('status', 'active');

      // Get maintenance data
      const { data: maintenance } = await supabase
        .from('maintenance_tasks')
        .select('actual_cost, status')
        .gte('start_date', dateRange.startDate.toISOString())
        .lte('start_date', dateRange.endDate.toISOString());

      const maintenanceCosts = maintenance?.reduce((sum, m) => sum + (m.actual_cost || 0), 0) || 0;
      const pendingMaintenance = maintenance?.filter(m => m.status === 'open').length || 0;

      setMetrics({
        totalRevenue: Math.round(totalRevenue),
        totalTrips: trips?.length || 0,
        activeVehicles: vehicleCount || 0,
        activeDrivers: driverCount || 0,
        avgTripDistance: Math.round(avgDistance),
        avgFuelEfficiency: 8.5,
        maintenanceCosts: Math.round(maintenanceCosts),
        pendingMaintenance
      });

      return true;
    } catch (error) {
      console.error('Error in fetchMetrics:', error);
      return false;
    }
  };

  const fetchTripTrends = async () => {
    try {
      const { data: trips, error } = await supabase
        .from('trips')
        .select('created_at, start_km, end_km')
        .gte('created_at', subMonths(dateRange.endDate, 6).toISOString())
        .lte('created_at', dateRange.endDate.toISOString())
        .order('created_at');

      if (error) {
        console.error('Error fetching trip trends:', error);
        return;
      }

      // Group by date
      const grouped = trips?.reduce((acc: any, trip) => {
        const date = format(new Date(trip.created_at), 'MMM dd');
        if (!acc[date]) {
          acc[date] = { date, trips: 0, revenue: 0 };
        }
        acc[date].trips++;
        const distance = (trip.end_km || 0) - (trip.start_km || 0);
        acc[date].revenue += distance * 10;
        return acc;
      }, {}) || {};

      const trendData = Object.values(grouped).slice(-7); // Last 7 days
      setChartData((prev: any) => ({ ...prev, tripTrends: trendData }));
      return true;
    } catch (error) {
      console.error('Error in fetchTripTrends:', error);
      return false;
    }
  };

  const fetchVehicleUtilization = async () => {
    try {
      const { data: vehicles, error } = await supabase
        .from('vehicles')
        .select('id, registration_number')
        .eq('status', 'active')
        .limit(5);

      if (error) {
        console.error('Error fetching vehicles:', error);
        return;
      }

      const utilizationData = await Promise.all(
        (vehicles || []).map(async (vehicle) => {
          const { count } = await supabase
            .from('trips')
            .select('*', { count: 'exact', head: true })
            .eq('vehicle_id', vehicle.id)
            .gte('created_at', dateRange.startDate.toISOString())
            .lte('created_at', dateRange.endDate.toISOString());

          const utilization = Math.min(((count || 0) / 30) * 100, 100);
          return {
            vehicle: vehicle.registration_number.slice(0, 10),
            trips: count || 0,
            utilization: Math.round(utilization)
          };
        })
      );

      setChartData((prev: any) => ({ ...prev, vehicleUtilization: utilizationData }));
      return true;
    } catch (error) {
      console.error('Error in fetchVehicleUtilization:', error);
      return false;
    }
  };

  const fetchDriverPerformance = async () => {
    try {
      const { data: drivers, error } = await supabase
        .from('drivers')
        .select('id, name')
        .eq('status', 'active')
        .limit(5);

      if (error) {
        console.error('Error fetching drivers:', error);
        return;
      }

      const performanceData = await Promise.all(
        (drivers || []).map(async (driver) => {
          const { data: trips } = await supabase
            .from('trips')
            .select('start_km, end_km, total_fuel_cost')
            .eq('driver_id', driver.id)
            .gte('created_at', dateRange.startDate.toISOString())
            .lte('created_at', dateRange.endDate.toISOString());

          const totalDistance = trips?.reduce((sum, trip) => 
            sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0) || 0;
          const efficiency = trips?.length ? totalDistance / trips.length : 0;

          return {
            driver: driver.name?.split(' ')[0] || 'Unknown',
            trips: trips?.length || 0,
            efficiency: Math.round(efficiency)
          };
        })
      );

      setChartData((prev: any) => ({ ...prev, driverPerformance: performanceData }));
      return true;
    } catch (error) {
      console.error('Error in fetchDriverPerformance:', error);
      return false;
    }
  };

  const fetchExpenseBreakdown = async () => {
    try {
      const { data: trips, error } = await supabase
        .from('trips')
        .select('total_fuel_cost, total_road_expenses, driver_expense, breakdown_expense')
        .gte('created_at', dateRange.startDate.toISOString())
        .lte('created_at', dateRange.endDate.toISOString());

      if (error) {
        console.error('Error fetching expenses:', error);
        return;
      }

      const expenses = {
        Fuel: 0,
        Road: 0,
        Driver: 0,
        Breakdown: 0,
        Maintenance: metrics.maintenanceCosts
      };

      trips?.forEach(trip => {
        expenses.Fuel += trip.total_fuel_cost || 0;
        expenses.Road += trip.total_road_expenses || 0;
        expenses.Driver += trip.driver_expense || 0;
        expenses.Breakdown += trip.breakdown_expense || 0;
      });

      const expenseData = Object.entries(expenses)
        .map(([category, amount]) => ({ category, amount: Math.round(amount) }))
        .filter(item => item.amount > 0);

      setChartData((prev: any) => ({ ...prev, expenseBreakdown: expenseData }));
      return true;
    } catch (error) {
      console.error('Error in fetchExpenseBreakdown:', error);
      return false;
    }
  };

  const generatePDFReport = async (reportType: string) => {
    setGeneratingReport(reportType);
    
    try {
      const pdf = new jsPDF();
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();
      
      // Add colorful header with gradient effect
      pdf.setFillColor(59, 130, 246); // Blue background
      pdf.rect(0, 0, pageWidth, 50, 'F');
      
      // White text on blue background
      pdf.setTextColor(255, 255, 255);
      pdf.setFontSize(24);
      pdf.setFont('helvetica', 'bold');
      pdf.text('AVS Fleet Management', pageWidth / 2, 20, { align: 'center' });
      
      pdf.setFontSize(16);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Intelligent Fleet Solutions', pageWidth / 2, 30, { align: 'center' });
      
      // Report type with accent color
      pdf.setFillColor(16, 185, 129); // Green accent
      pdf.rect(0, 50, pageWidth, 20, 'F');
      
      pdf.setTextColor(255, 255, 255);
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text(reportType.replace(/-/g, ' ').toUpperCase(), pageWidth / 2, 62, { align: 'center' });
      
      // Date and period info with subtle background
      pdf.setFillColor(249, 250, 251); // Light gray
      pdf.rect(0, 70, pageWidth, 25, 'F');
      
      pdf.setTextColor(75, 85, 99);
      pdf.setFontSize(11);
      pdf.setFont('helvetica', 'normal');
      pdf.text(`Generated on: ${format(new Date(), 'dd MMM yyyy, HH:mm')}`, pageWidth / 2, 82, { align: 'center' });
      pdf.text(`Period: ${format(dateRange.startDate, 'dd/MM/yyyy')} to ${format(dateRange.endDate, 'dd/MM/yyyy')}`, pageWidth / 2, 90, { align: 'center' });
      
      let yPosition = 110;

      // Key Metrics Section with colored cards
      pdf.setFontSize(16);
      pdf.setFont('helvetica', 'bold');
      pdf.setTextColor(31, 41, 55);
      pdf.text(' Key Performance Indicators', 14, yPosition);
      yPosition += 15;

      // Create colored metric cards
      const metricCards = [
        { label: 'Total Revenue', value: `${metrics.totalRevenue.toLocaleString('en-IN')}`, color: [34, 197, 94] }, // Green
        { label: 'Total Trips', value: metrics.totalTrips.toString(), color: [59, 130, 246] }, // Blue
        { label: 'Active Vehicles', value: metrics.activeVehicles.toString(), color: [168, 85, 247] }, // Purple
        { label: 'Active Drivers', value: metrics.activeDrivers.toString(), color: [245, 158, 11] } // Orange
      ];

      metricCards.forEach((metric, index) => {
        const x = 14 + (index % 2) * 90;
        const y = yPosition + Math.floor(index / 2) * 25;
        
        // Colored background
        pdf.setFillColor(...metric.color);
        pdf.roundedRect(x, y, 80, 20, 3, 3, 'F');
        
        // White text
        pdf.setTextColor(255, 255, 255);
        pdf.setFontSize(9);
        pdf.setFont('helvetica', 'normal');
        pdf.text(metric.label, x + 5, y + 8);
        
        pdf.setFontSize(12);
        pdf.setFont('helvetica', 'bold');
        pdf.text(metric.value, x + 5, y + 16);
      });

      yPosition += 60;

      // Executive Summary Section
      pdf.setFillColor(239, 246, 255); // Light blue background
      pdf.rect(14, yPosition, pageWidth - 28, 40, 'F');
      
      pdf.setTextColor(31, 41, 55);
      pdf.setFontSize(14);
      pdf.setFont('helvetica', 'bold');
      pdf.text(' Executive Summary', 20, yPosition + 12);
      
      pdf.setFontSize(10);
      pdf.setFont('helvetica', 'normal');
      pdf.setTextColor(75, 85, 99);
      
      const summaryText = [
        `This comprehensive ${reportType.replace(/-/g, ' ')} report provides detailed insights into your fleet operations.`,
        `During the selected period, your fleet generated ${metrics.totalRevenue.toLocaleString('en-IN')} in revenue through ${metrics.totalTrips} completed trips.`,
        `With ${metrics.activeVehicles} active vehicles and ${metrics.activeDrivers} drivers, your fleet utilization shows strong operational efficiency.`,
        `The data presented below is sourced directly from your Supabase database and reflects real-time operational metrics.`
      ];
      
      summaryText.forEach((text, index) => {
        pdf.text(text, 20, yPosition + 20 + (index * 5));
      });

      yPosition += 50;

      // Detailed Analysis Section
      pdf.setFontSize(14);
      pdf.setFont('helvetica', 'bold');
      pdf.setTextColor(31, 41, 55);
      pdf.text(' Detailed Analysis', 14, yPosition);
      yPosition += 15;

      // Add specific report content based on type
      switch (reportType) {
        case 'trip-summary':
          await addTripSummaryContent(pdf, yPosition, dateRange);
          break;
        case 'week-comparison':
        case 'month-comparison':
          await addComparisonContent(pdf, yPosition, reportType, dateRange);
          break;
        case 'fuel-analysis':
          await addFuelAnalysisContent(pdf, yPosition, dateRange);
          break;
        case 'expense-report':
          await addExpenseReportContent(pdf, yPosition, dateRange);
          break;
        default:
          await addGenericReportContent(pdf, yPosition, reportType, dateRange);
      }

      // Add footer with company branding
      const pageCount = pdf.internal.pages.length - 1;
      for (let i = 1; i <= pageCount; i++) {
        pdf.setPage(i);
        
        // Footer background
        pdf.setFillColor(31, 41, 55);
        pdf.rect(0, pageHeight - 20, pageWidth, 20, 'F');
        
        // Footer text
        pdf.setTextColor(255, 255, 255);
        pdf.setFontSize(8);
        pdf.setFont('helvetica', 'normal');
        pdf.text(`Page ${i} of ${pageCount}`, pageWidth / 2, pageHeight - 8, { align: 'center' });
        pdf.text(' 2024 AVS - Auto Vital Solution | Intelligent Fleet Management', 14, pageHeight - 8);
        pdf.text('Generated by AVS Fleet Management System', pageWidth - 14, pageHeight - 8, { align: 'right' });
      }

      // Save the PDF
      pdf.save(`AVS-${reportType}-${format(new Date(), 'yyyy-MM-dd')}.pdf`);
      
      // Show success message
      alert('Enhanced report generated successfully!');
      
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Error generating report. Please try again.');
    } finally {
      setGeneratingReport(null);
    }
  };

  // Helper function to add trip summary content
  const addTripSummaryContent = async (pdf: any, yPosition: number, dateRange: any) => {
    try {
      const { data: trips } = await supabase
        .from('trips')
        .select(`
          *,
          vehicle:vehicles(registration_number),
          driver:drivers(name)
        `)
        .gte('created_at', dateRange.startDate.toISOString())
        .lte('created_at', dateRange.endDate.toISOString())
        .limit(50);

      if (trips && trips.length > 0) {
        // Summary statistics
        const totalDistance = trips.reduce((sum, trip) => 
          sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0);
        const totalFuel = trips.reduce((sum, trip) => 
          sum + (trip.total_fuel_cost || 0), 0);
        const avgDistance = trips.length ? totalDistance / trips.length : 0;

        pdf.setFontSize(12);
        pdf.setFont('helvetica', 'bold');
        pdf.setTextColor(31, 41, 55);
        pdf.text('Trip Summary Statistics', 14, yPosition);
        yPosition += 10;

        pdf.setFontSize(10);
        pdf.setFont('helvetica', 'normal');
        pdf.setTextColor(75, 85, 99);
        pdf.text(` Total Distance Covered: ${totalDistance.toFixed(0)} km`, 20, yPosition);
        yPosition += 6;
        pdf.text(` Total Fuel Cost: ${totalFuel.toFixed(2)}`, 20, yPosition);
        yPosition += 6;
        pdf.text(` Average Distance per Trip: ${avgDistance.toFixed(1)} km`, 20, yPosition);
        yPosition += 6;
        pdf.text(` Fuel Efficiency: ${totalDistance > 0 ? (totalDistance / totalFuel).toFixed(2) : 0} km/`, 20, yPosition);
        yPosition += 15;

        // Trip details table
        const tableData = trips.slice(0, 20).map(trip => [
          format(new Date(trip.created_at), 'dd/MM/yy'),
          trip.vehicle?.registration_number || 'N/A',
          trip.driver?.name || 'N/A',
          `${trip.from_location || 'N/A'}  ${trip.to_location || 'N/A'}`,
          `${((trip.end_km || 0) - (trip.start_km || 0)).toFixed(0)} km`,
          `${(trip.total_fuel_cost || 0).toFixed(2)}`
        ]);

        if (tableData.length > 0) {
          pdf.autoTable({
            head: [['Date', 'Vehicle', 'Driver', 'Route', 'Distance', 'Fuel Cost']],
            body: tableData,
            startY: yPosition,
            theme: 'grid',
            headStyles: { 
              fillColor: [59, 130, 246],
              textColor: [255, 255, 255],
              fontSize: 9
            },
            bodyStyles: { fontSize: 8 },
            alternateRowStyles: { fillColor: [249, 250, 251] }
          });
        }
      } else {
        pdf.setFontSize(10);
        pdf.setTextColor(239, 68, 68);
        pdf.text('No trip data available for the selected period.', 14, yPosition);
      }
    } catch (error) {
      console.error('Error adding trip summary:', error);
    }
  };

  // Helper function to add comparison content
  const addComparisonContent = async (pdf: any, yPosition: number, reportType: string, dateRange: any) => {
    try {
      let currentPeriodStart, currentPeriodEnd, previousPeriodStart, previousPeriodEnd;
      
      if (reportType === 'week-comparison') {
        currentPeriodStart = startOfWeek(new Date(), { weekStartsOn: 1 });
        currentPeriodEnd = endOfWeek(new Date(), { weekStartsOn: 1 });
        previousPeriodStart = startOfWeek(subDays(new Date(), 7), { weekStartsOn: 1 });
        previousPeriodEnd = endOfWeek(subDays(new Date(), 7), { weekStartsOn: 1 });
      } else {
        currentPeriodStart = startOfMonth(new Date());
        currentPeriodEnd = endOfMonth(new Date());
        previousPeriodStart = startOfMonth(subMonths(new Date(), 1));
        previousPeriodEnd = endOfMonth(subMonths(new Date(), 1));
      }

      const { data: currentTrips } = await supabase
        .from('trips')
        .select('*')
        .gte('created_at', currentPeriodStart.toISOString())
        .lte('created_at', currentPeriodEnd.toISOString());

      const { data: previousTrips } = await supabase
        .from('trips')
        .select('*')
        .gte('created_at', previousPeriodStart.toISOString())
        .lte('created_at', previousPeriodEnd.toISOString());

      const calculateMetrics = (trips: any[]) => {
        return {
          trips: trips?.length || 0,
          distance: trips?.reduce((sum, trip) => sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0) || 0,
          fuel: trips?.reduce((sum, trip) => sum + (trip.total_fuel_cost || 0), 0) || 0,
          revenue: trips?.reduce((sum, trip) => sum + ((trip.end_km || 0) - (trip.start_km || 0)) * 10, 0) || 0
        };
      };

      const current = calculateMetrics(currentTrips || []);
      const previous = calculateMetrics(previousTrips || []);

      const calculatePercentChange = (old: number, newVal: number) => {
        if (old === 0) return newVal > 0 ? 100 : 0;
        return ((newVal - old) / old) * 100;
      };

      pdf.setFontSize(12);
      pdf.setFont('helvetica', 'bold');
      pdf.setTextColor(31, 41, 55);
      pdf.text('Period-over-Period Comparison', 14, yPosition);
      yPosition += 15;

      const comparisonData = [
        ['Metric', reportType === 'week-comparison' ? 'This Week' : 'This Month', 
         reportType === 'week-comparison' ? 'Last Week' : 'Last Month', 'Change', '% Change'],
        ['Total Trips', current.trips.toString(), previous.trips.toString(), 
         (current.trips - previous.trips).toString(),
         `${calculatePercentChange(previous.trips, current.trips).toFixed(1)}%`],
        ['Distance (km)', current.distance.toFixed(0), previous.distance.toFixed(0),
         (current.distance - previous.distance).toFixed(0),
         `${calculatePercentChange(previous.distance, current.distance).toFixed(1)}%`],
        ['Revenue ()', current.revenue.toFixed(0), previous.revenue.toFixed(0),
         (current.revenue - previous.revenue).toFixed(0),
         `${calculatePercentChange(previous.revenue, current.revenue).toFixed(1)}%`],
        ['Fuel Cost ()', current.fuel.toFixed(2), previous.fuel.toFixed(2),
         (current.fuel - previous.fuel).toFixed(2),
         `${calculatePercentChange(previous.fuel, current.fuel).toFixed(1)}%`]
      ];

      pdf.autoTable({
        head: [comparisonData[0]],
        body: comparisonData.slice(1),
        startY: yPosition,
        theme: 'striped',
        headStyles: { 
          fillColor: [34, 197, 94],
          textColor: [255, 255, 255],
          fontSize: 9
        },
        bodyStyles: { fontSize: 8 },
        alternateRowStyles: { fillColor: [249, 250, 251] }
      });
    } catch (error) {
      console.error('Error adding comparison content:', error);
    }
  };

  // Helper function to add fuel analysis content
  const addFuelAnalysisContent = async (pdf: any, yPosition: number, dateRange: any) => {
    try {
      const { data: trips } = await supabase
        .from('trips')
        .select('total_fuel_cost, start_km, end_km, vehicle_id')
        .gte('created_at', dateRange.startDate.toISOString())
        .lte('created_at', dateRange.endDate.toISOString());

      if (trips && trips.length > 0) {
        const totalFuel = trips.reduce((sum, trip) => sum + (trip.total_fuel_cost || 0), 0);
        const totalDistance = trips.reduce((sum, trip) => 
          sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0);
        const avgFuelCost = trips.length ? totalFuel / trips.length : 0;
        const fuelEfficiency = totalFuel > 0 ? totalDistance / totalFuel : 0;

        pdf.setFontSize(12);
        pdf.setFont('helvetica', 'bold');
        pdf.setTextColor(31, 41, 55);
        pdf.text('Fuel Analysis Summary', 14, yPosition);
        yPosition += 15;

        pdf.setFontSize(10);
        pdf.setFont('helvetica', 'normal');
        pdf.setTextColor(75, 85, 99);
        pdf.text(` Total Fuel Expenditure: ${totalFuel.toFixed(2)}`, 20, yPosition);
        yPosition += 6;
        pdf.text(` Average Fuel Cost per Trip: ${avgFuelCost.toFixed(2)}`, 20, yPosition);
        yPosition += 6;
        pdf.text(` Fuel Efficiency: ${fuelEfficiency.toFixed(2)} km/`, 20, yPosition);
        yPosition += 6;
        pdf.text(` Total Distance Covered: ${totalDistance.toFixed(0)} km`, 20, yPosition);
      } else {
        pdf.setFontSize(10);
        pdf.setTextColor(239, 68, 68);
        pdf.text('No fuel data available for the selected period.', 14, yPosition);
      }
    } catch (error) {
      console.error('Error adding fuel analysis:', error);
    }
  };

  // Helper function to add expense report content
  const addExpenseReportContent = async (pdf: any, yPosition: number, dateRange: any) => {
    try {
      const { data: trips } = await supabase
        .from('trips')
        .select('total_fuel_cost, total_road_expenses, driver_expense, breakdown_expense, unloading_expense, miscellaneous_expense')
        .gte('created_at', dateRange.startDate.toISOString())
        .lte('created_at', dateRange.endDate.toISOString());

      if (trips && trips.length > 0) {
        const expenses = {
          'Fuel': 0,
          'Road Expenses': 0,
          'Driver Expenses': 0,
          'Breakdown': 0,
          'Unloading': 0,
          'Miscellaneous': 0
        };

        trips.forEach(trip => {
          expenses['Fuel'] += trip.total_fuel_cost || 0;
          expenses['Road Expenses'] += trip.total_road_expenses || 0;
          expenses['Driver Expenses'] += trip.driver_expense || 0;
          expenses['Breakdown'] += trip.breakdown_expense || 0;
          expenses['Unloading'] += trip.unloading_expense || 0;
          expenses['Miscellaneous'] += trip.miscellaneous_expense || 0;
        });

        const totalExpenses = Object.values(expenses).reduce((sum, val) => sum + val, 0);

        pdf.setFontSize(12);
        pdf.setFont('helvetica', 'bold');
        pdf.setTextColor(31, 41, 55);
        pdf.text('Expense Breakdown Analysis', 14, yPosition);
        yPosition += 15;

        const expenseData = Object.entries(expenses)
          .filter(([_, amount]) => amount > 0)
          .map(([category, amount]) => [
            category,
            `${amount.toFixed(2)}`,
            `${((amount / totalExpenses) * 100).toFixed(1)}%`
          ]);

        if (expenseData.length > 0) {
          pdf.autoTable({
            head: [['Expense Category', 'Amount', 'Percentage']],
            body: expenseData,
            startY: yPosition,
            theme: 'grid',
            headStyles: { 
              fillColor: [239, 68, 68],
              textColor: [255, 255, 255],
              fontSize: 9
            },
            bodyStyles: { fontSize: 8 },
            alternateRowStyles: { fillColor: [254, 242, 242] }
          });
        }
      } else {
        pdf.setFontSize(10);
        pdf.setTextColor(239, 68, 68);
        pdf.text('No expense data available for the selected period.', 14, yPosition);
      }
    } catch (error) {
      console.error('Error adding expense report:', error);
    }
  };

  // Helper function to add generic report content
  const addGenericReportContent = async (pdf: any, yPosition: number, reportType: string, dateRange: any) => {
    pdf.setFontSize(12);
    pdf.setFont('helvetica', 'bold');
    pdf.setTextColor(31, 41, 55);
    pdf.text(`${reportType.replace(/-/g, ' ').toUpperCase()} Analysis`, 14, yPosition);
    yPosition += 15;

    pdf.setFontSize(10);
    pdf.setFont('helvetica', 'normal');
    pdf.setTextColor(75, 85, 99);
    pdf.text('This report provides comprehensive insights into your fleet operations.', 14, yPosition);
    yPosition += 6;
    pdf.text('All data is sourced from your live Supabase database and reflects current operational status.', 14, yPosition);
    yPosition += 6;
    pdf.text('For more detailed analysis, please contact your fleet management team.', 14, yPosition);
  };

  const reportTypes = [
    {
      id: 'week-comparison',
      name: 'Weekly Comparison',
      description: 'This week vs last week',
      icon: <TrendingUp className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'month-comparison',
      name: 'Monthly Comparison',
      description: 'Month-over-month analysis',
      icon: <BarChart3 className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'year-comparison',
      name: 'Yearly Comparison',
      description: 'Year-over-year analysis',
      icon: <FileSpreadsheet className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'trip-summary',
      name: 'Trip Summary',
      description: 'All trip details',
      icon: <Package className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'vehicle-utilization',
      name: 'Vehicle Utilization',
      description: 'Vehicle usage patterns',
      icon: <Truck className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'driver-performance',
      name: 'Driver Performance',
      description: 'Driver efficiency',
      icon: <Users className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'fuel-analysis',
      name: 'Fuel Analysis',
      description: 'Fuel usage & costs',
      icon: <Fuel className="h-4 w-4" />,
      category: 'financial'
    },
    {
      id: 'expense-report',
      name: 'Expense Report',
      description: 'All expenses',
      icon: <DollarSign className="h-4 w-4" />,
      category: 'financial'
    },
    {
      id: 'maintenance',
      name: 'Maintenance',
      description: 'Service schedules',
      icon: <Wrench className="h-4 w-4" />,
      category: 'maintenance'
    },
    {
      id: 'compliance',
      name: 'Compliance',
      description: 'Document validity',
      icon: <FileText className="h-4 w-4" />,
      category: 'compliance'
    }
  ];

  const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6'];

  return (
    <Layout>
      <div className="p-4 sm:p-6 max-w-7xl mx-auto">
      {/* Header - Fixed to match AVS style */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
          <div>
            <h1 className="text-2xl font-semibold text-gray-900 flex items-center">
              <Activity className="h-6 w-6 mr-2 text-primary-600" />
              Reporting & Analytics
            </h1>
            <p className="text-sm text-gray-500 mt-1">Visual insights and downloadable reports</p>
          </div>
          
          {/* Tab Switcher */}
          <div className="flex bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setActiveTab('dashboard')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${
                activeTab === 'dashboard' 
                  ? 'bg-primary-600 text-white shadow-sm' 
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Eye className="h-4 w-4 inline mr-2" />
              Visual Dashboard
            </button>
            <button
              onClick={() => setActiveTab('reports')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${
                activeTab === 'reports' 
                  ? 'bg-primary-600 text-white shadow-sm' 
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Download className="h-4 w-4 inline mr-2" />
              Download Reports
            </button>
          </div>
        </div>
      </div>

      {/* Date Range Selector */}
      <div className="bg-white rounded-lg shadow-sm p-4 mb-6">
        <div className="flex flex-wrap items-center gap-4">
          <Calendar className="h-5 w-5 text-gray-400" />
          <select
            value={selectedDateRange}
            onChange={(e) => updateDateRange(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
          >
            <option value="today">Today</option>
            <option value="yesterday">Yesterday</option>
            <option value="thisWeek">This Week</option>
            <option value="lastWeek">Last Week</option>
            <option value="thisMonth">This Month</option>
            <option value="lastMonth">Last Month</option>
            <option value="last30Days">Last 30 Days</option>
            <option value="custom">Custom Range</option>
          </select>
          
          {selectedDateRange === 'custom' && (
            <div className="flex items-center gap-2">
              <input
                type="date"
                value={customStartDate}
                onChange={(e) => {
                  setCustomStartDate(e.target.value);
                  if (e.target.value && customEndDate) {
                    updateDateRange('custom');
                  }
                }}
                className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
              <span className="text-gray-500">to</span>
              <input
                type="date"
                value={customEndDate}
                onChange={(e) => {
                  setCustomEndDate(e.target.value);
                  if (customStartDate && e.target.value) {
                    updateDateRange('custom');
                  }
                }}
                className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
          )}
          
          {activeTab === 'dashboard' && (
            <button
              onClick={fetchDashboardData}
              disabled={loading}
              className="ml-auto px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50 transition-colors flex items-center gap-2 text-sm font-medium"
            >
              <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
              Refresh
            </button>
          )}
        </div>
      </div>

      {/* Visual Dashboard Tab */}
      {activeTab === 'dashboard' && (
        <>
          {/* Metrics Grid - Updated with Rupees */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <div className="bg-white rounded-lg shadow-sm p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Total Revenue</p>
                  <p className="mt-1 text-2xl font-semibold text-gray-900">
                    {metrics.totalRevenue.toLocaleString('en-IN')}
                  </p>
                </div>
                <div className="p-3 bg-green-100 rounded-lg">
                  <DollarSign className="h-6 w-6 text-green-600" />
                </div>
              </div>
            </div>
            
            <div className="bg-white rounded-lg shadow-sm p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Total Trips</p>
                  <p className="mt-1 text-2xl font-semibold text-gray-900">{metrics.totalTrips}</p>
                </div>
                <div className="p-3 bg-blue-100 rounded-lg">
                  <Package className="h-6 w-6 text-blue-600" />
                </div>
              </div>
            </div>
            
            <div className="bg-white rounded-lg shadow-sm p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Active Vehicles</p>
                  <p className="mt-1 text-2xl font-semibold text-gray-900">{metrics.activeVehicles}</p>
                </div>
                <div className="p-3 bg-purple-100 rounded-lg">
                  <Truck className="h-6 w-6 text-purple-600" />
                </div>
              </div>
            </div>
            
            <div className="bg-white rounded-lg shadow-sm p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Active Drivers</p>
                  <p className="mt-1 text-2xl font-semibold text-gray-900">{metrics.activeDrivers}</p>
                </div>
                <div className="p-3 bg-orange-100 rounded-lg">
                  <Users className="h-6 w-6 text-orange-600" />
                </div>
              </div>
            </div>
          </div>

          {/* Charts Grid */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Trip Trends */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Trip Trends</h3>
              {chartData.tripTrends.length > 0 ? (
                <ResponsiveContainer width="100%" height={300}>
                  <LineChart data={chartData.tripTrends}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                    <XAxis dataKey="date" stroke="#6B7280" fontSize={12} />
                    <YAxis stroke="#6B7280" fontSize={12} />
                    <Tooltip formatter={(value: any, name: string) => 
                      name === 'Revenue' ? `${value}` : value
                    } />
                    <Legend />
                    <Line type="monotone" dataKey="trips" stroke="#3B82F6" name="Trips" strokeWidth={2} />
                    <Line type="monotone" dataKey="revenue" stroke="#10B981" name="Revenue" strokeWidth={2} />
                  </LineChart>
                </ResponsiveContainer>
              ) : (
                <div className="h-[300px] flex items-center justify-center text-gray-500">
                  No data available for the selected period
                </div>
              )}
            </div>

            {/* Vehicle Utilization */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Vehicle Utilization</h3>
              {chartData.vehicleUtilization.length > 0 ? (
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={chartData.vehicleUtilization}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                    <XAxis dataKey="vehicle" stroke="#6B7280" fontSize={12} />
                    <YAxis stroke="#6B7280" fontSize={12} />
                    <Tooltip formatter={(value: any) => `${value}%`} />
                    <Bar dataKey="utilization" fill="#3B82F6" />
                  </BarChart>
                </ResponsiveContainer>
              ) : (
                <div className="h-[300px] flex items-center justify-center text-gray-500">
                  No vehicle data available
                </div>
              )}
            </div>

            {/* Driver Performance */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Top Driver Performance</h3>
              {chartData.driverPerformance.length > 0 ? (
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={chartData.driverPerformance}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                    <XAxis dataKey="driver" stroke="#6B7280" fontSize={12} />
                    <YAxis stroke="#6B7280" fontSize={12} />
                    <Tooltip formatter={(value: any) => `${value} km/`} />
                    <Bar dataKey="efficiency" fill="#10B981" />
                  </BarChart>
                </ResponsiveContainer>
              ) : (
                <div className="h-[300px] flex items-center justify-center text-gray-500">
                  No driver data available
                </div>
              )}
            </div>

            {/* Expense Breakdown */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Expense Breakdown</h3>
              {chartData.expenseBreakdown.length > 0 ? (
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={chartData.expenseBreakdown}
                      cx="50%"
                      cy="50%"
                      labelLine={false}
                      label={(entry: any) => `${entry.category}: ${entry.amount}`}
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="amount"
                    >
                      {chartData.expenseBreakdown.map((entry: any, index: number) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(value: any) => `${value}`} />
                  </PieChart>
                </ResponsiveContainer>
              ) : (
                <div className="h-[300px] flex items-center justify-center text-gray-500">
                  No expense data available
                </div>
              )}
            </div>
          </div>
        </>
      )}

      {/* Download Reports Tab */}
      {activeTab === 'reports' && (
        <div>
          {/* Quick Downloads */}
          <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Downloads</h3>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3">
              {reportTypes.slice(0, showAllReports ? reportTypes.length : 5).map((report) => (
                <button
                  key={report.id}
                  onClick={() => generatePDFReport(report.id)}
                  disabled={generatingReport === report.id}
                  className="flex flex-col items-center p-4 bg-gray-50 hover:bg-gray-100 rounded-lg transition-all hover:shadow-md disabled:opacity-50"
                >
                  <div className={`p-3 rounded-lg mb-2 ${
                    report.category === 'comparison' ? 'bg-green-100 text-green-600' :
                    report.category === 'financial' ? 'bg-blue-100 text-blue-600' :
                    report.category === 'operations' ? 'bg-purple-100 text-purple-600' :
                    report.category === 'maintenance' ? 'bg-orange-100 text-orange-600' :
                    'bg-gray-100 text-gray-600'
                  }`}>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-5 w-5 animate-spin" />
                    ) : (
                      report.icon
                    )}
                  </div>
                  <span className="text-sm font-medium text-gray-900">{report.name}</span>
                  <span className="text-xs text-gray-500 mt-1 text-center">{report.description}</span>
                </button>
              ))}
            </div>
            
            {!showAllReports && reportTypes.length > 5 && (
              <button
                onClick={() => setShowAllReports(true)}
                className="mt-4 flex items-center justify-center w-full py-2 text-primary-600 hover:text-primary-700 font-medium"
              >
                <ChevronDown className="h-4 w-4 mr-1" />
                Show All Reports ({reportTypes.length - 5} more)
              </button>
            )}
            
            {showAllReports && (
              <button
                onClick={() => setShowAllReports(false)}
                className="mt-4 flex items-center justify-center w-full py-2 text-primary-600 hover:text-primary-700 font-medium"
              >
                <ChevronUp className="h-4 w-4 mr-1" />
                Show Less
              </button>
            )}
          </div>

          {/* Categorized Reports */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Smart Comparisons */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <TrendingUp className="h-5 w-5 mr-2 text-green-600" />
                Smart Comparisons
              </h3>
              <div className="space-y-2">
                {reportTypes.filter(r => r.category === 'comparison').map(report => (
                  <button
                    key={report.id}
                    onClick={() => generatePDFReport(report.id)}
                    disabled={generatingReport === report.id}
                    className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center group"
                  >
                    <span className="text-sm text-gray-700">{report.name}</span>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-4 w-4 animate-spin text-primary-600" />
                    ) : (
                      <Download className="h-4 w-4 text-gray-400 group-hover:text-primary-600" />
                    )}
                  </button>
                ))}
              </div>
            </div>

            {/* Financial Reports */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <DollarSign className="h-5 w-5 mr-2 text-blue-600" />
                Financial Reports
              </h3>
              <div className="space-y-2">
                {reportTypes.filter(r => r.category === 'financial').map(report => (
                  <button
                    key={report.id}
                    onClick={() => generatePDFReport(report.id)}
                    disabled={generatingReport === report.id}
                    className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center group"
                  >
                    <span className="text-sm text-gray-700">{report.name}</span>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-4 w-4 animate-spin text-primary-600" />
                    ) : (
                      <Download className="h-4 w-4 text-gray-400 group-hover:text-primary-600" />
                    )}
                  </button>
                ))}
              </div>
            </div>

            {/* Operations Reports */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Package className="h-5 w-5 mr-2 text-purple-600" />
                Operations Reports
              </h3>
              <div className="space-y-2">
                {reportTypes.filter(r => r.category === 'operations').map(report => (
                  <button
                    key={report.id}
                    onClick={() => generatePDFReport(report.id)}
                    disabled={generatingReport === report.id}
                    className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center group"
                  >
                    <span className="text-sm text-gray-700">{report.name}</span>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-4 w-4 animate-spin text-primary-600" />
                    ) : (
                      <Download className="h-4 w-4 text-gray-400 group-hover:text-primary-600" />
                    )}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
      </div>
    </Layout>
  );
};

export default CompleteFixedReportingDashboard;

import React, { useState, useEffect } from 'react';
import {
  BarChart,
  Bar,
  LineChart,
  Line,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';
import {
  Calendar,
  TrendingUp,
  Users,
  Truck,
  DollarSign,
  Package,
  AlertCircle,
  Download,
  Filter,
  RefreshCw,
  FileText,
  Fuel,
  MapPin,
  Clock,
  Wrench,
  BarChart3,
  PieChart as PieChartIcon,
  FileSpreadsheet,
  Eye,
  ChevronDown,
  ChevronUp,
  Activity
} from 'lucide-react';
import { supabase } from '../../utils/supabaseClient';
import { format, startOfDay, endOfDay, startOfWeek, endOfWeek, startOfMonth, endOfMonth, subDays, subMonths } from 'date-fns';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

// Extend jsPDF type for autoTable
declare module 'jspdf' {
  interface jsPDF {
    autoTable: (options: any) => jsPDF;
  }
}

interface ReportMetrics {
  totalRevenue: number;
  totalTrips: number;
  activeVehicles: number;
  activeDrivers: number;
  avgTripDistance: number;
  avgFuelEfficiency: number;
  maintenanceCosts: number;
  pendingMaintenance: number;
}

const FixedUnifiedReportingDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'dashboard' | 'reports'>('dashboard');
  const [metrics, setMetrics] = useState<ReportMetrics>({
    totalRevenue: 0,
    totalTrips: 0,
    activeVehicles: 0,
    activeDrivers: 0,
    avgTripDistance: 0,
    avgFuelEfficiency: 0,
    maintenanceCosts: 0,
    pendingMaintenance: 0
  });

  const [chartData, setChartData] = useState<any>({
    tripTrends: [],
    vehicleUtilization: [],
    driverPerformance: [],
    expenseBreakdown: []
  });

  const [dateRange, setDateRange] = useState({
    startDate: startOfMonth(new Date()),
    endDate: endOfMonth(new Date())
  });

  const [selectedDateRange, setSelectedDateRange] = useState('thisMonth');
  const [customStartDate, setCustomStartDate] = useState('');
  const [customEndDate, setCustomEndDate] = useState('');
  const [loading, setLoading] = useState(false);
  const [generatingReport, setGeneratingReport] = useState<string | null>(null);
  const [showAllReports, setShowAllReports] = useState(false);

  // Initialize date range properly
  useEffect(() => {
    updateDateRange(selectedDateRange);
  }, []);

  // Fetch data when date range changes
  useEffect(() => {
    if (activeTab === 'dashboard') {
      fetchDashboardData();
    }
  }, [dateRange, activeTab]);

  const updateDateRange = (rangeType: string) => {
    const now = new Date();
    let start: Date, end: Date;

    switch (rangeType) {
      case 'today':
        start = startOfDay(now);
        end = endOfDay(now);
        break;
      case 'yesterday':
        start = startOfDay(subDays(now, 1));
        end = endOfDay(subDays(now, 1));
        break;
      case 'thisWeek':
        start = startOfWeek(now, { weekStartsOn: 1 });
        end = endOfWeek(now, { weekStartsOn: 1 });
        break;
      case 'lastWeek':
        start = startOfWeek(subDays(now, 7), { weekStartsOn: 1 });
        end = endOfWeek(subDays(now, 7), { weekStartsOn: 1 });
        break;
      case 'thisMonth':
        start = startOfMonth(now);
        end = endOfMonth(now);
        break;
      case 'lastMonth':
        start = startOfMonth(subMonths(now, 1));
        end = endOfMonth(subMonths(now, 1));
        break;
      case 'last30Days':
        start = subDays(now, 30);
        end = now;
        break;
      case 'custom':
        start = customStartDate ? new Date(customStartDate) : startOfMonth(now);
        end = customEndDate ? new Date(customEndDate) : endOfMonth(now);
        break;
      default:
        start = startOfMonth(now);
        end = endOfMonth(now);
    }

    setDateRange({ startDate: start, endDate: end });
    setSelectedDateRange(rangeType);
  };

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      // Fetch all data in parallel
      const [metricsData, trends, utilization, performance, expenses] = await Promise.all([
        fetchMetrics(),
        fetchTripTrends(),
        fetchVehicleUtilization(),
        fetchDriverPerformance(),
        fetchExpenseBreakdown()
      ]);

      console.log('Dashboard data fetched successfully');
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchMetrics = async () => {
    try {
      // Get trips for the selected period
      const { data: trips, error: tripsError } = await supabase
        .from('trips')
        .select('*')
        .gte('created_at', dateRange.startDate.toISOString())
        .lte('created_at', dateRange.endDate.toISOString());

      if (tripsError) {
        console.error('Error fetching trips:', tripsError);
      }

      // Calculate revenue (using a simple formula - adjust based on your business logic)
      const totalRevenue = trips?.reduce((sum, trip) => {
        const distance = (trip.end_km || 0) - (trip.start_km || 0);
        return sum + (distance * 10); // Example: $10 per km
      }, 0) || 0;

      const totalDistance = trips?.reduce((sum, trip) => 
        sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0) || 0;

      const avgDistance = trips?.length ? totalDistance / trips.length : 0;

      // Get active vehicles
      const { count: vehicleCount } = await supabase
        .from('vehicles')
        .select('*', { count: 'exact', head: true })
        .eq('status', 'active');

      // Get active drivers
      const { count: driverCount } = await supabase
        .from('drivers')
        .select('*', { count: 'exact', head: true })
        .eq('status', 'active');

      // Get maintenance data
      const { data: maintenance } = await supabase
        .from('maintenance')
        .select('cost, status')
        .gte('scheduled_date', dateRange.startDate.toISOString())
        .lte('scheduled_date', dateRange.endDate.toISOString());

      const maintenanceCosts = maintenance?.reduce((sum, m) => sum + (m.cost || 0), 0) || 0;
      const pendingMaintenance = maintenance?.filter(m => m.status === 'scheduled').length || 0;

      setMetrics({
        totalRevenue: Math.round(totalRevenue),
        totalTrips: trips?.length || 0,
        activeVehicles: vehicleCount || 0,
        activeDrivers: driverCount || 0,
        avgTripDistance: Math.round(avgDistance),
        avgFuelEfficiency: 8.5,
        maintenanceCosts: Math.round(maintenanceCosts),
        pendingMaintenance
      });

      return true;
    } catch (error) {
      console.error('Error in fetchMetrics:', error);
      return false;
    }
  };

  const fetchTripTrends = async () => {
    try {
      const { data: trips, error } = await supabase
        .from('trips')
        .select('created_at, start_km, end_km')
        .gte('created_at', subMonths(dateRange.endDate, 6).toISOString())
        .lte('created_at', dateRange.endDate.toISOString())
        .order('created_at');

      if (error) {
        console.error('Error fetching trip trends:', error);
        return;
      }

      // Group by date
      const grouped = trips?.reduce((acc: any, trip) => {
        const date = format(new Date(trip.created_at), 'MMM dd');
        if (!acc[date]) {
          acc[date] = { date, trips: 0, revenue: 0 };
        }
        acc[date].trips++;
        const distance = (trip.end_km || 0) - (trip.start_km || 0);
        acc[date].revenue += distance * 10;
        return acc;
      }, {}) || {};

      const trendData = Object.values(grouped).slice(-7); // Last 7 days
      setChartData((prev: any) => ({
        ...prev,
        tripTrends: trendData
      }));

      return true;
    } catch (error) {
      console.error('Error in fetchTripTrends:', error);
      return false;
    }
  };

  const fetchVehicleUtilization = async () => {
    try {
      const { data: vehicles, error } = await supabase
        .from('vehicles')
        .select('id, registration_number')
        .eq('status', 'active')
        .limit(5);

      if (error) {
        console.error('Error fetching vehicles:', error);
        return;
      }

      const utilizationData = await Promise.all(
        (vehicles || []).map(async (vehicle) => {
          const { count } = await supabase
            .from('trips')
            .select('*', { count: 'exact', head: true })
            .eq('vehicle_id', vehicle.id)
            .gte('created_at', dateRange.startDate.toISOString())
            .lte('created_at', dateRange.endDate.toISOString());

          const utilization = Math.min(((count || 0) / 30) * 100, 100);
          return {
            vehicle: vehicle.registration_number.slice(0, 10),
            trips: count || 0,
            utilization: Math.round(utilization)
          };
        })
      );

      setChartData((prev: any) => ({
        ...prev,
        vehicleUtilization: utilizationData
      }));

      return true;
    } catch (error) {
      console.error('Error in fetchVehicleUtilization:', error);
      return false;
    }
  };

  const fetchDriverPerformance = async () => {
    try {
      const { data: drivers, error } = await supabase
        .from('drivers')
        .select('id, name')
        .eq('status', 'active')
        .limit(5);

      if (error) {
        console.error('Error fetching drivers:', error);
        return;
      }

      const performanceData = await Promise.all(
        (drivers || []).map(async (driver) => {
          const { data: trips } = await supabase
            .from('trips')
            .select('start_km, end_km, total_fuel_cost')
            .eq('driver_id', driver.id)
            .gte('created_at', dateRange.startDate.toISOString())
            .lte('created_at', dateRange.endDate.toISOString());

          const totalDistance = trips?.reduce((sum, trip) => 
            sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0) || 0;

          const efficiency = trips?.length ? totalDistance / trips.length : 0;

          return {
            driver: driver.name?.split(' ')[0] || 'Unknown',
            trips: trips?.length || 0,
            efficiency: Math.round(efficiency)
          };
        })
      );

      setChartData((prev: any) => ({
        ...prev,
        driverPerformance: performanceData
      }));

      return true;
    } catch (error) {
      console.error('Error in fetchDriverPerformance:', error);
      return false;
    }
  };

  const fetchExpenseBreakdown = async () => {
    try {
      const { data: trips, error } = await supabase
        .from('trips')
        .select('total_fuel_cost, total_road_expenses, driver_expense, breakdown_expense')
        .gte('created_at', dateRange.startDate.toISOString())
        .lte('created_at', dateRange.endDate.toISOString());

      if (error) {
        console.error('Error fetching expenses:', error);
        return;
      }

      const expenses = {
        Fuel: 0,
        Road: 0,
        Driver: 0,
        Breakdown: 0,
        Maintenance: metrics.maintenanceCosts
      };

      trips?.forEach(trip => {
        expenses.Fuel += trip.total_fuel_cost || 0;
        expenses.Road += trip.total_road_expenses || 0;
        expenses.Driver += trip.driver_expense || 0;
        expenses.Breakdown += trip.breakdown_expense || 0;
      });

      const expenseData = Object.entries(expenses).map(([category, amount]) => ({
        category,
        amount: Math.round(amount)
      })).filter(item => item.amount > 0);

      setChartData((prev: any) => ({
        ...prev,
        expenseBreakdown: expenseData
      }));

      return true;
    } catch (error) {
      console.error('Error in fetchExpenseBreakdown:', error);
      return false;
    }
  };

  const generatePDFReport = async (reportType: string) => {
    setGeneratingReport(reportType);
    
    try {
      const pdf = new jsPDF();
      const pageWidth = pdf.internal.pageSize.getWidth();
      
      // Add header
      pdf.setFontSize(20);
      pdf.setTextColor(31, 41, 55); // gray-800
      pdf.text('AVS Fleet Management Report', pageWidth / 2, 20, { align: 'center' });
      
      pdf.setFontSize(14);
      pdf.setTextColor(75, 85, 99); // gray-600
      pdf.text(reportType.replace(/-/g, ' ').toUpperCase(), pageWidth / 2, 30, { align: 'center' });
      
      pdf.setFontSize(10);
      pdf.text(`Generated on: ${format(new Date(), 'dd MMM yyyy, HH:mm')}`, pageWidth / 2, 38, { align: 'center' });
      pdf.text(`Period: ${format(dateRange.startDate, 'dd/MM/yyyy')} to ${format(dateRange.endDate, 'dd/MM/yyyy')}`, pageWidth / 2, 45, { align: 'center' });
      
      let yPosition = 60;

      switch (reportType) {
        case 'trip-summary': {
          const { data: trips } = await supabase
            .from('trips')
            .select(`
              *,
              vehicle:vehicles(registration_number),
              driver:drivers(name)
            `)
            .gte('created_at', dateRange.startDate.toISOString())
            .lte('created_at', dateRange.endDate.toISOString())
            .limit(100);

          // Summary section
          pdf.setFontSize(12);
          pdf.setTextColor(31, 41, 55);
          pdf.text('Summary', 14, yPosition);
          yPosition += 10;

          const totalTrips = trips?.length || 0;
          const totalDistance = trips?.reduce((sum, trip) => 
            sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0) || 0;
          const totalFuel = trips?.reduce((sum, trip) => 
            sum + (trip.total_fuel_cost || 0), 0) || 0;

          pdf.setFontSize(10);
          pdf.setTextColor(75, 85, 99);
          pdf.text(`Total Trips: ${totalTrips}`, 14, yPosition);
          yPosition += 6;
          pdf.text(`Total Distance: ${totalDistance.toFixed(0)} km`, 14, yPosition);
          yPosition += 6;
          pdf.text(`Total Fuel Cost: $${totalFuel.toFixed(2)}`, 14, yPosition);
          yPosition += 6;
          pdf.text(`Average Distance per Trip: ${totalTrips ? (totalDistance / totalTrips).toFixed(1) : 0} km`, 14, yPosition);
          yPosition += 15;

          // Table
          const tableData = trips?.map(trip => [
            format(new Date(trip.created_at), 'dd/MM/yyyy'),
            trip.vehicle?.registration_number || 'N/A',
            trip.driver?.name || 'N/A',
            `${trip.from_location || 'N/A'}  ${trip.to_location || 'N/A'}`,
            `${((trip.end_km || 0) - (trip.start_km || 0)).toFixed(0)} km`,
            `$${(trip.total_fuel_cost || 0).toFixed(2)}`
          ]) || [];

          if (tableData.length > 0) {
            pdf.autoTable({
              head: [['Date', 'Vehicle', 'Driver', 'Route', 'Distance', 'Fuel Cost']],
              body: tableData.slice(0, 20), // First 20 rows
              startY: yPosition,
              theme: 'grid',
              headStyles: { fillColor: [59, 130, 246] }, // blue-600
              styles: { fontSize: 8 }
            });
          }
          break;
        }

        case 'week-comparison':
        case 'month-comparison': {
          // Comparison logic
          let currentPeriodStart, currentPeriodEnd, previousPeriodStart, previousPeriodEnd;
          
          if (reportType === 'week-comparison') {
            currentPeriodStart = startOfWeek(new Date(), { weekStartsOn: 1 });
            currentPeriodEnd = endOfWeek(new Date(), { weekStartsOn: 1 });
            previousPeriodStart = startOfWeek(subDays(new Date(), 7), { weekStartsOn: 1 });
            previousPeriodEnd = endOfWeek(subDays(new Date(), 7), { weekStartsOn: 1 });
          } else {
            currentPeriodStart = startOfMonth(new Date());
            currentPeriodEnd = endOfMonth(new Date());
            previousPeriodStart = startOfMonth(subMonths(new Date(), 1));
            previousPeriodEnd = endOfMonth(subMonths(new Date(), 1));
          }

          const { data: currentTrips } = await supabase
            .from('trips')
            .select('*')
            .gte('created_at', currentPeriodStart.toISOString())
            .lte('created_at', currentPeriodEnd.toISOString());

          const { data: previousTrips } = await supabase
            .from('trips')
            .select('*')
            .gte('created_at', previousPeriodStart.toISOString())
            .lte('created_at', previousPeriodEnd.toISOString());

          const calculateMetrics = (trips: any[]) => {
            return {
              trips: trips?.length || 0,
              distance: trips?.reduce((sum, trip) => sum + ((trip.end_km || 0) - (trip.start_km || 0)), 0) || 0,
              fuel: trips?.reduce((sum, trip) => sum + (trip.total_fuel_cost || 0), 0) || 0,
              expenses: trips?.reduce((sum, trip) => sum + 
                (trip.total_fuel_cost || 0) + 
                (trip.total_road_expenses || 0) + 
                (trip.driver_expense || 0), 0) || 0
            };
          };

          const current = calculateMetrics(currentTrips || []);
          const previous = calculateMetrics(previousTrips || []);

          pdf.setFontSize(12);
          pdf.text('Comparison Analysis', 14, yPosition);
          yPosition += 10;

          const comparisonData = [
            ['Metric', reportType === 'week-comparison' ? 'This Week' : 'This Month', 
             reportType === 'week-comparison' ? 'Last Week' : 'Last Month', 'Change', '% Change'],
            ['Total Trips', current.trips.toString(), previous.trips.toString(), 
             (current.trips - previous.trips).toString(),
             previous.trips ? `${(((current.trips - previous.trips) / previous.trips) * 100).toFixed(1)}%` : 'N/A'],
            ['Distance (km)', current.distance.toFixed(0), previous.distance.toFixed(0),
             (current.distance - previous.distance).toFixed(0),
             previous.distance ? `${(((current.distance - previous.distance) / previous.distance) * 100).toFixed(1)}%` : 'N/A'],
            ['Fuel Cost', `$${current.fuel.toFixed(2)}`, `$${previous.fuel.toFixed(2)}`,
             `$${(current.fuel - previous.fuel).toFixed(2)}`,
             previous.fuel ? `${(((current.fuel - previous.fuel) / previous.fuel) * 100).toFixed(1)}%` : 'N/A'],
            ['Total Expenses', `$${current.expenses.toFixed(2)}`, `$${previous.expenses.toFixed(2)}`,
             `$${(current.expenses - previous.expenses).toFixed(2)}`,
             previous.expenses ? `${(((current.expenses - previous.expenses) / previous.expenses) * 100).toFixed(1)}%` : 'N/A']
          ];

          pdf.autoTable({
            head: [comparisonData[0]],
            body: comparisonData.slice(1),
            startY: yPosition,
            theme: 'striped',
            headStyles: { fillColor: [34, 197, 94] }, // green-500
            styles: { fontSize: 9 }
          });
          break;
        }

        default:
          pdf.text('Report generation in progress...', 14, yPosition);
      }

      // Add footer
      const pageCount = pdf.internal.pages.length - 1;
      for (let i = 1; i <= pageCount; i++) {
        pdf.setPage(i);
        pdf.setFontSize(8);
        pdf.setTextColor(156, 163, 175); // gray-400
        pdf.text(`Page ${i} of ${pageCount}`, pageWidth / 2, pdf.internal.pageSize.getHeight() - 10, { align: 'center' });
        pdf.text(' 2024 AVS - Auto Vital Solution', 14, pdf.internal.pageSize.getHeight() - 10);
      }

      // Save the PDF
      pdf.save(`AVS-${reportType}-${format(new Date(), 'yyyy-MM-dd')}.pdf`);
      
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Error generating report. Please try again.');
    } finally {
      setGeneratingReport(null);
    }
  };

  const reportTypes = [
    {
      id: 'week-comparison',
      name: 'Weekly Comparison',
      description: 'This week vs last week',
      icon: <TrendingUp className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'month-comparison',
      name: 'Monthly Comparison',
      description: 'Month-over-month analysis',
      icon: <BarChart3 className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'year-comparison',
      name: 'Yearly Comparison',
      description: 'Year-over-year analysis',
      icon: <FileSpreadsheet className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'trip-summary',
      name: 'Trip Summary',
      description: 'All trip details',
      icon: <Package className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'vehicle-utilization',
      name: 'Vehicle Utilization',
      description: 'Vehicle usage patterns',
      icon: <Truck className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'driver-performance',
      name: 'Driver Performance',
      description: 'Driver efficiency',
      icon: <Users className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'fuel-analysis',
      name: 'Fuel Analysis',
      description: 'Fuel usage & costs',
      icon: <Fuel className="h-4 w-4" />,
      category: 'financial'
    },
    {
      id: 'expense-report',
      name: 'Expense Report',
      description: 'All expenses',
      icon: <DollarSign className="h-4 w-4" />,
      category: 'financial'
    },
    {
      id: 'maintenance',
      name: 'Maintenance',
      description: 'Service schedules',
      icon: <Wrench className="h-4 w-4" />,
      category: 'maintenance'
    },
    {
      id: 'compliance',
      name: 'Compliance',
      description: 'Document validity',
      icon: <FileText className="h-4 w-4" />,
      category: 'compliance'
    }
  ];

  const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6'];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="p-4 sm:p-6 max-w-7xl mx-auto">
        {/* Header - AVS Style */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
            <div>
              <h1 className="text-2xl font-semibold text-gray-900 flex items-center">
                <Activity className="h-6 w-6 mr-2 text-primary-600" />
                Reporting & Analytics
              </h1>
              <p className="text-sm text-gray-500 mt-1">Visual insights and downloadable reports</p>
            </div>
            
            {/* Tab Switcher */}
            <div className="flex bg-gray-100 rounded-lg p-1">
              <button
                onClick={() => setActiveTab('dashboard')}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${
                  activeTab === 'dashboard' 
                    ? 'bg-primary-600 text-white shadow-sm' 
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <Eye className="h-4 w-4 inline mr-2" />
                Visual Dashboard
              </button>
              <button
                onClick={() => setActiveTab('reports')}
                className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${
                  activeTab === 'reports' 
                    ? 'bg-primary-600 text-white shadow-sm' 
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <Download className="h-4 w-4 inline mr-2" />
                Download Reports
              </button>
            </div>
          </div>
        </div>

        {/* Date Range Selector */}
        <div className="bg-white rounded-lg shadow-sm p-4 mb-6">
          <div className="flex flex-wrap items-center gap-4">
            <Calendar className="h-5 w-5 text-gray-400" />
            <select
              value={selectedDateRange}
              onChange={(e) => updateDateRange(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
            >
              <option value="today">Today</option>
              <option value="yesterday">Yesterday</option>
              <option value="thisWeek">This Week</option>
              <option value="lastWeek">Last Week</option>
              <option value="thisMonth">This Month</option>
              <option value="lastMonth">Last Month</option>
              <option value="last30Days">Last 30 Days</option>
              <option value="custom">Custom Range</option>
            </select>
            
            {selectedDateRange === 'custom' && (
              <div className="flex items-center gap-2">
                <input
                  type="date"
                  value={customStartDate}
                  onChange={(e) => {
                    setCustomStartDate(e.target.value);
                    if (e.target.value && customEndDate) {
                      updateDateRange('custom');
                    }
                  }}
                  className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                />
                <span className="text-gray-500">to</span>
                <input
                  type="date"
                  value={customEndDate}
                  onChange={(e) => {
                    setCustomEndDate(e.target.value);
                    if (customStartDate && e.target.value) {
                      updateDateRange('custom');
                    }
                  }}
                  className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                />
              </div>
            )}
            
            {activeTab === 'dashboard' && (
              <button
                onClick={fetchDashboardData}
                disabled={loading}
                className="ml-auto px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50 transition-colors flex items-center gap-2 text-sm font-medium"
              >
                <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
                Refresh
              </button>
            )}
          </div>
        </div>

        {/* Visual Dashboard Tab */}
        {activeTab === 'dashboard' && (
          <>
            {/* Metrics Grid */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
              <div className="bg-white rounded-lg shadow-sm p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Total Revenue</p>
                    <p className="mt-1 text-2xl font-semibold text-gray-900">
                      ${metrics.totalRevenue.toLocaleString()}
                    </p>
                  </div>
                  <div className="p-3 bg-green-100 rounded-lg">
                    <DollarSign className="h-6 w-6 text-green-600" />
                  </div>
                </div>
              </div>
              
              <div className="bg-white rounded-lg shadow-sm p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Total Trips</p>
                    <p className="mt-1 text-2xl font-semibold text-gray-900">{metrics.totalTrips}</p>
                  </div>
                  <div className="p-3 bg-blue-100 rounded-lg">
                    <Package className="h-6 w-6 text-blue-600" />
                  </div>
                </div>
              </div>
              
              <div className="bg-white rounded-lg shadow-sm p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Active Vehicles</p>
                    <p className="mt-1 text-2xl font-semibold text-gray-900">{metrics.activeVehicles}</p>
                  </div>
                  <div className="p-3 bg-purple-100 rounded-lg">
                    <Truck className="h-6 w-6 text-purple-600" />
                  </div>
                </div>
              </div>
              
              <div className="bg-white rounded-lg shadow-sm p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs font-medium text-gray-500 uppercase tracking-wider">Active Drivers</p>
                    <p className="mt-1 text-2xl font-semibold text-gray-900">{metrics.activeDrivers}</p>
                  </div>
                  <div className="p-3 bg-orange-100 rounded-lg">
                    <Users className="h-6 w-6 text-orange-600" />
                  </div>
                </div>
              </div>
            </div>

            {/* Charts Grid */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Trip Trends */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Trip Trends</h3>
                {chartData.tripTrends.length > 0 ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <LineChart data={chartData.tripTrends}>
                      <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                      <XAxis dataKey="date" stroke="#6B7280" fontSize={12} />
                      <YAxis stroke="#6B7280" fontSize={12} />
                      <Tooltip />
                      <Legend />
                      <Line type="monotone" dataKey="trips" stroke="#3B82F6" name="Trips" strokeWidth={2} />
                      <Line type="monotone" dataKey="revenue" stroke="#10B981" name="Revenue" strokeWidth={2} />
                    </LineChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="h-[300px] flex items-center justify-center text-gray-500">
                    No data available for the selected period
                  </div>
                )}
              </div>

              {/* Vehicle Utilization */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Vehicle Utilization</h3>
                {chartData.vehicleUtilization.length > 0 ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={chartData.vehicleUtilization}>
                      <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                      <XAxis dataKey="vehicle" stroke="#6B7280" fontSize={12} />
                      <YAxis stroke="#6B7280" fontSize={12} />
                      <Tooltip formatter={(value: any) => `${value}%`} />
                      <Bar dataKey="utilization" fill="#3B82F6" />
                    </BarChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="h-[300px] flex items-center justify-center text-gray-500">
                    No vehicle data available
                  </div>
                )}
              </div>

              {/* Driver Performance */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Top Driver Performance</h3>
                {chartData.driverPerformance.length > 0 ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={chartData.driverPerformance}>
                      <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                      <XAxis dataKey="driver" stroke="#6B7280" fontSize={12} />
                      <YAxis stroke="#6B7280" fontSize={12} />
                      <Tooltip />
                      <Bar dataKey="efficiency" fill="#10B981" />
                    </BarChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="h-[300px] flex items-center justify-center text-gray-500">
                    No driver data available
                  </div>
                )}
              </div>

              {/* Expense Breakdown */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Expense Breakdown</h3>
                {chartData.expenseBreakdown.length > 0 ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <PieChart>
                      <Pie
                        data={chartData.expenseBreakdown}
                        cx="50%"
                        cy="50%"
                        labelLine={false}
                        label={(entry: any) => `${entry.category}: $${entry.amount}`}
                        outerRadius={80}
                        fill="#8884d8"
                        dataKey="amount"
                      >
                        {chartData.expenseBreakdown.map((entry: any, index: number) => (
                          <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip formatter={(value: any) => `$${value}`} />
                    </PieChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="h-[300px] flex items-center justify-center text-gray-500">
                    No expense data available
                  </div>
                )}
              </div>
            </div>
          </>
        )}

        {/* Download Reports Tab */}
        {activeTab === 'reports' && (
          <div>
            {/* Quick Downloads */}
            <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Downloads</h3>
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3">
                {reportTypes.slice(0, showAllReports ? reportTypes.length : 5).map((report) => (
                  <button
                    key={report.id}
                    onClick={() => generatePDFReport(report.id)}
                    disabled={generatingReport === report.id}
                    className="flex flex-col items-center p-4 bg-gray-50 hover:bg-gray-100 rounded-lg transition-all hover:shadow-md disabled:opacity-50"
                  >
                    <div className={`p-3 rounded-lg mb-2 ${
                      report.category === 'comparison' ? 'bg-green-100 text-green-600' :
                      report.category === 'financial' ? 'bg-blue-100 text-blue-600' :
                      report.category === 'operations' ? 'bg-purple-100 text-purple-600' :
                      report.category === 'maintenance' ? 'bg-orange-100 text-orange-600' :
                      'bg-gray-100 text-gray-600'
                    }`}>
                      {generatingReport === report.id ? (
                        <RefreshCw className="h-5 w-5 animate-spin" />
                      ) : (
                        report.icon
                      )}
                    </div>
                    <span className="text-sm font-medium text-gray-900">{report.name}</span>
                    <span className="text-xs text-gray-500 mt-1 text-center">{report.description}</span>
                  </button>
                ))}
              </div>
              
              {!showAllReports && reportTypes.length > 5 && (
                <button
                  onClick={() => setShowAllReports(true)}
                  className="mt-4 flex items-center justify-center w-full py-2 text-primary-600 hover:text-primary-700 font-medium"
                >
                  <ChevronDown className="h-4 w-4 mr-1" />
                  Show All Reports ({reportTypes.length - 5} more)
                </button>
              )}
              
              {showAllReports && (
                <button
                  onClick={() => setShowAllReports(false)}
                  className="mt-4 flex items-center justify-center w-full py-2 text-primary-600 hover:text-primary-700 font-medium"
                >
                  <ChevronUp className="h-4 w-4 mr-1" />
                  Show Less
                </button>
              )}
            </div>

            {/* Categorized Reports */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {/* Smart Comparisons */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                  <TrendingUp className="h-5 w-5 mr-2 text-green-600" />
                  Smart Comparisons
                </h3>
                <div className="space-y-2">
                  {reportTypes.filter(r => r.category === 'comparison').map(report => (
                    <button
                      key={report.id}
                      onClick={() => generatePDFReport(report.id)}
                      disabled={generatingReport === report.id}
                      className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center group"
                    >
                      <span className="text-sm text-gray-700">{report.name}</span>
                      {generatingReport === report.id ? (
                        <RefreshCw className="h-4 w-4 animate-spin text-primary-600" />
                      ) : (
                        <Download className="h-4 w-4 text-gray-400 group-hover:text-primary-600" />
                      )}
                    </button>
                  ))}
                </div>
              </div>

              {/* Financial Reports */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                  <DollarSign className="h-5 w-5 mr-2 text-blue-600" />
                  Financial Reports
                </h3>
                <div className="space-y-2">
                  {reportTypes.filter(r => r.category === 'financial').map(report => (
                    <button
                      key={report.id}
                      onClick={() => generatePDFReport(report.id)}
                      disabled={generatingReport === report.id}
                      className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center group"
                    >
                      <span className="text-sm text-gray-700">{report.name}</span>
                      {generatingReport === report.id ? (
                        <RefreshCw className="h-4 w-4 animate-spin text-primary-600" />
                      ) : (
                        <Download className="h-4 w-4 text-gray-400 group-hover:text-primary-600" />
                      )}
                    </button>
                  ))}
                </div>
              </div>

              {/* Operations Reports */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                  <Package className="h-5 w-5 mr-2 text-purple-600" />
                  Operations Reports
                </h3>
                <div className="space-y-2">
                  {reportTypes.filter(r => r.category === 'operations').map(report => (
                    <button
                      key={report.id}
                      onClick={() => generatePDFReport(report.id)}
                      disabled={generatingReport === report.id}
                      className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center group"
                    >
                      <span className="text-sm text-gray-700">{report.name}</span>
                      {generatingReport === report.id ? (
                        <RefreshCw className="h-4 w-4 animate-spin text-primary-600" />
                      ) : (
                        <Download className="h-4 w-4 text-gray-400 group-hover:text-primary-600" />
                      )}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FixedUnifiedReportingDashboard;

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../../components/layout/Layout'; 
import { MapPin, Building2, ChevronLeft, Plus, Package, Settings, Loader, Edit, Trash2 } from 'lucide-react';
import { Archive, ArchiveRestore } from 'lucide-react';
import Button from '../../components/ui/Button';
import WarehouseForm from '../../components/admin/WarehouseForm';
import DestinationForm from '../../components/admin/DestinationForm';
import ConfirmationModal from '../../components/admin/ConfirmationModal';
import { toast } from 'react-toastify';
import MaterialTypeManager from '../../components/admin/MaterialTypeManager';
import { getDestinations, createDestination, hardDeleteDestination } from '../../utils/storage';
import { getDestinationsWithAnalytics, DestinationUsageStats, PaginatedDestinations, DestinationFilters } from '../../utils/destinationAnalytics';
import { listWarehouses, createWarehouse, updateWarehouse, deleteWarehouse, hardDeleteWarehouse, archiveWarehouse, restoreWarehouse } from '../../utils/warehouseService';
import { getMaterialTypes, MaterialType } from '../../utils/materialTypes'; // Added MaterialType import
import { Warehouse, Destination } from '@/types'; // Added Warehouse and Destination imports
import Checkbox from '../../components/ui/Checkbox'; // Import Checkbox

const TripLocationsPage: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<'warehouses' | 'destinations'>('warehouses');
  const [isAddingWarehouse, setIsAddingWarehouse] = useState(false);
  const [isAddingDestination, setIsAddingDestination] = useState(false);
  const [editingWarehouse, setEditingWarehouse] = useState<Warehouse | null>(null);
  const [deletingWarehouse, setDeletingWarehouse] = useState<Warehouse | null>(null); // Used for archive confirmation
  const [hardDeletingWarehouse, setHardDeletingWarehouse] = useState<Warehouse | null>(null); // New state for hard delete confirmation
  const [deletingDestination, setDeletingDestination] = useState<Destination | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isManagingMaterialTypes, setIsManagingMaterialTypes] = useState(false);
  const [warehouses, setWarehouses] = useState<Warehouse[]>([]);
  const [destinations, setDestinations] = useState<Destination[]>([]);
  const [destinationsWithAnalytics, setDestinationsWithAnalytics] = useState<PaginatedDestinations | null>(null);
  const [materialTypes, setMaterialTypes] = useState<MaterialType[]>([]);
  const [loading, setLoading] = useState(true);
  const [showInactive, setShowInactive] = useState(false); // New state for showing inactive warehouses
  
  // Destination pagination and filtering
  const [currentPage, setCurrentPage] = useState(1);
  const [destinationsPerPage] = useState(25);
  const [destinationFilters, setDestinationFilters] = useState<DestinationFilters>({
    search: '',
    sortBy: 'usage_count',
    sortOrder: 'desc',
    active: true
  });

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [warehousesData, destinationsData, destinationsAnalyticsData, materialTypesData] = await Promise.all([
          listWarehouses({ includeInactive: showInactive }), // Use showInactive filter
          getDestinations(),
          getDestinationsWithAnalytics(currentPage, destinationsPerPage, destinationFilters),
          getMaterialTypes()
        ]);
        
        setWarehouses(Array.isArray(warehousesData) ? warehousesData : []);
        setDestinations(Array.isArray(destinationsData) ? destinationsData : []);
        setDestinationsWithAnalytics(destinationsAnalyticsData);
        setMaterialTypes(Array.isArray(materialTypesData) ? materialTypesData : []);
      } catch (error) {
        console.error('Error fetching location data:', error);
        toast.error('Failed to load location data');
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, [showInactive, currentPage, destinationFilters]); // Add pagination and filter dependencies

  // Separate effect for fetching destinations analytics when filters change
  useEffect(() => {
    const fetchDestinationsAnalytics = async () => {
      if (activeTab === 'destinations') {
        try {
          const analyticsData = await getDestinationsWithAnalytics(currentPage, destinationsPerPage, destinationFilters);
          setDestinationsWithAnalytics(analyticsData);
        } catch (error) {
          console.error('Error fetching destinations analytics:', error);
        }
      }
    };
    
    fetchDestinationsAnalytics();
  }, [activeTab, currentPage, destinationFilters, destinationsPerPage]);

  const handleAddWarehouse = async (data: any) => {
    setIsSubmitting(true);
    try {
      const newWarehouse = await createWarehouse(data);
      
      setWarehouses(prev => [...prev, newWarehouse]);
      setIsAddingWarehouse(false);
      toast.success('Warehouse added successfully');
    } catch (error) {
      console.error('Error adding warehouse:', error);
      toast.error('Failed to add warehouse');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleUpdateWarehouse = async (data: any) => {
    if (!editingWarehouse) return;
    
    setIsSubmitting(true);
    try {
      const updatedWarehouse = await updateWarehouse(editingWarehouse.id, data);
      
      setWarehouses(prev => 
        prev.map(w => w.id === editingWarehouse.id ? updatedWarehouse : w)
      );
      setEditingWarehouse(null);
      toast.success('Warehouse updated successfully');
    } catch (error) {
      console.error('Error updating warehouse:', error);
      toast.error('Failed to update warehouse');
    } finally {
      setIsSubmitting(false);
    }
  };

  // New function for soft-delete (archive)
  const handleArchiveWarehouse = async () => {
    if (!deletingWarehouse) return;
    setIsSubmitting(true);
    try {
      await archiveWarehouse(deletingWarehouse.id);
      setWarehouses(prev => prev.map(w => w.id === deletingWarehouse.id ? { ...w, is_active: false } : w));
      setDeletingWarehouse(null);
      toast.success('Warehouse archived successfully');
    } catch (error) {
      console.error('Error archiving warehouse:', error);
      toast.error('Failed to archive warehouse');
    } finally {
      setIsSubmitting(false);
    }
  };

  // New function for restoring soft-deleted
  const handleRestoreWarehouse = async (warehouseId: string) => {
    setIsSubmitting(true);
    try {
      await restoreWarehouse(warehouseId);
      setWarehouses(prev => prev.map(w => w.id === warehouseId ? { ...w, is_active: true } : w));
      toast.success('Warehouse restored successfully');
    } catch (error) {
      console.error('Error restoring warehouse:', error);
      toast.error('Failed to restore warehouse');
    } finally {
      setIsSubmitting(false);
    }
  };

  // New function for hard delete (admin maintenance only)
  const handleHardDeleteWarehouse = async () => {
    if (!hardDeletingWarehouse) return;
    setIsSubmitting(true);
    try {
      await hardDeleteWarehouse(hardDeletingWarehouse.id);
      setWarehouses(prev => prev.filter(w => w.id !== hardDeletingWarehouse.id));
      setHardDeletingWarehouse(null);
      toast.success('Warehouse permanently deleted');
    } catch (error) {
      console.error('Error hard deleting warehouse:', error);
      const errorMessage = (error as any)?.message || '';
      if (errorMessage.includes('23503') || errorMessage.includes('foreign key constraint')) {
        toast.error('Cannot delete warehouse: It is linked to existing trips. Consider archiving instead.');
      } else {
        toast.error('Failed to permanently delete warehouse');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleAddDestination = async (data: any) => {
    try {
      const destinationDataForSupabase = {
        ...data,
        historical_deviation: data.historical_deviation === undefined ? 0 : data.historical_deviation,
        active: data.active === undefined ? true : data.active,
      };
      const newDestination = await createDestination(destinationDataForSupabase);
      if (!newDestination) {
        throw new Error('Failed to create destination');
      }
      
      // Fetch updated destination list to ensure UI is in sync with DB
      const updatedDestinations = await getDestinations();
      setDestinations(Array.isArray(updatedDestinations) ? updatedDestinations : []);
      setIsAddingDestination(false);
      toast.success('Destination added successfully');
    } catch (error) {
      console.error('Error adding destination:', error);
      toast.error('Failed to add destination. Please try again.');
    }
  };

  return (
    <Layout>
      {/* Page Header */}
      <div className="rounded-xl border bg-white dark:bg-white px-4 py-3 shadow-sm mb-6">
        <div className="flex items-center group">
          <MapPin className="h-5 w-5 mr-2 text-gray-500 dark:text-gray-400 group-hover:text-primary-600 transition" />
          <h1 className="text-2xl font-semibold text-gray-900 dark:text-gray-100">Trip Locations</h1>
        </div>
        <p className="text-sm text-gray-500 dark:text-gray-400 mt-1 ml-7">Manage warehouses and delivery destinations</p>
        <div className="mt-4 flex flex-wrap gap-2">
          <Button
            variant="outline"
            onClick={() => navigate('/admin')}
            icon={<ChevronLeft className="h-4 w-4" />}
          >
            Back to Admin
          </Button>
        </div>
      </div>

      <div className="space-y-6">
        {/* Tabs */}
        <div className="bg-white rounded-lg shadow-sm">
          <div className="border-b border-gray-200 flex justify-between items-center">
            <div className="flex space-x-4 p-4">
              <button
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'warehouses'
                    ? 'bg-primary-50 text-primary-700 font-medium'
                    : 'text-gray-600 hover:bg-gray-50'
                }`}
                onClick={() => setActiveTab('warehouses')}
              >
                <Building2 className="h-5 w-5" />
                <span>Warehouses</span>
              </button>
              <button
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'destinations'
                    ? 'bg-primary-50 text-primary-700 font-medium'
                    : 'text-gray-600 hover:bg-gray-50'
                }`}
                onClick={() => setActiveTab('destinations')}
              >
                <MapPin className="h-5 w-5" />
                <span>Destinations</span>
              </button>
            </div>
            <div className="p-4">
              <Button
                variant="outline"
                inputSize="sm"
                onClick={() => setIsManagingMaterialTypes(true)}
                icon={<Settings className="h-4 w-4" />}
              >
                Manage Material Types
              </Button>
            </div>
          </div>

          <div className="p-6">
            {activeTab === 'warehouses' ? (
              <div className="space-y-6">
                {loading ? (
                  <div className="flex justify-center items-center h-64">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
                    <p className="ml-3 text-gray-600">Loading warehouses...</p>
                  </div>
                ) : (
                  <>
                    <div className="flex justify-between items-center">
                      <div className="border-l-2 border-blue-500 pl-2">
                        <h2 className="text-lg font-medium text-gray-900">Origin Warehouses</h2>
                        <p className="text-sm text-gray-500">Manage warehouse locations</p>
                      </div>
                      <div className="flex items-center gap-3">
                        <Checkbox
                          label="Show Inactive"
                          checked={showInactive}
                          onChange={(e) => setShowInactive(e.target.checked)}
                        />
                        {!editingWarehouse && (
                          <Button
                            onClick={() => setIsAddingWarehouse(true)}
                            icon={<Plus className="h-4 w-4" />}
                          >
                            Add Warehouse
                          </Button>
                        )}
                      </div>
                    </div>

                    {(isAddingWarehouse || editingWarehouse) ? (
                      <WarehouseForm
                        initialData={editingWarehouse || undefined}
                        onSubmit={editingWarehouse ? handleUpdateWarehouse : handleAddWarehouse}
                        onCancel={() => {
                          setIsAddingWarehouse(false);
                          setEditingWarehouse(null);
                        }}
                        isSubmitting={isSubmitting}
                      />
                    ) : (
                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {warehouses.length === 0 ? (
                          <div className="col-span-3 text-center py-8 text-gray-500 bg-gray-50 rounded-lg border border-gray-200">
                            No warehouses found. Add your first warehouse to get started.
                          </div>
                        ) : (
                          warehouses.map(warehouse => (
                            <div
                              key={warehouse.id}
                              className={`bg-white rounded-lg border p-4 hover:shadow-md transition-shadow relative ${warehouse.is_active ? '' : 'opacity-50'}`}
                            >
                              {/* Action buttons */}
                              <div className="absolute top-2 right-2 flex space-x-1">
                                <button
                                  onClick={() => setEditingWarehouse(warehouse)}
                                  className="p-1 text-gray-400 hover:text-primary-600 rounded"
                                  title="Edit warehouse"
                                >
                                  <Edit className="h-4 w-4" />
                                </button>
                                {warehouse.is_active ? (
                                  <button
                                    onClick={() => setDeletingWarehouse(warehouse)} // Use setDeletingWarehouse for archive
                                    className="p-1 text-gray-400 hover:text-warning-600 rounded"
                                    title="Archive warehouse"
                                  >
                                    <Archive className="h-4 w-4" />
                                  </button>
                                ) : (
                                  <button
                                    onClick={() => handleRestoreWarehouse(warehouse.id)} // Direct call for restore
                                    className="p-1 text-gray-400 hover:text-success-600 rounded"
                                    title="Restore warehouse"
                                  >
                                    <ArchiveRestore className="h-4 w-4" />
                                  </button>
                                )}
                                {/* Hard delete option */}
                                <button
                                  onClick={() => setHardDeletingWarehouse(warehouse)} // New state for hard delete
                                  className="p-1 text-gray-400 hover:text-error-600 rounded"
                                  title="Permanently delete warehouse (admin only)"
                                >
                                  <Trash2 className="h-4 w-4" />
                                </button>
                              </div>

                              {/* Inactive badge */}
                              {!warehouse.is_active && (
                                <span className="absolute top-2 left-2 px-2 py-1 text-xs font-medium bg-gray-100 text-gray-600 rounded-full">
                                  Inactive
                                </span>
                              )}
                              
                              <div className="flex items-center space-x-3">
                                <Building2 className="h-5 w-5 text-gray-400" />
                                <div>
                                  <h3 className="font-medium text-gray-900">{warehouse.name}</h3>
                                  <div className="flex items-center space-x-2">
                                    <p className="text-sm text-gray-500">{warehouse.pincode}</p>
                                  </div>
                                  <div className="flex flex-wrap gap-1 mt-1">
                                    {warehouse.material_type_ids.map(typeId => (
                                      <span key={typeId} className="px-2 py-0.5 bg-primary-100 text-primary-700 text-xs rounded-full">
                                        {materialTypes.find(t => t.id === typeId)?.name || 'Unknown'}
                                      </span>
                                    ))}
                                  </div>
                                </div>
                              </div>
                              {warehouse.latitude !== undefined && warehouse.longitude !== undefined && 
                               warehouse.latitude !== null && warehouse.longitude !== null &&
                               warehouse.latitude !== 0 && warehouse.longitude !== 0 ? (
                                <div className="mt-3 text-sm text-gray-500">
                                  Coordinates: {warehouse.latitude.toFixed(6)}, {warehouse.longitude.toFixed(6)}
                                </div>
                              ) : (
                                <div className="mt-3 text-sm text-red-500">
                                  Coordinates: Invalid or missing
                                </div>
                              )}
                            </div>
                          ))
                        )}
                      </div>
                    )}
                  </>
                )}
              </div>
            ) : (
              <div className="space-y-6">
                {loading ? (
                  <div className="flex justify-center items-center h-64">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
                    <p className="ml-3 text-gray-600">Loading destinations...</p>
                  </div>
                ) : (
                  <>
                    <div className="flex justify-between items-center">
                      <div className="border-l-2 border-blue-500 pl-2">
                        <h2 className="text-lg font-medium text-gray-900">Delivery Destinations</h2>
                        <p className="text-sm text-gray-500">Manage delivery points</p>
                      </div>
                      <Button
                        onClick={() => setIsAddingDestination(true)}
                        icon={<Plus className="h-4 w-4" />}
                      >
                        Add Destination
                      </Button>
                    </div>

                    {isAddingDestination ? (
                      <DestinationForm
                        onSubmit={handleAddDestination}
                        onCancel={() => setIsAddingDestination(false)}
                      />
                    ) : (
                      <>
                        {/* Search and Filter Controls */}
                        <div className="mb-4 flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
                          <div className="flex flex-col sm:flex-row gap-2">
                            <input
                              type="text"
                              placeholder="Search destinations..."
                              value={destinationFilters.search || ''}
                              onChange={(e) => setDestinationFilters(prev => ({ ...prev, search: e.target.value }))}
                              className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                            />
                            <select
                              value={destinationFilters.sortBy || 'usage_count'}
                              onChange={(e) => setDestinationFilters(prev => ({ ...prev, sortBy: e.target.value as any }))}
                              className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                            >
                              <option value="name">Sort by Name</option>
                              <option value="usage_count">Sort by Usage</option>
                              <option value="last_used">Sort by Last Used</option>
                              <option value="created_at">Sort by Created</option>
                            </select>
                            <select
                              value={destinationFilters.sortOrder || 'desc'}
                              onChange={(e) => setDestinationFilters(prev => ({ ...prev, sortOrder: e.target.value as any }))}
                              className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                            >
                              <option value="desc">Descending</option>
                              <option value="asc">Ascending</option>
                            </select>
                          </div>
                          <div className="text-sm text-gray-500">
                            {destinationsWithAnalytics ? (
                              `Showing ${destinationsWithAnalytics.destinations.length} of ${destinationsWithAnalytics.totalCount} destinations`
                            ) : (
                              `Total: ${destinations.length} destinations`
                            )}
                          </div>
                        </div>

                        {/* Destinations Grid */}
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                          {destinationsWithAnalytics?.destinations.length === 0 ? (
                            <div className="col-span-3 text-center py-8 text-gray-500 bg-gray-50 rounded-lg border border-gray-200">
                              No destinations found. Add your first destination to get started.
                            </div>
                          ) : (
                            destinationsWithAnalytics?.destinations.map(destination => (
                              <div
                                key={destination.id}
                                className="bg-white rounded-lg border p-4 hover:shadow-md transition-shadow relative"
                              >
                                {/* Action buttons */}
                                <div className="absolute top-2 right-2 flex space-x-1">
                                  <button
                                    onClick={() => setDeletingDestination(destination)}
                                    className="p-1 text-gray-400 hover:text-error-600 rounded"
                                    title="Delete destination"
                                  >
                                    <Trash2 className="h-4 w-4" />
                                  </button>
                                </div>
                                
                                <div className="flex items-center space-x-3">
                                  <MapPin className="h-5 w-5 text-gray-400" />
                                  <div className="flex-1">
                                    <h3 className="font-medium text-gray-900">{destination.name}</h3>
                                    <div className="flex items-center space-x-2 text-sm text-gray-500">
                                      <span>{destination.standard_distance} km</span>
                                      <span></span>
                                      <span>{destination.estimated_time}</span>
                                    </div>
                                    {/* Usage Analytics */}
                                    <div className="mt-2 flex items-center space-x-4 text-xs text-gray-400">
                                      <span className="flex items-center">
                                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-1"></span>
                                        Used {destination.usage_count} times
                                      </span>
                                      {destination.last_used && (
                                        <span>
                                          Last: {new Date(destination.last_used).toLocaleDateString()}
                                        </span>
                                      )}
                                    </div>
                                  </div>
                                </div>
                                {destination.latitude !== null && destination.longitude !== null && 
                                 destination.latitude !== 0 && destination.longitude !== 0 ? (
                                  <div className="mt-3 text-sm text-gray-500">
                                    Coordinates: {destination.latitude.toFixed(6)}, {destination.longitude.toFixed(6)}
                                  </div>
                                ) : (
                                  <div className="mt-3 text-sm text-red-500">
                                    Coordinates: Invalid or missing
                                  </div>
                                )}
                              </div>
                            ))
                          )}
                        </div>

                        {/* Pagination */}
                        {destinationsWithAnalytics && destinationsWithAnalytics.totalPages > 1 && (
                          <div className="mt-6 flex items-center justify-between">
                            <div className="text-sm text-gray-500">
                              Page {destinationsWithAnalytics.currentPage} of {destinationsWithAnalytics.totalPages}
                            </div>
                            <div className="flex space-x-2">
                              <button
                                onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                                disabled={!destinationsWithAnalytics.hasPreviousPage}
                                className="px-3 py-1 text-sm border border-gray-300 rounded-md disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                              >
                                Previous
                              </button>
                              <button
                                onClick={() => setCurrentPage(prev => Math.min(destinationsWithAnalytics.totalPages, prev + 1))}
                                disabled={!destinationsWithAnalytics.hasNextPage}
                                className="px-3 py-1 text-sm border border-gray-300 rounded-md disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                              >
                                Next
                              </button>
                            </div>
                          </div>
                        )}
                      </>
                    )}
                  </>
                )}
              </div>
            )}
          </div>
        </div>
        
        {/* Destination Delete Confirmation Modal */}
        <ConfirmationModal
          isOpen={!!deletingDestination}
          title="Delete Destination"
          message={`Are you sure you want to delete "${deletingDestination?.name}"? This action cannot be undone.`}
          confirmText="Delete"
          cancelText="Cancel"
          type="delete"
          isLoading={isSubmitting}
          onCancel={() => setDeletingDestination(null)}
          onConfirm={async () => {
            if (!deletingDestination) return;
            setIsSubmitting(true);
            try {
              await hardDeleteDestination(deletingDestination.id);
              setDestinations(prev => prev.filter(d => d.id !== deletingDestination.id));
              toast.success('Destination deleted successfully');
              setDeletingDestination(null);
            } catch (err: any) {
              const msg = String(err?.message || '');
              if (msg.includes('23503') || msg.toLowerCase().includes('foreign key')) {
                toast.error('Cannot delete destination: it is linked to existing trips.');
              } else {
                toast.error('Failed to delete destination.');
              }
            } finally {
              setIsSubmitting(false);
            }
          }}
        />
        
        {/* Archive Confirmation Modal */}
        <ConfirmationModal
          isOpen={!!deletingWarehouse}
          title={deletingWarehouse?.is_active ? "Archive Warehouse" : "Restore Warehouse"}
          message={deletingWarehouse?.is_active ?
            `Are you sure you want to archive warehouse "${deletingWarehouse?.name}"? It will be hidden from most views but can be restored later.` :
            `Are you sure you want to restore warehouse "${deletingWarehouse?.name}"? It will become active again.`
          }
          confirmText={deletingWarehouse?.is_active ? "Archive" : "Restore"}
          cancelText="Cancel"
          onConfirm={deletingWarehouse?.is_active ? handleArchiveWarehouse : () => handleRestoreWarehouse(deletingWarehouse!.id)}
          onCancel={() => setDeletingWarehouse(null)}
          type={deletingWarehouse?.is_active ? "archive" : "info"}
          isLoading={isSubmitting}
        />
        
        {/* Hard Delete Confirmation Modal */}
        <ConfirmationModal
          isOpen={!!hardDeletingWarehouse}
          title="Permanently Delete Warehouse"
          message={`WARNING: Are you sure you want to permanently delete warehouse "${hardDeletingWarehouse?.name}"? This action cannot be undone and may break existing trip records if this warehouse is referenced.`}
          confirmText="Delete Permanently"
          cancelText="Cancel"
          onConfirm={handleHardDeleteWarehouse}
          onCancel={() => setHardDeletingWarehouse(null)}
          type="delete"
          isLoading={isSubmitting}
        />
        
        {isManagingMaterialTypes && (
          <MaterialTypeManager onClose={() => setIsManagingMaterialTypes(false)} />
        )}
      </div>
    </Layout>
  );
};

export default TripLocationsPage;

import React, { useState, useEffect } from 'react';
import {
  BarChart,
  Bar,
  LineChart,
  Line,
  PieChart,
  Pie,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';
import {
  Calendar,
  TrendingUp,
  Users,
  Truck,
  DollarSign,
  Package,
  AlertCircle,
  Download,
  Filter,
  RefreshCw,
  FileText,
  Fuel,
  MapPin,
  Clock,
  Wrench,
  BarChart3,
  PieChart as PieChartIcon,
  FileSpreadsheet,
  Eye,
  ChevronDown,
  ChevronUp
} from 'lucide-react';
import { supabase } from '../../utils/supabaseClient';
import { format, startOfDay, endOfDay, startOfWeek, endOfWeek, startOfMonth, endOfMonth, subDays, subMonths } from 'date-fns';

interface ReportMetrics {
  totalRevenue: number;
  totalTrips: number;
  activeVehicles: number;
  activeDrivers: number;
  avgTripDistance: number;
  avgFuelEfficiency: number;
  maintenanceCosts: number;
  pendingMaintenance: number;
}

interface ChartData {
  tripTrends: Array<{ date: string; trips: number; revenue: number }>;
  vehicleUtilization: Array<{ vehicle: string; trips: number; utilization: number }>;
  driverPerformance: Array<{ driver: string; trips: number; efficiency: number }>;
  expenseBreakdown: Array<{ category: string; amount: number }>;
}

interface ReportType {
  id: string;
  name: string;
  description: string;
  icon: React.ReactNode;
  category: 'operations' | 'financial' | 'maintenance' | 'compliance' | 'comparison';
}

const UnifiedReportingDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'dashboard' | 'reports'>('dashboard');
  const [metrics, setMetrics] = useState<ReportMetrics>({
    totalRevenue: 0,
    totalTrips: 0,
    activeVehicles: 0,
    activeDrivers: 0,
    avgTripDistance: 0,
    avgFuelEfficiency: 0,
    maintenanceCosts: 0,
    pendingMaintenance: 0
  });

  const [chartData, setChartData] = useState<ChartData>({
    tripTrends: [],
    vehicleUtilization: [],
    driverPerformance: [],
    expenseBreakdown: []
  });

  const [dateRange, setDateRange] = useState({
    startDate: startOfMonth(new Date()),
    endDate: endOfMonth(new Date())
  });

  const [selectedDateRange, setSelectedDateRange] = useState('thisMonth');
  const [customStartDate, setCustomStartDate] = useState('');
  const [customEndDate, setCustomEndDate] = useState('');
  const [loading, setLoading] = useState(true);
  const [generatingReport, setGeneratingReport] = useState<string | null>(null);
  const [showAllReports, setShowAllReports] = useState(false);

  // Helper functions for comparison reports
  const calculateMetrics = (trips: any[]) => {
    const totalTrips = trips.length;
    const totalDistance = trips.reduce((sum, trip) => sum + (trip.end_km - trip.start_km || 0), 0);
    const totalRevenue = trips.reduce((sum, trip) => sum + ((trip.end_km - trip.start_km || 0) * 10), 0);
    const totalExpenses = trips.reduce((sum, trip) => 
      sum + (trip.total_fuel_cost || 0) + 
      (trip.total_road_expenses || 0) + 
      (trip.driver_expense || 0) +
      (trip.unloading_expense || 0) +
      (trip.breakdown_expense || 0) +
      (trip.miscellaneous_expense || 0), 0
    );
    const fuelCosts = trips.reduce((sum, trip) => sum + (trip.total_fuel_cost || 0), 0);
    const avgTripDistance = totalTrips > 0 ? totalDistance / totalTrips : 0;
    
    return {
      totalTrips,
      totalDistance: Math.round(totalDistance),
      totalRevenue: Math.round(totalRevenue),
      totalExpenses: Math.round(totalExpenses),
      fuelCosts: Math.round(fuelCosts),
      avgTripDistance: Math.round(avgTripDistance)
    };
  };

  const calculatePercentChange = (oldValue: number, newValue: number): string => {
    if (oldValue === 0) return newValue > 0 ? '+100%' : '0%';
    const change = ((newValue - oldValue) / oldValue) * 100;
    return `${change >= 0 ? '+' : ''}${change.toFixed(1)}%`;
  };

  const reportTypes: ReportType[] = [
    // Smart Comparison Reports
    {
      id: 'week-comparison',
      name: 'Weekly Comparison',
      description: 'This week vs last week',
      icon: <TrendingUp className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'month-comparison',
      name: 'Monthly Comparison',
      description: 'Month-over-month analysis',
      icon: <BarChart3 className="h-4 w-4" />,
      category: 'comparison'
    },
    {
      id: 'year-comparison',
      name: 'Yearly Comparison',
      description: 'Year-over-year analysis',
      icon: <FileSpreadsheet className="h-4 w-4" />,
      category: 'comparison'
    },
    // Standard Reports
    {
      id: 'trip-summary',
      name: 'Trip Summary',
      description: 'All trip details',
      icon: <Package className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'vehicle-utilization',
      name: 'Vehicle Utilization',
      description: 'Vehicle usage patterns',
      icon: <Truck className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'driver-performance',
      name: 'Driver Performance',
      description: 'Driver efficiency',
      icon: <Users className="h-4 w-4" />,
      category: 'operations'
    },
    {
      id: 'fuel-consumption',
      name: 'Fuel Analysis',
      description: 'Fuel usage & costs',
      icon: <Fuel className="h-4 w-4" />,
      category: 'financial'
    },
    {
      id: 'expense-analysis',
      name: 'Expense Report',
      description: 'All expenses',
      icon: <DollarSign className="h-4 w-4" />,
      category: 'financial'
    },
    {
      id: 'maintenance-schedule',
      name: 'Maintenance',
      description: 'Service schedules',
      icon: <Wrench className="h-4 w-4" />,
      category: 'maintenance'
    },
    {
      id: 'compliance-audit',
      name: 'Compliance',
      description: 'Document validity',
      icon: <FileText className="h-4 w-4" />,
      category: 'compliance'
    }
  ];

  // Fetch all metrics and data
  useEffect(() => {
    if (activeTab === 'dashboard') {
      fetchDashboardData();
    }
  }, [dateRange, activeTab]);

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      await Promise.all([
        fetchMetrics(),
        fetchTripTrends(),
        fetchVehicleUtilization(),
        fetchDriverPerformance(),
        fetchExpenseBreakdown()
      ]);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchMetrics = async () => {
    try {
      const { data: trips } = await supabase
        .from('trips')
        .select('*')
        .gte('start_time', dateRange.startDate.toISOString())
        .lte('start_time', dateRange.endDate.toISOString());

      const totalRevenue = trips?.reduce((sum, trip) => {
        const revenue = (trip.end_km - trip.start_km) * 10;
        return sum + revenue;
      }, 0) || 0;

      const avgDistance = trips?.length ? 
        trips.reduce((sum, trip) => sum + (trip.end_km - trip.start_km), 0) / trips.length : 0;

      const { count: vehicleCount } = await supabase
        .from('vehicles')
        .select('*', { count: 'exact', head: true })
        .eq('status', 'active');

      const { count: driverCount } = await supabase
        .from('drivers')
        .select('*', { count: 'exact', head: true })
        .eq('status', 'active');

      const { data: maintenance } = await supabase
        .from('maintenance')
        .select('*')
        .gte('scheduled_date', dateRange.startDate.toISOString())
        .lte('scheduled_date', dateRange.endDate.toISOString());

      const maintenanceCosts = maintenance?.reduce((sum, m) => sum + (m.cost || 0), 0) || 0;
      const pendingMaintenance = maintenance?.filter(m => m.status === 'scheduled').length || 0;

      setMetrics({
        totalRevenue,
        totalTrips: trips?.length || 0,
        activeVehicles: vehicleCount || 0,
        activeDrivers: driverCount || 0,
        avgTripDistance: Math.round(avgDistance),
        avgFuelEfficiency: 8.5,
        maintenanceCosts,
        pendingMaintenance
      });
    } catch (error) {
      console.error('Error fetching metrics:', error);
    }
  };

  const fetchTripTrends = async () => {
    try {
      const { data: trips } = await supabase
        .from('trips')
        .select('start_time, end_km, start_km')
        .gte('start_time', subMonths(dateRange.endDate, 6).toISOString())
        .lte('start_time', dateRange.endDate.toISOString())
        .order('start_time');

      const grouped = trips?.reduce((acc: any, trip) => {
        const date = format(new Date(trip.start_time), 'MMM dd');
        if (!acc[date]) {
          acc[date] = { date, trips: 0, revenue: 0 };
        }
        acc[date].trips++;
        acc[date].revenue += (trip.end_km - trip.start_km) * 10;
        return acc;
      }, {});

      setChartData(prev => ({
        ...prev,
        tripTrends: Object.values(grouped || {})
      }));
    } catch (error) {
      console.error('Error fetching trip trends:', error);
    }
  };

  const fetchVehicleUtilization = async () => {
    try {
      const { data: vehicles } = await supabase
        .from('vehicles')
        .select('id, registration_number')
        .eq('status', 'active')
        .limit(10);

      const utilizationData = await Promise.all(
        vehicles?.map(async (vehicle) => {
          const { count } = await supabase
            .from('trips')
            .select('*', { count: 'exact', head: true })
            .eq('vehicle_id', vehicle.id)
            .gte('start_time', dateRange.startDate.toISOString())
            .lte('start_time', dateRange.endDate.toISOString());

          return {
            vehicle: vehicle.registration_number,
            trips: count || 0,
            utilization: Math.min(((count || 0) / 30) * 100, 100)
          };
        }) || []
      );

      setChartData(prev => ({
        ...prev,
        vehicleUtilization: utilizationData.sort((a, b) => b.utilization - a.utilization)
      }));
    } catch (error) {
      console.error('Error fetching vehicle utilization:', error);
    }
  };

  const fetchDriverPerformance = async () => {
    try {
      const { data: drivers } = await supabase
        .from('drivers')
        .select('id, name')
        .eq('status', 'active')
        .limit(10);

      const performanceData = await Promise.all(
        drivers?.map(async (driver) => {
          const { data: trips } = await supabase
            .from('trips')
            .select('*')
            .eq('driver_id', driver.id)
            .gte('start_time', dateRange.startDate.toISOString())
            .lte('start_time', dateRange.endDate.toISOString());

          const efficiency = trips?.length ? 
            trips.reduce((sum, trip) => {
              const fuelEfficiency = (trip.end_km - trip.start_km) / (trip.total_fuel_cost || 1);
              return sum + fuelEfficiency;
            }, 0) / trips.length : 0;

          return {
            driver: driver.name,
            trips: trips?.length || 0,
            efficiency: Math.round(efficiency * 10) / 10
          };
        }) || []
      );

      setChartData(prev => ({
        ...prev,
        driverPerformance: performanceData.sort((a, b) => b.efficiency - a.efficiency)
      }));
    } catch (error) {
      console.error('Error fetching driver performance:', error);
    }
  };

  const fetchExpenseBreakdown = async () => {
    try {
      const { data: trips } = await supabase
        .from('trips')
        .select('total_fuel_cost, total_road_expenses, driver_expense, breakdown_expense')
        .gte('start_time', dateRange.startDate.toISOString())
        .lte('start_time', dateRange.endDate.toISOString());

      const expenses = {
        Fuel: 0,
        'Road Expenses': 0,
        'Driver Expenses': 0,
        'Breakdown': 0,
        'Maintenance': metrics.maintenanceCosts
      };

      trips?.forEach(trip => {
        expenses.Fuel += trip.total_fuel_cost || 0;
        expenses['Road Expenses'] += trip.total_road_expenses || 0;
        expenses['Driver Expenses'] += trip.driver_expense || 0;
        expenses['Breakdown'] += trip.breakdown_expense || 0;
      });

      setChartData(prev => ({
        ...prev,
        expenseBreakdown: Object.entries(expenses).map(([category, amount]) => ({
          category,
          amount
        }))
      }));
    } catch (error) {
      console.error('Error fetching expense breakdown:', error);
    }
  };

  const getDateRange = () => {
    const now = new Date();
    let start: Date, end: Date;

    switch (selectedDateRange) {
      case 'today':
        start = startOfDay(now);
        end = endOfDay(now);
        break;
      case 'yesterday':
        start = startOfDay(subDays(now, 1));
        end = endOfDay(subDays(now, 1));
        break;
      case 'thisWeek':
        start = startOfWeek(now, { weekStartsOn: 1 });
        end = endOfWeek(now, { weekStartsOn: 1 });
        break;
      case 'lastWeek':
        start = startOfWeek(subDays(now, 7), { weekStartsOn: 1 });
        end = endOfWeek(subDays(now, 7), { weekStartsOn: 1 });
        break;
      case 'thisMonth':
        start = startOfMonth(now);
        end = endOfMonth(now);
        break;
      case 'lastMonth':
        start = startOfMonth(subMonths(now, 1));
        end = endOfMonth(subMonths(now, 1));
        break;
      case 'last30Days':
        start = subDays(now, 30);
        end = now;
        break;
      case 'custom':
        start = customStartDate ? new Date(customStartDate) : now;
        end = customEndDate ? new Date(customEndDate) : now;
        break;
      default:
        start = startOfMonth(now);
        end = endOfMonth(now);
    }

    return { start, end };
  };

  const generateReport = async (reportId: string) => {
    setGeneratingReport(reportId);
    const { start, end } = getDateRange();
    
    try {
      let fileName = '';
      let headers: string[] = [];
      let rows: any[][] = [];

      switch (reportId) {
        case 'week-comparison': {
          const thisWeekStart = startOfWeek(new Date(), { weekStartsOn: 1 });
          const lastWeekStart = startOfWeek(subDays(new Date(), 7), { weekStartsOn: 1 });

          const { data: thisWeekTrips } = await supabase
            .from('trips')
            .select('*')
            .gte('start_time', thisWeekStart.toISOString())
            .lte('start_time', new Date().toISOString());

          const { data: lastWeekTrips } = await supabase
            .from('trips')
            .select('*')
            .gte('start_time', lastWeekStart.toISOString())
            .lte('start_time', endOfWeek(subDays(new Date(), 7), { weekStartsOn: 1 }).toISOString());

          const thisWeekMetrics = calculateMetrics(thisWeekTrips || []);
          const lastWeekMetrics = calculateMetrics(lastWeekTrips || []);

          fileName = `week-comparison-${format(new Date(), 'yyyy-MM-dd')}.csv`;
          headers = ['Metric', 'This Week', 'Last Week', 'Change', '% Change'];
          rows = [
            ['Total Trips', thisWeekMetrics.totalTrips, lastWeekMetrics.totalTrips, 
             thisWeekMetrics.totalTrips - lastWeekMetrics.totalTrips,
             calculatePercentChange(lastWeekMetrics.totalTrips, thisWeekMetrics.totalTrips)],
            ['Total Distance (km)', thisWeekMetrics.totalDistance, lastWeekMetrics.totalDistance,
             thisWeekMetrics.totalDistance - lastWeekMetrics.totalDistance,
             calculatePercentChange(lastWeekMetrics.totalDistance, thisWeekMetrics.totalDistance)],
            ['Total Revenue', thisWeekMetrics.totalRevenue, lastWeekMetrics.totalRevenue,
             thisWeekMetrics.totalRevenue - lastWeekMetrics.totalRevenue,
             calculatePercentChange(lastWeekMetrics.totalRevenue, thisWeekMetrics.totalRevenue)]
          ];
          break;
        }

        case 'trip-summary': {
          const { data: trips } = await supabase
            .from('trips')
            .select(`
              *,
              vehicle:vehicles(registration_number),
              driver:drivers(name)
            `)
            .gte('start_time', start.toISOString())
            .lte('start_time', end.toISOString());
          
          fileName = `trip-summary-${format(start, 'yyyy-MM-dd')}.csv`;
          headers = ['Date', 'Vehicle', 'Driver', 'From', 'To', 'Distance', 'Status'];
          rows = trips?.map(trip => [
            format(new Date(trip.start_time), 'yyyy-MM-dd'),
            trip.vehicle?.registration_number || '',
            trip.driver?.name || '',
            trip.from_location || '',
            trip.to_location || '',
            (trip.end_km - trip.start_km) || 0,
            trip.status || ''
          ]) || [];
          break;
        }
      }

      // Create CSV content
      const csvContent = [
        headers.join(','),
        ...rows.map(row => row.join(','))
      ].join('\n');

      // Download CSV
      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = fileName;
      a.click();
      
    } catch (error) {
      console.error('Error generating report:', error);
    } finally {
      setGeneratingReport(null);
    }
  };

  const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6'];

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* Header */}
      <div className="mb-6">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Reporting & Analytics</h1>
            <p className="text-gray-600">Visual insights and downloadable reports</p>
          </div>
          
          {/* Tab Switcher */}
          <div className="flex bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setActiveTab('dashboard')}
              className={`px-4 py-2 rounded-md transition-colors ${
                activeTab === 'dashboard' 
                  ? 'bg-white text-blue-600 shadow' 
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Eye className="h-4 w-4 inline mr-2" />
              Visual Dashboard
            </button>
            <button
              onClick={() => setActiveTab('reports')}
              className={`px-4 py-2 rounded-md transition-colors ${
                activeTab === 'reports' 
                  ? 'bg-white text-blue-600 shadow' 
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Download className="h-4 w-4 inline mr-2" />
              Download Reports
            </button>
          </div>
        </div>
      </div>

      {/* Date Range Selector (Common for both tabs) */}
      <div className="bg-white rounded-lg shadow p-4 mb-6">
        <div className="flex items-center space-x-4">
          <Calendar className="h-5 w-5 text-gray-500" />
          <select
            value={selectedDateRange}
            onChange={(e) => {
              setSelectedDateRange(e.target.value);
              if (e.target.value !== 'custom') {
                const { start, end } = getDateRange();
                setDateRange({ startDate: start, endDate: end });
              }
            }}
            className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="today">Today</option>
            <option value="yesterday">Yesterday</option>
            <option value="thisWeek">This Week</option>
            <option value="lastWeek">Last Week</option>
            <option value="thisMonth">This Month</option>
            <option value="lastMonth">Last Month</option>
            <option value="last30Days">Last 30 Days</option>
            <option value="custom">Custom Range</option>
          </select>
          
          {selectedDateRange === 'custom' && (
            <div className="flex space-x-3">
              <input
                type="date"
                value={customStartDate}
                onChange={(e) => {
                  setCustomStartDate(e.target.value);
                  if (e.target.value && customEndDate) {
                    setDateRange({
                      startDate: new Date(e.target.value),
                      endDate: new Date(customEndDate)
                    });
                  }
                }}
                className="px-3 py-2 border rounded"
              />
              <span className="self-center">to</span>
              <input
                type="date"
                value={customEndDate}
                onChange={(e) => {
                  setCustomEndDate(e.target.value);
                  if (customStartDate && e.target.value) {
                    setDateRange({
                      startDate: new Date(customStartDate),
                      endDate: new Date(e.target.value)
                    });
                  }
                }}
                className="px-3 py-2 border rounded"
              />
            </div>
          )}
          
          {activeTab === 'dashboard' && (
            <button
              onClick={fetchDashboardData}
              className="flex items-center px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
              Refresh
            </button>
          )}
        </div>
      </div>

      {/* Visual Dashboard Tab */}
      {activeTab === 'dashboard' && (
        <>
          {/* Metrics Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <div className="bg-white rounded-lg shadow p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Total Revenue</p>
                  <p className="text-2xl font-bold text-gray-900">
                    ${metrics.totalRevenue.toLocaleString()}
                  </p>
                </div>
                <DollarSign className="h-8 w-8 text-green-500" />
              </div>
            </div>
            
            <div className="bg-white rounded-lg shadow p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Total Trips</p>
                  <p className="text-2xl font-bold text-gray-900">{metrics.totalTrips}</p>
                </div>
                <Package className="h-8 w-8 text-blue-500" />
              </div>
            </div>
            
            <div className="bg-white rounded-lg shadow p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Vehicles</p>
                  <p className="text-2xl font-bold text-gray-900">{metrics.activeVehicles}</p>
                </div>
                <Truck className="h-8 w-8 text-purple-500" />
              </div>
            </div>
            
            <div className="bg-white rounded-lg shadow p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Drivers</p>
                  <p className="text-2xl font-bold text-gray-900">{metrics.activeDrivers}</p>
                </div>
                <Users className="h-8 w-8 text-orange-500" />
              </div>
            </div>
          </div>

          {/* Charts Grid */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Trip Trends */}
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4">Trip Trends</h3>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={chartData.tripTrends}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="trips" stroke="#3B82F6" name="Trips" />
                  <Line type="monotone" dataKey="revenue" stroke="#10B981" name="Revenue" />
                </LineChart>
              </ResponsiveContainer>
            </div>

            {/* Vehicle Utilization */}
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4">Vehicle Utilization</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chartData.vehicleUtilization.slice(0, 5)}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="vehicle" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="utilization" fill="#3B82F6" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            {/* Driver Performance */}
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4">Top Driver Performance</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chartData.driverPerformance.slice(0, 5)}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="driver" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="efficiency" fill="#10B981" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            {/* Expense Breakdown */}
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4">Expense Breakdown</h3>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={chartData.expenseBreakdown}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={(entry) => `${entry.category}: $${entry.amount.toFixed(0)}`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="amount"
                  >
                    {chartData.expenseBreakdown.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </div>
        </>
      )}

      {/* Download Reports Tab */}
      {activeTab === 'reports' && (
        <div>
          {/* Quick Download Section */}
          <div className="bg-white rounded-lg shadow p-6 mb-6">
            <h3 className="text-lg font-semibold mb-4">Quick Downloads</h3>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3">
              {reportTypes.slice(0, showAllReports ? reportTypes.length : 5).map((report) => (
                <button
                  key={report.id}
                  onClick={() => generateReport(report.id)}
                  disabled={generatingReport === report.id}
                  className="flex flex-col items-center p-3 bg-gray-50 hover:bg-gray-100 rounded-lg transition-colors disabled:opacity-50"
                >
                  <div className={`p-2 rounded-lg mb-2 ${
                    report.category === 'comparison' ? 'bg-green-100 text-green-600' :
                    report.category === 'financial' ? 'bg-blue-100 text-blue-600' :
                    report.category === 'operations' ? 'bg-purple-100 text-purple-600' :
                    report.category === 'maintenance' ? 'bg-orange-100 text-orange-600' :
                    'bg-gray-100 text-gray-600'
                  }`}>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-5 w-5 animate-spin" />
                    ) : (
                      report.icon
                    )}
                  </div>
                  <span className="text-sm font-medium text-gray-900">{report.name}</span>
                  <span className="text-xs text-gray-500 mt-1">{report.description}</span>
                </button>
              ))}
            </div>
            {!showAllReports && reportTypes.length > 5 && (
              <button
                onClick={() => setShowAllReports(true)}
                className="mt-4 flex items-center justify-center w-full py-2 text-blue-600 hover:text-blue-700"
              >
                <ChevronDown className="h-4 w-4 mr-1" />
                Show All Reports ({reportTypes.length - 5} more)
              </button>
            )}
            {showAllReports && (
              <button
                onClick={() => setShowAllReports(false)}
                className="mt-4 flex items-center justify-center w-full py-2 text-blue-600 hover:text-blue-700"
              >
                <ChevronUp className="h-4 w-4 mr-1" />
                Show Less
              </button>
            )}
          </div>

          {/* Comparison Reports Section */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4 flex items-center">
                <TrendingUp className="h-5 w-5 mr-2 text-green-600" />
                Smart Comparisons
              </h3>
              <div className="space-y-2">
                {reportTypes.filter(r => r.category === 'comparison').map(report => (
                  <button
                    key={report.id}
                    onClick={() => generateReport(report.id)}
                    disabled={generatingReport === report.id}
                    className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center"
                  >
                    <span className="text-sm">{report.name}</span>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-4 w-4 animate-spin text-blue-600" />
                    ) : (
                      <Download className="h-4 w-4 text-gray-400" />
                    )}
                  </button>
                ))}
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4 flex items-center">
                <DollarSign className="h-5 w-5 mr-2 text-blue-600" />
                Financial Reports
              </h3>
              <div className="space-y-2">
                {reportTypes.filter(r => r.category === 'financial').map(report => (
                  <button
                    key={report.id}
                    onClick={() => generateReport(report.id)}
                    disabled={generatingReport === report.id}
                    className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center"
                  >
                    <span className="text-sm">{report.name}</span>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-4 w-4 animate-spin text-blue-600" />
                    ) : (
                      <Download className="h-4 w-4 text-gray-400" />
                    )}
                  </button>
                ))}
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4 flex items-center">
                <Package className="h-5 w-5 mr-2 text-purple-600" />
                Operations Reports
              </h3>
              <div className="space-y-2">
                {reportTypes.filter(r => r.category === 'operations').map(report => (
                  <button
                    key={report.id}
                    onClick={() => generateReport(report.id)}
                    disabled={generatingReport === report.id}
                    className="w-full text-left p-3 hover:bg-gray-50 rounded-lg transition-colors flex justify-between items-center"
                  >
                    <span className="text-sm">{report.name}</span>
                    {generatingReport === report.id ? (
                      <RefreshCw className="h-4 w-4 animate-spin text-blue-600" />
                    ) : (
                      <Download className="h-4 w-4 text-gray-400" />
                    )}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UnifiedReportingDashboard;

import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import {
  format,
  parseISO,
  isValid,
  startOfMonth,
  subMonths,
  differenceInDays,
} from "date-fns";
import {
  ChevronLeft,
  Users,
  Calendar,
  IndianRupee,
  Package,
  Gauge,
  TrendingUp,
  BarChart2,
  BarChart3,
  Filter,
  Search,
  Download,
  RefreshCw,
  User,
} from "lucide-react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Cell,
  LineChart,
  Line,
} from "recharts";
import { toast } from "react-toastify";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import Layout from "../../components/layout/Layout";
import LoadingScreen from "../../components/LoadingScreen";
import Button from "../../components/ui/Button";
import DriverSummaryModal from "../../components/drivers/DriverSummaryModal";
import Input from "../../components/ui/Input";
import Select from "../../components/ui/Select";
import StatCard from "../../components/ui/StatCard";
import { cn } from "../../utils/cn";
import { getTrips, getVehicles } from "../../utils/storage";
import { getDrivers } from "../../utils/api/drivers";
import type { Driver, Trip, Vehicle } from "@/types";

// Interface for driver performance metrics
interface DriverPerformance {
  driverId: string;
  name: string;
  totalTrips: number;
  totalDistance: number;
  totalFuel: number;
  avgMileage: number;
  totalExpenses: number;
  costPerKm: number;
  totalGrossWeight: number;
  tripDays: Set<string>;
  avgLoadPerTrip: number;
  driverUtilizationPercentage: number;
  lastTripDate: string | null;
}

const DriverInsightsPage: React.FC = () => {
  const navigate = useNavigate();
  const [drivers, setDrivers] = useState<Driver[]>([]);
  const [trips, setTrips] = useState<Trip[]>([]);
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [loading, setLoading] = useState(true);
  const [exportLoading, setExportLoading] = useState(false);

  // Filters
  const [dateRange, setDateRange] = useState<
    "thisMonth" | "lastThreeMonths" | "lastSixMonths" | "lastYear" | "allTime"
  >("allTime");
  const [selectedDriver, setSelectedDriver] = useState<string>("all");
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [showFilters, setShowFilters] = useState(true);
  const [selectedDriverForModal, setSelectedDriverForModal] = useState<Driver | null>(null);
  const [showDriverModal, setShowDriverModal] = useState(false);

  // Handle opening driver summary modal
  const handleViewDriverDetails = (driverId: string) => {
    const driver = drivers.find(d => d.id === driverId);
    if (driver) {
      setSelectedDriverForModal(driver);
      setShowDriverModal(true);
    }
  };
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [driversData, tripsData, vehiclesData] = await Promise.all([
          getDrivers(),
          getTrips(),
          getVehicles(),
        ]);

        setDrivers(driversData);
        setTrips(tripsData);
        setVehicles(vehiclesData);
      } catch (error) {
        console.error("Error fetching data:", error);
        toast.error("Failed to load driver analytics data");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Calculate date range based on filter
  const effectiveDateRange = useMemo(() => {
    const now = new Date();

    switch (dateRange) {
      case "thisMonth":
        return {
          start: startOfMonth(now),
          end: now,
        };
      case "lastThreeMonths":
        return {
          start: subMonths(now, 3),
          end: now,
        };
      case "lastSixMonths":
        return {
          start: subMonths(now, 6),
          end: now,
        };
      case "lastYear":
        return {
          start: subMonths(now, 12),
          end: now,
        };
      case "allTime":
        return {
          start: new Date(0), // January 1, 1970
          end: now,
        };
    }
  }, [dateRange]);

  // Filter trips based on date range and driver selection
  const filteredTrips = useMemo(() => {
    return trips.filter((trip) => {
      // Filter by date
      const tripDate = parseISO(trip.trip_start_date);
      if (
        !isValid(tripDate) ||
        tripDate < effectiveDateRange.start ||
        tripDate > effectiveDateRange.end
      ) {
        return false;
      }

      // Filter by driver if selected
      if (selectedDriver !== "all" && trip.driver_id !== selectedDriver) {
        return false;
      }

      return true;
    });
  }, [trips, effectiveDateRange, selectedDriver]);

  // Calculate driver performance metrics
  const driverPerformance = useMemo(() => {
    const performanceMap = new Map<string, DriverPerformance>();

    // Initialize with all drivers (even those with no trips)
    drivers.forEach((driver) => {
      performanceMap.set(driver.id || "", {
        driverId: driver.id || "",
        name: driver.name,
        totalTrips: 0,
        totalDistance: 0,
        totalFuel: 0,
        avgMileage: 0,
        totalExpenses: 0,
        costPerKm: 0,
        totalGrossWeight: 0,
        tripDays: new Set<string>(),
        avgLoadPerTrip: 0,
        driverUtilizationPercentage: 0,
        lastTripDate: null,
      });
    });

    // Process filtered trips
    filteredTrips.forEach((trip) => {
      const driverId = trip.driver_id;
      if (!driverId) return;

      const performance = performanceMap.get(driverId) || {
        driverId,
        name: drivers.find((d) => d.id === driverId)?.name || "Unknown",
        totalTrips: 0,
        totalDistance: 0,
        totalFuel: 0,
        avgMileage: 0,
        totalExpenses: 0,
        costPerKm: 0,
        totalGrossWeight: 0,
        tripDays: new Set<string>(),
        avgLoadPerTrip: 0,
        driverUtilizationPercentage: 0,
        lastTripDate: null,
      };

      const distance = trip.end_km - trip.start_km;
      const fuel = trip.fuel_quantity || 0;
      const expenses =
        (trip.total_fuel_cost || 0) + (trip.total_road_expenses || 0);
      const grossWeight = trip.gross_weight || 0;
      const tripStart = trip.trip_start_date
        ? trip.trip_start_date.slice(0, 10)
        : null;

      // Update metrics
      performance.totalTrips++;
      performance.totalDistance += distance;
      performance.totalFuel += fuel;
      performance.totalExpenses += expenses;
      performance.totalGrossWeight += grossWeight;
      if (tripStart) {
        performance.tripDays.add(tripStart);
      }

      // Update last trip date if newer
      const tripDate = trip.trip_end_date;
      if (
        tripDate &&
        (!performance.lastTripDate || tripDate > performance.lastTripDate)
      ) {
        performance.lastTripDate = tripDate;
      }

      performanceMap.set(driverId, performance);
    });

    // Calculate derived metrics
    performanceMap.forEach((performance) => {
      if (performance.totalFuel > 0) {
        performance.avgMileage =
          performance.totalDistance / performance.totalFuel;
      }

      if (performance.totalDistance > 0) {
        performance.costPerKm =
          performance.totalExpenses / performance.totalDistance;
      }

      performance.avgLoadPerTrip =
        performance.totalTrips > 0
          ? performance.totalGrossWeight / performance.totalTrips
          : 0;
      const totalDaysInPeriod =
        differenceInDays(effectiveDateRange.end, effectiveDateRange.start) + 1;
      performance.driverUtilizationPercentage =
        totalDaysInPeriod > 0
          ? (performance.tripDays.size / totalDaysInPeriod) * 100
          : 0;
    });

    // Convert to array and filter by search term
    return Array.from(performanceMap.values())
      .filter((performance) => {
        if (!searchTerm) return true;
        const lowerSearch = searchTerm.toLowerCase();
        return performance.name.toLowerCase().includes(lowerSearch);
      })
      .sort((a, b) => b.totalTrips - a.totalTrips);
  }, [drivers, filteredTrips, searchTerm]);

  // Calculate summary metrics
  const summaryMetrics = useMemo(() => {
    const totalDrivers = drivers.length;
    const activeDrivers = driverPerformance.filter(
      (p) => p.totalTrips > 0,
    ).length;

    let totalDistance = 0;
    let totalFuel = 0;
    let totalExpenses = 0;
    let avgLoadPerTripSum = 0;
    let utilizationSum = 0;

    driverPerformance.forEach((p) => {
      totalDistance += p.totalDistance;
      totalFuel += p.totalFuel;
      totalExpenses += p.totalExpenses;
      avgLoadPerTripSum += p.avgLoadPerTrip || 0;
      utilizationSum += p.driverUtilizationPercentage || 0;
    });

    const avgKmPerDay =
      totalDistance /
      Math.max(
        1,
        Math.round(
          (effectiveDateRange.end.getTime() -
            effectiveDateRange.start.getTime()) /
            (1000 * 60 * 60 * 24),
        ),
      );

    const avgCostPerKm = totalDistance > 0 ? totalExpenses / totalDistance : 0;

    const avgLoadPerTrip =
      driverPerformance.length > 0
        ? avgLoadPerTripSum / driverPerformance.length
        : 0;
    const avgDriverUtilization =
      driverPerformance.length > 0
        ? utilizationSum / driverPerformance.length
        : 0;

    // Find top performing driver
    const topDriver =
      driverPerformance.length > 0
        ? [...driverPerformance]
            .filter((p) => p.totalTrips > 0)
            .sort((a, b) => a.costPerKm - b.costPerKm)[0]
        : null;

    return {
      totalDrivers,
      activeDrivers,
      avgKmPerDay,
      avgCostPerKm,
      avgLoadPerTrip,
      avgDriverUtilization,
      topDriver,
    };
  }, [drivers, driverPerformance, effectiveDateRange]);

  // Monthly performance data for charts
  const monthlyPerformanceData = useMemo(() => {
    const monthlyData: Record<
      string,
      {
        month: string;
        totalDistance: number;
        totalFuel: number;
        driverCount: number;
      }
    > = {};

    filteredTrips.forEach((trip) => {
      const tripDate = parseISO(trip.trip_start_date);
      if (!isValid(tripDate)) return;

      const monthKey = format(tripDate, "MMM yyyy");

      if (!monthlyData[monthKey]) {
        monthlyData[monthKey] = {
          month: monthKey,
          totalDistance: 0,
          totalFuel: 0,
          driverCount: 0,
        };
      }

      monthlyData[monthKey].totalDistance += trip.end_km - trip.start_km;
      monthlyData[monthKey].totalFuel += trip.fuel_quantity || 0;

      // Count unique drivers
      const uniqueDrivers = new Set<string>();
      Object.values(monthlyData).forEach((data) => {
        filteredTrips.forEach((trip) => {
          if (trip.driver_id) {
            uniqueDrivers.add(trip.driver_id);
          }
        });
      });
      monthlyData[monthKey].driverCount = uniqueDrivers.size;
    });

    // Convert to array and sort by date
    return Object.values(monthlyData).sort((a, b) => {
      return new Date(a.month).getTime() - new Date(b.month).getTime();
    });
  }, [filteredTrips]);

  // Export data to Excel
  const handleExport = () => {
    setExportLoading(true);
    try {
      const exportData = driverPerformance.map((driver) => ({
        "Driver Name": driver.name,
        "Total Trips": driver.totalTrips,
        "Total Distance (km)": driver.totalDistance.toFixed(0),
        "Total Fuel (L)": driver.totalFuel.toFixed(2),
        "Avg Mileage (km/L)": driver.avgMileage.toFixed(2),
        "Total Expenses ()": driver.totalExpenses.toFixed(2),
        "Cost per KM ()": driver.costPerKm.toFixed(2),
        "Last Trip Date": driver.lastTripDate
          ? new Date(driver.lastTripDate).toLocaleDateString()
          : "N/A",
      }));

      const worksheet = XLSX.utils.json_to_sheet(exportData);
      const workbook = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(workbook, worksheet, "Driver Performance");

      const excelBuffer = XLSX.write(workbook, {
        bookType: "xlsx",
        type: "array",
      });
      const blob = new Blob([excelBuffer], {
        type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
      });

      saveAs(
        blob,
        `Driver_Performance_${format(new Date(), "yyyy-MM-dd")}.xlsx`,
      );
      toast.success("Export successful");
    } catch (error) {
      console.error("Export error:", error);
      toast.error("Failed to export data");
    } finally {
      setExportLoading(false);
    }
  };

  return (
    <>
      <LoadingScreen isLoading={loading} />
      <Layout>
        {/* Page Header */}
        <div className="rounded-xl border bg-white dark:bg-white px-4 py-3 shadow-sm mb-6">
          <div className="flex items-center group">
            <BarChart3 className="h-5 w-5 mr-2 text-gray-500 dark:text-gray-400 group-hover:text-primary-600 transition" />
            <h1 className="text-2xl font-semibold text-gray-900 dark:text-gray-100">Driver Insights</h1>
          </div>
          <p className="text-sm text-gray-500 dark:text-gray-400 mt-1 ml-7">Analytics and performance metrics for your drivers</p>
          <div className="mt-4 flex flex-wrap gap-2">
            <Button
              variant="outline"
              onClick={() => navigate("/drivers")}
              icon={<ChevronLeft className="h-4 w-4" />}
            >
              Back to Drivers
            </Button>

            <Button
              variant="outline"
              onClick={handleExport}
              icon={<Download className="h-4 w-4" />}
              isLoading={exportLoading}
              disabled={driverPerformance.length === 0}
            >
              Export Data
            </Button>
          </div>
        </div>

        <div className={cn('space-y-6', loading && 'opacity-50 pointer-events-none')}>
          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-4">
            <StatCard
              title="Total Drivers"
              value={summaryMetrics.totalDrivers}
              icon={<Users className="h-5 w-5 text-primary-600" />}
            />

            <StatCard
              title="Average KM/Day"
              value={Math.round(summaryMetrics.avgKmPerDay)}
              subtitle="km"
              icon={<Calendar className="h-5 w-5 text-primary-600" />}
            />

            <StatCard
              title="Average Cost/KM"
              value={`${summaryMetrics.avgCostPerKm.toFixed(2)}`}
              icon={<IndianRupee className="h-5 w-5 text-primary-600" />}
            />

            <StatCard
              title="Avg Load/Trip"
              value={
                summaryMetrics.avgLoadPerTrip
                  ? summaryMetrics.avgLoadPerTrip.toFixed(1)
                  : "0"
              }
              subtitle="tons"
              icon={<Package className="h-5 w-5 text-primary-600" />}
            />

            <StatCard
              title="Driver Utilization"
              value={
                summaryMetrics.avgDriverUtilization
                  ? `${summaryMetrics.avgDriverUtilization.toFixed(1)}%`
                  : "0%"
              }
              icon={<Gauge className="h-5 w-5 text-primary-600" />}
            />

            <StatCard
              title="Top Performing Driver"
              value={summaryMetrics.topDriver?.name || "N/A"}
              subtitle={
                summaryMetrics.topDriver
                  ? `${summaryMetrics.topDriver.costPerKm.toFixed(2)}/km`
                  : undefined
              }
              icon={<User className="h-5 w-5 text-primary-600" />}
            />
          </div>

          {/* Filters */}
          <div className="bg-white p-4 rounded-lg shadow-sm">
            <div className="flex flex-wrap gap-4 justify-between">
              <div className="flex items-center border-l-2 border-blue-500 pl-2">
                <h2 className="text-lg font-medium text-gray-900 flex items-center">
                  <Filter className="h-5 w-5 mr-2 text-primary-500" />
                  Filters
                </h2>
              </div>

              <Button
                variant="outline"
                inputSize="sm"
                onClick={() => setShowFilters(!showFilters)}
              >
                {showFilters ? "Hide Filters" : "Show Filters"}
              </Button>
            </div>

            {showFilters && (
              <div className="mt-4 grid grid-cols-1 md:grid-cols-3 gap-4">
                <Select
                  label="Time Period"
                  options={[
                    { value: "thisMonth", label: "This Month" },
                    { value: "lastThreeMonths", label: "Last 3 Months" },
                    { value: "lastSixMonths", label: "Last 6 Months" },
                    { value: "lastYear", label: "Last 12 Months" },
                    { value: "allTime", label: "All Time" },
                  ]}
                  value={dateRange}
                  onChange={(e) => setDateRange(e.target.value as any)}
                />

                <Select
                  label="Driver"
                  options={[
                    { value: "all", label: "All Drivers" },
                    ...drivers.map((driver) => ({
                      value: driver.id || "",
                      label: driver.name,
                    })),
                  ]}
                  value={selectedDriver}
                  onChange={(e) => setSelectedDriver(e.target.value)}
                />

                <Input
                  label="Search"
                  placeholder="Search drivers..."
                  icon={<Search className="h-4 w-4" />}
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            )}
          </div>

          {/* Driver Performance Table */}
          <div className="bg-white rounded-lg shadow-sm overflow-hidden">
            <div className="px-4 py-5 border-b border-gray-200 sm:px-6 flex items-center justify-between">
              <div>
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                  Driver Performance
                </h3>
                <p className="mt-1 text-sm text-gray-500">
                  Showing{" "}
                  {driverPerformance.filter((d) => d.totalTrips > 0).length}{" "}
                  active drivers
                </p>
              </div>

              <Button
                variant="outline"
                inputSize="sm"
                onClick={() => {
                  setDateRange("allTime");
                  setSelectedDriver("all");
                  setSearchTerm("");
                }}
                icon={<RefreshCw className="h-4 w-4" />}
              >
                Reset Filters
              </Button>
            </div>

            <div className="overflow-x-auto">
              {driverPerformance.filter((d) => d.totalTrips > 0).length > 0 ? (
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Driver
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Total Trips
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Distance
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Fuel
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Mileage
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Total Expenses
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Cost per KM
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        Last Trip
                      </th>
                      <th
                        scope="col"
                        className="px-6 py-4 whitespace-nowrap text-sm text-center"
                      >
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {driverPerformance
                      .filter((driver) => driver.totalTrips > 0)
                      .map((driver) => (
                        <tr
                          key={driver.driverId}
                          className="hover:bg-gray-50 cursor-pointer"
                        >
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="flex items-center">
                              <div className="flex-shrink-0 h-10 w-10 rounded-full bg-gray-100 flex items-center justify-center">
                                <User className="h-6 w-6 text-gray-400" />
                              </div>
                              <div className="ml-4">
                                <div className="text-sm font-medium text-gray-900">
                                  {driver.name}
                                </div>
                                <div className="text-xs text-gray-500">
                                  ID: {driver.driverId.slice(0, 8)}...
                                </div>
                              </div>
                            </div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-center">
                            {driver.totalTrips}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-center">
                            {driver.totalDistance.toLocaleString()} km
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-center">
                            {driver.totalFuel.toFixed(2)} L
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-center">
                            {driver.avgMileage > 0
                              ? `${driver.avgMileage.toFixed(2)} km/L`
                              : "-"}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-center">
                            
                            {driver.totalExpenses.toLocaleString(undefined, {
                              maximumFractionDigits: 0,
                            })}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-center font-medium">
                            <span
                              className={
                                driver.costPerKm < 10
                                  ? "text-green-600"
                                  : driver.costPerKm < 15
                                    ? "text-amber-600"
                                    : "text-red-600"
                              }
                            >
                              {driver.costPerKm.toFixed(2)}
                            </span>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-center">
                            {driver.lastTripDate
                              ? format(
                                  new Date(driver.lastTripDate),
                                  "dd MMM yyyy",
                                )
                              : "-"}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-center">
                            <div className="flex justify-center space-x-2">
                              <Button
                                variant="outline"
                                inputSize="sm"
                                onClick={() => navigate(`/drivers/${driver.driverId}`)}
                              >
                                View Profile
                              </Button>
                              <Button
                                variant="outline"
                                inputSize="sm"
                                onClick={() => handleViewDriverDetails(driver.driverId)}
                              >
                                View Details
                              </Button>
                            </div>
                          </td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-16 bg-gray-50">
                  <User className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900">
                    No driver activity found
                  </h3>
                  <p className="mt-2 text-gray-500">
                    {searchTerm
                      ? `No drivers matching "${searchTerm}" have recorded trips.`
                      : "No drivers have recorded trips in the selected time period."}
                  </p>
                </div>
              )}
            </div>
          </div>

          {/* Charts */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <div className="bg-white p-6 rounded-lg shadow-sm">
              <h3 className="text-lg font-medium text-gray-900 mb-4 border-l-2 border-blue-500 pl-2">
                Distance Trend
              </h3>
              <div className="h-80">
                {monthlyPerformanceData.length > 0 ? (
                  <ResponsiveContainer width="100%" height="100%">
                    <LineChart
                      data={monthlyPerformanceData}
                      margin={{ top: 5, right: 30, left: 20, bottom: 25 }}
                    >
                      <CartesianGrid strokeDasharray="3 3" vertical={false} />
                      <XAxis
                        dataKey="month"
                        tick={{ angle: -45, textAnchor: "end", fontSize: 12 }}
                        height={60}
                        tickMargin={10}
                      />
                      <YAxis
                        yAxisId="left"
                        tickFormatter={(value) =>
                          `${value.toLocaleString()} km`
                        }
                      />
                      <Tooltip />
                      <Line
                        yAxisId="left"
                        type="monotone"
                        dataKey="totalDistance"
                        name="Distance"
                        stroke="#4CAF50"
                        strokeWidth={2}
                        activeDot={{ r: 8 }}
                      />
                    </LineChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="flex flex-col items-center justify-center h-full">
                    <TrendingUp className="h-12 w-12 text-gray-300 mb-4" />
                    <p className="text-gray-500">No data available</p>
                  </div>
                )}
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow-sm">
              <h3 className="text-lg font-medium text-gray-900 mb-4 border-l-2 border-blue-500 pl-2">
                Driver Performance Comparison
              </h3>
              <div className="h-80">
                {driverPerformance.filter((d) => d.totalTrips > 0).length >
                0 ? (
                  <ResponsiveContainer width="100%" height="100%">
                    <BarChart
                      data={driverPerformance
                        .filter((d) => d.totalTrips > 0)
                        .slice(0, 10)}
                      margin={{ top: 5, right: 30, left: 20, bottom: 25 }}
                      layout="vertical"
                    >
                      <CartesianGrid
                        strokeDasharray="3 3"
                        horizontal={true}
                        vertical={false}
                      />
                      <XAxis
                        type="number"
                        tickFormatter={(value) => `${value}`}
                      />
                      <YAxis
                        type="category"
                        dataKey="name"
                        width={100}
                        tick={{ fontSize: 12 }}
                      />
                      <Tooltip
                        formatter={(value: number) => [
                          `${value.toFixed(2)}`,
                          "Cost per KM",
                        ]}
                        labelFormatter={(label) => `Driver: ${label}`}
                      />
                      <Bar
                        dataKey="costPerKm"
                        name="Cost per KM"
                        fill="#8884d8"
                        minPointSize={2}
                        label={{
                          position: "right",
                          formatter: (value: number) => `${value.toFixed(2)}`,
                        }}
                      >
                        {driverPerformance
                          .filter((d) => d.totalTrips > 0)
                          .slice(0, 10)
                          .map((driver, index) => (
                            <Cell
                              key={`cell-${index}`}
                              fill={
                                driver.costPerKm < 10
                                  ? "#4CAF50"
                                  : driver.costPerKm < 15
                                    ? "#FFC107"
                                    : "#EF5350"
                              }
                            />
                          ))}
                      </Bar>
                    </BarChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="flex flex-col items-center justify-center h-full">
                    <BarChart2 className="h-12 w-12 text-gray-300 mb-4" />
                    <p className="text-gray-500">No data available</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Driver of the Month */}
          {summaryMetrics.topDriver && (
            <div className="fixed bottom-6 right-6 bg-white p-4 rounded-lg shadow-lg border-l-4 border-primary-500 max-w-xs animate-slide-up z-10">
              <div className="flex items-start space-x-3">
                <div className="flex-shrink-0 bg-primary-100 p-2 rounded-full">
                  <User className="h-6 w-6 text-primary-600" />
                </div>
                <div>
                  <h3 className="font-medium text-gray-900">
                    Driver of the Month
                  </h3>
                  <p className="text-lg font-bold text-primary-600">
                    {summaryMetrics.topDriver.name}
                  </p>
                  <p className="text-sm text-gray-600">
                    {summaryMetrics.topDriver.costPerKm.toFixed(2)}/km {" "}
                    {summaryMetrics.topDriver.totalTrips} trips
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>
      </Layout>
      
      {/* Driver Summary Modal */}
      {showDriverModal && selectedDriverForModal && (
        <DriverSummaryModal
          isOpen={showDriverModal}
          onClose={() => {
            setShowDriverModal(false);
            setSelectedDriverForModal(null);
          }}
          driver={selectedDriverForModal}
          trips={trips}
          vehicles={vehicles}
          maintenanceTasks={[]} // Add maintenance tasks if available
        />
      )}
    </>
  );
};

export default DriverInsightsPage;

import React, { createContext, useContext, useState, useEffect } from 'react';

type Theme = 'light' | 'dark';

interface ThemeContextType {
  theme: Theme;
  setTheme: (theme: Theme) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export const ThemeProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [theme, setTheme] = useState<Theme>(() => {
    // Check for saved theme preference only - no system preference check
    const savedTheme = localStorage.getItem('theme') as Theme;
    // Only use 'dark' if explicitly saved, otherwise always default to 'light'
    return savedTheme === 'dark' ? 'dark' : 'light';
  });

  useEffect(() => {
    // Update localStorage when theme changes
    localStorage.setItem('theme', theme);
    
    // Update document class for Tailwind dark mode
    if (theme === 'dark') {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = (): ThemeContextType => {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};
